--
-- Cấu trúc bảng cho bảng `product`
--

CREATE TABLE `product` (
  `id` int(11) NOT NULL,
  `product_name` varchar(100) NOT NULL,
  `price_new` int(50) NOT NULL,
  `price_old` int(50) NOT NULL,
  `qty_product` int(11) NOT NULL DEFAULT 30,
  `product_desc` text NOT NULL,
  `product_thumb` varchar(100) NOT NULL,
  `list_thumb_1` varchar(100) NOT NULL,
  `list_thumb_2` varchar(100) NOT NULL,
  `list_thumb_3` varchar(100) NOT NULL,
  `list_thumb_4` varchar(100) NOT NULL,
  `list_thumb_5` varchar(100) NOT NULL,
  `list_thumb_6` varchar(100) NOT NULL,
  `product_content` text NOT NULL,
  `cat_id` int(11) NOT NULL,
  `created_at` timestamp NOT NULL DEFAULT current_timestamp(),
  `selling_products` enum('Bình thường','Bán chạy') NOT NULL,
  `featured_products` enum('Bình thường','Nổi bật') NOT NULL DEFAULT 'Bình thường',
  `status` tinyint(4) NOT NULL DEFAULT 0
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_vietnamese_ci;

--
-- Đang đổ dữ liệu cho bảng `product`
--

INSERT INTO `product` (`id`, `product_name`, `price_new`, `price_old`, `qty_product`, `product_desc`, `product_thumb`, `list_thumb_1`, `list_thumb_2`, `list_thumb_3`, `list_thumb_4`, `list_thumb_5`, `list_thumb_6`, `product_content`, `cat_id`, `created_at`, `selling_products`, `featured_products`, `status`) VALUES
(2, 'Dell G3 3579-G5I58564 (Black) | i5-8300H | 8GB DDR4 | 256GB SSD', 20900000, 21900000, 4, 'CPU: Intel Core i5-8300H (2.3 GHz 4.0 GHz/8MB/4 nhân, 8 luồng)\r\n\r\nMàn hình: 15.6\" IPS (1920 x 1080) , không cảm ứng\r\n\r\nRAM: 1 x 4GB DDR4 2666MHz\r\n\r\nĐồ họa: Intel UHD Graphics 630/ NVIDIA GeForce GTX 1050 4GB GDDR5\r\n\r\nLưu trữ: 1TB HDD 5400RPM\r\n\r\nHệ điều hành: Ubuntu\r\n\r\nPin: 4 cell 56 Wh Pin liền                                                ', 'dell-g3-3579.png', 'dell-g3-3579-1.jpg', 'dell-g3-3579-2.jpg', 'dell-g3-3579-3.jpg', 'dell-g3-3579-4.jpg', 'dell-g3-3579-5.jpg', 'dell-g3-3579-6.jpg', '<h5><strong><span style=\"font-size:24px\">Thiết kế cứng c&aacute;p, độ bền cao</span></strong></h5>\r\n\r\n<p><strong>Laptop Dell Inspiron G3 G5I54114 Core i5-8300H/ Dos (15.6 inch FHD IPS) - H&agrave;ng Ch&iacute;nh H&atilde;ng</strong>&nbsp;đđược mệnh danh l&agrave; mẫu laptop gaming mỏng nhất của Dell. Sản phẩm c&oacute; thiết kế cứng c&aacute;p, với độ bền ấn tượng. D&ugrave; vẻ ngo&agrave;i kh&ocirc;ng qu&aacute; hầm hố nhưng mẫu laptop n&agrave;y vẫn thu h&uacute;t&nbsp; người d&ugrave;ng nhờ thiết kế tinh tế, t&ocirc;ng m&agrave;u tươi s&aacute;ng, nổi bật.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell_g3_content_1.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><strong><span style=\"font-size:24px\">M&agrave;n h&igrave;nh Full HD, IPS</span></strong></h5>\r\n\r\n<p>M&agrave;n h&igrave;nh 15.6 inch với độ ph&acirc;n giải Full HD (1920 x 1080) đem đến cho người d&ugrave;ng những trải nghiệm tuyệt vời khi l&agrave;m việc, học tập, giải tr&iacute; hay chơi game với những h&igrave;nh ảnh chi tiết sắc n&eacute;t, m&agrave;u sắc ch&acirc;n thực sống động.</p>\r\n\r\n<p>M&agrave;n h&igrave;nh của Dell G3 c&ograve;n được phủ một lớp chống ch&oacute;i gi&uacute;p sản phẩm hiển thị r&otilde; n&eacute;t h&igrave;nh ảnh ngay cả khi ở điều kiện &aacute;nh s&aacute;ng phức tạp, &aacute;nh s&aacute;ng ngo&agrave;i trời,...</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell_g3_content_2.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Cấu h&igrave;nh mạnh mẽ</strong></span></h5>\r\n\r\n<p>Sản phẩm được trang bị bộ xử l&yacute; Intel Core i5-8300H, RAM 4GB DDR4 đảm bảo xử l&yacute; mượt m&agrave;, ổn định c&aacute;c t&aacute;c vụ đa nhiệm. Ổ cứng dung lượng 1TB thoải m&aacute;i lưu trữ dữ liệu v&agrave; r&uacute;t ngắn thời gian khởi động m&aacute;y.&nbsp;</p>\r\n\r\n<p>Ngo&agrave;i ra, card đồ họa rời NVIDIA GTX1050 TI 4GB DDR5 hỗ trợ tối đa cho bạn trong những ứng dụng y&ecirc;u cầu t&iacute;nh đồ họa cao như thiết kế đồ họa chuy&ecirc;n nghiệp, chơi game y&ecirc;u cầu cấu h&igrave;nh đồ họa mạnh một c&aacute;ch mượt m&agrave;.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell_g3_content_3.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Khả năng kết nối đa dạng</strong></span></h5>\r\n\r\n<p>C&aacute;c cổng 2x SuperSpeed USB 3.1 Type-A, 1x USB 2.0, đảm bảo khả năng kết nối v&agrave; trao đổi dữ liệu giữa Laptop Dell Inspiron 3579 G3 G5I54114 với c&aacute;c thiết bị ngoại vi kh&aacute;c một c&aacute;ch nhanh ch&oacute;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell_g3_content_4.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m v&agrave; Touchpad</strong></span></h5>\r\n\r\n<p>B&agrave;n ph&iacute;m được thiết kế khoa học với vị tr&iacute; c&aacute;c ph&iacute;m hợp l&yacute;, khoảng c&aacute;ch giữa c&aacute;c ph&iacute;m vừa phải, ph&iacute;m bấm &ecirc;m tạo cảm gi&aacute;c thoải m&aacute;i cho người d&ugrave;ng trong từng thao t&aacute;c nhập liệu. Touchpad rộng với độ ch&iacute;nh x&aacute;c cao khiến việc k&eacute;o, di chuyển, ph&oacute;ng to thu nhỏ trở n&ecirc;n dễ d&agrave;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell_g3_content_5.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n', 1, '2019-07-05 15:08:13', 'Bình thường', 'Bình thường', 1),
(3, 'HP Envy 13-ah1011TU 5HZ28PA: Core i5-8265U / Windows 10', 20400000, 21900000, 30, 'Chip: Intel Core i5-8265U (1.6GHz up to 3.9GHz 6MB)\r\n\r\nRAM: 8GB LPDDR3 2133MHz (Onboard)\r\n\r\nỔ cứng: 256GB PCIe NVMe M.2 SSD\r\n\r\nChipset đồ họa: Intel UHD Graphics 620\r\n\r\nMàn hình: 13.3 inch Full HD (1920 x 1080) IPS, BrightView Micro-Edge\r\n\r\nHệ điều hành: Windows 10 Home\r\n\r\nPin: 4 Cell 53WHr                        ', 'hpenvy.jpg', 'hp1-1.jpg', 'hp1-2.jpg', 'hp1-3.jpg', 'hp1-4.jpg', 'hp1-5.jpg', 'hp1-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế tinh tế</strong></span></h5>\r\n\r\n<p>Laptop HP Envy 13-ah1011TU 5HZ28PA&nbsp;được thiết kế kiểu d&aacute;ng si&ecirc;u mỏng với đ&ocirc;̣ dày chỉ 14.9mm, với bốn g&oacute;c cạnh được bo tr&ograve;n vừa phải, mang đến vẻ đẹp mạnh mẽ v&agrave; đẳng cấp cho chiếc m&aacute;y, cùng với vẻ ngo&agrave;i đơn giản m&agrave; cứng c&aacute;p tạo cảm gi&aacute;c chắc tay cho người d&ugrave;ng khi cầm nắm.&nbsp;Mọi chi tiết đường n&eacute;t được gia c&ocirc;ng tinh xảo, thời trang, kết hợp với m&agrave;u sắc nhẹ nh&agrave;ng, trẻ trung.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/hp-content-1.jpg\" style=\"height:452px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh 13.3 inch&nbsp;Full HD</strong></span></h5>\r\n\r\n<p>Sở hữu m&agrave;n h&igrave;nh 13.3 inch gi&uacute;p hiển thị h&igrave;nh ảnh Full HD (1920 x 1080) để xem mọi thứ tốt hơn, cùng c&ocirc;ng nghệ màn hình BrightView Micro-Edge c&oacute; độ tương phản &aacute;nh s&aacute;ng ho&agrave;n hảo, m&agrave;u sắc tốt tăng cường chất lượng h&igrave;nh ảnh vượt trội mang đến trải nghiệm xem phim, chơi game đồ họa cực đỉnh, giải tr&iacute; hấp dẫn tuyệt vời hơn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/hp-content-2.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<p>Tấm nền IPS gi&uacute;p bạn c&oacute; thể trải nghiệm g&oacute;c nh&igrave;n rộng 178&deg; đem lại h&igrave;nh ảnh sắc n&eacute;t, sống động v&agrave; độ tương phản cao ngay cả ở những g&oacute;c nh&igrave;n hẹp.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bộ vi xử l&yacute; Intel Core i5</strong></span></h5>\r\n\r\n<p>Được trang bị bộ vi xử l&yacute; Intel Core i5-8265U tốc độ l&ecirc;n đ&ecirc;́n 3.9GHz, đem lại cho m&aacute;y một sức mạnh xử l&yacute; ổn định v&agrave; tiết kiệm điện năng hiệu quả. C&ugrave;ng bộ nhớ RAM LPDDR3 dung lượng 8GB cho hiệu suất xử l&yacute; tốt cũng như đ&aacute;p ứng c&aacute;c t&aacute;c vụ đa nhiệm mượt m&agrave; c&aacute;c ứng dụng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/hp-content-3.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<p>Chipset đồ họa tích hợp Intel UHD Graphics 620 hỗ trợ xử l&yacute; hiệu ứng h&igrave;nh ảnh 3D c&aacute;c c&ocirc;ng việc li&ecirc;n quan đến đồ hoạ, giải tr&iacute; của bạn. Ngo&agrave;i ra, HP c&ograve;n trang bị cho m&aacute;y ổ cứng SSD dung lượng 256GB PCIe NVMe M.2 gi&uacute;p m&aacute;y hoạt động mượt m&agrave; hơn, khởi động nhanh ch&oacute;ng hơn.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>&Acirc;m thanh r&otilde; r&agrave;ng</strong></span></h5>\r\n\r\n<p>Được trang bị 4 loa v&agrave; t&iacute;ch hợp cả 2 c&ocirc;ng nghệ lừng danh Bang &amp; Olufsen + HP Audio Boost cho chất lượng &acirc;m thanh to r&otilde;, tuyệt vời cung cấp &acirc;m thanh chuy&ecirc;n nghiệp, tự động điều chỉnh &acirc;m thanh của bạn với độ cao r&otilde; n&eacute;t hơn, tăng &acirc;m trầm v&agrave; tăng &acirc;m lượng để mọi thứ m&agrave; bạn nghe đều ch&iacute;nh x&aacute;c theo c&aacute;ch bạn mu&ocirc;́n, bạn sẽ được trải nghiệm chất lượng &acirc;m thanh đỉnh cao.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Tr&ograve; chuyện thỏa th&iacute;ch</strong></span></h5>\r\n\r\n<p>HD Webcam c&oacute; độ ph&acirc;n giải 1MP cho h&igrave;nh ảnh kh&aacute; tốt. Nhờ đ&oacute; bạn c&oacute; thể chụp ảnh &ldquo;tự sướng&rdquo; v&agrave; chia sẻ h&igrave;nh ảnh với bạn b&egrave; qua c&aacute;c mạng x&atilde; hội như Facebook, Twitter &hellip;Ngo&agrave;i ra, bạn cũng c&oacute; thể tr&ograve; chuyện thỏa th&iacute;ch với người th&acirc;n qua c&aacute;c ứng dụng như: Skype, yahoo chat.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hệ điều h&agrave;nh Windows 10</strong></span></h5>\r\n\r\n<p>M&aacute;y được c&agrave;i sẵn hệ điều h&agrave;nh Windows 10 tiện &iacute;ch, gi&uacute;p m&aacute;y chạy mượt m&agrave;. Hệ điều h&agrave;nh sẽ hỗ trợ hầu hết những phần mềm hiện nay n&ecirc;n rất tiện cho c&ocirc;ng việc.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m t&iacute;ch hợp hệ thống đ&egrave;n nền v&agrave; touchpad</strong></span></h5>\r\n\r\n<p>B&agrave;n ph&iacute;m của m&aacute;y với c&aacute;c ph&iacute;m k&iacute;ch thước kh&aacute; lớn, khoảng c&aacute;ch giữa c&aacute;c ph&iacute;m hợp l&yacute; v&agrave; được sắp xếp hợp l&yacute; mang lại trải nghiệm tốt cho người d&ugrave;ng, b&agrave;n ph&iacute;m c&ograve;n được t&iacute;ch hợp hệ thống đ&egrave;n nền ở ph&iacute;a dưới gi&uacute;p người d&ugrave;ng thao t&aacute;c tốt hơn trong điều kiện thiếu s&aacute;ng. Touchpad cũng kh&aacute; rộng r&atilde;i, b&agrave;n r&ecirc; mịn cho cảm gi&aacute;c chạm vuốt kh&aacute; thoải m&aacute;i.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/hp-content-4.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Thời lượng pin l&acirc;u hơn</strong></span></h5>\r\n\r\n<p>Được thiết kế để ph&ugrave; hợp với phong c&aacute;ch sống năng động hiện nay, HP Envy 13-ah1011TU 5HZ28PA sẽ cung cấp cho bạn thời lượng pin để c&oacute; thể sử dụng suốt cả ng&agrave;y. Với c&ocirc;ng nghệ pin Lithium Polymer sẽ cho m&aacute;y khả năng vận h&agrave;nh li&ecirc;n tục l&ecirc;n đến 7 tiếng đồng hồ. Điểm đặc biệt của d&ograve;ng pin Lithium Polymer c&oacute; thể duy tr&igrave; được năng lượng sạc lần đầu cho d&ugrave; sau h&agrave;ng trăm chu kỳ sạc, l&acirc;u hơn gấp 3 lần so với c&aacute;c loại pin ti&ecirc;u chuẩn hiện nay, gi&uacute;p bạn khỏi phải lo lắng về hao m&ograve;n hao m&ograve;n điện năng pin.</p>\r\n', 3, '2019-06-12 04:20:12', 'Bình thường', 'Bình thường', 1),
(4, 'Dell G7 7588 N7588D Core i7-8750H/ Free Dos (15.6 inch) - Đen', 26390000, 28900000, 11, 'Chip: Intel Core i7-8750H (2.2GHz up to 4.1GHz, 9MB)\r\n\r\nRAM: 8GB DDR4 2400MHz\r\n\r\nỔ cứng: HDD 1TB 5400rpm + 128GB SSD M.2\r\n\r\nChipset đồ họa: NVIDIA GeForce GTX 1050Ti 4GB GDDR5\r\n\r\nMàn hình: 15.6 inch Full HD (1920 x 1080) IPS, Anti-Glare, LED Backlit Display\r\n\r\nHệ điều hành: Free DOS\r\n\r\nPin: 4 Cell 56 Whrs', 'dell-g7-7588.png', 'dell-g7-7588 (1).png', 'dell-g7-7588-1.jpg', 'dell-g7-7588-2.jpg', 'dell-g7-7588-3.jpg', 'dell-g7-7588-4.jpg', 'dell_g7.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế hợp kim magi&ecirc; si&ecirc;u bền</strong></span></h5>\r\n\r\n<p><strong>Laptop Dell G7 7588 N7588D Core i7-8750H/ Free Dos</strong>&nbsp;mang t&iacute;nh di động với độ d&agrave;y 25mm d&agrave;nh cho d&ograve;ng laptop gaming, cấu tạo bền bỉ với hợp kim magi&ecirc; si&ecirc;u bền bao bọc xung quanh khung m&aacute;y đem lại độ cứng c&aacute;p tốt nhất cũng như khả năng chơi game mạnh mẽ mọi l&uacute;c mọi nơi. Thiết kế logo ấn tượng với t&ocirc;ng m&agrave;u xanh bằng kim loại b&oacute;ng ho&agrave;n to&agrave;n mới chưa từng c&oacute; tr&ecirc;n laptop gaming tầm trung trước đ&acirc;y.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dellg7/dell-g7-ct-1.jpg\" style=\"height:452px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh LED&nbsp;15.6 inch&nbsp;Full HD,&nbsp;IPS</strong></span></h5>\r\n\r\n<p>Màn hình 15.6 inch với đ&ocirc;̣ ph&acirc;n giải đạt mức Full HD (1920 x 1080) v&agrave; sử dụng card đồ họa NVIDIA GeForce GTX 1060 c&ocirc;ng nghệ Max-Q l&ecirc;n đến 4GB bộ nhớ video GDDR5, gameplay r&otilde; r&agrave;ng v&agrave; chi tiết. Dell G7 7588 N7588D&nbsp;sử dụng c&ocirc;ng nghệ màn hình LED-Backlit mang đến những h&igrave;nh ảnh chi tiết, r&otilde; n&eacute;t, m&agrave;u sắc cũng cực kỳ trung thực v&agrave; sống động.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dellg7/dell-g7-ct-2.jpg\" style=\"height:400px; width:750px\" /></p>\r\n\r\n<p>Ngo&agrave;i ra, m&agrave;n h&igrave;nh IPS c&ograve;n cung cấp g&oacute;c nh&igrave;n l&ecirc;n tới 178 độ so với phương ngang, điều n&agrave;y c&oacute; nghĩa l&agrave; người d&ugrave;ng kh&ocirc;ng nhất thiết phải ngồi trực diện vẫn c&oacute; thể trải nghiệm hết chất lượng của h&igrave;nh ảnh, tấm nền chống ch&oacute;i Anti-Glare gi&uacute;p bạn c&oacute; thể trải nghiệm tốt hơn ở m&ocirc;i trường b&ecirc;n ngo&agrave;i, c&oacute; khả năng chống ch&oacute;i tốt.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bộ vi xử l&yacute; Intel Core i7</strong></span></h5>\r\n\r\n<p>M&aacute;y được trang bị bộ vi xử l&yacute; Intel Core i7-8750H với xung nhịp 4.1GHz, cho bạn những gi&acirc;y ph&uacute;t lướt web nhanh hơn, mạnh mẽ hơn. B&ecirc;n cạnh đ&oacute;, với bộ nhớ trong Ram 8GB DDR4 (2400MHz) đảm bảo xử l&iacute; c&aacute;c t&aacute;c vụ đa nhiệm thật mượt m&agrave; v&agrave; ổn định. Ngo&agrave;i ra, m&aacute;y c&ograve;n trang bị th&ecirc;m &ocirc;̉ cứng kép 1TB HDD + 128GB SSD, cho bạn lưu trữ cũng như vận h&agrave;nh m&aacute;y một c&aacute;ch nhanh ch&oacute;ng, mượt m&agrave; hơn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dellg7/dell-g7-ct-3.jpg\" style=\"height:340px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>&Acirc;m thanh&nbsp;r&otilde; n&eacute;t&nbsp;với Waves MaxxAudio</strong></span></h5>\r\n\r\n<p>Waves MaxxAudio Pro cung cấp &acirc;m thanh chuy&ecirc;n nghiệp, tự động điều chỉnh &acirc;m thanh của bạn với độ cao r&otilde; n&eacute;t hơn, tăng &acirc;m trầm v&agrave; tăng &acirc;m lượng để mọi thứ m&agrave; bạn nghe đều ch&iacute;nh x&aacute;c theo c&aacute;ch bạn mu&ocirc;́n, bạn sẽ được trải nghiệm chất lượng &acirc;m thanh đỉnh cao với chuy&ecirc;n gia &acirc;m thanh Waves MaxxAudio Pro đ&ocirc;̣c quy&ecirc;̀n từ Dell G7 7588 N7588D.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m chắc chắn</strong></span></h5>\r\n\r\n<p>Ngo&agrave;i ra, m&aacute;y được trang bị b&agrave;n ph&iacute;m chắc chắn, kh&ocirc;ng c&oacute; hiện tượng Flex khi ấn ph&iacute;m với c&aacute;c ph&iacute;m bấm được sắp xếp khoa học, k&iacute;ch thước v&agrave; khoảng c&aacute;ch hợp l&iacute;, độ nảy ph&iacute;m tốt gi&uacute;p người d&ugrave;ng nhập liệu nhanh ch&oacute;ng v&agrave; ch&iacute;nh x&aacute;c. Kết hợp với đ&egrave;n nền m&agrave;u xanh rất ấn tượng với c&aacute;c ph&iacute;m WASD nổi bật. Touchpad của d&ograve;ng Dell G7 7588 N7588D&nbsp;c&oacute; thiết kế rộng r&atilde;i, mịn m&agrave;ng, cực nhạy, gi&uacute;p bạn thao t&aacute;c đơn giản, dễ d&agrave;ng v&agrave; ch&iacute;nh x&aacute;c tương tự như sử dụng chuột rời.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dellg7/dell-g7-ct-4.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Khe tản nhiệt lớn v&agrave; c&aacute;ch t&acirc;n&nbsp;</strong></span></h5>\r\n\r\n<p>Dell rất quan t&acirc;m vấn đề tản nhiệt cho sản phẩm Gaming, đảm bảo hệ thống kh&ocirc;ng bị t&igrave;nh trạng qu&aacute; tải nhiệt khi chơi game trong khi vẫn giữ được sự im lặng v&agrave; y&ecirc;n tĩnh. Dell G7 7588 N7588D&nbsp;c&oacute; thiết kế khe tản nhiệt tinh tế với biểu tưởng G7 l&agrave;m cho phần khe tản nhiệt th&ecirc;m lung linh hơn v&agrave; cho khả năng tản nhiệt tốt hơn. Kết hợp với lớp lưới m&agrave;u xanh b&ecirc;n trong gi&uacute;p ngăn bụi hiệu quả v&agrave; tặng th&ecirc;m vẻ đẹp cho d&ograve;ng Gaming n&agrave;y.&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dellg7/dell-g7-ct-5.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<p>Trang bị 2 quạt tản nhiệt lớn (tr&ecirc;n - dưới) gi&uacute;p m&aacute;y hoạt động m&aacute;t mẻ hơn v&agrave; 2 ống đồng, đồng thời gi&uacute;p cho việc n&acirc;ng cấp cực kỳ đơn giản, chỉ cần th&aacute;o 1 ốc l&agrave; bạn c&oacute; thể thay thế hoặc n&acirc;ng cấp to&agrave;n bộ linh kiện b&ecirc;n trong m&aacute;y. Mặt sau được trang bị 2 thanh cao su lớn, gi&uacute;p m&aacute;y chắc chắn tr&ecirc;n mặt phẳng, đồng thời n&acirc;ng đỡ m&aacute;y gi&uacute;p tản nhiệt được tốt hơn.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hỗ trợ nhiều cổng kết nối</strong></span></h5>\r\n\r\n<p>Laptop sở hữu cổng kết nối USB Type C đa dụng nhất hiện nay, với thiết kế đồng đẳng gi&uacute;p bạn c&oacute; thể cắm cổng kết nối ở bất cứ chiều n&agrave;o. Ngo&agrave;i ra với c&ocirc;ng nghệ n&agrave;y sẽ cho tốc độ truyền tải dữ liệu nhanh gấp 10 lần so với c&aacute;c cổng kết nối cũ hiện nay. Với chuẩn wifi 802.11 AC (2X2) cho tốc độ kết nối nhanh ch&oacute;ng v&agrave; hỗ trợ nhiều cổng kết nối như HDMI, Bluetooth, khe thẻ nhớ, jack tai nghe cho khả năng kết nối với c&aacute;c thiết bị kh&aacute;c một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dellg7/dell-g7-ct-6.jpg\" style=\"height:415px; width:750px\" /></p>\r\n\r\n<p>Thiết kế 2 khe RAM hỗ trợ n&acirc;ng cấp đến 32GB v&agrave; hỗ trợ SSD PCIe chạy nhanh hơn gấp 4 lần so với SATA th&ocirc;ng thường, tải ứng dụng hoặc dữ liệu nhanh hơn, chạy m&aacute;y nhanh hơn.&nbsp;</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Thời lượng pin l&acirc;u hơn</strong></span></h5>\r\n\r\n<p>Với vi&ecirc;n Pin 4 cell&nbsp;Dell G7 7588 N7588D&nbsp;cho thời gian sử dụng tương đối. C&ocirc;ng nghệ tiết kiệm pin hoạt động m&aacute;t, &ecirc;m &aacute;i v&agrave; tiết kiệm điện năng n&ecirc;n c&oacute; thể đ&aacute;p ứng tốt nhu cầu sử dụng b&igrave;nh thường của bạn.&nbsp;Với c&ocirc;ng nghệ sạc nhanh, pin của&nbsp;Dell G7 7588 N7588D&nbsp;sẽ sạc được 25% trong v&ograve;ng 15 ph&uacute;t, gi&uacute;p bạn kh&ocirc;ng bỏ lỡ bất kỳ khoảng khắc gay cấn n&agrave;o khi chơi game.</p>\r\n', 1, '2019-07-07 07:07:48', 'Bán chạy', 'Bình thường', 1),
(5, 'Dell Inspiron 5379-JYN0N1 | i5-8250U | 8GB DDR4 | 1TB HDD', 20300000, 23760000, 18, 'Bộ vi xử lý: Intel Core i5-8250U 1.6Ghz\r\n\r\nRAM: 8GB DDR4 2400MHz\r\n\r\nỔ cứng: 1TB SATA3 5400rpm\r\n\r\nCard đồ họa: Intel UHD Graphics 620\r\n\r\nMàn hình: Cảm ứng 13.3 inch FHD (1920 x 1080)\r\n\r\nHệ điều hành: Windows 10\r\n\r\nPin: 3 Cells 42WHrs', 'dell-inspiron-53781.jpg', 'dell-inspiron-53781 (1).jpg', 'dell-inspiron-5378-51.jpg', 'dell-inspiron-5378-11.jpg', 'dell-inspiron-5378-21.jpg', 'dell-inspiron-5378-31.jpg', 'dell-inspiron-5378-41.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế bẻ gập, chắc chắn</strong></span></h5>\r\n\r\n<p><strong>Laptop DELL Inspiron 13 5379 JYN0N1</strong>&nbsp;sở hữu kiểu d&aacute;ng vu&ocirc;ng vắn sang trọng, mạnh mẽ với những đường n&eacute;t sắc sảo kết hợp những g&oacute;c bo chắc chắn. Đặc biệt, d&ograve;ng laptop n&agrave;y c&ograve;n được thiết kế c&oacute; thể bẻ gập để bạn c&oacute; thể t&ugrave;y chỉnh t&ugrave;y theo nhu cầu sử dụng của m&igrave;nh. Khi bạn muốn xem phim hay cần tr&igrave;nh chiếu dữ liệu th&igrave; c&oacute; thể đặt m&aacute;y dạng lều hoặc đứng, khi kh&ocirc;ng sử dụng đến b&agrave;n ph&iacute;m bạn c&oacute; thể gập m&aacute;y lại dạng m&aacute;y t&iacute;nh bảng. B&ecirc;n cạnh đ&oacute;, laptop với k&iacute;ch thước nhỏ gọn, trọng lượng m&aacute;y nhẹ gi&uacute;p bạn dễ d&agrave;ng mang theo l&agrave;m việc, học tập, cũng như vui chơi giải tr&iacute;.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-2/inri-ct-1.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Cấu h&igrave;nh vượt ngo&agrave;i mong đợi</strong></span></h5>\r\n\r\n<p>Với việc trang bị chip xử l&yacute; core i5 Kaby Lake thế hệ thứ 8 gi&uacute;p m&aacute;y lu&ocirc;n đảm bảo khả năng hoạt động ở tốc độ cao v&agrave; hiệu quả. RAM 8GB, tốc độ CPU 1.6GHz v&agrave; c&oacute; thể đạt tối đa 3.4GHz mang đến khả năng xử l&yacute; nhanh ch&oacute;ng nhiều c&ocirc;ng việc c&ugrave;ng một l&uacute;c gi&uacute;p bạn n&acirc;ng cao năng suất l&agrave;m việc cũng như dễ d&agrave;ng chinh phục c&aacute;c tr&ograve; chơi y&ecirc;u cầu cấu h&igrave;nh đồ họa nặng. Ngo&agrave;i ra, ổ cứng c&oacute; dung lượng ấn lượng l&ecirc;n đến 1TB gi&uacute;p người d&ugrave;ng y&ecirc;n t&acirc;m lưu trữ nhiều bộ phim, b&agrave;i h&aacute;t y&ecirc;u th&iacute;ch v&agrave; dữ liệu c&aacute; nh&acirc;n tiện lợi, an to&agrave;n.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-2/inri-ct-2.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh 13.3 inch chất lượng Full HD</strong></span></h5>\r\n\r\n<p>M&agrave;n h&igrave;nh cảm ứng 13.3 inch, chất lượng Full HD&nbsp;1920 x 1080 pixels với tấm nền&nbsp;IPS mang đến những h&igrave;nh ảnh sống động, sắc n&eacute;t gi&uacute;p bạn tận hưởng những bộ phim HD với chất lượng ở mức cao nhất. Ngo&agrave;i ra, m&agrave;n h&igrave;nh cảm ứng cho ph&eacute;p bạn thực hiện c&aacute;c t&aacute;c vụ mượt m&agrave; như tr&ecirc;n smartphone, m&aacute;y t&iacute;nh bảng gi&uacute;p bạn c&oacute; những trải nghiệm mới mẻ, th&uacute; vị tr&ecirc;n laptop. B&ecirc;n cạnh đ&oacute;, dưới sự hỗ trợ của&nbsp;Card đồ họa Intel UHD Graphics 620, người d&ugrave;ng cũng c&oacute; thể thoải m&aacute;i thao t&aacute;c tr&ecirc;n c&aacute;c phần mềm đồ họa hoặc c&aacute;c game đ&ograve;i hỏi cao về mặt cấu h&igrave;nh.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-2/inri-ct-3.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>C&agrave;i đặt sẵn Win 10 v&agrave; MS Office</strong></span></h5>\r\n\r\n<p>M&aacute;y đ&atilde; được c&agrave;i đặt sẵn hệ điều h&agrave;nh Windows 10 v&agrave; phần mềm văn ph&ograve;ng MS Office bản quyền hoạt động ổn định, nhằm mang đến những trải nghiệm mới v&agrave; thoải m&aacute;i nhất cho người d&ugrave;ng. Đặc biệt, ưu điểm nổi trội n&agrave;y c&ograve;n gi&uacute;p c&aacute; nh&acirc;n, cơ quan, tổ chức c&oacute; thể n&acirc;ng cao an to&agrave;n bảo mật th&ocirc;ng tin, tr&aacute;nh những rắc rối về bản quyền điều h&agrave;nh đồng thời gi&uacute;p bạn tiết kiệm một khoảng chi ph&iacute; c&agrave;i đặt vẫn c&oacute; thể sử dụng được c&aacute;c ứng dụng của Microsoft trong thời gian d&agrave;i.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-2/inri-ct-4.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Đầy đủ c&aacute;c cổng kết nối</strong></span></h5>\r\n\r\n<p>DELL Inspiron 13 5379 JYN0N1 cũng đ&atilde; được trang bị đầy đủ c&aacute;c cổng kết nối th&ocirc;ng dụng hiện nay như USB 2.0, USB 3.0, HDMI, jack tai nghe 3.5mm gi&uacute;p bạn kết nối dễ d&agrave;ng, đ&aacute;p ứng tối đa nhu cầu sử dụng. B&ecirc;n cạnh đ&oacute;, b&agrave;n ph&iacute;m laptop được thiết kế chuẩn mang đến độ nảy &ecirc;m &aacute;i, tốc độ phản hồi nhanh ch&oacute;ng gi&uacute;p bạn n&acirc;ng cao tốc độ xử l&yacute; c&ocirc;ng việc cũng như mang đến cảm gi&aacute;c thoải m&aacute;i, giảm t&igrave;nh trạng đau nhức tay khi sử dụng m&aacute;y trong thời gian d&agrave;i. Hơn thế nữa, b&agrave;n ph&iacute;m c&ograve;n được trang bị đ&egrave;n nền cho người d&ugrave;ng dễ d&agrave;ng g&otilde; ph&iacute;m trong điều kiện thiếu &aacute;nh s&aacute;ng gi&uacute;p bạn c&oacute; thể l&agrave;m việc v&agrave;o ban đ&ecirc;m cũng như chinh phục c&aacute;c tr&ograve; chơi y&ecirc;u th&iacute;ch kh&ocirc;ng ảnh hưởng đến mọi người xung quanh.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-2/inri-ct-5.jpg\" style=\"height:500px; width:750px\" /></p>\r\n', 1, '2019-06-12 06:38:16', 'Bình thường', 'Bình thường', 1),
(6, 'Dell Inspiron 5482-C2CPX1 (Silver) | i7-8565U | 8GB DDR4 | 256GB SSD', 25000000, 27900000, 30, 'Chip: Intel Core i7-8565U (1.80GHz Up to 4.60 GHz, 4Cores, 8Threads, 8MB Cache, FSB 4GT/s)\r\n\r\nRAM: 8GB DDR4 2666MHz, Max 16GB\r\n\r\nỔ cứng: 256GB SSD M.2 Sata + 1 Slot HDD 5400rpm\r\n\r\nChipset đồ họa: NVIDIA GeForce MX130 2GB GDDR5 + Intel UHD Graphics 620\r\n\r\nMàn hình: 14 inch Full HD (1920 x 1080) IPS LED-Backlit Touch Display Pen Enabled\r\n\r\nHệ điều hành: Windows 10 Home + Office 365 bản quyền\r\n\r\nPin: 3-Cell 42Whr', 'Dell-Inspiron-5482-silver1.jpg', 'Dell-Inspiron-5482-silver1 (1).jpg', 'Dell-Inspiron-5482-silver-1.jpg', 'Dell-Inspiron-5482-silver-2.jpg', 'Dell-Inspiron-5482-silver-3.jpg', 'Dell-Inspiron-5482-silver-4.jpg', 'Dell-Inspiron-5482-silver-5.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế gập 180 độ</strong></span></h5>\r\n\r\n<p><strong>Laptop Dell Inspiron 5482 C2CPX1</strong>&nbsp;mang đến cho người d&ugrave;ng một chiếc laptop mạnh mẽ v&agrave; bền vững nhờ được bao bọc bởi bộ khung chắc chắn. Lớp vỏ ngo&agrave;i l&agrave;m từ chất liệu cao cấp vừa mang lại một độ bền vượt trội để đủ sức bảo vệ phần m&agrave;n h&igrave;nh v&agrave; c&aacute;c linh kiện b&ecirc;n trong nhưng vẫn đảm bảo tối ưu sự linh hoạt v&agrave; tiện dụng cho người d&ugrave;ng mỗi khi mang theo m&aacute;y b&ecirc;n m&igrave;nh. Ngo&agrave;i ra, bản lề của m&aacute;y được thiết kế đặc biệt gi&uacute;p m&aacute;y c&oacute; thể gập 180 độ như quyển tập.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-2/1-1.jpg\" style=\"height:404px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh cảm ứng&nbsp;14 inch Full HD</strong></span></h5>\r\n\r\n<p>Sở hữu m&agrave;n h&igrave;nh 14 inch gi&uacute;p hiển thị h&igrave;nh ảnh&nbsp;Full HD (1920 x 1080) để xem mọi thứ tốt hơn, cùng c&ocirc;ng nghệ màn hình LED mang đến những h&igrave;nh ảnh chất lượng, chi tiết, r&otilde; n&eacute;t, m&agrave;u sắc cũng cực kỳ trung thực v&agrave; sống động. Tấm nền IPS gi&uacute;p bạn c&oacute; thể trải nghiệm g&oacute;c nh&igrave;n rộng 178&deg; đem lại h&igrave;nh ảnh sắc n&eacute;t, sống động v&agrave; độ tương phản cao ngay cả ở những g&oacute;c nh&igrave;n hẹp.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-2/1-2.jpg\" style=\"height:390px; width:750px\" /></p>\r\n\r\n<p>C&ocirc;ng ngh&ecirc;̣ màn hình cảm ứng được tích hợp tr&ecirc;n Dell Inspiron 5482 C2CPX1&nbsp;giúp bạn ki&ecirc;̉m soát mọi thứ trong t&acirc;̀m tay chỉ c&acirc;̀n trượt, ph&oacute;ng to, thu nhỏ hoặc t&ugrave;y chỉnh c&aacute;c c&ocirc;ng cụ bằng cảm ứng đa điểm, đơn giản như c&aacute;ch bạn sử dụng chiếc điện thoại th&ocirc;ng minh.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bộ vi xử l&yacute; Intel Core i7</strong></span></h5>\r\n\r\n<p>M&aacute;y được trang bị bộ vi xử l&yacute; Intel Core i7-8565U với xung nhịp 4.6GHz, cho bạn những gi&acirc;y ph&uacute;t lướt web nhanh hơn, mạnh mẽ hơn. B&ecirc;n cạnh đ&oacute;, với bộ nhớ trong Ram 8GB DDR4 2666MHz đảm bảo xử l&iacute; c&aacute;c t&aacute;c vụ đa nhiệm thật mượt m&agrave; v&agrave; ổn định. Ngo&agrave;i ra, m&aacute;y c&ograve;n trang bị th&ecirc;m bộ nhớ 256GB SSD, cho bạn lưu trữ cũng như vận h&agrave;nh m&aacute;y một c&aacute;ch nhanh ch&oacute;ng, mượt m&agrave; hơn.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Card đồ họa NVIDIA GeForce MX130</strong></span></h5>\r\n\r\n<p>Đặc biệt, với card đồ họa NVIDIA GeForce MX130 bạn c&oacute; thể ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m rằng m&aacute;y c&oacute; thể h&ocirc;̃ trợ bạn tối đa từ c&aacute;c việc đơn giản đến c&aacute;c c&ocirc;ng&nbsp; việc mang t&iacute;nh chất đ&ograve;i hỏi cao như thiết kế đồ họa, 3D, photoshop,... cũng như giải tr&iacute; với những game 3D khủng nhất hiện nay.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>&Acirc;m thanh&nbsp;r&otilde; n&eacute;t&nbsp;với Waves MaxxAudio Pro</strong></span></h5>\r\n\r\n<p>Waves MaxxAudio Pro cung cấp &acirc;m thanh chuy&ecirc;n nghiệp, tự động điều chỉnh &acirc;m thanh của bạn với độ cao r&otilde; n&eacute;t hơn, tăng &acirc;m trầm v&agrave; tăng &acirc;m lượng để mọi thứ m&agrave; bạn nghe đều ch&iacute;nh x&aacute;c theo c&aacute;ch bạn mu&ocirc;́n, bạn sẽ được trải nghiệm chất lượng &acirc;m thanh đỉnh cao với chuy&ecirc;n gia &acirc;m thanh Waves MaxxAudio Pro đ&ocirc;̣c quy&ecirc;̀n từ Dell Inspiron 5482 C2CPX1.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m độ nảy cao, ph&iacute;m &ecirc;m v&agrave; touchpad rộng r&atilde;i</strong></span></h5>\r\n\r\n<p>Hệ thống b&agrave;n ph&iacute;m được thiết kế với khoảng c&aacute;ch vừa phải, rộng r&atilde;i, độ nảy cao, ph&iacute;m &ecirc;m, b&agrave;n ph&iacute;m chữ v&agrave; số thuận tiện gi&uacute;p bạn đ&aacute;nh m&aacute;y hay g&otilde; ph&iacute;m một c&aacute;ch hiệu quả nhất. Touchpad của d&ograve;ng Dell Inspiron 5482 C2CPX1&nbsp;được thiết kế rộng r&atilde;i, mịn m&agrave;ng, cực nhạy, gi&uacute;p bạn thao t&aacute;c đơn giản, dễ d&agrave;ng v&agrave; ch&iacute;nh x&aacute;c tương tự như sử dụng chuột rời.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hỗ trợ nhiều cổng kết nối</strong></span></h5>\r\n\r\n<p>Hỗ trợ nhiều cổng kết nối như cổng HDMI, Bluetooth, khe thẻ nhớ, jack tai nghe cho khả năng kết nối với c&aacute;c thiết bị kh&aacute;c một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-2/1-3.jpg\" style=\"height:382px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Thời lượng pin l&acirc;u hơn</strong></span></h5>\r\n\r\n<p>Với vi&ecirc;n Pin 3 cell Dell Inspiron 5482 C2CPX1&nbsp;cho thời gian sử dụng tương đối. C&ocirc;ng nghệ tiết kiệm pin hoạt động m&aacute;t, &ecirc;m &aacute;i v&agrave; tiết kiệm điện năng n&ecirc;n c&oacute; thể đ&aacute;p ứng tốt nhu cầu sử dụng b&igrave;nh thường của bạn.</p>\r\n', 1, '2019-06-12 06:38:16', 'Bình thường', 'Bình thường', 1),
(7, 'Dell Vostro 3578 V3578B Core i5-8250U/ Win10 (Black)/4GB DDr4 Bus\r\n', 15229000, 14729000, 30, 'CPU: Intel Core i5 8250U Processor (1.60 GHz, 6M Cache, up to 3.40 GHz)\r\n\r\nRAM : 4GB DDr4 Bus 2400MHz\r\n\r\nỔ cứng: HDD 1TB (1000GB) 5400rpm Hard Drive Sata\r\n\r\nChip set đồ họa: AMD Radeon 520 Graphics with 2GB GDDR5 / Integrated Intel UHD Graphics\r\n\r\nMàn hình: 15.6 inch Full HD (1920 x 1080) Anti Glare LED Backlit Display\r\n\r\nHệ điều hành: Windows 10 Single Language\r\n\r\nPin: 4 Cells 40 Whrs Battery', 'dell2.jpg', 'dell2-1.jpg', 'dell2-2.jpg', 'dell2-3.jpg', 'dell2-4.jpg', 'dell2-5.jpg', 'dell2-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Kiểu d&aacute;ng mạnh mẽ</strong></span></h5>\r\n\r\n<p><strong>Laptop Dell Vostro 3578</strong>&nbsp;<strong>V3578B Core i5-8250U</strong>&nbsp;l&agrave; một d&ograve;ng m&aacute;y t&iacute;nh x&aacute;ch tay c&oacute; cấu h&igrave;nh kh&aacute;, cực kỳ ph&ugrave; hợp với c&aacute;c bạn học sinh, sinh vi&ecirc;n hay d&acirc;n văn ph&ograve;ng. Hơn thế nữa, Dell Vostro 3578 sở hữu kiểu d&aacute;ng mạnh mẽ c&ugrave;ng với m&agrave;n h&igrave;nh lớn&nbsp;tạo cho bạn một kh&ocirc;ng gian hiển thị thoải m&aacute;i hơn m&agrave; c&ograve;n mang ưu điểm hứa hẹn sẽ l&agrave; một trong những thiết bị học tập tuyệt vời cho bạn. Một số c&aacute;c chi tiết kh&aacute;c cũng được h&atilde;ng thiết kế v&ocirc; c&ugrave;ng kỹ lưỡng. Phần khung b&ecirc;n ngo&agrave;i chắc chắn v&agrave; cứng c&aacute;p nhằm đảm bảo độ bền v&agrave; gi&uacute;p k&eacute;o d&agrave;i tuổi thọ cho m&aacute;y t&iacute;nh của bạn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-3/ct-1.jpg\" style=\"height:399px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh 15.6 inches cho khả năng hiển thị tốt</strong></span></h5>\r\n\r\n<p>Dell Vostro 3578 sở hữu c&ocirc;ng nghệ m&agrave;n h&igrave;nh LED-backlit với h&igrave;nh ảnh hiển thị tr&ecirc;n m&agrave;n h&igrave;nh to 15.6 inches gi&uacute;p c&aacute;c ph&ocirc;ng chữ v&agrave; h&igrave;nh ảnh cực kỳ sắc n&eacute;t với g&oacute;c nh&igrave;n rộng chất lượng v&agrave; chi tiết, m&agrave;u sắc hết sức trung thực, sống động khi độ ph&acirc;n giải m&agrave;n h&igrave;nh l&ecirc;n đến FHD (1920x1080). Bạn v&agrave; gia đ&igrave;nh c&oacute; thể c&ugrave;ng với laptop Dell Vostro 3578 thưởng thức những bộ phim hay, tham gia c&aacute;c tr&ograve; chơi hấp dẫn v&agrave; thoải m&aacute;i nhất.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-3/ct-2.jpg\" style=\"height:373px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hiệu năng xử l&yacute; ấn tượng</strong></span></h5>\r\n\r\n<p>Dell Vostro 3578 đem lại hiệu năng hoạt động ổn định nhờ bộ vi xử l&yacute; Intel Core i5-8250U mạnh mẽ với xung nhịp 3.4GHz. Ch&iacute;nh điều n&agrave;y đ&atilde; cho ph&eacute;p m&aacute;y hoạt động trơn tru, hiệu quả, ho&agrave;n to&agrave;n thừa sức chạy mượt m&agrave; c&aacute;c t&aacute;c vụ l&agrave;m việc, học tập v&agrave; giải tr&iacute; của người d&ugrave;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-3/ct-3.jpg\" style=\"height:403px; width:750px\" /></p>\r\n\r\n<p>Kh&ocirc;ng những vậy, thiết bị n&agrave;y c&ograve;n được trang bị RAM lớn v&agrave; dung lượng ổ cứng l&ecirc;n đến 1TB t&iacute;ch hợp card đồ họa Intel HD Graphics 620 c&ugrave;ng card rời AMD RADEON 520 2GB. Dell Vostro 3578 đ&atilde; đ&aacute;p ứng được nhu cầu lưu trữ c&aacute;c dữ liệu quan trọng hay giải quyết c&aacute;c c&ocirc;ng việc, học tập li&ecirc;n quan đến lĩnh vực đồ họa.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Chuy&ecirc;n gia &acirc;m thanh Waves MaxxAudio Pro</strong></span></h5>\r\n\r\n<p>Waves MaxxAudio Pro cung cấp &acirc;m thanh chuy&ecirc;n nghiệp, tự động điều chỉnh &acirc;m thanh của bạn với độ cao r&otilde; n&eacute;t hơn, tăng &acirc;m trầm v&agrave; tăng &acirc;m lượng để mọi thứ m&agrave; bạn nghe đều ch&iacute;nh x&aacute;c theo c&aacute;ch bạn mu&ocirc;́n. Dell Vostro 3578 được trang bị Loa stereo t&iacute;ch hợp (2W x 2) c&ugrave;ng &acirc;m thanh HD được cải tiến n&ecirc;n mặc d&ugrave; l&agrave; d&ograve;ng laptop phổ th&ocirc;ng nhưng &acirc;m thanh tr&ecirc;n Dell Vostro 3578 ph&aacute;t ra từ loa ở mức chấp nhận được, khi mở to hết cỡ vẫn kh&ocirc;ng xảy ra hiện tượng vỡ tiếng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-3/ct-4.jpg\" style=\"height:405px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Touchpad nhạy b&eacute;n</strong></span></h5>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute; th&igrave; Dell Vostro 3578 c&ograve;n được trang bị Touchpad cực nhạy b&eacute;n, gi&uacute;p người d&ugrave;ng thực hiện c&aacute;c thao t&aacute;c chuột nhanh ch&oacute;ng v&agrave; hiệu quả nhất. Kh&ocirc;ng chỉ vậy, c&aacute;c kết nối th&ocirc;ng dụng như Wi-Fi, Bluetooth, HDMI... để thuận tiện cho việc chia sẻ dữ liệu cũng được lưu &yacute;.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-3/ct-5.jpg\" style=\"height:415px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hỗ trợ nhiều cổng kết nối</strong></span></h5>\r\n\r\n<p>Với chuẩn wifi 802.11ac nhanh gấp 6 lần so với chuẩn wifi 802.11n. Hỗ trợ nhiều cổng kết nối như cổng 2x USB 3.1 Gen 1 Type-A ,1x USB 2.0, 1xHDMI, 1xVGA, khe cắm thẻ nhớ, 1x jack 3.5mm cho khả năng kết nối với c&aacute;c thiết bị kh&aacute;c một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Dung lượng pin 4 Cells</strong></span></h5>\r\n\r\n<p>Với vi&ecirc;n Pin 4 cell Dell Vostro V3578 cho thời gian sử dụng tương đối. C&ocirc;ng nghệ tiết kiệm pin hoạt động m&aacute;t, &ecirc;m &aacute;i v&agrave; tiết kiệm điện năng n&ecirc;n c&oacute; thể đ&aacute;p ứng tốt nhu cầu sử dụng b&igrave;nh thường của bạn.</p>\r\n', 1, '2019-07-03 09:15:26', 'Bình thường', 'Bình thường', 1),
(8, 'Dell Vostro 5581 70175950 Core i5-8265U/ Win10 + Office365', 16570000, 18990000, 28, 'Chip: Intel Core i5-8265U (1.60GHz Up to 3.90 GHz, 4Cores, 8Threads, 6MB Cache, FSB 4GT/s)\r\n\r\nRAM: 4GB DDR4-2666MHz, 2 Slot RAM\r\n\r\nỔ cứng: 1TB HDD 5400rpm + 1 Slot SSD M.2 PCIe\r\n\r\nChipset đồ họa: Intel UHD Graphics 620\r\n\r\nMàn hình: 15.6-inch FHD (1920x1080) Anti-glare LED Backlight Narrow Border IPS Display\r\n\r\nHệ điều hành: Windows 10 home\r\n\r\nPin: 3 Cell 42 Whrs', 'dell7.jpg', 'dell7(1).jpg', 'dell7-1.jpg', 'dell7-2.jpg', 'dell7-3.jpg', 'dell7-4.jpg', 'dell7-5.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế tinh tế</strong></span></h5>\r\n\r\n<p><strong>Laptop Dell Vostro 5581 70175950</strong><strong>&nbsp;</strong>sở hữu thiết kế đẹp mắt, chắc chắn với lớp vỏ ngo&agrave;i cứng c&aacute;p, vu&ocirc;ng vắn c&ugrave;ng m&agrave;u hồng ấn tượng gi&uacute;p t&ocirc;n th&ecirc;m vẻ hiện đại, sang trọng. C&aacute;c g&oacute;c cạnh được bo tr&ograve;n tạo cảm gi&aacute;c chắc tay cho người d&ugrave;ng khi cầm nắm, c&oacute; thể di chuyển đi mọi nơi m&agrave; kh&ocirc;ng lo tốn diện t&iacute;ch hoặc mang v&aacute;c nặng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-4/dell7_ct_1.jpg\" style=\"height:480px; width:750px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh 15.6-inch Full HD</strong></span></h5>\r\n\r\n<p>Sở hữu m&agrave;n h&igrave;nh 15.6-inch gi&uacute;p hiển thị h&igrave;nh ảnh Full HD (1920 x 1080) để xem mọi thứ tốt hơn đẹp v&agrave; sắc n&eacute;t, m&agrave;u sắc tươi tắn, sống động cùng với tấm nền chống ch&oacute;i Anti-glare gi&uacute;p bạn c&oacute; thể trải nghiệm tốt hơn ở m&ocirc;i trường b&ecirc;n ngo&agrave;i.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-4/dell7_ct_2.jpg\" style=\"height:480px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bộ vi xử l&yacute; Intel Core i5 mạnh mẽ</strong></span></h5>\r\n\r\n<p>Được trang bị bộ vi xử l&yacute; Intel Core i5-8265U tốc độ l&ecirc;n đ&ecirc;́n 3.9GHz, đem lại cho m&aacute;y một sức mạnh xử l&yacute; ổn định v&agrave; tiết kiệm điện năng hiệu quả. C&ugrave;ng bộ nhớ RAM DDR4 dung lượng 4GB cho hiệu suất xử l&yacute; tốt cũng như đ&aacute;p ứng c&aacute;c t&aacute;c vụ đa nhiệm mượt m&agrave; c&aacute;c ứng dụng. Chipset đồ họa Intel UHD Graphics 620 hỗ trợ xử l&yacute; hiệu ứng h&igrave;nh ảnh 3D c&aacute;c c&ocirc;ng việc li&ecirc;n quan đến đồ hoạ, đáp ứng giải tr&iacute; với game đ&ocirc;̀ họa nặng. Ngo&agrave;i ra, Dell c&ograve;n trang bị cho m&aacute;y ổ cứng 1TB HDD 5400rpm + 1 Slot SSD M.2 PCIe gi&uacute;p m&aacute;y hoạt động mượt m&agrave; hơn, khởi động nhanh ch&oacute;ng hơn, cho ph&eacute;p bạn lưu trữ số lượng nội dung đ&aacute;ng kinh ngạc m&agrave; kh&ocirc;ng phải lo lắng về hết dung lượng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-4/dell7_ct_3.jpg\" style=\"height:480px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m v&agrave; touchpad</strong></span></h5>\r\n\r\n<p>B&agrave;n ph&iacute;m với đ&egrave;n nền t&ugrave;y chọn mức s&aacute;ng c&oacute; k&iacute;ch thước vừa phải, khoảng c&aacute;ch ph&iacute;m hợp l&yacute; v&agrave; độ nảy &ecirc;m, gi&uacute;p người d&ugrave;ng nhập liệu nhanh v&agrave; ch&iacute;nh x&aacute;c. Touchpad rộng r&atilde;i với bề mặt nhẵn mịn, cảm ứng đa điểm c&oacute; độ nhạy vừa phải cho ph&eacute;p bạn thao t&aacute;c dễ d&agrave;ng m&agrave; kh&ocirc;ng cần đến chuột.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-4/dell7_ct_4.jpg\" style=\"height:480px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>USB Type C &ndash; tất cả trong một kết nối</strong></span></h5>\r\n\r\n<p>Laptop sở hữu cổng kết nối USB Type C đa dụng nhất hiện nay, với thiết kế đồng đẳng gi&uacute;p bạn c&oacute; thể cắm cổng kết nối ở bất cứ chiều n&agrave;o. Ngo&agrave;i ra với c&ocirc;ng nghệ n&agrave;y sẽ cho tốc độ truyền tải dữ liệu nhanh gấp 10 lần so với c&aacute;c cổng kết nối cũ hiện nay.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-4/dell7_ct_5.jpg\" style=\"height:480px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hỗ trợ nhiều cổng kết nối</strong></span></h5>\r\n\r\n<p>Với chuẩn wifi 802.11ac nhanh gấp 6 lần so với chuẩn wifi 802.11n, hỗ trợ nhiều cổng kết nối như cổng&nbsp; HDMI, jack tai nghe, Bluetooth 4.2, 1 khe thẻ nhớ cho khả năng kết nối với c&aacute;c thiết bị kh&aacute;c một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-4/dell7_ct_6.jpg\" style=\"height:480px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Thời lượng pin l&acirc;u hơn</strong></span></h5>\r\n\r\n<p>Được thiết kế để ph&ugrave; hợp với phong c&aacute;ch sống năng động hiện nay,&nbsp;Dell Vostro 5581 70175950&nbsp;sẽ cung cấp cho bạn thời lượng pin để c&oacute; thể sử dụng suốt cả ng&agrave;y. Với c&ocirc;ng nghệ pin Lithium Polymer sẽ cho m&aacute;y khả năng vận h&agrave;nh li&ecirc;n tục l&ecirc;n đến 7 tiếng đồng hồ. Điểm đặc biệt của d&ograve;ng pin Lithium Polymer c&oacute; thể duy tr&igrave; được năng lượng sạc lần đầu cho d&ugrave; sau h&agrave;ng trăm chu kỳ sạc, l&acirc;u hơn gấp 3 lần so với c&aacute;c loại pin ti&ecirc;u chuẩn hiện nay, gi&uacute;p bạn khỏi phải lo lắng về hao m&ograve;n hao m&ograve;n điện năng pin.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-4/dell7_ct_7.jpg\" style=\"height:320px; width:750px\" /></p>\r\n', 1, '2019-07-05 15:10:57', 'Bình thường', 'Bình thường', 1),
(9, 'Dell Vostro V3568-XF6C611 Core i5-7200U / Win 10 (15.6inch) - Black', 12390000, 14600000, 30, 'Màn hình: 15.6\" HD (1366 x 768 pixels)\r\n\r\nCPU: Intel Core i5-7200U Processor\r\n\r\nỔ cứng: HDD 1TB\r\n\r\nRAM: 4GB DDR4\r\n\r\nHĐH: Windows 10\r\n\r\nPin: 3 Cell', 'dell8-1.jpg', 'dell8-1(1).jpg', 'dell8-2.jpg', 'dell8-3.jpg', 'dell8-4.jpg', 'dell8-5.jpg', 'dell8-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế bắt mắt</strong></span></h5>\r\n\r\n<p>Laptop Dell Vostro V3568-XF6C611 c&oacute; thiết kế cứng c&aacute;p, khung nhựa m&agrave;u đen b&oacute;ng khắp th&acirc;n m&aacute;y c&ugrave;ng với những đường họa tiết v&acirc;n s&acirc;̀n như lớp da rắn nhằm gi&uacute;p chống trầy xước cho m&aacute;y. Các góc v&acirc;̃n được bo vu&ocirc;ng vức tạo nét đẹp mắt cho sản phẩm.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-3/dell8_ct_1.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh lớn 15.6 inch</strong></span></h5>\r\n\r\n<p>Chiếc m&aacute;y t&iacute;nh sở hữu m&agrave;n h&igrave;nh lớn 15.6 inch, độ ph&acirc;n giải chuẩn HD, cho khả năng hiển thị h&igrave;nh ảnh sắc n&eacute;t, đem đến những h&igrave;nh ảnh chất lượng cao, gi&uacute;p bạn l&agrave;m việc dễ d&agrave;ng v&agrave; đem lại những trải nghiệm giải tr&iacute; th&uacute; vị.&nbsp; M&agrave;n h&igrave;nh lớn cũng gi&uacute;p mắt d&ecirc;̃ nhìn nhờ hình ảnh to rõ hơn so với các dòng laptop có màn hình nhỏ 14 inch th&ocirc;ng thường.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-3/dell8_ct_2.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Cấu h&igrave;nh mạnh mẽ</strong></span></h5>\r\n\r\n<p>Laptop Dell Vostro V3568-XF6C611 được trang bị chip Core i5 lõi kép tốc độ cao 2,5Ghz v&acirc;̣n hành ở mức t&acirc;̀m trung cho phép bạn giải quy&ecirc;́t c&ocirc;ng vi&ecirc;̣c được nhanh chóng, có khả năng xử lý được các tác vụ mạnh như Photoshop, Misa, game ph&ocirc;̉ th&ocirc;ng,&hellip;</p>\r\n\r\n<p>K&ecirc;́t hợp với RAM 4G DDR4 có băng th&ocirc;ng 2400Mbps gi&uacute;p chạy mượt c&aacute;c chương tr&igrave;nh ứng dụng trong m&aacute;y cũng như c&aacute;c chương tr&igrave;nh bạn c&agrave;i đặt.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-3/dell8_ct_3.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>&Acirc;m thanh sống động</strong></span></h5>\r\n\r\n<p>Ngoài nhu c&acirc;̀u xem phim, chơi game giải trí cơ bản với ch&acirc;́t lượng màn hình HD c&ocirc;ng ngh&ecirc;̣ th&acirc;n thi&ecirc;̣n Truelife và vi xử lý onboard của Intel thì với c&ocirc;ng nghệ Wave MaxxAudio k&ecirc;́t hợp b&ocirc;̣ loa 2.0 tr&ecirc;n chiếc laptop n&agrave;y sẽ mang đến cho bạn hiệu ứng &acirc;m thanh tuyệt vời, to r&otilde; hơn, mang đến &acirc;m thanh sống động hơn bao giờ hết.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Cảm biến v&acirc;n tay</strong></span></h5>\r\n\r\n<p>M&aacute;y được trang bị cảm biến v&acirc;n tay dạng qu&eacute;t. Bạn phải vuốt ng&oacute;n tay từ tr&ecirc;n xuống dưới để k&iacute;ch hoạt cảm biến mở khoá. Việc n&agrave;y sẽ gi&uacute;p m&aacute;y bạn được bảo mật tốt hơn bao giờ hết.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-3/dell8_ct_4.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Đa dạng kết nối</strong></span></h5>\r\n\r\n<p>M&aacute;y được trang bị đầy đủ c&aacute;c cổng USB 3.0; USB 2.0; HDMI; jack 3.5mn,&hellip; bạn c&oacute; thể kết nối nhiều thiết bị điện tử c&ugrave;ng một l&uacute;c, đ&aacute;p ứng được nhu cầu học tập v&agrave; l&agrave;m việc của bạn v&agrave; người th&acirc;n.</p>\r\n', 1, '2019-06-13 06:38:16', 'Bình thường', 'Bình thường', 1);
INSERT INTO `product` (`id`, `product_name`, `price_new`, `price_old`, `qty_product`, `product_desc`, `product_thumb`, `list_thumb_1`, `list_thumb_2`, `list_thumb_3`, `list_thumb_4`, `list_thumb_5`, `list_thumb_6`, `product_content`, `cat_id`, `created_at`, `selling_products`, `featured_products`, `status`) VALUES
(10, 'Asus Vivobook S13 S330UA-EY027T Core i5-8250U/256GB SSD M.2 Sata', 20000000, 16090000, 6, 'Chip: Intel Core i5-8250U (1.60GHz Up to 3.40 GHz, 4Cores, 8Threads, 6MB Cache, FSB 4GT/s)\r\n\r\nRAM: 8GB LPDDR3-2133MHz\r\n\r\nỔ cứng: 256GB SSD M.2 Sata\r\n\r\nChipset đồ họa: Intel UHD Graphics 620\r\n\r\nMàn hình: 13.3-inch Full HD (1920 x 1080) IPS 60Hz, Anti-Glare with 45% NTSC\r\n\r\nHệ điều hành: Windows 10 bản quyền\r\n\r\nPin: 3 Cell 42 Whrs                                                                                                                                                ', 'asus1.jpg', 'asus1(1).jpg', 'asus1-2.jpg', 'asus1-3.jpg', 'asus1-4.jpg', 'asus1-5.jpg', 'asus1-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế di động cho c&ocirc;ng việc v&agrave; giải tr&iacute;</strong></span></h5>\r\n\r\n<p><strong>Laptop Asus Vivobook S13 S330UA-EY027T</strong><strong>&nbsp;</strong>với thiết kế si&ecirc;u di động tối ưu về độ mỏng nhẹ gi&uacute;p bạn c&oacute; thể l&agrave;m việc v&agrave; giải tr&iacute; một c&aacute;ch hiệu quả ở bất cứ đ&acirc;u, bạn c&oacute; thể x&aacute;ch vội trong một cuộc họp bất chợt hoặc giải tr&iacute; nhẹ nh&agrave;ng sau giờ l&agrave;m việc ở một qu&aacute;n c&agrave; ph&ecirc;.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-1/asus-ct-1.jpg\" style=\"height:451px; width:750px\" /></p>\r\n\r\n<p>Mỏng hơn v&agrave; nhẹ hơn bao giờ hết với trọng lượng 1.2kg, đ&ocirc;̣ dày 17.9 mm giúp bạn có th&ecirc;̉ mang đi h&agrave;ng ng&agrave;y m&ocirc;̣t cách d&ecirc;̉ dàng. Sở hữu thiết kế thanh lịch đẹp mắt, cấu tạo m&aacute;y mỏng nhẹ, m&agrave;u sắc m&aacute;y trẻ trung, sang trọng. C&aacute;c g&oacute;c m&aacute;y bo cong tinh tế tạo cảm gi&aacute;c thoải m&aacute;i khi l&agrave;m việc, rất nhẹ nh&agrave;ng để cầm tr&ecirc;n tay, di chuyển.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-1/asus-ct-2.jpg\" style=\"height:509px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bản lề ErgoLift &ndash; trải nghiệm g&otilde; ph&iacute;m tốt hơn</strong></span></h5>\r\n\r\n<p>Bản lề gia c&ocirc;ng kiểu ErgoLift đem đến một t&aacute;c dụng k&eacute;p cho&nbsp;Asus Vivobook S13 S330UA-EY027T. B&agrave;n ph&iacute;m được n&acirc;ng l&ecirc;n một g&oacute;c 2.5 độ cho cảm gi&aacute;c g&otilde; ph&iacute;m thoải m&aacute;i hơn trong khi m&agrave;n h&igrave;nh được giữ cố định. Khoảng kh&ocirc;ng gian ph&iacute;a dưới khi b&agrave;n ph&iacute;m được n&acirc;ng l&ecirc;n gi&uacute;p tăng cường khả năng tản nhiệt cho hệ thống hoạt động ổn định trong thời gian d&agrave;i.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-1/asus-ct-3.jpg\" style=\"height:411px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh 13.3 inch Full HD</strong></span></h5>\r\n\r\n<p>Sở hữu m&agrave;n h&igrave;nh 13.3 inch gi&uacute;p hiển thị h&igrave;nh ảnh Full HD (1920 x 1080) để xem mọi thứ tốt hơn, mang đến những h&igrave;nh ảnh chất lượng, chi tiết, r&otilde; n&eacute;t, m&agrave;u sắc cũng cực kỳ trung thực v&agrave; sống động. Tấm nền chống ch&oacute;i Anti-glare gi&uacute;p bạn c&oacute; thể trải nghiệm tốt hơn ở m&ocirc;i trường b&ecirc;n ngo&agrave;i,&nbsp;v&agrave; độ phủ m&agrave;u 45% NTSC đảm bảo xuất ra h&igrave;nh ảnh với chất lượng sắc n&eacute;t v&agrave; ch&acirc;n thật nhất.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-1/asus-ct-4.jpg\" style=\"height:499px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bộ vi xử l&yacute; Intel Core i5</strong></span></h5>\r\n\r\n<p>Được trang bị bộ vi xử l&yacute; Intel Core i5-8250U tốc độ l&ecirc;n đ&ecirc;́n 3.4GHz, đem lại cho m&aacute;y một sức mạnh xử l&yacute; ổn định v&agrave; tiết kiệm điện năng hiệu quả. Đi k&egrave;m với đ&oacute; l&agrave; bộ nhớ RAM 8GB LPDDR3-2133MHz. Được trang bị ổ cứng dung lượng l&ecirc;n đến 256GB SSD M.2 Sata đem lại khả năng lưu trữ v&ocirc; c&ugrave;ng to&agrave;n diện với khả năng truy xuất dữ liệu nhanh v&agrave; lưu trữ dung lượng lớn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-1/asus-ct-5.jpg\" style=\"height:454px; width:750px\" /></p>\r\n\r\n<p>Khả năng xử l&yacute; đồ họa của Asus Vivobook S13 S330UA-EY027T&nbsp;cũng được tăng cường với card đồ họa t&iacute;ch hợp Intel UHD Graphics 620, cho khả năng chạy tốt v&agrave; ổn định c&aacute;c phần mềm đồ họa cũng như đ&aacute;p ứng nhu cầu giải tr&iacute; của người d&ugrave;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>C&ocirc;ng nghệ độc quyền về &acirc;m thanh</strong></span></h5>\r\n\r\n<p>ASUS SonicMaster l&agrave; sự kết hợp giữa phần cứng, phần mềm điều chỉnh &acirc;m thanh được thiết kế với mục ti&ecirc;u đem đến cho bạn trải nghiệm &acirc;m thanh tuyệt vời nhất. Một codec chuy&ecirc;n nghiệp&nbsp; đảm bảo ch&iacute;nh x&aacute;c m&atilde; h&oacute;a &acirc;m thanh v&agrave; giải m&atilde;. Trong khi bộ khuếch đại lớn, loa lớn v&agrave; c&aacute;c buồng cộng hưởng ho&agrave;n hảo cho thiết bị cụ thể của bạn đảm bảo &acirc;m thanh mạnh mẽ v&agrave; bass s&acirc;u hơn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-1/asus-ct-6.jpg\" style=\"height:342px; width:850px\" /></p>\r\n\r\n<p>Asus Vivobook S13 S330UA-EY027T xử l&yacute; t&iacute;n hiệu bổ sung v&agrave; điều chỉnh gi&uacute;p tinh chỉnh chi tiết nhỏ nhất, lọc tiếng ồn v&agrave; cải thiện &acirc;m thanh r&otilde; r&agrave;ng để bạn c&oacute; thể trải nghiệm &acirc;m thanh thực sự đắm ch&igrave;m.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bảo mật tối ưu với cảm biến v&acirc;n tay</strong></span></h5>\r\n\r\n<p>C&ocirc;ng nghệ cảm biến v&acirc;n tay được h&atilde;ng t&iacute;ch hợp gọn ghẽ v&agrave; ho&agrave;n hảo tr&ecirc;n Asus Vivobook S13 S330UA-EY027T, đi k&egrave;m với t&iacute;nh năng Windows Hello tr&ecirc;n Windows 10, chỉ với một chạm bạn sẽ dễ d&agrave;ng đăng nhập v&agrave;o m&agrave;n h&igrave;nh desktop v&agrave; tăng cường khả năng bảo mật cho bạn.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m v&agrave; touchpad c&ocirc;ng th&aacute;i học</strong></span></h5>\r\n\r\n<p>Trang bị b&agrave;n ph&iacute;m được thiết kế c&ocirc;ng th&aacute;i học c&oacute; h&agrave;nh tr&igrave;nh ph&iacute;m 1.4mm ti&ecirc;u chuẩn, đem lại cảm gi&aacute;c thoải m&aacute;i v&agrave; kh&ocirc;ng bị mỏi sau nhiều giờ l&agrave;m việc v&agrave; giải tr&iacute;.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-1/asus-ct-7.jpg\" style=\"height:483px; width:750px\" /></p>\r\n\r\n<p>Touchpad của Asus Vivobook S13 S330UA-EY027T&nbsp;được sản xuất đem lại độ ch&iacute;nh x&aacute;c v&agrave; thoải m&aacute;i, mang đến khả năng theo d&otilde;i, viết v&agrave; nhận biết cử chỉ đa ng&oacute;n một c&aacute;ch mượt m&agrave;.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>USB Type C &ndash; tất cả trong một kết nối</strong></span></h5>\r\n\r\n<p>Laptop sở hữu cổng kết nối USB Type C đa dụng nhất hiện nay, với thiết kế đồng đẳng gi&uacute;p bạn c&oacute; thể cắm cổng kết nối ở bất cứ chiều n&agrave;o. Ngo&agrave;i ra với c&ocirc;ng nghệ n&agrave;y sẽ cho tốc độ truyền tải dữ liệu nhanh gấp 10 lần so với c&aacute;c cổng kết nối cũ hiện nay.</p>\r\n\r\n<p>Asus Vivobook S13 S330UA-EY027T Hỗ trợ nhiều cổng kết nối như cổng HDMI, Bluetooth, khe thẻ nhớ, jack tai nghe cho khả năng kết nối với c&aacute;c thiết bị kh&aacute;c một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Kết nối Wifi si&ecirc;u nhanh</strong></span></h5>\r\n\r\n<p>Asus Vivobook S13 S330UA-EY027T mang đến cho bạn kết nối kh&ocirc;ng d&acirc;y 2 băng tần mới nhất cho kết nối nhanh hơn v&agrave; xa hơn. D&ugrave; đang ở gần những nguồn g&acirc;y nhiễu, th&igrave; bạn vẫn c&oacute; thể tận hưởng giải tr&iacute; v&agrave; chơi game một c&aacute;ch mượt m&agrave; kh&ocirc;ng bị đứt quảng.</p>\r\n', 2, '2019-07-07 07:26:47', 'Bình thường', 'Nổi bật', 1),
(11, 'Asus ROG Zephyrus G GA502DU-AL024T AMD R7-3750H/ GTX 1660Ti 6GB', 30709000, 30209000, 26, 'CPU: AMD Ryzen 7-3750H (2.3GHz upto 4.0GHz, 4Cores, 8Threads, 4MB cache)\r\n\r\nMàn hình: 15.6-inch FHD (1920x1080) 120Hz vIPS, Non-glare\r\n\r\nRAM: 8GB DDR4-2400MHz, 1 slot RAM, max 32GB\r\n\r\nĐồ họa: NVIDIA GeForce GTX 1660Ti 6GB GDDR6\r\n\r\nLưu trữ: 512GB SSD PCIE G3X4\r\n\r\nHệ điều hành: Windows 10 Home\r\n\r\nPin: 4 Cell 76WHr', 'asus2.jpg', 'asus2-1.jpg', 'asus2-2.jpg', 'asus2-3.jpg', 'asus2-4.jpg', 'asus2-5.jpg', 'asus2-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế si&ecirc;u mỏng</strong></span></h5>\r\n\r\n<p><strong>Laptop Asus ROG Zephyrus G GA502DU-AL024T</strong>&nbsp;c&oacute; thiết kế ấn tượng với độ mỏng 18.9mm v&agrave; trọng lượng dưới 2kg. Zephyrus G GA502DU-AL024T mỏng hơn 25% v&agrave; nhẹ hơn 42% so với laptop 15 inch th&ocirc;ng thường. Zephyrus G GA502DU-AL024T ho&agrave;n to&agrave;n c&oacute; thể c&acirc;n bằng giữa c&ocirc;ng việc v&agrave; giải tr&iacute;. Khung kim loại được gia cố, vẻ ngo&agrave;i b&oacute;ng bẩy gi&uacute;p gia tăng tuổi thọ khi sử dụng h&agrave;ng ng&agrave;y. B&ecirc;n trong được thiết kế tinh tế nhỏ gọn l&agrave;m cho người sử dụng c&oacute; sự trải nghiệm chuy&ecirc;n nghiệp ở bất cứ đ&acirc;u.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/asus-ct-8.jpg\" style=\"height:852px; width:1600px\" /></p>\r\n\r\n<p>Asus Zephyrus G GA502DU-AL024T được gia c&ocirc;ng tỉ mỉ cực mỏng đặc biệt vẫn duy tr&igrave; độ bền. Bề ngo&agrave;i được phủ 1 lớp sơn giảm thiểu dấu v&acirc;n tay khi chạm v&agrave;o. C&aacute;c g&oacute;c cạnh được cắt kim cương một c&aacute;ch tinh tế. Logo ROG ph&aacute;t s&aacute;ng m&agrave;u đỏ tạo n&ecirc;n sự tương phản nổi bật của Zephyrus G.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh lớn 15.6 inch</strong></span></h5>\r\n\r\n<p>Chiếc laptop được trang bị m&agrave;n h&igrave;nh với k&iacute;ch thước 15.6 inch độ ph&acirc;n giải Full HD gi&uacute;p m&aacute;y c&oacute; nhịp độ nhanh chạy mượt m&agrave; với đồ họa sắc n&eacute;t cực cao, tần số qu&eacute;t m&agrave;n h&igrave;nh l&ecirc;n đến 120Hz nhanh v&agrave; thời gian phản hồi 3ms. Khung viền mỏng bao quanh, m&agrave;n h&igrave;nh vIPS mở rộng khả năng hiển thị h&igrave;nh ảnh.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/asus-ct-9.jpg\" style=\"height:828px; width:1600px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hiệu năng Gaming đỉnh cao</strong></span></h5>\r\n\r\n<p>Với d&ograve;ng laptop gaming si&ecirc;u mỏng, linh hoạt v&agrave; tinh tế, Asus đ&atilde; ưu &aacute;i trang bị bộ vi xử l&yacute; CPU AMD Ryzen 7 3750H của AMD v&agrave; card đồ họa GPU GeForce GTX 1660 Ti với ROG Boost cho xung nhịp l&ecirc;n tới 1335MHz tại 60W ở chế độ Turbo để mang đến nguồn sức mạnh dồi d&agrave;o, sẵn s&agrave;ng đ&aacute;p ứng nhiều tựa game đang phổ biến v&agrave; trải nghiệm VR đa dạng. Bứt ph&aacute; mọi giới hạn cho ph&eacute;p bạn vượt qua đa nhiệm nặng, load game v&agrave; ứng dụng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/asus-ct-10.jpg\" style=\"height:842px; width:1600px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Chế độ l&agrave;m m&aacute;t th&ocirc;ng minh</strong></span></h5>\r\n\r\n<p>Chế độ l&agrave;m m&aacute;t của Zephyrus G GA502DU-AL024T pha trộn c&aacute;c t&iacute;nh năng v&agrave; c&agrave;i đặt hệ thống ph&ugrave; hợp để đảm bảo trải nghiệm tốt nhất cho m&aacute;y v&agrave; c&aacute;c bộ phận, bất kể bạn đang sử dụng t&aacute;c vụ g&igrave;. Hệ thống tản nhiệt kiểu mới với c&aacute;c quạt c&aacute;nh chữ N để tối đa h&oacute;a luồng kh&iacute; lưu th&ocirc;ng. CPU v&agrave; GPU đều c&oacute; c&aacute;c ống dẫn nhiệt ri&ecirc;ng kết nối với c&aacute;c bộ tản nhiệt d&ugrave;ng chung gi&uacute;p đảm bảo hoạt động l&agrave;m m&aacute;t hiệu quả. Cho ph&eacute;p lưu th&ocirc;ng kh&ocirc;ng kh&iacute; nhiều hơn v&agrave; sức cản kh&ocirc;ng kh&iacute; thấp hơn so với m&aacute;y th&ocirc;ng thường.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/asus-ct-11.jpg\" style=\"height:805px; width:1600px\" /></p>\r\n\r\n<p>Ba ống dẫn nhiệt uốn quanh b&ecirc;n dưới bề mặt để kh&ocirc;ng chỉ dẫn nhiệt từ CPU v&agrave; GPU m&agrave; c&ograve;n từ hệ mạch điện cấp nguồn cho hai bộ xử l&yacute; n&agrave;y. ROG Armoury Crate phần m&ecirc;m quản l&yacute; th&ocirc;ng tin gi&uacute;p bạn c&oacute; thể t&ugrave;y chỉnh tốc độ quạt. Kết hợp thiết kế th&ocirc;ng minh với c&ocirc;ng nghệ ti&ecirc;n tiến để đ&aacute;p ứng mong muốn của c&aacute;c game thủ: m&aacute;y t&iacute;nh x&aacute;ch tay si&ecirc;u mỏng, vận h&agrave;nh m&aacute;t mẻ v&agrave; &ecirc;m &aacute;i m&agrave; kh&ocirc;ng phải hy sinh bớt hiệu năng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>&Acirc;m thanh tuyệt hảo</strong></span></h5>\r\n\r\n<p>N&acirc;ng cao trải nghiệm tai nghe của bạn với loa Side-Firing k&eacute;p, tự h&agrave;o c&oacute; chứng nhận Hi-Res Audio. &Acirc;m thanh r&otilde; r&agrave;ng, phong ph&uacute; được cấp bởi bộ vi xử l&yacute; cao cấp kết hợp với &acirc;m thanh v&ograve;m ảo để l&agrave;m s&acirc;u sắc hơn cho chơi game v&agrave; giải tr&iacute;. Khuếch đại th&ocirc;ng minh n&acirc;ng cao loa. Thưởng thức &acirc;m lượng to hơn ch&acirc;t thực hơn, cộng th&ecirc;m &acirc;m trầm v&agrave; treble cho &acirc;m thanh thực sự vượt trội.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/asus-ct-12.jpg\" style=\"height:863px; width:1600px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m cho thao t&aacute;c ch&iacute;nh x&aacute;c</strong></span></h5>\r\n\r\n<p>Phần b&aacute;n ph&iacute;m được thiết kế để thao t&aacute;c ch&iacute;nh x&aacute;c, b&agrave;n ph&iacute;m c&oacute; khoảng c&aacute;ch giữa c&aacute;c ph&iacute;m chức năng để nhận dạng trực quan. C&aacute;c ph&iacute;m n&oacute;ng chuy&ecirc;n dụng để giữ c&aacute;c lệnh quan trọng trong tầm với của bạn. C&aacute;c t&ugrave;y chỉnh d&agrave;nh ri&ecirc;ng cho c&ocirc;ng việc gi&uacute;p tiết kiệm thời gian. Đồng thời c&ocirc;ng nghệ N-key rollover ghi nhận từng lần nhấn một cash ch&iacute;nh x&aacute;c. L&agrave;m việc hoặc chơi game xuy&ecirc;n m&agrave;n đ&ecirc;m với &aacute;nh s&aacute;ng tinh kh&ocirc;i của đ&egrave;n nền m&agrave;u trắng thuần khiết.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Thời lượng pin l&acirc;u</strong></span></h5>\r\n\r\n<p>Zephyrus G GA502DU-AL024T g&acirc;y ấn tượng bởi sự c&acirc;n bằng ho&agrave;n hảo giữa sức mạnh v&agrave; khả năng di động. APU AMD Ryzen hiệu quả năng lượng được trang bị tr&ecirc;n m&aacute;y mang đến sức mạnh đủ để chơi game cường độ cao m&agrave; vẫn cung cấp đủ thời lượng pin trong thời gian l&ecirc;n đến gần 7 giờ ph&aacute;t video 1080p v&agrave; duyệt web qua Wi-Fi. R&uacute;t ngắn thời gian cắm sạc v&agrave; d&agrave;nh th&ecirc;m thời gian để di chuyển, lướt web, ph&aacute;t trực tuyến v&agrave; giải tr&iacute;.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Kết nối đa dạng</strong></span></h5>\r\n\r\n<p>Asus Zephyrus G GA502DU-AL024T trang bị khả năng kết nối tối t&acirc;n nhất mang lại cho bạn tốc độ truyền si&ecirc;u nhanh v&agrave; khả năng tương th&iacute;ch tối đa. USB 3.1 Gen 2 Type-C với DisplayPort 1.4. Cổng Type-A để truyền cực nhanh v&agrave; hai cổng USB 3.1 Gen 1 ho&agrave;n hảo cho chuột v&agrave; gamepad. Giải tr&iacute; tr&ecirc;n m&agrave;n h&igrave;nh lớn với đầu ra HDMI 2.0 hỗ trợ 4K UHD ở tần số qu&eacute;t 60Hz. Wi-Fi 802.11ac truy cập trực tuyến nhanh ch&oacute;ng cho c&aacute;c hoạt động h&agrave;ng ng&agrave;y. Trong khi giắc cắm Gigabit Ethernet độ trễ thấp nhất trong mạng LAN.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/asus-ct-13.jpg\" style=\"height:846px; width:1600px\" /></p>\r\n', 2, '2019-07-05 03:48:37', 'Bình thường', 'Bình thường', 1),
(12, 'Asus TUF FX505GE-BQ037T Core i7-8750H/Win10 (15.6″ FHD)', 25209000, 24709000, 18, 'Chip: Intel Core i7-8750H (2.20GHz upto 4.10GHz, 6Cores, 12Threads, 9MB cache, FSB 8GT/s)\r\n\r\nRAM: 8GB DDR4 2666MHz, 2 Slots RAM, Max 32GB\r\n\r\nỔ cứng: 128GB SSD M.2 PCIe + 1TB HDD 5400rpm\r\n\r\nChipset đồ họa: NVIDIA GeForce GTX 1050Ti 4GB GDDR5\r\n\r\nMàn hình: 15.6 inch Full HD (1920 x 1080) 60Hz Anti-Glare IPS-level Panel 45% NTSC\r\n\r\nHệ điều hành: Windows 10 bản quyền\r\n\r\nPin: 4 Cells 48 Whrs', 'asus3.jpg', 'asus3-1.jpg', 'asus3-2.jpg', 'asus3-3.jpg', 'asus3-4.jpg', 'asus3-5.jpg', 'asus3-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Laptop TUF Gaming đầu ti&ecirc;n - Vượt xa chuẩn mực</strong></span></h5>\r\n\r\n<p><strong>Laptop Gaming Asus TUF FX505GE-BQ037T</strong><strong>&nbsp;</strong>với thiết kế ấn tượng v&agrave; hiệu năng mạnh mẽ, c&oacute; thể chơi được hầu hết c&aacute;c tựa game mới nhất v&agrave; đa nhiệm dễ d&agrave;ng, đồng thời c&oacute; độ bền cao hơn, ổn định hơn v&agrave; vẫn kh&ocirc;ng bị &#39;lỗi thời&#39; trong nhiều năm nữa.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-2/asus-ct-14.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<p>Hệ thống tản nhiệt chống bụi th&ocirc;ng minh cung cấp đầy đủ luồng kh&iacute; m&aacute;t cho m&aacute;y ngay cả khi chơi những game nặng hơn, k&eacute;o d&agrave;i tuổi thọ hệ thống, loại bỏ bụi bẩn để kh&ocirc;ng g&acirc;y tắc nghẽn hệ thống tản nhiệt.&nbsp;Asus TUF FX505GE-BQ037T&nbsp;c&oacute; độ bền vượt ra khỏi chuẩn mực th&ocirc;ng thường d&ugrave; l&agrave; trong c&ocirc;ng việc hay khi chơi game.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh 15.6 inch Full HD hiển thị cực nhanh</strong></span></h5>\r\n\r\n<p>M&agrave;n h&igrave;nh 15.6 inch Full HD&nbsp;(1920 x 1080) độ phủ m&agrave;u 45% NTSC đảm bảo xuất ra h&igrave;nh ảnh với chất lượng sắc n&eacute;t v&agrave; ch&acirc;n thật nhất.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-2/asus-ct-15.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<p>Asus TUF FX505GE-BQ037T n&acirc;ng cấp tầm nh&igrave;n của bạn, với t&acirc;̀n s&ocirc;́ quét 60Hz để mang lại h&igrave;nh ảnh mượt m&agrave;, ch&iacute;nh x&aacute;c, cũng như loại bỏ độ trễ v&agrave; mờ của chuyển động. Tấm nền IPS gi&uacute;p bạn c&oacute; thể trải nghiệm g&oacute;c nh&igrave;n rộng 178&deg; đem lại h&igrave;nh ảnh sắc n&eacute;t, sống động v&agrave; độ tương phản cao ngay cả ở những g&oacute;c nh&igrave;n hẹp.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bộ vi xử l&yacute; Intel Core i7</strong></span></h5>\r\n\r\n<p>Được trang bị bộ vi xử l&yacute; Intel Core i7-8750H, c&oacute; khả năng đạt được tốc độ 4.1GHz đ&aacute;ng kinh ngạc. Hiệu năng được bổ trợ với pin dung lượng cao để đảm bảo sự mượt m&agrave; cho những trận đấu k&eacute;o d&agrave;i. Cho d&ugrave; bạn cần phải sử dụng đa nhiệm cho c&ocirc;ng việc hoặc học tập, trong khi đang xem phim, nghe nhạc v&agrave; gaming tr&ecirc;n Asus TUF FX505GE-BQ037T.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-2/asus-ct-16.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<p>C&ugrave;ng bộ nhớ RAM DDR4 dung lượng 8GB cho hiệu suất xử l&yacute; tốt cũng như đ&aacute;p ứng c&aacute;c t&aacute;c vụ đa nhiệm mượt m&agrave; c&aacute;c ứng dụng. Ngo&agrave;i ra, Asus c&ograve;n trang bị cho m&aacute;y ổ cứng kép HDD 1TB +&nbsp;128GB SSD gi&uacute;p m&aacute;y hoạt động mượt m&agrave; hơn, khởi động nhanh ch&oacute;ng hơn.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Card đồ họa NVIDIA GeForce GTX 1050Ti</strong></span></h5>\r\n\r\n<p>Được trang bị card đồ họa NVIDIA GeForce GTX 1050Ti b&ocirc;̣ nhớ đ&ocirc;̀ họa 4GB được x&acirc;y dựng tr&ecirc;n kiến tr&uacute;c Pascal mới nhất, cho m&aacute;y t&iacute;nh của bạn một hiệu suất mạnh mẽ c&oacute; thể xử l&yacute; tốt hầu hết c&aacute;c tựa game v&agrave; c&aacute;c ứng dụng đ&ograve;i hỏi khả năng đồ họa cao.</p>\r\n\r\n<p>C&ocirc;ng nghệ NVIDIA G-SYNC đồng bộ h&oacute;a tốc độ l&agrave;m tươi m&agrave;n h&igrave;nh với tốc độ khung h&igrave;nh của GPU để l&agrave;m cho gameplay mịn m&agrave;ng. G-SYNC giảm thiểu việc h&igrave;nh ảnh bị lắp, giảm độ trễ đầu v&agrave;o, v&agrave; loại bỏ hiện tượng r&aacute;nh m&agrave;n h&igrave;nh trực quan m&agrave; kh&ocirc;ng ảnh hưởng đến hiệu năng của hệ thống. Bạn sẽ c&oacute; được trải nghiệm mịn m&agrave;ng, nhanh nhất v&agrave; hấp dẫn nhất qua một loạt c&aacute;c tr&ograve; chơi bom tấn tr&ecirc;n Asus TUF FX505GE-BQ037T.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>DTS HeadphoneX - &Acirc;m thanh v&ograve;m 7.1</strong></span></h5>\r\n\r\n<p>Được t&iacute;ch hợp c&ocirc;ng nghệ tai nghe DTS Headphone: X cho trải nghi&ecirc;̣m &acirc;m thanh v&ograve;m giả lập 7.1 tr&ecirc;n tai nghe hoặc loa ngo&agrave;i. Chất lượng &acirc;m thanh ch&acirc;n thật như đưa người nghe nhập vai v&agrave;o tr&ograve; chơi.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-2/asus-ct-17.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<p>Ngo&agrave;i ra n&oacute; c&oacute; t&iacute;nh năng c&agrave;i đặt tối ưu kh&ocirc;ng chỉ cho tr&ograve; chơi v&agrave; &acirc;m nhạc, phim ảnh v&agrave; thể thao, bộ c&acirc;n bằng t&iacute;ch hợp để tinh chỉnh th&ecirc;m. Gi&uacute;p mang lại cho người d&ugrave;ng trải nghiệm &acirc;m thanh tốt nhất.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>C&ocirc;ng nghệ Hypercool cải thiện nhiệt độ v&agrave; tuổi thọ pin</strong></span></h5>\r\n\r\n<p>C&ocirc;ng nghệ HyperCool độc quyền của ASUS bao gồm hệ thống Anti-Dust Cooling (Quạt thổi bụi &ndash; ADC) v&agrave; bạn c&oacute; thể t&ugrave;y chỉnh 3 chế độ quạt kh&aacute;c nhau. Đặc biệt c&ocirc;ng nghệ Fan Overboost đảm bảo nhiệt độ ổn định trong khi chơi game nặng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-2/asus-ct-18.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<p>Với thiết kế 2 quạt tản nhiệt độc lập để l&agrave;m m&aacute;t CPU v&agrave; GPU ri&ecirc;ng biệt. Hơn nữa quạt l&agrave;m m&aacute;t cũng được cải thiện bởi nắp h&igrave;nh thang tản nhiệt hơn l&ecirc;n đ&ecirc;́n 25% với quạt th&ocirc;ng thường.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m Hyperstrike</strong></span></h5>\r\n\r\n<p>Laptop sở hữu h&agrave;nh tr&igrave;nh ph&iacute;m 1.8 mm với 20 triệu lần nhấn, đủ cho nhiều năm l&agrave;m việc v&agrave; chơi game. B&agrave;n ph&iacute;m c&oacute; t&iacute;nh năng RGB-backlighting tr&ecirc;n tất cả c&aacute;c khu vực tr&ecirc;n ph&iacute;m c&oacute; thể được điều khiển th&ocirc;ng qua bảng điều khiển TUF Aura Core để bạn c&oacute; thể thể hiện phong c&aacute;ch độc đ&aacute;o của ri&ecirc;ng m&igrave;nh.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-2/asus-ct-19.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<p>C&ocirc;ng nghệ Overstroke v&agrave; HyperStrike độc ​​quyền đảm bảo việc đ&aacute;nh m&aacute;y nhanh hơn, ch&iacute;nh x&aacute;c hơn, lấy cảm hứng từ m&aacute;y t&iacute;nh để b&agrave;n với nh&oacute;m ph&iacute;m WASD được thiết kế kh&aacute;c với những ph&iacute;m c&ograve;n lại, keycap được thiết kế để kh&ocirc;ng gian ph&iacute;m rộng hơn gi&uacute;p thao t&aacute;c giữa c&aacute;c ph&iacute;m dễ v&agrave; trơn tru hơn.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>WiFi v&agrave; cổng kết nối</strong></span></h5>\r\n\r\n<p>Asus TUF FX505GE-BQ037T sở hữu một trong những chuẩn Wi-Fi nhanh nhất hiện nay đ&oacute; l&agrave; Intel 802.11ac Wave 2 mới nhất c&oacute; tốc độ truyền dữ liệu l&ecirc;n đến 1.7Gbps nhanh hơn 12X so với mẫu cũ, cấu h&igrave;nh MIMO 2&times;2 cung cấp phạm vi phủ s&oacute;ng rộng hơn 30% so với thiết lập kh&aacute;c.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-2/asus-ct-20.png\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<p>Xem những bộ phim y&ecirc;u th&iacute;ch ở độ ph&acirc;n giải cao hoặc ph&aacute;t trực tuyến kh&ocirc;ng sợ delay v&agrave; tải file với tốc độ b&agrave;n thờ, ngay cả khi m&aacute;y c&aacute;ch xa wifi của bạn. Asus TUF FX505GE-BQ037T&nbsp;c&oacute; một cổng USB 2.0 v&agrave; hai cổng USB 3.1 Gen 1 loại A cho c&aacute;c thiết bị ngoại vi chơi game của bạn, cũng như HDMI 2.0 để kết nối với m&agrave;n h&igrave;nh ngo&agrave;i. Tất cả c&aacute;c cổng đều nằm ở ph&iacute;a b&ecirc;n tr&aacute;i của m&aacute;y, phần b&ecirc;n phải sẽ để trống để thuận tiện việc sự dụng chuột của bạn.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Pin 4 cell</strong></span></h5>\r\n\r\n<p>Với vi&ecirc;n pin 4 cell Asus TUF FX505GE-BQ037T&nbsp;cho thời gian sử dụng tương đối. C&ocirc;ng nghệ tiết kiệm pin hoạt động m&aacute;t, &ecirc;m &aacute;i v&agrave; tiết kiệm điện năng n&ecirc;n c&oacute; thể đ&aacute;p ứng tốt nhu cầu sử dụng b&igrave;nh thường của bạn. Với c&ocirc;ng nghệ pin Lithium Polymer sẽ cho m&aacute;y khả năng vận h&agrave;nh li&ecirc;n tục l&ecirc;n đến 8 tiếng đồng hồ.</p>\r\n\r\n<p>Điểm đặc biệt của d&ograve;ng pin Lithium Polymer c&oacute; thể duy tr&igrave; được năng lượng sạc lần đầu cho d&ugrave; sau h&agrave;ng trăm chu kỳ sạc, l&acirc;u hơn gấp 3 lần so với c&aacute;c loại pin ti&ecirc;u chuẩn hiện nay, gi&uacute;p bạn khỏi phải lo lắng về hao m&ograve;n hao m&ograve;n điện năng pin.</p>\r\n', 2, '2019-06-14 06:38:16', 'Bán chạy', 'Bình thường', 1),
(13, 'Asus Zenbook 14 UX433FA-A6111T Core i7-8565U/ Win10', 25679000, 25179000, 10, 'Chip: Intel Core i7-8565U (1.80GHz Up to 4.60 GHz, 4Cores, 8Threads, 8MB Cache, FSB 4GT/s)\r\n\r\nRAM: 8GB LPDDR3-2133Mhz (Onboard)\r\n\r\nỔ cứng: 512GB SSD M.2 PCIe\r\n\r\nChipset đồ họa: Intel UHD Graphics 620\r\n\r\nMàn hình: 14 inch Full HD (1920 x 1080) NanoEdge, 100% sRGB, 178° wide-view\r\n\r\nHệ điều hành: Windows 10\r\n\r\nPin: 3 Cells 50 Whrs', 'asus4.jpg', 'asus4-1.jpg', 'asus4-2.jpg', 'asus4-3.jpg', 'asus4-4.jpg', 'asus4-5.jpg', 'asus4-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế vỏ nh&ocirc;m&nbsp;cao cấp</strong></span></h5>\r\n\r\n<p><strong>Laptop Asus Zenbook 14 UX433FA-A6111T</strong><strong>&nbsp;</strong>tr&ecirc;n nắp m&aacute;y được thiết kế bằng hợp kim nh&ocirc;m với c&aacute;c v&ograve;ng tr&ograve;n đồng t&acirc;m đặc trưng, ngo&agrave;i ra mặt tr&ecirc;n c&ograve;n được phủ th&ecirc;m một lớp giả kim cương h&agrave;o nho&aacute;ng với kỹ thuật dập ảnh nổi nano cao cấp, đạt chuẩn &quot;mỏng nhẹ&quot; với trọng lượng 1.09kg. Thiết kế của m&aacute;y thon dần về ph&iacute;a b&agrave;n ph&iacute;m, do vậy việc cầm m&aacute;y hay di chuyển cũng vẫn thoải m&aacute;i, dễ d&agrave;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-3/asus-ct-21.jpg\" style=\"height:351px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bản lề ErgoLift Hinge</strong></span></h5>\r\n\r\n<p>Mọi chiếc ZenBook đều đặt sự thoải m&aacute;i của bạn l&agrave; một trong những ưu ti&ecirc;n h&agrave;ng đầu. ZenBook 14 nỗ lực vượt bậc với bản lề ErgoLift, một tuyệt t&aacute;c kỹ thuật được thiết kế để tự động n&acirc;ng b&agrave;n ph&iacute;m tới vị tr&iacute; g&otilde; thoải m&aacute;i nhất. Nhưng đ&oacute; chưa phải l&agrave; tất cả: độ n&acirc;ng được t&iacute;nh to&aacute;n tỉ mỉ cũng cải thiện hiệu suất l&agrave;m m&aacute;t v&agrave; hiệu suất &acirc;m thanh.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-3/asus-ct-22.jpg\" style=\"height:338px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh NanoEdge 14 inch Full HD</strong></span></h5>\r\n\r\n<p>Laptop có m&agrave;n h&igrave;nh NanoEdge 14 inch Full HD&nbsp;(1920 x 1080) tối đa h&oacute;a trải nghiệm xem của bạn, với dải m&agrave;u rộng đạt 100% sRGB v&agrave; độ tương phản cao, đ&aacute;p ứng xuất sắc nhu cầu chơi games lẫn chỉnh sửa đồ họa của người d&ugrave;ng chuy&ecirc;n nghiệp, đảm bảo xuất ra h&igrave;nh ảnh với chất lượng sắc n&eacute;t v&agrave; ch&acirc;n thật nhất. Tấm nền chống ch&oacute;i Anti-glare gi&uacute;p bạn c&oacute; thể trải nghiệm tốt hơn ở m&ocirc;i trường b&ecirc;n ngo&agrave;i, c&oacute; khả năng chống ch&oacute;i tốt.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-3/asus-ct-23.jpg\" style=\"height:384px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>T&ugrave;y chỉnh m&agrave;u sắc tối ưu</strong></span></h5>\r\n\r\n<p>Nhờ tiện &iacute;ch ASUS Splendid được t&iacute;ch hợp sẵn tr&ecirc;n&nbsp;Asus Zenbook 14 UX433FA-A6111T, bạn sẽ c&oacute; thể thoải m&aacute;i thiết lập m&agrave;u sắc tối ưu tr&ecirc;n m&agrave;n h&igrave;nh đem lại một trải nghiệm h&igrave;nh ảnh ph&ugrave; hợp mọi l&uacute;c mọi nơi. Với c&aacute;c chế độ được thiết lập sẵn như sau: Chế độ thường &ndash; m&agrave;u sắc được thiết lập tối ưu, chế độ Eye Care &ndash; giảm thiểu &aacute;nh xanh gi&uacute;p bảo vệ mắt, chế độ sống động &ndash; m&agrave;u sặc được hiệu chỉnh rực rỡ hơn, chế độ chỉnh tay &ndash; t&ugrave;y chỉnh m&agrave;u sắc theo sở th&iacute;ch của bạn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-3/asus-ct-24.jpg\" style=\"height:416px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bộ vi xử l&yacute; Intel Core i7</strong></span></h5>\r\n\r\n<p>M&aacute;y được trang bị bộ vi xử l&yacute; Intel Core i7-8565U với xung nhịp c&oacute; thể l&ecirc;n đến 4.6GHz, cho bạn những gi&acirc;y ph&uacute;t lướt web nhanh hơn, mạnh mẽ hơn. B&ecirc;n cạnh đ&oacute;, với bộ nhớ trong Ram 8GB LPDDR3-2133Mhz đảm bảo xử l&iacute; c&aacute;c t&aacute;c vụ đa nhiệm thật mượt m&agrave; v&agrave; ổn định. Ngo&agrave;i ra, m&aacute;y c&ograve;n trang bị th&ecirc;m bộ nhớ 512GB SSD M.2 PCIe, cho bạn lưu trữ cũng như vận h&agrave;nh m&aacute;y một c&aacute;ch nhanh ch&oacute;ng, mượt m&agrave; hơn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-3/asus-ct-25.jpg\" style=\"height:570px; width:750px\" /></p>\r\n\r\n<p>Khả năng xử l&yacute; đồ họa của Asus Zenbook 14 UX433FA-A6111T&nbsp;cũng được tăng cường với card đồ họa t&iacute;ch hợp Intel UHD Graphics 620, cho khả năng chạy tốt v&agrave; ổn định c&aacute;c phần mềm đồ họa cũng như đ&aacute;p ứng nhu cầu giải tr&iacute; của người d&ugrave;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Độ bền chuẩn qu&acirc;n đội</strong></span></h5>\r\n\r\n<p>Đ&aacute;p ứng ti&ecirc;u chuẩn qu&acirc;n đội MIL-STD-810G cực kỳ khắt khe về độ tin cậy v&agrave; độ bền bỉ, trải qua một chế độ thử nghiệm &ldquo;tra tấn&rdquo; gồm nhiều b&agrave;i thử nghiệm mở rộng về khả năng hoạt động trong c&aacute;c m&ocirc;i trường khắc nghiệt bao gồm độ cao, nhiệt độ v&agrave; độ ẩm tới hạn. Thiết bị cũng đ&atilde; vượt qua c&aacute;c b&agrave;i thử nghiệm m&aacute;y t&iacute;nh x&aacute;ch tay nội bộ của ASUS cao hơn nhiều so với c&aacute;c ti&ecirc;u chuẩn do ng&agrave;nh đặt ra.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-3/asus-ct-26.jpg\" style=\"height:468px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>&Acirc;m thanh Harman Kardon vượt ngo&agrave;i mong đợi</strong></span></h5>\r\n\r\n<p>&Acirc;m thanh Harman Kardon bao gồm c&ocirc;ng nghệ khuếch đại th&ocirc;ng minh cho &acirc;m thanh lớn hơn m&agrave; kh&ocirc;ng bị m&eacute;o tiếng v&agrave; phần mềm được thiết kế th&ocirc;ng minh đảm bảo rằng Asus Zenbook 14 UX433FA-A6111T&nbsp;sẽ cho bạn một trải nghiệm &acirc;m thanh đỉnh cao.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-3/asus-ct-27.jpg\" style=\"height:521px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m chiclet v&agrave; b&agrave;n di chuột Numpad</strong></span></h5>\r\n\r\n<p>B&agrave;n ph&iacute;m được trang bị tr&ecirc;n Asus Zenbook 14 UX433FA-A6111T&nbsp;l&agrave; dạng b&agrave;n ph&iacute;m chiclet với thiết kế nguy&ecirc;n khối, h&agrave;nh tr&igrave;nh ph&iacute;m 1.4mm chuẩn mực v&agrave; hỗ trợ th&ecirc;m đ&egrave;n nền b&agrave;n ph&iacute;m sẽ gi&uacute;p bạn cảm gi&aacute;c g&otilde; ph&iacute;m tốt hơn trong khi l&agrave;m việc v&agrave; giải tr&iacute;. B&ecirc;n cạnh đ&oacute; với touchpad Numpad được chế t&aacute;c th&ocirc;ng minh cho độ chuẩn x&aacute;c cao, được phủ th&ecirc;m lớp k&iacute;nh để di chuột thoải m&aacute;i hơn, sử dụng c&ocirc;ng nghệ ph&aacute;t hiện l&ograve;ng b&agrave;n tay v&agrave; hỗ trợ đa điểm.</p>\r\n\r\n<h5><span style=\"font-size:24px\">USB Type C tất cả trong một kết nối</span></h5>\r\n\r\n<p>Sở hữu cổng kết nối USB Type C đa dụng nhất hiện nay, với thiết kế đồng đẳng gi&uacute;p bạn c&oacute; thể cắm cổng kết nối ở bất cứ chiều n&agrave;o. Ngo&agrave;i ra với c&ocirc;ng nghệ n&agrave;y sẽ cho tốc độ truyền tải dữ liệu nhanh gấp 10 lần so với c&aacute;c cổng kết nối cũ hiện nay.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-3/asus-ct-28.jpg\" style=\"height:356px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Thời lượng pin d&agrave;i cho nhịp sống kh&ocirc;ng ngừng nghỉ</strong></span></h5>\r\n\r\n<p>Nguồn điện năng của Asus Zenbook 14 UX433FA-A6111T&nbsp;đến từ vi&ecirc;n pin dung lượng lớn sẽ kh&ocirc;ng l&agrave;m bạn thất vọng, cho d&ugrave; ng&agrave;y l&agrave;m việc của bạn d&agrave;i v&agrave; c&oacute; đ&ograve;i hỏi khắt khe như thế n&agrave;o đi chăng nữa. Những chuyến đi d&agrave;i hay những cuộc họp k&eacute;o d&agrave;i cả ng&agrave;y đều kh&ocirc;ng th&agrave;nh vấn đ&ecirc;̀ với ZenBook 14.</p>\r\n', 2, '2019-07-05 15:10:35', 'Bán chạy', 'Bình thường', 1),
(14, 'Macbook Pro 2017 (13.3 inch) Core i5/128GB SSD PCIe/8GB 2133MHz LPDDR3', 28290000, 29990000, 21, 'Chip: Intel Core i5 processor\r\n\r\nRAM: 8GB 2133MHz LPDDR3\r\n\r\nỔ cứng: 128GB SSD PCIe\r\n\r\nChipset đồ họa: Intel Iris Plus Graphics 640\r\n\r\nMàn hình: 13.3 Inches (2560 x 1600 pixels) LED-Backlit IPS\r\n\r\nHệ điều hành: MacOS Sierra\r\n\r\nPin: Lithium polymer', 'mb1.jpg', 'mb1-1.jpg', 'mb1-2.jpg', 'mb1-3.jpg', 'mb1-4.jpg', 'mb1-5.jpg', 'mb1-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Kiểu d&aacute;ng hiện đại, sang trọng</strong></span></h5>\r\n\r\n<p>Macbook Pro 2017 MPXQ2&nbsp;với trọng lượng nhẹ 1.37kg, d&agrave;y 1.49cm tạo t&iacute;nh linh hoạt, tiện dụng cao. Sản phẩm với vật liệu kim loại nguy&ecirc;n khối chắc chắn, m&agrave;u sắc nh&atilde; nhặn kh&ocirc;ng k&eacute;m phần sang trọng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hiệu suất mạnh mẽ</strong></span></h5>\r\n\r\n<p>Kh&ocirc;ng chỉ ăn điểm bởi sự sang trọng v&agrave; đẳng cấp, m&agrave; c&ograve;n ăn điểm về bộ vi xử l&yacute; được ẩn chứa b&ecirc;n trong. M&aacute;y được trang bị dual-core Intel Core i5 2.3GHz v&agrave; Turbo Boost up to 3.6GHz. Với RAM 8GB, 128GB SSD v&agrave; card đồ họa Intel Iris Plus Graphics 640 chắc chắn sẽ l&agrave;m v&ocirc; c&ugrave;ng h&agrave;i l&ograve;ng khi sử dụng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/mb-ct-1.jpg\" style=\"height:667px; width:1000px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh LED-Backlit IPS tăng khả năng hiển thị</strong></span></h5>\r\n\r\n<p>Được trang bị m&agrave;n h&igrave;nh LED-backlit 13.3 inch với c&ocirc;ng nghệ chống ch&oacute;i IPS độ ph&acirc;n giải (2560 x 1600 pixels) cho chất lượng h&igrave;nh ảnh hiển thị sắc n&eacute;t hơn, ch&acirc;n thật hơn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/mb-ct-2.jpg\" style=\"height:667px; width:1000px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Cổng Thunderbolt 3</strong></span></h5>\r\n\r\n<p>Cổng Thunderbolt 3 cung cấp kết nối với tốc độ cao v&agrave; linh hoạt, cung cấp băng th&ocirc;ng gấp đ&ocirc;i so với Thunderbolt 2 cho khả năng truyền dữ liệu, video, sạc chỉ trong 1 kết nối duy nhất. Thunderbolt 3 c&ograve;n cho ph&eacute;p chuyển dữ liệu với tốc độ l&ecirc;n đến 40Gbps.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>&Acirc;m thanh trung thực</strong></span></h5>\r\n\r\n<p>Bạn sẽ được thưởng thức những &acirc;m thanh trung thực v&agrave; sống động nhất. Loa được kết nối trực tiếp với điện năng của hệ thống, cho ph&eacute;p khuếch đại đỉnh cao hơn. MacBook Pro chắc hẳn l&agrave; sự lựa chọn ho&agrave;n hảo chỉnh sửa video, hoặc thưởng thức &acirc;m nhạc hay những bộ phim bom tấn.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Đ&egrave;n nền b&agrave;n ph&iacute;m</strong></span></h5>\r\n\r\n<p>Đ&egrave;n LED nền đẹp mắt tr&ecirc;n b&agrave;n ph&iacute;m gi&uacute;p tăng độ ch&iacute;nh x&aacute;c khi d&ugrave;ng ph&iacute;m nhất l&agrave; v&agrave;o ban đ&ecirc;m, ngo&agrave;i ra đ&egrave;n ph&iacute;m c&ograve;n tạo điểm nhấn kh&aacute;c biệt v&agrave; đầy sang trọng gi&uacute;p chiếc Macbook Pro 2017 c&agrave;ng long lanh hơn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/mb-ct-3.jpg\" style=\"height:667px; width:1000px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Chuột cảm ứng lực</strong></span></h5>\r\n\r\n<p>Được trang bị th&ecirc;m tr&ecirc;n Macbook Pro 2017 là chuột cảm ứng lực (Force Touch) mới, gi&uacute;p đem lại cho người d&ugrave;ng một cảm gi&aacute;c si&ecirc;u mượt m&agrave; v&agrave; cực tiện lợi để xem th&ocirc;ng tin nhanh khi sử dụng nữa.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/mb-ct-4.jpg\" style=\"height:667px; width:1000px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Thời lượng pin 10 giờ</strong></span></h5>\r\n\r\n<p>Được thiết kế để ph&ugrave; hợp với phong c&aacute;ch sống năng động hiện nay, Macbook Pro 2017&nbsp;sẽ cung cấp cho bạn thời lượng pin để c&oacute; thể sử dụng suốt cả ng&agrave;y. Với c&ocirc;ng nghệ pin Lithium Polymer sẽ cho m&aacute;y khả năng vận h&agrave;nh li&ecirc;n tục l&ecirc;n đến 10 tiếng đồng hồ. Điểm đặc biệt của d&ograve;ng pin Lithium Polymer c&oacute; thể duy tr&igrave; được năng lượng sạc lần đầu cho d&ugrave; sau h&agrave;ng trăm chu kỳ sạc, l&acirc;u hơn gấp 3 lần so với c&aacute;c loại pin ti&ecirc;u chuẩn hiện nay, gi&uacute;p bạn khỏi phải lo lắng về hao m&ograve;n hao m&ograve;n điện năng pin.</p>\r\n', 4, '2019-07-05 03:48:37', 'Bình thường', 'Bình thường', 1),
(15, 'HP Pavilion 15-cs1009TU 5JL43PA Core i5-8265U/Win10 (15.6\" FHD)', 14090000, 15490000, 30, 'Chip: Intel Core i5-8265U (1.60GHz Up to 3.90 GHz, 4Cores, 8Threads, 6MB Cache, FSB 4GT/s)\r\n\r\nRAM: 4GB DDR4-2666MHz\r\n\r\nỔ cứng: 1TB HDD 5400rpm\r\n\r\nChipset đồ họa: Intel UHD Graphics 620\r\n\r\nMàn hình: 15.6 inch Full HD (1920 x 1080) SVA BrightView WLED-backlit\r\n\r\nHệ điều hành: Windows 10 Home\r\n\r\nPin: 3 cell 41 Wh, Pin liền', 'hp2.jpg', 'hp2-2.jpg', 'hp2-3.jpg', 'hp2-4.jpg', 'hp2-5.jpg', 'hp2-6.jpg', 'hp2-1.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế kiểu d&aacute;ng cứng c&aacute;p</strong></span></h5>\r\n\r\n<p><strong>Laptop HP Pavilion 15-cs1009TU 5JL43PA</strong><strong>&nbsp;</strong>được thiết kế kiểu d&aacute;ng cứng c&aacute;p với bốn g&oacute;c cạnh được bo tr&ograve;n vừa phải, mang đến vẻ đẹp mạnh mẽ v&agrave; đẳng cấp cho chiếc m&aacute;y, cùng với vẻ ngo&agrave;i đơn giản m&agrave; cứng c&aacute;p, ch&acirc;́t li&ecirc;̣u vỏ nh&ocirc;m cao c&acirc;́p tạo cảm gi&aacute;c chắc tay cho người d&ugrave;ng khi cầm nắm, dễ d&agrave;ng bỏ v&agrave;o t&uacute;i x&aacute;ch để mang theo mọi l&uacute;c mọi nơi.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/hp-ct-1.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh WLED-backlit 15.6 inch&nbsp;Full HD</strong></span></h5>\r\n\r\n<p>Được trang bị m&agrave;n h&igrave;nh SVA BrightView WLED-backlit 15.6 inch độ ph&acirc;n giải Full HD (1920 x 1080) n&ecirc;n cho chất lượng hiển thị kh&aacute; tốt khi h&igrave;nh ảnh được hiển thị sắc n&eacute;t, độ chi tiết cao v&agrave; m&agrave;u sắc trung thực ở khoảng c&aacute;ch sử dụng th&ocirc;ng thường của người d&ugrave;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/hp-ct-2.jpg\" style=\"height:499px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bộ vi xử l&yacute; Intel Core i5</strong></span></h5>\r\n\r\n<p>Được trang bị vi xử l&yacute;&nbsp;Intel Core i5-8265U xung nhịp 3.9GHz đi k&egrave;m l&agrave; dung lượng RAM 4GB DDR4-2666MHz, ổ cứng 1TB HDD 5400rpm laptop đ&aacute;p ứng tốt nhu cầu sử dụng hằng ng&agrave;y của người d&ugrave;ng. M&aacute;y c&oacute; thể xử l&yacute; mượt m&agrave; v&agrave; trơn tru tất cả c&aacute;c t&aacute;c vụ thường ng&agrave;y của người d&ugrave;ng như duyệt web, xem phim,&hellip;</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/hp-ct-3.jpg\" style=\"height:499px; width:750px\" /></p>\r\n\r\n<p>Đặc biệt, với card đồ họa Intel UHD Graphics 620 m&aacute;y c&ograve;n c&oacute; thể đ&aacute;p ứng tốt c&aacute;c như cầu giải tr&iacute; v&agrave; l&agrave;m việc kh&aacute;c như game online, nhiều game 3D, c&aacute;c c&ocirc;ng cụ đồ họa Photoshop, Lightroom&hellip;</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>&Acirc;m thanh bang &amp; olufsen (B&amp;O PLAY)</strong></span></h5>\r\n\r\n<p>Laptop sẽ giúp bạn trải nghiệm chiều s&acirc;u &acirc;m nhạc mới đầy sống động v&agrave; ch&acirc;n thực với loa k&eacute;p, c&ocirc;ng nghệ khuếch &acirc;m HP Audio Boost v&agrave; &acirc;m thanh tinh chỉnh bởi c&aacute;c chuy&ecirc;n gia của B&amp;O PLAY.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/hp-ct-4.jpg\" style=\"height:499px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m&nbsp;bấm &ecirc;m v&agrave; Touchpad&nbsp;rộng r&atilde;i, nhạy</strong></span></h5>\r\n\r\n<p>Được trang bị b&agrave;n ph&iacute;m hiện đại với khoảng c&aacute;ch giữa c&aacute;c ph&iacute;m kh&aacute; hợp l&yacute;, bấm &ecirc;m v&agrave; h&agrave;nh tr&igrave;nh ph&iacute;m đủ s&acirc;u n&ecirc;n cho cảm gi&aacute;c g&otilde; kh&aacute; tốt, cho cảm gi&aacute;c thoải m&aacute;i d&ugrave; sử dụng trong một thời gian d&agrave;i. Chưa hết phần touchpad đi k&egrave;m cũng c&oacute; diện t&iacute;ch kh&aacute; rộng r&atilde;i, nhạy v&agrave; hỗ trợ đa điểm kh&aacute; tốt n&ecirc;n c&aacute;c thao t&aacute;c như thu ph&oacute;ng, cuộn văn bản hay xoay h&igrave;nh ảnh đều được thực hiện dễ d&agrave;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hỗ trợ nhiều cổng kết nối</strong></span></h5>\r\n\r\n<p>HP Pavilion 15-cs1009TU 5JL43PA&nbsp;còn hỗ trợ nhiều cổng kết nối như 1 jack &acirc;m thanh combo 3.5 mm, HDMI, Ethernet RJ45 c&ugrave;ng 1 khe thẻ nhớ SD media cho khả năng kết nối với c&aacute;c thiết bị kh&aacute;c một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Thời lượng pin l&acirc;u hơn</strong></span></h5>\r\n\r\n<p>Được thiết kế để ph&ugrave; hợp với phong c&aacute;ch sống năng động hiện nay, HP Pavilion 15-cs1009TU 5JL43PA&nbsp;sẽ cung cấp cho bạn thời lượng pin để c&oacute; thể sử dụng suốt cả ng&agrave;y. Với c&ocirc;ng nghệ pin Lithium Polymer sẽ cho m&aacute;y khả năng vận h&agrave;nh li&ecirc;n tục l&ecirc;n đến 8 tiếng đồng hồ. Điểm đặc biệt của d&ograve;ng pin Lithium Polymer c&oacute; thể duy tr&igrave; được năng lượng sạc lần đầu cho d&ugrave; sau h&agrave;ng trăm chu kỳ sạc, l&acirc;u hơn gấp 3 lần so với c&aacute;c loại pin ti&ecirc;u chuẩn hiện nay, gi&uacute;p bạn khỏi phải lo lắng về hao m&ograve;n hao m&ograve;n điện năng pin.</p>\r\n', 3, '2019-06-15 06:38:16', 'Bình thường', 'Bình thường', 1),
(16, 'HP Pavilion Gaming 15-cx0179TX 5EF42PA Core i5-8300H/Win10 (15.6\" FHD)', 19399000, 21990000, 26, 'Chip: Intel Core i5-8300H (2.3GHz upto 4.0GHz, 4Cores, 8Threads, 8MB cache, FSB 8GT/s)\r\n\r\nRAM: 8GB DDR4-2666MHz, 2 Slots, Max 32GB\r\n\r\nỔ cứng: 1TB HDD 7200rpm + 1 Slot SSD M.2 PCIe\r\n\r\nChipset đồ họa: NVIDIA GeForce GTX 1050 4GB GDDR5 + Intel UHD Graphics 630\r\n\r\nMàn hình: 15.6-inch Diagonal Full HD (1920 x 1080) IPS anti-glare micro-edge WLED-backlit\r\n\r\nHệ điều hành: Windows 10 Home\r\n\r\nPin: 3-Cell 52.5 Whr', 'hp3.jpg', 'hp3-1.jpg', 'hp3-2.jpg', 'hp3-3.jpg', 'hp3-4.jpg', 'hp3-5.jpg', 'hp3-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế kiểu d&aacute;ng cứng c&aacute;p</strong></span></h5>\r\n\r\n<p><strong>Laptop HP Pavilion Gaming 15-cx0179TX 5EF42PA</strong><strong>&nbsp;</strong>được thiết kế kiểu d&aacute;ng cứng c&aacute;p với bốn g&oacute;c cạnh được bo tr&ograve;n vừa phải, mang đến vẻ đẹp mạnh mẽ v&agrave; đẳng cấp cho chiếc m&aacute;y, cùng với vẻ ngo&agrave;i đơn giản m&agrave; cứng c&aacute;p tạo cảm gi&aacute;c chắc tay cho người d&ugrave;ng khi cầm nắm, dễ d&agrave;ng bỏ v&agrave;o t&uacute;i x&aacute;ch để mang theo mọi l&uacute;c mọi nơi.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/hp-ct-5.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh 15.6 inch&nbsp;Full HD</strong></span></h5>\r\n\r\n<p>Được trang bị m&agrave;n h&igrave;nh 15.6 inch độ ph&acirc;n giải Full HD (1920 x 1080) c&ugrave;ng c&ocirc;ng nghệ BrightView WLED-backlit c&oacute; chất lượng ho&agrave;n thiện kh&aacute; tốt, m&agrave;u sắc trung t&iacute;nh, viền m&agrave;n h&igrave;nh chắc chắn, c&aacute;c lớp hiển thị được &eacute;p chặt v&agrave;o nhau, hiển thị chi tiết đủ đ&aacute;p ứng nhu cầu giải tr&iacute; cơ bản của người d&ugrave;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/hp-ct-6.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<p>Tấm nền IPS gi&uacute;p bạn c&oacute; thể trải nghiệm g&oacute;c nh&igrave;n rộng 178&deg; đem lại h&igrave;nh ảnh sắc n&eacute;t, sống động v&agrave; độ tương phản cao ngay cả ở những g&oacute;c nh&igrave;n hẹp.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bộ vi xử l&yacute; Intel Core i5</strong></span></h5>\r\n\r\n<p>Được trang bị vi xử l&yacute;&nbsp;Intel Core i5-8300H xung nhịp 4.0GHz đi k&egrave;m l&agrave; dung lượng 8GB DDR4-2666MHz, 2 Slots, Max 32GB, ổ cứng 1TB HDD giúp laptop đ&aacute;p ứng tốt nhu cầu sử dụng hằng ng&agrave;y của người d&ugrave;ng. M&aacute;y c&oacute; thể xử l&yacute; mượt m&agrave; v&agrave; trơn tru tất cả c&aacute;c t&aacute;c vụ thường ng&agrave;y của người d&ugrave;ng như duyệt web, xem phim.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Card đồ họa NVIDIA GeForce GTX 1050</strong></span></h5>\r\n\r\n<p>Đặc biệt, với card đồ họa rời&nbsp;NVIDIA GeForce GTX 1050 với b&ocirc;̣ nhớ đ&ocirc;̀ họa&nbsp;4GB GDDR5, bạn c&oacute; thể ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m rằng m&aacute;y c&oacute; thể h&ocirc;̃ trợ bạn tối đa từ c&aacute;c việc đơn giản đến c&aacute;c c&ocirc;ng việc mang t&iacute;nh chất đ&ograve;i hỏi cao như thiết kế đồ họa, 3D, photoshop, cũng như giải tr&iacute; với những game 3D khủng nhất hiện nay.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>&Acirc;m thanh bang &amp; olufsen (B&amp;O PLAY)</strong></span></h5>\r\n\r\n<p>Laptop sẽ giúp bạn trải nghiệm chiều s&acirc;u &acirc;m nhạc mới đầy sống động v&agrave; ch&acirc;n thực với loa k&eacute;p, c&ocirc;ng nghệ khuếch &acirc;m HP Audio Boost v&agrave; &acirc;m thanh tinh chỉnh bởi c&aacute;c chuy&ecirc;n gia của B&amp;O PLAY.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/hp-ct-7.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m&nbsp;hỗ trợ đ&egrave;n nền&nbsp;v&agrave; Touchpad rộng r&atilde;i</strong></span></h5>\r\n\r\n<p>B&agrave;n ph&iacute;m được trang bị tr&ecirc;n&nbsp;HP Pavilion Gaming 15-cx0179TX 5EF42PA&nbsp;l&agrave; dạng b&agrave;n ph&iacute;m chiclet với thiết kế nguy&ecirc;n khối, hỗ trợ th&ecirc;m đ&egrave;n nền màu xanh lá b&agrave;n ph&iacute;m sẽ gi&uacute;p bạn cảm gi&aacute;c g&otilde; ph&iacute;m tốt hơn trong khi l&agrave;m việc v&agrave; giải tr&iacute;.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/hp-ct-8.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<p>Chưa hết phần touchpad đi k&egrave;m cũng c&oacute; diện t&iacute;ch kh&aacute; rộng r&atilde;i, nhạy v&agrave; hỗ trợ đa điểm kh&aacute; tốt n&ecirc;n c&aacute;c thao t&aacute;c như thu ph&oacute;ng, cuộn văn bản hay xoay h&igrave;nh ảnh đều được thực hiện dễ d&agrave;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>USB Type C tất cả trong một kết nối</strong></span></h5>\r\n\r\n<p>Laptop sở hữu cổng kết nối USB Type C đa dụng nhất hiện nay, với thiết kế đồng đẳng gi&uacute;p bạn c&oacute; thể cắm cổng kết nối ở bất cứ chiều n&agrave;o. Ngo&agrave;i ra với c&ocirc;ng nghệ n&agrave;y sẽ cho tốc độ truyền tải dữ liệu nhanh gấp 10 lần so với c&aacute;c cổng kết nối cũ hiện nay.</p>\r\n\r\n<p>HP Pavilion Gaming 15-cx0179TX 5EF42PA còn hỗ trợ nhiều cổng kết nối như jack &acirc;m thanh, HDMI, Ethernet RJ45 c&ugrave;ng 1 khe thẻ nhớ SD media cho khả năng kết nối với c&aacute;c thiết bị kh&aacute;c một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Thời lượng pin l&acirc;u hơn</strong></span></h5>\r\n\r\n<p>Được thiết kế để ph&ugrave; hợp với phong c&aacute;ch sống năng động hiện nay, HP Pavilion Gaming 15-cx0179TX 5EF42PA&nbsp;sẽ cung cấp cho bạn thời lượng pin để c&oacute; thể sử dụng suốt cả ng&agrave;y. Với c&ocirc;ng nghệ pin Lithium Polymer sẽ cho m&aacute;y khả năng vận h&agrave;nh li&ecirc;n tục l&ecirc;n đến 8 tiếng đồng hồ. Điểm đặc biệt của d&ograve;ng pin Lithium Polymer c&oacute; thể duy tr&igrave; được năng lượng sạc lần đầu cho d&ugrave; sau h&agrave;ng trăm chu kỳ sạc, l&acirc;u hơn gấp 3 lần so với c&aacute;c loại pin ti&ecirc;u chuẩn hiện nay, gi&uacute;p bạn khỏi phải lo lắng về hao m&ograve;n hao m&ograve;n điện năng pin.</p>\r\n', 3, '2019-07-07 07:07:58', 'Bình thường', 'Bình thường', 1);
INSERT INTO `product` (`id`, `product_name`, `price_new`, `price_old`, `qty_product`, `product_desc`, `product_thumb`, `list_thumb_1`, `list_thumb_2`, `list_thumb_3`, `list_thumb_4`, `list_thumb_5`, `list_thumb_6`, `product_content`, `cat_id`, `created_at`, `selling_products`, `featured_products`, `status`) VALUES
(17, 'HP Pavilion 15-cs1080TX 5RB14PA Core i7-8565U/Win10 (15.6\" FHD)', 18939000, 20990000, 30, 'Chip: Intel Core i7-8565U (1.80GHz Up to 4.60 GHz, 4Cores, 8Threads, 8MB Cache, FSB 4GT/s)\r\n\r\nRAM: 8GB DDR4 2666MHz\r\n\r\nỔ cứng: 1TB HDD 5400rpm\r\n\r\nChipset đồ họa: NVIDIA GeForce MX150 2GB GDDR5 + Intel UHD Graphics 620\r\n\r\nMàn hình: 15.6-inch diagonal Full HD (1920 x 1080) SVA BrightView WLED-backlit\r\n\r\nHệ điều hành: Windows 10 Home\r\n\r\nPin: 3-Cell 41 Whr Li-ion', 'hp4.jpg', 'hp4-1.jpg', 'hp4-2.jpg', 'hp4-3.jpg', 'hp4-4.jpg', 'hp4-5.jpg', 'hp4-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế kiểu d&aacute;ng cứng c&aacute;p</strong></span></h5>\r\n\r\n<p><strong>Laptop HP Pavilion 15-cs1080TX 5RB14PA</strong><strong>&nbsp;</strong>được thiết kế kiểu d&aacute;ng cứng c&aacute;p với bốn g&oacute;c cạnh được bo tr&ograve;n vừa phải, mang đến vẻ đẹp mạnh mẽ v&agrave; đẳng cấp cho chiếc m&aacute;y, cùng với vẻ ngo&agrave;i đơn giản m&agrave; cứng c&aacute;p, ch&acirc;́t li&ecirc;̣u vỏ nh&ocirc;m cao c&acirc;́p tạo cảm gi&aacute;c chắc tay cho người d&ugrave;ng khi cầm nắm, dễ d&agrave;ng bỏ v&agrave;o t&uacute;i x&aacute;ch để mang theo mọi l&uacute;c mọi nơi.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/hp-ct-9.jpg\" style=\"height:422px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh WLED-backlit 15.6 inch&nbsp;Full HD</strong></span></h5>\r\n\r\n<p>Được trang bị m&agrave;n h&igrave;nh SVA BrightView WLED-backlit 15.6 inch độ ph&acirc;n giải Full HD (1920 x 1080) n&ecirc;n cho chất lượng hiển thị kh&aacute; tốt khi h&igrave;nh ảnh được hiển thị sắc n&eacute;t, độ chi tiết cao v&agrave; m&agrave;u sắc trung thực ở khoảng c&aacute;ch sử dụng th&ocirc;ng thường của người d&ugrave;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/hp-ct-10.jpg\" style=\"height:499px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bộ vi xử l&yacute; Intel Core i7</strong></span></h5>\r\n\r\n<p>Được trang bị vi xử l&yacute;&nbsp;Intel Core i7-8565U xung nhịp 4.6GHz đi k&egrave;m l&agrave; dung lượng RAM 8GB DDR4 2666MHz, ổ cứng 1TB HDD 5400RPM laptop đ&aacute;p ứng tốt nhu cầu sử dụng hằng ng&agrave;y của người d&ugrave;ng. M&aacute;y c&oacute; thể xử l&yacute; mượt m&agrave; v&agrave; trơn tru tất cả c&aacute;c t&aacute;c vụ thường ng&agrave;y của người d&ugrave;ng như duyệt web, xem phim,&hellip;</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/hp-ct-11.jpg\" style=\"height:499px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Card đồ họa NVIDIA GeForce MX150</strong></span></h5>\r\n\r\n<p>Đặc biệt, với card đồ họa NVIDIA GeForce MX150 bạn c&oacute; thể ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m rằng m&aacute;y c&oacute; thể h&ocirc;̃ trợ bạn tối đa từ c&aacute;c việc đơn giản đến c&aacute;c c&ocirc;ng&nbsp; việc mang t&iacute;nh chất đ&ograve;i hỏi cao như thiết kế đồ họa, 3D, photoshop, cũng như giải tr&iacute; với những game 3D khủng nhất hiện nay.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>&Acirc;m thanh bang &amp; olufsen (B&amp;O PLAY)</strong></span></h5>\r\n\r\n<p>Laptop sẽ giúp bạn trải nghiệm chiều s&acirc;u &acirc;m nhạc mới đầy sống động v&agrave; ch&acirc;n thực với loa k&eacute;p, c&ocirc;ng nghệ khuếch &acirc;m HP Audio Boost v&agrave; &acirc;m thanh tinh chỉnh bởi c&aacute;c chuy&ecirc;n gia của B&amp;O PLAY.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/images/hp-ct-12.jpg\" style=\"height:499px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m&nbsp;bấm &ecirc;m v&agrave; Touchpad&nbsp;rộng r&atilde;i, nhạy</strong></span></h5>\r\n\r\n<p>Được trang bị b&agrave;n ph&iacute;m hiện đại với khoảng c&aacute;ch giữa c&aacute;c ph&iacute;m kh&aacute; hợp l&yacute;, bấm &ecirc;m v&agrave; h&agrave;nh tr&igrave;nh ph&iacute;m đủ s&acirc;u n&ecirc;n cho cảm gi&aacute;c g&otilde; kh&aacute; tốt, cho cảm gi&aacute;c thoải m&aacute;i d&ugrave; sử dụng trong một thời gian d&agrave;i. Chưa hết phần touchpad đi k&egrave;m cũng c&oacute; diện t&iacute;ch kh&aacute; rộng r&atilde;i, nhạy v&agrave; hỗ trợ đa điểm kh&aacute; tốt n&ecirc;n c&aacute;c thao t&aacute;c như thu ph&oacute;ng, cuộn văn bản hay xoay h&igrave;nh ảnh đều được thực hiện dễ d&agrave;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hỗ trợ nhiều cổng kết nối</strong></span></h5>\r\n\r\n<p>HP Pavilion 15-cs1080TX 5RB14PA&nbsp;còn hỗ trợ nhiều cổng kết nối như 1 jack &acirc;m thanh combo 3.5 mm, HDMI, Ethernet RJ45 c&ugrave;ng 1 khe thẻ nhớ SD media cho khả năng kết nối với c&aacute;c thiết bị kh&aacute;c một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Thời lượng pin l&acirc;u hơn</strong></span></h5>\r\n\r\n<p>Được thiết kế để ph&ugrave; hợp với phong c&aacute;ch sống năng động hiện nay, HP Pavilion 15-cs1080TX 5RB14PA&nbsp;sẽ cung cấp cho bạn thời lượng pin để c&oacute; thể sử dụng suốt cả ng&agrave;y. Với c&ocirc;ng nghệ pin Lithium Polymer sẽ cho m&aacute;y khả năng vận h&agrave;nh li&ecirc;n tục l&ecirc;n đến 8 tiếng đồng hồ. Điểm đặc biệt của d&ograve;ng pin Lithium Polymer c&oacute; thể duy tr&igrave; được năng lượng sạc lần đầu cho d&ugrave; sau h&agrave;ng trăm chu kỳ sạc, l&acirc;u hơn gấp 3 lần so với c&aacute;c loại pin ti&ecirc;u chuẩn hiện nay, gi&uacute;p bạn khỏi phải lo lắng về hao m&ograve;n hao m&ograve;n điện năng pin.</p>\r\n', 3, '2019-07-07 07:08:51', 'Bình thường', 'Nổi bật', 1),
(18, 'MSI GF63 Thin 9SC-070VN Core i7-9750H/GTX 1650 4GB/Win10', 25498000, 27990000, 30, 'CPU: Intel Core i7-9750H (2.6GHz up to 4.5GHz 12MB)\r\n\r\nRAM: 8GB DDR4 2666MHz\r\n\r\nHDD: 256GB NVMe PCIe SSD\r\n\r\nVGA: nVidia Geforce GTX1650 MAX Q 4GB GDDR5\r\n\r\nMàn hình: 15.6\" FHD (1920 x 1080)\r\n\r\nIPS-Level 60Hz 45%NTSCThin Bezel\r\n\r\nHệ điều hành: Windows 10 Home\r\n\r\nPin: 3 cell, 51Whr', 'msi1.jpg', 'msi1-1.jpg', 'msi1-2.jpg', 'msi1-3.jpg', 'msi1-4.jpg', 'msi1-5.jpg', 'msi1-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế cổ điển của MSI</strong></span></h5>\r\n\r\n<p><strong>Laptop Gaming MSI GF63 Thin 9SC-070VN Core i7-9750H/GTX 1650 4GB/Win10 (15.6 FHD IPS)</strong>&nbsp;ho&agrave;n to&agrave;n thiết kế theo hướng classic ở ngoại h&igrave;nh tổng quan, ngo&agrave;i logo Rồng MSI m&agrave;u đỏ đặt ở vị tr&iacute; ch&iacute;nh giữa tạo tương phản th&igrave; to&agrave;n bộ th&acirc;n m&aacute;y l&agrave; một m&agrave;u đen. Đ&acirc;y l&agrave; một hướng thiết kế đ&atilde; v&agrave; đang được rất nhiều game thủ ưa th&iacute;ch do to&aacute;t l&ecirc;n được sự cứng c&aacute;p nguy&ecirc;n khối đồng thời kh&ocirc;ng c&oacute; những chi tiết &quot;thừa&quot;.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/msi-1/msi-ct-1.jpg\" style=\"height:600px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh IPS sắc n&eacute;t</strong></span></h5>\r\n\r\n<p>M&agrave;n h&igrave;nh của GF63 9SC l&agrave; m&agrave;n FullHD IPS c&oacute; tần số qu&eacute;t 60Hz cho g&oacute;c nh&igrave;n rộng. M&agrave;u sắc hiển thị ch&iacute;nh x&aacute;c v&agrave; s&aacute;ng hơn nhiều so với c&aacute;c laptop gaming vốn thường chỉ sử dụng tấm nền TN, m&aacute;y hiển thị m&agrave;u sắc mịn m&agrave;ng, v&ugrave;ng tối rất c&oacute; chiều s&acirc;u.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Viền si&ecirc;u mỏng cho trải nghiệm game đ&atilde; mắt</strong></span></h5>\r\n\r\n<p>Khi nhắc đến d&ograve;ng m&aacute;y GF63 n&agrave;y, ch&uacute;ng ta cũng phải nhắc đến viền m&agrave;n h&igrave;nh si&ecirc;u mỏng của m&aacute;y. Thứ m&agrave; trước đ&acirc;y chỉ xuất hiện tr&ecirc;n c&aacute;c ph&acirc;n kh&uacute;c m&aacute;y cao cấp, nhưng hiện n&agrave;y MSI đ&atilde; đưa xuống d&ograve;ng m&aacute;y gi&aacute; rẻ GF63 n&agrave;y để c&oacute; thể cạnh tranh với c&aacute;c h&atilde;ng sản xuất kh&aacute;c. Viền m&agrave;n h&igrave;nh m&aacute;y mỏng cho m&aacute;y một cảm gi&aacute;c tinh tế v&agrave; ph&ugrave; hợp với xu hướng thiết kế của laptop hi&ecirc;n nay. N&oacute; cũng gi&uacute;p cho tỉ lệ m&agrave;n h&igrave;nh với th&acirc;n m&aacute;y được tăng l&ecirc;n nhờ đ&oacute; trải nghiệm của người d&ugrave;ng cũng được tăng l&ecirc;n theo.&nbsp;</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Cấu h&igrave;nh mạnh mẽ, ấn tượng</strong></span></h5>\r\n\r\n<p>Ở phi&ecirc;n bản GF63 9SC n&agrave;y, MSI đ&atilde; tập trung trang bị cho m&aacute;y những linh kiện tốt nhất để tối ưu hiệu năng v&agrave; trải nghiệm cho người d&ugrave;ng. Ch&uacute;ng ta sẽ c&oacute; một vi xử l&yacute; thế hệ thứ 9 của Intel, i7-9750H (4 nh&acirc;n 8 luồng) kết hợp c&ugrave;ng với một t&acirc;n binh mới của đội xanh Nvidia GTX 1650 với sức mạnh tương đương GTX 1050 Ti nhưng được tối ưu v&agrave; ổn định hơn rất nhiều. Đ&aacute;nh gi&aacute; v&agrave; thử nghiệm hiệu năng của m&aacute;y qua c&aacute;c b&agrave;i test thực tế cũng cho kết quả rất tốt.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/msi-1/msi-ct-2.jpg\" style=\"height:600px; width:750px\" /></p>\r\n\r\n<p>Ngo&agrave;i ra th&igrave; m&aacute;y cũng được trang bị th&ecirc;m 8GB RAM DDR4 c&ugrave;ng một SSD 256 GB để bạn c&oacute; thể l&agrave;m việc với c&aacute;c t&aacute;c vụ đơn giản cũng như load game một c&aacute;ch nhanh nhất.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Nahimic 3 - Quản l&yacute; &acirc;m thanh ho&agrave;n hảo</strong></span></h5>\r\n\r\n<p>Phần mềm Nahimic 3 độc quyền của MSI tr&ecirc;n MSI GF63 9SC-070VN gi&uacute;p tăng cường chất lượng &acirc;m thanh v&ograve;m 3D trong tr&ograve; chơi v&agrave; kiểm so&aacute;t hữu hạn &acirc;m nhạc, phim v&agrave; cuộc gọi hội nghị của bạn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/msi-1/msi-ct-3.jpg\" style=\"height:600px; width:750px\" /></p>\r\n', 6, '2019-06-15 06:38:16', 'Bình thường', 'Bình thường', 1),
(19, 'MSI PS63 8SC-006VN Core i7-8565U/ GTX 1650 4GB/ Win10 (15.6 FHD IPS)', 37990000, 38990000, 30, 'CPU: Intel Core i7-8565U ( 1.8 GHz - 4.6 GHz / 8MB / 4 nhân, 8 luồng )\r\n\r\nMàn hình: 15.6\" IPS ( 1920 x 1080 ) , không cảm ứng\r\n\r\nRAM: 2 x 8GB DDR4 2666MHz\r\n\r\nĐồ họa: Intel UHD Graphics 620 / NVIDIA GeForce GTX 1650 4GB GDDR5\r\n\r\nLưu trữ: 512GB SSD M.2 NVMe\r\n\r\nHệ điều hành: Windows 10 Home SL 64-bit\r\n\r\nPin: 4 cell 82 Wh Pin liền', 'msi2.jpg', 'msi2-1.jpg', 'msi2-2.jpg', 'msi2-3.jpg', 'msi2-4.jpg', 'msi2-5.jpg', 'msi2-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Hiệu Suất T&iacute;nh To&aacute;n V&ocirc; Song</strong></span></h5>\r\n\r\n<p><strong>Laptop MSI PS63 8SC-006VN Core i7-8565U/ GTX 1650 4GB/ Win10 (15.6 FHD IPS)</strong>&nbsp;được trang bị bộ xử l&yacute; Intel Core i7 thế hệ thứ 8 mới nhất cho ph&eacute;p đa nhiệm đ&aacute;p ứng nhanh v&agrave; hiển thị đồ họa mượt m&agrave;. Với tốc độ chưa từng c&oacute;, sự s&aacute;ng tạo của bạn được tối ưu h&oacute;a ho&agrave;n to&agrave;n.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/msi-1/msi-ct-4.jpg\" style=\"height:474px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>THIN, FOR THE WIN &ndash; Mỏng hơn v&agrave; mạnh hơn</strong></span></h5>\r\n\r\n<p>Laptop MSI Prestige PS63 8SC-006VN mỏng hơn v&agrave; mạnh hơn với thiết kế GeForce Max-Q. C&ocirc;ng nghệ ti&ecirc;n tiến n&agrave;y được GPU NVIDIA GeForce trang bị cho c&aacute;c laptop mỏng nhất, nhanh nhất thế giới, ch&iacute;nh v&igrave; vậy mẫu laptop n&agrave;y cho ph&eacute;p bạn mang đi bất cứ đ&acirc;u.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Chi&ecirc;m ngưỡng thiết kế thanh lịch</strong></span></h5>\r\n\r\n<p>Thưởng thức trải nghiệm h&igrave;nh ảnh tuyệt đẹp của Laptop MSI PS63 8SC-006VN viền mỏng 5.6mm v&agrave; giải ph&oacute;ng tr&iacute; tưởng tượng của bạn khỏi khung h&igrave;nh. M&agrave;n h&igrave;nh FHD-IPS mang đến cho bạn một form m&aacute;y mỏng tuyệt vời m&agrave; kh&ocirc;ng cần số lượng lớn, v&agrave; cho ph&eacute;p bạn thực sự đắm m&igrave;nh trong trải nghiệm tinh tế.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/msi-1/msi-ct-5.jpg\" style=\"height:395px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Điểm s&aacute;ng kh&ocirc;ng gian l&agrave;m việc của bạn</strong></span></h5>\r\n\r\n<p>Được chế tạo trong khung nh&ocirc;m si&ecirc;u nhẹ v&agrave; si&ecirc;u mỏng với kết cấu cao cấp, PS63 Modern được sản xuất di động v&agrave; phong c&aacute;ch bất cứ nơi n&agrave;o bạn c&oacute; thể đi.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/msi-1/msi-ct-6.jpg\" style=\"height:511px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Pin L&ecirc;n Đến 16 Giờ, Cho Bạn C&oacute; Thể Di Chuyển Cả Ng&agrave;y</strong></span></h5>\r\n\r\n<p>Được thiết kế với lối sống bận rộn, di động trong t&acirc;m tr&iacute;, PS63 Modern cung cấp pin cả ng&agrave;y cho tất cả c&ocirc;ng việc v&agrave; giải tr&iacute; của bạn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/msi-1/msi-ct-7.jpg\" style=\"height:524px; width:750px\" /></p>\r\n', 6, '2019-06-15 06:38:16', 'Bình thường', 'Bình thường', 1),
(20, 'Lenovo ThinkPad X280 20KFS01B00 Core i7-8550U/Free Dos ', 33509000, 34990000, 30, 'Chip: Intel Core i7-8550U Processor 8M Cache, 1.8Ghz up to 4.00 GHz\r\n\r\nRAM: 8GB DDR4 2400MHz\r\n\r\nỔ cứng: 256GB SSD M.2 2280 NVME OPAL2\r\n\r\nChipset đồ họa: Intel UHD Graphics 620\r\n\r\nMàn hình: 12.5 inch Full HD (1920 x 1080) Anti-glare, LED backlight, IPS\r\n\r\nHệ điều hành: Free Dos\r\n\r\nPin: 6 Cells 48 Whrs', 'lenovo1.jpg', 'lenovo1-1.jpg', 'lenovo1-2.jpg', 'lenovo1-3.jpg', 'lenovo1-4.jpg', 'lenovo1-5.jpg', 'lenovo1-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế gập 180 độ</strong></span></h5>\r\n\r\n<p><strong>Laptop Lenovo ThinkPad X280 20KFS01B00</strong>&nbsp;mang đến cho người d&ugrave;ng một chiếc laptop mạnh mẽ v&agrave; bền vững nhờ được bao bọc bởi bộ khung chắc chắn. Lớp vỏ ngo&agrave;i l&agrave;m từ chất liệu cao cấp vừa mang lại một độ bền vượt trội để đủ sức bảo vệ phần m&agrave;n h&igrave;nh v&agrave; c&aacute;c linh kiện b&ecirc;n trong nhưng vẫn đảm bảo tối ưu sự linh hoạt v&agrave; tiện dụng cho người d&ugrave;ng mỗi khi mang theo m&aacute;y b&ecirc;n m&igrave;nh. Ngo&agrave;i ra, bản lề của m&aacute;y được thiết kế đặc biệt gi&uacute;p m&aacute;y c&oacute; thể gập 180 độ như quyển tập.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-1/lenovo-ct-1.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Màn hình&nbsp;LED 12.5 inch Full HD</strong></span></h5>\r\n\r\n<p>M&aacute;y được trang bị m&agrave;n h&igrave;nh&nbsp;LED 12.5 inch với độ ph&acirc;n giải Full HD (1920 x 1080) cho chất lượng hiển thị ấn tượng, sắc n&eacute;t, độ chi tiết cao. B&ecirc;n cạnh đ&oacute;, tấm nền IPS c&ograve;n mang lại m&agrave;u sắc trung thực, g&oacute;c nh&igrave;n rộng v&agrave; độ s&aacute;ng cao kết hợp với c&ocirc;ng nghệ Anti Glare chống ch&oacute;i n&ecirc;n người d&ugrave;ng c&oacute; thể sử dụng m&aacute;y thoải m&aacute;i trong điều kiện &aacute;nh s&aacute;ng mạnh với g&oacute;c nh&igrave;n kh&aacute;c nhau.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-1/lenovo-ct-2.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bộ vi xử l&yacute; Intel Core i7</strong></span></h5>\r\n\r\n<p>M&aacute;y được trang bị bộ vi xử l&yacute; Intel Core i7-8550U với xung nhịp c&oacute; thể l&ecirc;n đến 4GHz, cho bạn những gi&acirc;y ph&uacute;t lướt web nhanh hơn, mạnh mẽ hơn. B&ecirc;n cạnh đ&oacute;, với bộ nhớ trong Ram 8GB DDR4 (2400MHz) đảm bảo xử l&iacute; c&aacute;c t&aacute;c vụ đa nhiệm thật mượt m&agrave; v&agrave; ổn định. Ngo&agrave;i ra, m&aacute;y c&ograve;n trang bị th&ecirc;m bộ nhớ 256GB SSD, cho bạn lưu trữ cũng như vận h&agrave;nh m&aacute;y một c&aacute;ch nhanh ch&oacute;ng, mượt m&agrave; hơn.&nbsp;Khả năng xử l&yacute; đồ họa của ThinkPad X280 20KFS01B00&nbsp;cũng được tăng cường với card đồ họa t&iacute;ch hợp Intel UHD Graphics 620, cho khả năng chạy tốt v&agrave; ổn định c&aacute;c phần mềm đồ họa cũng như đ&aacute;p ứng nhu cầu giải tr&iacute; của người d&ugrave;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-1/lenovo-ct-3.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>C&ocirc;ng nghệ &acirc;m thanh Dolby Home Theater</strong></span></h5>\r\n\r\n<p>Laptop sử dụng c&ocirc;ng nghệ &acirc;m thanh Dolby Audio gi&uacute;p cải thiện tốt hơn &acirc;m thanh loa ngo&agrave;i đồng thời gi&uacute;p &acirc;m lượng to hơn nhưng vẫn kh&ocirc;ng thay đổi về chất lượng. Nhờ đ&oacute;, bạn sẽ c&oacute; những ph&uacute;t gi&acirc;y thật tuyệt vời c&ugrave;ng những giai điệu &acirc;m nhạc hay những bộ phim &ldquo;đỉnh&rdquo; nhất.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-1/lenovo-ct-4.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Tr&ograve; chuyện thỏa th&iacute;ch</strong></span></h5>\r\n\r\n<p>HD Webcam cho h&igrave;nh ảnh kh&aacute; tốt. Nhờ đ&oacute; bạn c&oacute; thể chụp ảnh &ldquo;tự sướng&rdquo; v&agrave; chia sẻ h&igrave;nh ảnh với bạn b&egrave; qua c&aacute;c mạng x&atilde; hội như Facebook, Twitter &hellip;Ngo&agrave;i ra, bạn cũng c&oacute; thể tr&ograve; chuyện thỏa th&iacute;ch với người th&acirc;n qua c&aacute;c ứng dụng như: Skype, yahoo chat.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-1/lenovo-ct-5.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m v&agrave; touchpad</strong></span></h5>\r\n\r\n<p>B&agrave;n ph&iacute;m của ThinkPad X280 20KFS01B00 được thiết kế khoa học, c&aacute;c ph&iacute;m bấm c&oacute; khoảng c&aacute;ch v&agrave; độ nảy cao tạo cảm gi&aacute;c thoải m&aacute;i v&agrave; sự tiện dụng tối đa cho người d&ugrave;ng trong từng thao t&aacute;c nhập liệu. B&ecirc;n cạnh đ&oacute;, sự hiện diện của n&uacute;t trackpoint đặc trưng cũng gi&uacute;p người d&ugrave;ng thuận tiện hơn, c&ugrave;ng với đ&oacute; l&agrave; b&agrave;n di chuột cảm ứng touchpad được thiết kế rộng hơn đi k&egrave;m với 3 n&uacute;t chức năng gi&uacute;p bạn l&agrave;m việc dễ d&agrave;ng v&agrave; chuy&ecirc;n nghiệp hơn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-1/lenovo-ct-6.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bảo mật tối ưu với cảm biến v&acirc;n tay</strong></span></h5>\r\n\r\n<p>C&ocirc;ng nghệ cảm biến v&acirc;n tay tr&ecirc;n ThinkPad X280 20KFS01B00&nbsp;được h&atilde;ng t&iacute;ch hợp gọn ghẽ v&agrave; ho&agrave;n hảo tr&ecirc;n máy chỉ với một chạm bạn sẽ dễ d&agrave;ng đăng nhập v&agrave;o m&agrave;n h&igrave;nh desktop v&agrave; tăng cường khả năng bảo mật cho bạn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-1/lenovo-ct-7.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>USB Type C tất cả trong một kết nối</strong></span></h5>\r\n\r\n<p>Laptop sở hữu cổng kết nối USB Type C đa dụng nhất hiện nay, với thiết kế đồng đẳng gi&uacute;p bạn c&oacute; thể cắm cổng kết nối ở bất cứ chiều n&agrave;o. Ngo&agrave;i ra với c&ocirc;ng nghệ n&agrave;y sẽ cho tốc độ truyền tải dữ liệu nhanh gấp 10 lần so với c&aacute;c cổng kết nối cũ hiện nay.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-1/lenovo-ct-8.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hỗ trợ nhiều cổng kết nối</strong></span></h5>\r\n\r\n<p>Với chuẩn wifi 802.11ac nhanh gấp 6 lần so với chuẩn wifi 802.11n. Hỗ trợ nhiều cổng kết nối như cổng 2 x USB 3.1 Gen 1, 1 x USB 3.1 Gen 1 Type-C, 1 x USB 3.1 Gen 2 Type-C / Intel Thunderbolt 3, HDMI, HD webcam, Bluetooth, khe thẻ nhớ, jack tai nghe cho khả năng kết nối với c&aacute;c thiết bị kh&aacute;c một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-1/lenovo-ct-9.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Pin 6 cell</strong></span></h5>\r\n\r\n<p>Được trang bị 6 cell pin cho ph&eacute;p người d&ugrave;ng sử dụng&nbsp;ThinkPad X280 20KFS01B00 xem phim li&ecirc;n tục l&ecirc;n đến 8 giờ. C&ugrave;ng với c&ocirc;ng nghệ tiết kiệm pin, m&aacute;y c&oacute; thể hoạt động m&aacute;t, &ecirc;m &aacute;i v&agrave; tiết kiệm điện năng n&ecirc;n c&oacute; thể đ&aacute;p ứng tốt nhu cầu sử dụng b&igrave;nh thường.</p>\r\n', 5, '2019-06-16 06:38:16', 'Bình thường', 'Bình thường', 1),
(21, 'Lenovo ThinkPad T480s 20L7S00T00 Core i5-8250U/Free Dos (14 inch)', 26710000, 28990000, 30, 'Chip: Intel Core i5-8250U\r\n\r\nRAM: 8GB DDR4 2400MHz\r\n\r\nỔ cứng: 256GB M.2 SSD NVMe\r\n\r\nChipset đồ họa: Intel UHD Graphics 620\r\n\r\nMàn hình: 14 inch Full HD (1920 x 1080) IPS Anti-Glare\r\n\r\nHệ điều hành: Free Dos\r\n\r\nPin: 3 Cells 57 Whrs', 'lenovo2.jpg', 'lenovo2-1.jpg', 'lenovo2-3.jpg', 'lenovo2-4.jpg', 'lenovo2-5.jpg', 'lenovo22.jpg', 'lenovo2-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế gập 180 độ</strong></span></h5>\r\n\r\n<p><strong>Laptop Lenovo ThinkPad T480s 20L7S00T00</strong><strong>&nbsp;</strong>mang đến cho người d&ugrave;ng một chiếc laptop mạnh mẽ v&agrave; bền vững nhờ được bao bọc bởi bộ khung chắc chắn. Lớp vỏ ngo&agrave;i l&agrave;m từ chất liệu cao cấp vừa mang lại một độ bền vượt trội để đủ sức bảo vệ phần m&agrave;n h&igrave;nh v&agrave; c&aacute;c linh kiện b&ecirc;n trong nhưng vẫn đảm bảo tối ưu sự linh hoạt v&agrave; tiện dụng cho người d&ugrave;ng mỗi khi mang theo m&aacute;y b&ecirc;n m&igrave;nh. Ngo&agrave;i ra, bản lề của m&aacute;y được thiết kế đặc biệt gi&uacute;p m&aacute;y c&oacute; thể gập 180 độ như quyển tập.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-2/lenovo-ct-10.jpg\" style=\"height:484px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Màn hình&nbsp;14 inch Full HD</strong></span></h5>\r\n\r\n<p>M&aacute;y được trang bị m&agrave;n h&igrave;nh 14 inch với độ ph&acirc;n giải Full HD (1920 x 1080) cho chất lượng hiển thị ấn tượng, sắc n&eacute;t, độ chi tiết cao. B&ecirc;n cạnh đ&oacute;, tấm nền IPS c&ograve;n mang lại m&agrave;u sắc trung thực, g&oacute;c nh&igrave;n rộng v&agrave; độ s&aacute;ng cao kết hợp với c&ocirc;ng nghệ Anti Glare chống ch&oacute;i n&ecirc;n người d&ugrave;ng c&oacute; thể sử dụng m&aacute;y thoải m&aacute;i trong điều kiện &aacute;nh s&aacute;ng mạnh với g&oacute;c nh&igrave;n kh&aacute;c nhau.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-2/lenovo-ct-11.jpg\" style=\"height:484px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bộ vi xử l&yacute; Intel Core i5</strong></span></h5>\r\n\r\n<p>M&aacute;y được trang bị bộ vi xử l&yacute; Intel Core i5-8250U với xung nhịp c&oacute; thể l&ecirc;n đến 3.4GHz, cho bạn những gi&acirc;y ph&uacute;t lướt web nhanh hơn, mạnh mẽ hơn. B&ecirc;n cạnh đ&oacute;, với bộ nhớ trong Ram 8GB DDR4 (2400MHz) đảm bảo xử l&iacute; c&aacute;c t&aacute;c vụ đa nhiệm thật mượt m&agrave; v&agrave; ổn định. Ngo&agrave;i ra, m&aacute;y c&ograve;n trang bị th&ecirc;m bộ nhớ 256GB SSD, cho bạn lưu trữ cũng như vận h&agrave;nh m&aacute;y một c&aacute;ch nhanh ch&oacute;ng, mượt m&agrave; hơn.&nbsp;Khả năng xử l&yacute; đồ họa của ThinkPad T480s 20L7S00T00&nbsp;cũng được tăng cường với card đồ họa t&iacute;ch hợp Intel UHD Graphics 620, cho khả năng chạy tốt v&agrave; ổn định c&aacute;c phần mềm đồ họa cũng như đ&aacute;p ứng nhu cầu giải tr&iacute; của người d&ugrave;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>C&ocirc;ng nghệ &acirc;m thanh Dolby Home Theater</strong></span></h5>\r\n\r\n<p>Laptop sử dụng c&ocirc;ng nghệ &acirc;m thanh Dolby Audio gi&uacute;p cải thiện tốt hơn &acirc;m thanh loa ngo&agrave;i đồng thời gi&uacute;p &acirc;m lượng to hơn nhưng vẫn kh&ocirc;ng thay đổi về chất lượng. Nhờ đ&oacute;, bạn sẽ c&oacute; những ph&uacute;t gi&acirc;y thật tuyệt vời c&ugrave;ng những giai điệu &acirc;m nhạc hay những bộ phim &ldquo;đỉnh&rdquo; nhất.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Tr&ograve; chuyện thỏa th&iacute;ch</strong></span></h5>\r\n\r\n<p>HD Webcam cho h&igrave;nh ảnh kh&aacute; tốt. Nhờ đ&oacute; bạn c&oacute; thể chụp ảnh &ldquo;tự sướng&rdquo; v&agrave; chia sẻ h&igrave;nh ảnh với bạn b&egrave; qua c&aacute;c mạng x&atilde; hội như Facebook, Twitter &hellip;Ngo&agrave;i ra, bạn cũng c&oacute; thể tr&ograve; chuyện thỏa th&iacute;ch với người th&acirc;n qua c&aacute;c ứng dụng như: Skype, yahoo chat.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-2/lenovo-ct-12.jpg\" style=\"height:422px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m v&agrave; touchpad</strong></span></h5>\r\n\r\n<p>B&agrave;n ph&iacute;m của ThinkPad T480s 20L7S00T00 được thiết kế khoa học, c&aacute;c ph&iacute;m bấm c&oacute; khoảng c&aacute;ch v&agrave; độ nảy cao tạo cảm gi&aacute;c thoải m&aacute;i v&agrave; sự tiện dụng tối đa cho người d&ugrave;ng trong từng thao t&aacute;c nhập liệu. B&ecirc;n cạnh đ&oacute;, sự hiện diện của n&uacute;t trackpoint đặc trưng cũng gi&uacute;p người d&ugrave;ng thuận tiện hơn, c&ugrave;ng với đ&oacute; l&agrave; b&agrave;n di chuột cảm ứng touchpad được thiết kế rộng hơn đi k&egrave;m với 3 n&uacute;t chức năng gi&uacute;p bạn l&agrave;m việc dễ d&agrave;ng v&agrave; chuy&ecirc;n nghiệp hơn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-2/lenovo-ct-13.jpg\" style=\"height:458px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bảo mật tối ưu với cảm biến v&acirc;n tay</strong></span></h5>\r\n\r\n<p>C&ocirc;ng nghệ cảm biến v&acirc;n tay tr&ecirc;n&nbsp;ThinkPad T480s 20L7S00T00&nbsp;được h&atilde;ng t&iacute;ch hợp gọn ghẽ v&agrave; ho&agrave;n hảo tr&ecirc;n máy chỉ với một chạm bạn sẽ dễ d&agrave;ng đăng nhập v&agrave;o m&agrave;n h&igrave;nh desktop v&agrave; tăng cường khả năng bảo mật cho bạn.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>USB Type C tất cả trong một kết nối</strong></span></h5>\r\n\r\n<p>Laptop sở hữu cổng kết nối USB Type C đa dụng nhất hiện nay, với thiết kế đồng đẳng gi&uacute;p bạn c&oacute; thể cắm cổng kết nối ở bất cứ chiều n&agrave;o. Ngo&agrave;i ra với c&ocirc;ng nghệ n&agrave;y sẽ cho tốc độ truyền tải dữ liệu nhanh gấp 10 lần so với c&aacute;c cổng kết nối cũ hiện nay.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-2/lenovo-ct-14.jpg\" style=\"height:484px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hỗ trợ nhiều cổng kết nối</strong></span></h5>\r\n\r\n<p>Với chuẩn wifi 802.11ac nhanh gấp 6 lần so với chuẩn wifi 802.11n. Hỗ trợ nhiều cổng kết nối như cổng&nbsp;2 x USB 3.1 Gen 1 (one Always On), 1 x USB 3.1 Gen 1 Type-C (Power Delivery, DisplayPort, Data transfer), 1 x USB 3.1 Gen 2 Type-C / Intel Thunderbolt 3 (Power Delivery, DisplayPort, Data transfer), HDMI, HD webcam, Bluetooth, khe thẻ nhớ, jack tai nghe cho khả năng kết nối với c&aacute;c thiết bị kh&aacute;c một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-2/lenovo-ct-15.jpg\" style=\"height:484px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Pin 3 cell</strong></span></h5>\r\n\r\n<p>Được trang bị 3 cell pin cho ph&eacute;p người d&ugrave;ng xem phim li&ecirc;n tục l&ecirc;n đến 8 giờ. C&ugrave;ng với c&ocirc;ng nghệ tiết kiệm pin, m&aacute;y c&oacute; thể hoạt động m&aacute;t, &ecirc;m &aacute;i v&agrave; tiết kiệm điện năng n&ecirc;n c&oacute; thể đ&aacute;p ứng tốt nhu cầu sử dụng b&igrave;nh thường.</p>\r\n', 5, '2019-07-07 07:07:54', 'Bình thường', 'Bình thường', 1),
(22, 'Lenovo ThinkPad L380 20M5S01500 Core i5-8250U/Free Dos', 17590000, 19990000, 30, 'Chip: Intel Core i5-8250U (1.60GHz Up to 3.40 GHz, 4Cores, 8Threads, 6MB Cache, FSB 4GT/s)\r\n\r\nRAM: 4GB DDR4-2400MHz\r\n\r\nỔ cứng: 256GB SSD M.2 PCIe\r\n\r\nChipset đồ họa: Intel UHD Graphics 620\r\n\r\nMàn hình: 13.3-inch Full HD (1920 x 1080) IPS anti-glare\r\n\r\nHệ điều hành: Free Dos\r\n\r\nPin: 3-Cells 45Whr', 'lenovo3.jpg', 'lenovo3-1.jpg', 'lenovo3-2.jpg', 'lenovo3-3.jpg', 'lenovo3-4.jpg', 'lenovo3-5.jpg', 'lenovo3-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế gập 180 độ</strong></span></h5>\r\n\r\n<p><strong>Laptop Lenovo ThinkPad L380 20M5S01500</strong>&nbsp;mang đến cho người d&ugrave;ng một chiếc laptop mạnh mẽ v&agrave; bền vững nhờ được bao bọc bởi bộ khung chắc chắn. Lớp vỏ ngo&agrave;i l&agrave;m từ chất liệu cao cấp vừa mang lại một độ bền vượt trội để đủ sức bảo vệ phần m&agrave;n h&igrave;nh v&agrave; c&aacute;c linh kiện b&ecirc;n trong nhưng vẫn đảm bảo tối ưu sự linh hoạt v&agrave; tiện dụng cho người d&ugrave;ng mỗi khi mang theo m&aacute;y b&ecirc;n m&igrave;nh. Ngo&agrave;i ra, bản lề của m&aacute;y được thiết kế đặc biệt gi&uacute;p m&aacute;y c&oacute; thể gập 180 độ như quyển tập.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-3/lenovo-ct-16.jpg\" style=\"height:563px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Màn hình&nbsp;13.3-inch Full HD</strong></span></h5>\r\n\r\n<p>M&aacute;y được trang bị m&agrave;n h&igrave;nh 13.3-inch với độ ph&acirc;n giải Full HD (1920 x 1080) cho chất lượng hiển thị ấn tượng, sắc n&eacute;t, độ chi tiết cao. B&ecirc;n cạnh đ&oacute;, tấm nền IPS c&ograve;n mang lại m&agrave;u sắc trung thực, g&oacute;c nh&igrave;n rộng v&agrave; độ s&aacute;ng cao kết hợp với c&ocirc;ng nghệ Anti Glare chống ch&oacute;i n&ecirc;n người d&ugrave;ng c&oacute; thể sử dụng m&aacute;y thoải m&aacute;i trong điều kiện &aacute;nh s&aacute;ng mạnh với g&oacute;c nh&igrave;n kh&aacute;c nhau.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-3/lenovo-ct-17.jpg\" style=\"height:484px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bộ vi xử l&yacute; Intel Core i5</strong></span></h5>\r\n\r\n<p>M&aacute;y được trang bị bộ vi xử l&yacute; Intel Core i5-8250U với xung nhịp c&oacute; thể l&ecirc;n đến 3.4GHz, cho bạn những gi&acirc;y ph&uacute;t lướt web nhanh hơn, mạnh mẽ hơn. B&ecirc;n cạnh đ&oacute;, với bộ nhớ trong Ram 4GB DDR4-2400MHz đảm bảo xử l&iacute; c&aacute;c t&aacute;c vụ đa nhiệm thật mượt m&agrave; v&agrave; ổn định. Ngo&agrave;i ra, m&aacute;y c&ograve;n trang bị th&ecirc;m bộ nhớ 256GB SSD M.2 PCIe, cho bạn lưu trữ cũng như vận h&agrave;nh m&aacute;y một c&aacute;ch nhanh ch&oacute;ng, mượt m&agrave; hơn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-3/lenovo-ct-18.jpg\" style=\"height:484px; width:750px\" /></p>\r\n\r\n<p>Khả năng xử l&yacute; đồ họa của&nbsp;Lenovo ThinkPad L380 20M5S01500&nbsp;cũng được tăng cường với card đồ họa t&iacute;ch hợp Intel UHD Graphics 620, cho khả năng chạy tốt v&agrave; ổn định c&aacute;c phần mềm đồ họa cũng như đ&aacute;p ứng nhu cầu giải tr&iacute; của người d&ugrave;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>C&ocirc;ng nghệ &acirc;m thanh Dolby Home Theater</strong></span></h5>\r\n\r\n<p>Laptop sử dụng c&ocirc;ng nghệ &acirc;m thanh Dolby Audio gi&uacute;p cải thiện tốt hơn &acirc;m thanh loa ngo&agrave;i đồng thời gi&uacute;p &acirc;m lượng to hơn nhưng vẫn kh&ocirc;ng thay đổi về chất lượng. Nhờ đ&oacute;, bạn sẽ c&oacute; những ph&uacute;t gi&acirc;y thật tuyệt vời c&ugrave;ng những giai điệu &acirc;m nhạc hay những bộ phim &ldquo;đỉnh&rdquo; nhất.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-3/lenovo-ct-19.jpg\" style=\"height:484px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m v&agrave; touchpad</strong></span></h5>\r\n\r\n<p>B&agrave;n ph&iacute;m của Lenovo ThinkPad L380 20M5S01500&nbsp;được thiết kế khoa học, c&aacute;c ph&iacute;m bấm c&oacute; khoảng c&aacute;ch v&agrave; độ nảy cao tạo cảm gi&aacute;c thoải m&aacute;i v&agrave; sự tiện dụng tối đa cho người d&ugrave;ng trong từng thao t&aacute;c nhập liệu. B&ecirc;n cạnh đ&oacute;, sự hiện diện của n&uacute;t trackpoint đặc trưng cũng gi&uacute;p người d&ugrave;ng thuận tiện hơn, c&ugrave;ng với đ&oacute; l&agrave; b&agrave;n di chuột cảm ứng touchpad được thiết kế rộng hơn đi k&egrave;m với 3 n&uacute;t chức năng gi&uacute;p bạn l&agrave;m việc dễ d&agrave;ng v&agrave; chuy&ecirc;n nghiệp hơn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-3/lenovo-ct-20.jpg\" style=\"height:484px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>USB Type C tất cả trong một kết nối</strong></span></h5>\r\n\r\n<p>Laptop sở hữu cổng kết nối USB Type C đa dụng nhất hiện nay, với thiết kế đồng đẳng gi&uacute;p bạn c&oacute; thể cắm cổng kết nối ở bất cứ chiều n&agrave;o. Ngo&agrave;i ra với c&ocirc;ng nghệ n&agrave;y sẽ cho tốc độ truyền tải dữ liệu nhanh gấp 10 lần so với c&aacute;c cổng kết nối cũ hiện nay.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-3/lenovo-ct-21.jpg\" style=\"height:484px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hỗ trợ nhiều cổng kết nối</strong></span></h5>\r\n\r\n<p>Với chuẩn wifi 802.11ac nhanh gấp 6 lần so với chuẩn wifi 802.11n. Hỗ trợ nhiều cổng kết nối như cổng HDMI, HD webcam, Bluetooth, khe thẻ nhớ, jack tai nghe cho khả năng kết nối với c&aacute;c thiết bị kh&aacute;c một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-3/lenovo-ct-22.jpg\" style=\"height:484px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Pin 3 cell</strong></span></h5>\r\n\r\n<p>Được trang bị 3 cell pin cho ph&eacute;p người d&ugrave;ng xem phim li&ecirc;n tục l&ecirc;n đến 8 giờ. C&ugrave;ng với c&ocirc;ng nghệ tiết kiệm pin, m&aacute;y c&oacute; thể hoạt động m&aacute;t, &ecirc;m &aacute;i v&agrave; tiết kiệm điện năng n&ecirc;n c&oacute; thể đ&aacute;p ứng tốt nhu cầu sử dụng b&igrave;nh thường.</p>\r\n', 5, '2019-06-16 06:38:16', 'Bình thường', 'Bình thường', 1),
(23, 'Acer Aspire ES1-572-32GZ NX.GKQSV.001 Core i3-7100U/Free Dos (15.6inch)', 8550000, 10000000, 30, 'Chip: Intel Core i3-7100U Processor (3M Cache, 2.40 GHz)\r\n\r\nRAM: 4GB DDR4\r\n\r\nỔ cứng: HDD 500GB\r\n\r\nChipset đồ họa: Intel HD Graphics 620\r\n\r\nMàn hình: 15.6 inches, HD (1366 x 768 pixels)\r\n\r\nHệ điều hành: OS Linux\r\n\r\nPin: 4 Cell', 'acer1.jpg', 'acer1-1.jpg', 'acer1-2.jpg', 'acer1-3.jpg', 'acer1-4.jpg', 'acer1-5.jpg', 'acer1-6.jpg', '<p><span style=\"font-size:24px\"><strong>Thiết kế sang trọng, hiện đại</strong></span></p>\r\n\r\n<p><strong>Laptop Acer Aspire ES1-572-32GZ NX.GKQSV.001 Core i3-7100U/Free Dos (15.6inch)&nbsp;</strong>c&oacute; thiết kế với kiểu d&aacute;ng vu&ocirc;ng vức c&ugrave;ng m&agrave;u đen sang trọng v&agrave; những đường v&acirc;n h&igrave;nh s&oacute;ng độc đ&aacute;o. Với thiết kế m&agrave;n h&igrave;nh lớn c&ugrave;ng b&agrave;n ph&iacute;m số được t&iacute;ch hợp, m&aacute;y sẽ hỗ trợ bạn c&oacute; thể thao t&aacute;c c&aacute;c ứng dụng văn ph&ograve;ng một c&aacute;ch nhanh ch&oacute;ng.</p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>Tốc độ xử l&yacute; mạnh mẽ</strong></span></p>\r\n\r\n<p>Acer Aspire ES1-572-32GZ NX.GKQSV.001 sử dụng bộ vi xử l&yacute; Intel Core i3-7100U, l&agrave; thế hệ chip mới nhất của Intel vừa được ra mắt cuối năm 2016, gi&uacute;p cho m&aacute;y c&oacute; khả năng thao t&aacute;c nhanh v&agrave; mượt m&agrave; hơn so với c&aacute;c thế hệ Core i3 đời trước, hỗ trợ bạn ho&agrave;n th&agrave;nh c&ocirc;ng việc trong thời gian nhanh hơn.</p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh 15.6 inches, độ ph&acirc;n giải HD (1366 x 768 pixels)</strong></span></p>\r\n\r\n<p>Laptop Acer Aspire ES1-572-32GZ NX.GKQSV.001 c&oacute; k&iacute;ch thước 15.6 inches, th&iacute;ch hợp cho những ai muốn sử dụng một chiếc laptop c&oacute; m&agrave;n h&igrave;nh lớn. Với độ ph&acirc;n giải HD (1366 x 768 pixels), m&aacute;y cho khả năng hiển thị h&igrave;nh ảnh mượt m&agrave; sắc n&eacute;t m&agrave; lại tiết kiệm điện năng ti&ecirc;u thụ hơn so với độ ph&acirc;n giải Full HD.</p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>Đa kết nối tiện &iacute;ch</strong></span></p>\r\n\r\n<p>Acer Aspire ES1-572-32GZ NX.GKQSV.001 hỗ trợ đầy đủ c&aacute;c cổng kết nối như HDMI, USB, giắc 3.5 mm, cổng LAN,... đặc biệt l&agrave; cổng USB 3.0 cho tốc độ nhanh g&acirc;́p 10 l&acirc;̀n so với c&ocirc;ng ngh&ecirc;̣ 2.0, gi&uacute;p bạn c&oacute; thể kết nối nhiều thiết bị điện tử c&ugrave;ng một l&uacute;c một c&aacute;ch dễ d&agrave;ng v&agrave; nhanh ch&oacute;ng.</p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>Pin 4 cell</strong></span></p>\r\n\r\n<p>Laptop Acer Aspire ES1-572-32GZ NX.GKQSV.001 được trang bị vi&ecirc;n pin 4 cell, kết hợp với c&ocirc;ng nghệ tiết kiệm điện năng của chip Intel, m&aacute;y cho thời gian sử dụng li&ecirc;n tục trong nhiều giờ liền với những t&aacute;c vụ cơ bản.</p>\r\n', 7, '2019-06-16 06:38:16', 'Bình thường', 'Bình thường', 1),
(24, 'Acer Swift SF314-52-55UF NX.GQGSV.002 Core i5-8250U/Win 10 (14 inch)', 16990000, 17990000, 30, 'Chip: Intel Core i5-8250U\r\n\r\nRAM: 4GB DDR4 2133 MHz\r\n\r\nỔ cứng: 256GB SSD M2 SATA3\r\n\r\nChipset đồ họa: Intel HD Graphics 620\r\n\r\nMàn hình: 14 inch Full HD (1920 x 1080) LED Anti-Glare\r\n\r\nHệ điều hành: Windows 10\r\n\r\nPin: 4 Cell', 'acer2.jpg', 'acer2-1.jpg', 'acer2-2.jpg', 'acer2-3.jpg', 'acer2-4.jpg', 'acer2-5.jpg', 'acer2-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế tinh tế</strong></span></h5>\r\n\r\n<p><strong>Laptop Acer Swift SF314-52-55UF NX.GQGSV.002</strong>&nbsp;được thiết kế kh&aacute; mỏng phong c&aacute;ch trẻ trung, năng động, c&aacute;c cạnh được bo tr&ograve;n mang lại sự cứng c&aacute;p hơn cho chiếc m&aacute;y, nhỏ gọn gi&uacute;p người d&ugrave;ng c&oacute; thể dễ d&agrave;ng bỏ m&aacute;y v&agrave;o trong t&uacute;i x&aacute;ch, balo v&agrave; mang theo b&ecirc;n m&igrave;nh để l&agrave;m việc m&agrave; kh&ocirc;ng cảm thấy vướng v&iacute;u kh&oacute; chịu.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/acer-1/acer-ct-1.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh hiển thị h&igrave;nh ảnh sắc n&eacute;t</strong></span></h5>\r\n\r\n<p>M&agrave;n h&igrave;nh&nbsp;Acer Swift&nbsp;hiển thị sắc n&eacute;t thưởng thức chi tiết r&otilde; n&eacute;t với m&agrave;n h&igrave;nh LED 14 inch, độ ph&acirc;n giải Full HD (1920 x 1080). Tấm nền IPS đi k&egrave;m v&agrave; c&ocirc;ng nghệ Acer Color Intelligence mang lại m&agrave;u s&aacute;c tươi s&aacute;ng v&agrave; rực rỡ cho từng khu&ocirc;n h&igrave;nh.&nbsp;Ngo&agrave;i ra, m&agrave;n h&igrave;nh cũng được phủ k&iacute;nh cường lực Coming Gorille Glass gi&uacute;p bảo vệ m&aacute;y v&agrave; hạn chế tối đa trầy xước cho m&agrave;n h&igrave;nh.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/acer-1/acer-ct-2.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Sở hữu cấu h&igrave;nh khủng</strong></span></h5>\r\n\r\n<p>Được trang bị bộ xử l&yacute; Intel Core i5-8250U cung cấp sức mạnh dồi d&agrave;o trong một khung gầm nhỏ gọn. Với hiệu năng tăng l&ecirc;n, tăng băng th&ocirc;ng v&agrave; hiệu suất năng lượng cao với bộ nhớ chuẩn DDR4 c&oacute; thể n&acirc;ng cấp tối đa 4GB, bạn sẽ chạy tất cả ứng dụng một c&aacute;ch nhanh ch&oacute;ng v&agrave; mượt m&agrave;. Cung cấp nhiều t&ugrave;y chọn lưu trữ cho hiệu suất đ&aacute;p ứng cao v&agrave; dung lượng lưu trữ lớn, bao gồm một ổ cứng 256GB SSD. Card đ&ocirc;̀ họa tích hợp&nbsp;Intel HD Graphics 620 cho m&aacute;y thực hiện c&aacute;c t&aacute;c vụ y&ecirc;u cầu khả năng đồ họa kh&aacute; như photoshop, hay chơi game.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>&Acirc;m thanh chuy&ecirc;n nghiệp</strong></span></h5>\r\n\r\n<p>Được trang bị bộ loa m&agrave;ng giấy Acer TrueHarmony cung cấp &acirc;m thanh chuy&ecirc;n nghiệp, tự động điều chỉnh &acirc;m thanh của bạn với độ cao r&otilde; n&eacute;t hơn, tăng &acirc;m trầm v&agrave; tăng &acirc;m lượng để mọi thứ m&agrave; bạn nghe đều ch&iacute;nh x&aacute;c theo c&aacute;ch bạn mu&ocirc;́n, bạn sẽ được trải nghiệm chất lượng &acirc;m thanh đỉnh cao với chuy&ecirc;n gia &acirc;m thanh TrueHarmony&nbsp;đ&ocirc;̣c quy&ecirc;̀n từ&nbsp;Acer Swift SF314-52-55UF NX.GQGSV.002.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hệ thống bảo mật v&acirc;n tay</strong></span></h5>\r\n\r\n<p>C&ocirc;ng nghệ cảm biến v&acirc;n tay cảm ứng TouchID&nbsp;tr&ecirc;n&nbsp;Acer Swift SF314-52-55UF NX.GQGSV.002&nbsp;được h&atilde;ng t&iacute;ch hợp gọn ghẽ v&agrave; ho&agrave;n hảo nằm b&ecirc;n tay phải của bàn phím giúp bạn chỉ với một chạm bạn sẽ dễ d&agrave;ng đăng nhập v&agrave;o m&agrave;n h&igrave;nh desktop v&agrave; tăng cường khả năng bảo mật dữ li&ecirc;̣u cho bạn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/acer-1/acer-ct-3.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m v&agrave; Touchpad</strong></span></h5>\r\n\r\n<p>B&agrave;n ph&iacute;m của m&aacute;y với c&aacute;c ph&iacute;m k&iacute;ch thước kh&aacute; lớn, khoảng c&aacute;ch giữa c&aacute;c ph&iacute;m hợp l&yacute; v&agrave; được sắp xếp hợp l&yacute; mang lại trải nghiệm tốt cho người d&ugrave;ng. Touchpad cũng kh&aacute; rộng r&atilde;i, b&agrave;n r&ecirc; mịn cho cảm gi&aacute;c chạm vuốt kh&aacute; thoải m&aacute;i.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/acer-1/acer-ct-4.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>USB Type C &ndash; tất cả trong một kết nối</strong></span></h5>\r\n\r\n<p>Laptop sở hữu cổng kết nối USB Type C đa dụng nhất hiện nay, với thiết kế đồng đẳng gi&uacute;p bạn c&oacute; thể cắm cổng kết nối ở bất cứ chiều n&agrave;o. Ngo&agrave;i ra với c&ocirc;ng nghệ n&agrave;y sẽ cho tốc độ truyền tải dữ liệu nhanh gấp 10 lần so với c&aacute;c cổng kết nối cũ hiện nay.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Kết nối kh&ocirc;ng giới hạn</strong></span></h5>\r\n\r\n<p>Với chuẩn Wifi 802.11ac băng tần k&eacute;p mới nhất nhanh gấp 6 lần so với chuẩn wifi 802.11n, c&aacute;c c&ocirc;ng kết nối đa dạng với 1 cổng USB 2.0, 1 cổng USB 3.0, 1 cổng USB type C, 1 cổng HDMI, 1 khe cắm tai nghe v&agrave; 1 webcam chuẩn HD và khe thẻ nhớ giúp bạn chép tài li&ecirc;̣u qua các thi&ecirc;́t bị khác. Do đ&oacute; bạn c&oacute; thể thoải m&aacute;i kết nối ở bất kỳ đ&acirc;u với Acer Swift SF314-52-55UF NX.GQGSV.002&nbsp;v&agrave; tự do l&agrave;m những điều bạn muốn.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Thời lượng pin l&acirc;u hơn</strong></span></h5>\r\n\r\n<p>Được thiết kế để ph&ugrave; hợp với phong c&aacute;ch sống năng động hiện nay, Acer Swift SF314-52-55UF NX.GQGSV.002&nbsp;sẽ cung cấp cho bạn thời lượng pin để c&oacute; thể sử dụng suốt cả ng&agrave;y. Với c&ocirc;ng nghệ pin Lithium Polymer sẽ cho m&aacute;y khả năng vận h&agrave;nh li&ecirc;n tục l&ecirc;n đến 7 tiếng đồng hồ. Điểm đặc biệt của d&ograve;ng pin Lithium Polymer c&oacute; thể duy tr&igrave; được năng lượng sạc lần đầu cho d&ugrave; sau h&agrave;ng trăm chu kỳ sạc, l&acirc;u hơn gấp 3 lần so với c&aacute;c loại pin ti&ecirc;u chuẩn hiện nay, gi&uacute;p bạn khỏi phải lo lắng về hao m&ograve;n hao m&ograve;n điện năng pin.</p>\r\n', 7, '2019-06-17 06:38:16', 'Bình thường', 'Bình thường', 1),
(25, 'Acer Swift 5 SF514-52T-87TF NX.GTMSV.002 Core i7-8550U/ Win10', 26380000, 27990000, 30, 'Chip: Intel Core i7-8550U\r\n\r\nRAM: 8GB LPDDR3-2133MHz\r\n\r\nỔ cứng: 256GB SSD M.2 Sata\r\n\r\nChipset đồ họa: Integrated Intel HD Graphics\r\n\r\nMàn hình: 14 inch Full HD (1920x1080) CineCrystal, In-plane Switching (IPS-Pro) Technology\r\n\r\nHệ điều hành: Win10', 'acer3.jpg', 'acer3-1.jpg', 'acer3-2.jpg', 'acer3-3.jpg', 'acer3-4.jpg', 'acer3-5.jpg', 'acer3-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Hướng đến đối tượng doanh nh&acirc;n</strong></span></h5>\r\n\r\n<p><strong>Laptop Acer Swift 5 SF514-52T-87TF</strong>&nbsp;c&oacute; kiểu d&aacute;ng mỏng nhẹ, cực kỳ đẳng cấp c&ugrave;ng một cấu h&igrave;nh mạnh mẽ, v&agrave; thời lượng pin ấn tượng sẽ l&agrave; một chiếc laptop hiệu quả để xử l&yacute; c&ocirc;ng việc khi đối tượng ch&iacute;nh m&agrave; h&atilde;ng hướng đến ch&iacute;nh l&agrave; tầng lớp doanh nh&acirc;n sang trọng.<img alt=\"\" src=\"/uploads/files/acer-1/acer-ct-5.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Chất liệu cao cấp</strong></span></h5>\r\n\r\n<p>Chiếc laptop Acer Swift 5 kh&ocirc;ng chỉ c&oacute; kiểu d&aacute;ng sang trọng, k&iacute;ch thước mỏng nhẹ gọn g&agrave;ng m&agrave; c&ograve;n được cấu th&agrave;nh từ vật liệu cao cấp khi&nbsp;sử dụng hợp kim Magie v&agrave; Liti, kim loại nhẹ nhất thế giới để chế tạo vỏ m&aacute;y, tăng t&iacute;nh mỏng nhẹ nhưng vẫn đảm bảo độ bền tối đa. Bề mặt đ&aacute;y cũng được cấu tr&uacute;c từ kim loại l&agrave;m cho m&aacute;y chắc chắn hơn, đẳng cấp hơn.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Chạm &quot;nhẹ như l&ocirc;ng hồng&quot;</strong></span></h5>\r\n\r\n<p>Laptop Acer cao cấp được trang bị c&ocirc;ng nghệ cảm ứng v&acirc;n tay Finger Print kết hợp Window Hello gi&uacute;p đăng nhập lập tức chỉ bằng 1 c&uacute; chạm m&agrave; vẫn kh&ocirc;ng l&agrave;m giảm khả năng bảo mật.&nbsp;Windows 10 Home bản quyền t&iacute;ch hợp sẵn trong m&aacute;y gi&uacute;p sử dụng ổn định v&agrave; tăng cường t&iacute;nh bảo mật cho thiết bị</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/acer-1/acer-ct-7.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>C&ocirc;ng nghệ Acer Color IntelligenceTM tăng khả năng hiển thị</strong></span></h5>\r\n\r\n<p>Swift 5 được trang bị m&agrave;n h&igrave;nh cảm ứng 14 inch FHD, sử dụng tấm nền IPS với viền m&agrave;n h&igrave;nh mỏng, c&ocirc;ng nghệ Acer Color IntelligenceTM cho h&igrave;nh ảnh sắc n&eacute;t v&agrave; ch&acirc;n thực, m&agrave;u sắc được t&aacute;i tạo ch&iacute;nh x&aacute;c, c&oacute; độ s&acirc;u v&agrave; ph&ugrave; hợp cho mọi hoạt động lướt web, shopping online, giải tr&iacute;&hellip; cũng như c&aacute;c c&ocirc;ng việc đặc th&ugrave;. Cộng th&ecirc;m khả năng lật ngang 180 độ, gi&uacute;p việc thao t&aacute;c thuận tiện hơn cho người d&ugrave;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/acer-1/acer-ct-8.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hiệu năng ổn định v&agrave; mạnh mẽ</strong></span></h5>\r\n\r\n<p>Được trang bị bộ vi xử l&yacute; Intel Core i5-8550U tốc độ tối đa l&ecirc;n đ&ecirc;́n 4GHz, đem lại cho m&aacute;y một sức mạnh xử l&yacute; ổn định v&agrave; tiết kiệm điện năng hiệu quả. C&ugrave;ng bộ nhớ RAM LPDDR3 dung lượng 8GB cho hiệu suất xử l&yacute; tốt cũng như đ&aacute;p ứng c&aacute;c t&aacute;c vụ đa nhiệm mượt m&agrave; c&aacute;c ứng dụng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/acer-1/acer-ct-9.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Thoải m&aacute;i cho ng&agrave;y d&agrave;i l&agrave;m việc</strong></span></h5>\r\n\r\n<p>Laptop được trang bị pin 2 Cell cho thời gian sử dụng ấn tượng l&ecirc;n đến 8 giờ li&ecirc;n tục. Vi&ecirc;n pin chuẩn Li-on, gi&uacute;p bạn khỏi phải lo lắng về hao m&ograve;n điện năng, cũng như đảm bảo độ an to&agrave;n v&agrave; tuổi thọ thiết bị.</p>\r\n', 7, '2019-07-07 07:08:48', 'Bình thường', 'Nổi bật', 1);
INSERT INTO `product` (`id`, `product_name`, `price_new`, `price_old`, `qty_product`, `product_desc`, `product_thumb`, `list_thumb_1`, `list_thumb_2`, `list_thumb_3`, `list_thumb_4`, `list_thumb_5`, `list_thumb_6`, `product_content`, `cat_id`, `created_at`, `selling_products`, `featured_products`, `status`) VALUES
(26, 'Acer Nitro 5 AN515-52-51LW NH.Q3LSV.002 Core i5-8300H/Free Dos', 19710000, 21970000, 18, 'Chip: Intel Core i5-8300H (2.3GHz Upto 4.0GHz, 4 Cores 8 Threads, 8MB Cache)\r\n\r\nRAM: 8GB DDR4 2400Mhz\r\n\r\nỔ cứng: 128GB SSD + 1TB HDD 5400rpm\r\n\r\nChipset đồ họa: NVIDIA GeForce GTX1050Ti 4GB GDDR5\r\n\r\nMàn hình: 15.6 inch Full HD (1920 x 1080) IPS, LED Backlit\r\n\r\nHệ điều hành: FreeDos\r\n\r\nPin: 4 Cell', 'acer4.jpg', 'acer4-1.png', 'acer4-2.png', 'acer4-3.png', 'acer4-4.png', 'acer4-5.png', 'acer4-6.png', '<h5><span style=\"font-size:24px\"><strong>Thiết kế lớp vỏ v&acirc;n Carbon mạnh mẽ</strong></span></h5>\r\n\r\n<p>Laptop Acer Nitro 5 AN515-52-51LW NH.Q3LSV.002&nbsp;với vẻ ngo&agrave;i đậm chất Gaming, cực ngầu với hai t&ocirc;ng m&agrave;u đỏ đen đồng nhất.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/acer-2/acer-ct-10.jpg\" style=\"height:416px; width:750px\" /></p>\r\n\r\n<p>Mặt trước nắp gập được thiết kế lớp vỏ v&acirc;n Carbon mạnh mẽ, bền bỉ v&agrave; hạn chế b&aacute;m bẩn, nước sơn đen hoạ tiết th&ecirc;m đường chỉ đỏ đặc trưng của d&ograve;ng laptop gaming Acer l&agrave;m tăng th&ecirc;m độ ngầu của m&aacute;y.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/acer-2/acer-ct-11.jpg\" style=\"height:484px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Sắc n&eacute;t mọi g&oacute;c độ</strong></span></h5>\r\n\r\n<p>Với k&iacute;ch thước m&agrave;n h&igrave;nh 15.6 inch, độ ph&acirc;n giải Full HD (1920 x 1080) với tỷ lệ 16:9 để xem mọi thứ tốt hơn, cùng c&ocirc;ng nghệ màn hình LED Backlit mang đến những h&igrave;nh ảnh chất lượng, chi tiết, r&otilde; n&eacute;t, m&agrave;u sắc cũng cực kỳ trung thực v&agrave; sống động. Tấm nền IPS cho m&agrave;u sắc trung thực ở mọi g&oacute;c độ gi&uacute;p game thủ c&oacute; những ph&uacute;t gi&acirc;y trải nghiệm game tuyệt vời nhất.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/acer-2/acer-ct-12.jpg\" style=\"height:489px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bộ vi xử l&yacute;&nbsp;Intel Core i5</strong></span></h5>\r\n\r\n<p>Nằm trong ph&acirc;n kh&uacute;c chuy&ecirc;n game v&agrave; phải cạnh tranh rất quyết liệt trong đấu trường &quot;UFC GAMING&quot;, v&igrave; vậy&nbsp;Acer Nitro 5 AN515-52-51LW NH.Q3LSV.002&nbsp;được trang bị bộ vi xử l&yacute; Intel Core i5-8300H tốc độ l&ecirc;n đ&ecirc;́n 4GHz, đem lại cho m&aacute;y một sức mạnh xử l&yacute; ổn định v&agrave; tiết kiệm điện năng hiệu quả.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/acer-2/acer-ct-13.jpg\" style=\"height:356px; width:750px\" /></p>\r\n\r\n<p>C&ugrave;ng với 8 GB RAM chuẩn DDR4 tốc độ bus 2400Mhz, ổ cứng kép&nbsp;128GB SSD + 1TB HDD thoải m&aacute;i lưu trữ dữ liệu của c&aacute;c game. Đặc biệt Acer Nitro 5 c&oacute; sự xuất hiện của vũ kh&iacute; card VGA rời NVIDIA GeForce GTX1050Ti&nbsp;ngo&agrave;i việc chơi game, m&aacute;y cũng l&agrave; sự lựa chọn cho người d&ugrave;ng l&agrave;m việc về thiết kế đồ họa.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/acer-2/acer-ct-14.jpg\" style=\"height:386px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>C&ocirc;ng nghệ&nbsp;&acirc;m thanh&nbsp;Dolby Audio Premium</strong></span></h5>\r\n\r\n<p>Loa k&eacute;p kết hợp c&ugrave;ng c&ocirc;ng nghệ&nbsp;&acirc;m thanh Dolby Audio Premium n&ecirc;n Acer Nitro 5 cho chất lượng &acirc;m thanh tốt, chất &acirc;m to r&otilde; v&agrave; c&oacute; lu&ocirc;n cả &acirc;m thanh v&ograve;m (&acirc;m thanh ph&aacute;t ra từ nhiều hướng) như trong rạp phim được thu nhỏ.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/acer-2/acer-ct-15.jpg\" style=\"height:414px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m LED backlit m&agrave;u đỏ v&agrave; touchpad đa điểm nhạy b&eacute;n</strong></span></h5>\r\n\r\n<p>Laptop c&oacute; b&agrave;n ph&iacute;m được gia c&ocirc;ng tốt, cảm gi&aacute;c g&otilde; tốt, kết hợp với cụm WASD m&agrave;u đỏ nổi bật đậm chất gaming.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/acer-2/acer-ct-16.jpg\" style=\"height:471px; width:750px\" /></p>\r\n\r\n<p>Ngo&agrave;i ra hệ thống đ&egrave;n LED backlit m&agrave;u đỏ nổi bật gi&uacute;p game thủ c&oacute; thể thao t&aacute;c ch&iacute;nh x&aacute;c trong những trận chiến game khốc liệt kể cả ng&agrave;y hay đ&ecirc;m.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/acer-2/acer-ct-17.jpg\" style=\"height:484px; width:750px\" /></p>\r\n\r\n<p>Thiết kế b&agrave;n ph&iacute;m c&acirc;n đối n&ecirc;n nh&igrave;n v&agrave;o kh&aacute; bắt mắt. Touchpad cảm ứng đa điểm nhạy b&eacute;n theo từng thao t&aacute;c chạm.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/acer-2/acer-ct-18.jpg\" style=\"height:475px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Wifi cực mạnh</strong></span></h5>\r\n\r\n<p>Kh&aacute;c với c&aacute;c m&aacute;y laptop kh&aacute;c, Acer Nitro 5 AN515-52-51LW NH.Q3LSV.002&nbsp;được trang bị 2 ăng-ten wifi n&ecirc;n bắt s&oacute;ng mạng internet sẽ mạnh v&agrave; xa hơn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/acer-2/acer-ct-19.jpg\" style=\"height:484px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>USB Type C tất cả trong một kết nối</strong></span></h5>\r\n\r\n<p>Laptop sở hữu cổng kết nối USB Type C đa dụng nhất hiện nay, với thiết kế đồng đẳng gi&uacute;p bạn c&oacute; thể cắm cổng kết nối ở bất cứ chiều n&agrave;o. Ngo&agrave;i ra với c&ocirc;ng nghệ n&agrave;y sẽ cho tốc độ truyền tải dữ liệu nhanh gấp 10 lần so với c&aacute;c cổng kết nối cũ hiện nay.</p>\r\n\r\n<p>Hỗ trợ nhiều cổng kết nối như cổng HDMI, LAN (RJ45), Bluetooth, khe thẻ nhớ, jack tai nghe cho khả năng kết nối với c&aacute;c thiết bị kh&aacute;c một c&aacute;ch dễ d&agrave;ng.</p>\r\n', 7, '2019-06-17 06:38:16', 'Bán chạy', 'Bình thường', 1),
(27, 'Apple Macbook Air 2017 13.3 Inch Core i7/ 8GB/ 128GB (Z0UU3)', 23390000, 25900000, 30, 'Chip: Intel Core i7\r\n\r\nMàn hình: 13.3\" LED-Backlit Glossy ( 1440 x 900px )\r\n\r\nBộ nhớ RAM: 8GB of 2400 Mhz DDR4\r\n\r\nLưu trữ SSD: 128GB PCIe-Based Flash\r\n\r\nVGA: Intel HD Graphics 6000 (Tích hợp)', 'mb2.jpg', 'mb2-1.jpg', 'mb2-2.jpg', 'mb2-3.jpg', 'mb2-4.jpg', 'mb2-5.jpg', 'mb2-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế tinh tế v&agrave; hiện đại</strong></span></h5>\r\n\r\n<p><strong>Apple Macbook Air 2017 13.3 Inch Core i7/ 8GB/ 128GB (Z0UU3)</strong>&nbsp;c&oacute; thiết kế tối giản nhưng lại v&ocirc; c&ugrave;ng sang trọng. To&agrave;n th&acirc;n m&aacute;y được gia c&ocirc;ng từ nh&ocirc;m nguy&ecirc;n khối một c&aacute;ch tỉ mỉ v&agrave; ch&iacute;nh x&aacute;c, tạo n&ecirc;n vẻ liền lạc v&agrave; chắc chắn lại vừa thanh tho&aacute;t, sang trọng. N&uacute;t Power khởi động m&aacute;y được t&iacute;ch hợp lu&ocirc;n v&agrave;o g&oacute;c tr&ecirc;n b&ecirc;n phải của b&agrave;n ph&iacute;m, vừa gọn vừa thẩm mỹ. Cả phần m&agrave;n h&igrave;nh cũng được gia c&ocirc;ng kiểu Unibody hợp kim nh&ocirc;m nguy&ecirc;n khối, c&aacute;c cạnh được bo tr&ograve;n v&agrave; d&aacute;t mỏng tạo n&ecirc;n tổng thể m&aacute;y một thiết kế tuyệt mỹ, c&oacute; thể n&oacute;i l&agrave; đẹp nhất trong c&aacute;c d&ograve;ng Ultrabook.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/mb-1/mb-ct-1.jpg\" style=\"height:308px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Cấu h&igrave;nh mạnh mẽ, hiệu năng ổn định</strong></span></h5>\r\n\r\n<p>Apple Macbook Air 2017 Z0UU3 được trang bị bộ vi xử l&yacute; Intel Core i7 dual-core 2.2GHz, đạt tối đa 3.2GHz, Cache 4MB, RAM 8GB 1600MHz, card đồ họa Intel HD Graphics 6000 gi&uacute;p m&aacute;y c&oacute; thể xử l&yacute; nhanh ch&oacute;ng v&agrave; mượt m&agrave; c&aacute;c t&aacute;c vụ của người d&ugrave;ng như soạn thảo văn bản, chơi game, lướt web, nghe nhạc, Autocad, Photoshop&hellip; Ngo&agrave;i ra, m&aacute;y c&ograve;n được trang bị ổ cứng 128GB SSD cung cấp cho người d&ugrave;ng kh&ocirc;ng gian rộng r&atilde;i để lưu trữ dữ liệu hay những bộ phim y&ecirc;u th&iacute;ch.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/mb-1/mb-ct-2.jpg\" style=\"height:308px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Kết nối nhanh ch&oacute;ng với Thunderbolt 2 v&agrave; USB 3.0</strong></span></h5>\r\n\r\n<p>M&aacute;y sử dụng hệ điều h&agrave;nh macOS Sierra c&oacute; giao diện đẹp, bố cục được sắp xếp gọn g&agrave;ng. Thiết bị c&ograve;n cho ph&eacute;p đồng bộ dữ liệu giữa Mac v&agrave; c&aacute;c thiết bị chạy iOS, hỗ trợ nhiều ứng dụng chuy&ecirc;n biệt. Hơn nữa, thiết bị trang bị 1 cổng Thunderbolt độc quyền c&oacute; tốc độ truyền tải dữ liệu nhanh gấp 2 lần USB 3.0 hiện nay, 2 cổng USB 3.0 n&ecirc;n bạn c&oacute; thể chuyển đổi dễ d&agrave;ng từ cổng Thunderbolt th&ocirc;ng qua c&aacute;p chuyển đổi.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Cảm ứng Multi-Touch v&agrave; hơn thế nữa</strong></span></h5>\r\n\r\n<p>C&aacute;c động t&aacute;c của Multi-Touch trong macOS Sierra l&agrave;m cho tất cả mọi thứ bạn l&agrave;m tr&ecirc;n MacBook Air trực quan hơn, trực tiếp, v&agrave; vui vẻ. V&agrave; Multi-Touch trackpad rộng r&atilde;i được thiết kế ho&agrave;n hảo cho họ, cho d&ugrave; đ&oacute; l&agrave; một swipe ba ng&oacute;n tay để k&iacute;ch hoạt Mission Control hoặc một nh&uacute;m bốn ng&oacute;n tay để xem tất cả c&aacute;c ứng dụng của bạn trong Launchpad. Phản ứng cử chỉ được mịn m&agrave;ng v&agrave; thực tế.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/mb-1/mb-ct-3.jpg\" style=\"height:290px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m Backlight nổi bật</strong></span></h5>\r\n\r\n<p>B&agrave;n ph&iacute;m l&agrave; backlit, do đ&oacute; bạn c&oacute; thể g&otilde; trong điều kiện &aacute;nh s&aacute;ng yếu một c&aacute;ch dễ d&agrave;ng. Một cảm biến t&iacute;ch hợp trong ph&aacute;t hiện những thay đổi trong &aacute;nh s&aacute;ng m&ocirc;i trường xung quanh v&agrave; điều chỉnh độ s&aacute;ng b&agrave;n ph&iacute;m v&agrave; m&agrave;n h&igrave;nh tự động, đem lại cho bạn sự chiếu s&aacute;ng ho&agrave;n hảo trong bất kỳ m&ocirc;i trường.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/mb-1/mb-ct-4.jpg\" style=\"height:252px; width:750px\" /></p>\r\n', 4, '2019-06-17 06:38:16', 'Bình thường', 'Bình thường', 1),
(28, 'Macbook Pro 2017 (13.3 inch) Core i5/256GB - Hàng Chính Hãng', 32290000, 34990000, 30, 'CPU: Intel Core i5 2.3GHz\r\n\r\nMàn hình LCD 13.3 inch\r\n\r\nVGA: Intel Graphics 640\r\n\r\nRAM: 8GB\r\n\r\nHDD: 256SSD', 'mb3.jpg', 'mb3-1.jpg', 'mb3-2.png', 'mb3-3.jpg', 'mb3-4.jpg', 'mb3-5.jpg', 'mb3-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế đặc trưng Unibody từ Apple</strong></span></h5>\r\n\r\n<p><strong>Apple Macbook Pro 2017 13.3 inch MPXU2 Silver</strong>&nbsp;được trang bị lớp vỏ nh&ocirc;m nguy&ecirc;n khối Unibody sang trọng v&agrave; chắc chắn. Thiết kế mỏng, nhẹ v&agrave; cực gọn g&agrave;ng chỉ 1.49 cm, trọng lượng 1.37kg mang t&iacute;nh di động cao, rất tiện lợi khi di chuyển.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hiệu năng mượt m&agrave;</strong></span></h5>\r\n\r\n<p>Apple Macbook Pro 2017 c&oacute; bộ xử l&yacute; Intel Core i5 Kabylake tốc độ 2.30 GHz, card đồ họa t&iacute;ch hợp Intel Iris Graphics 640, bộ nhớ RAM 8 GB, c&ugrave;ng ổ cứng lưu trữ SSD 256 GB gi&uacute;p m&aacute;y chạy mượt m&agrave; c&aacute;c thao t&aacute;c sử dụng v&agrave; gi&uacute;p bạn thoải m&aacute;i lưu trữ dữ liệu. C&ocirc;ng nghệ ổ cứng SSD cho tốc độ lưu trữ nhanh hơn v&agrave; tuổi thọ của ổ cứng cũng l&acirc;u hơn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/mb-1/mb-ct-5.jpg\" style=\"height:416px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Đ&egrave;n nền b&agrave;n ph&iacute;m</strong></span></h5>\r\n\r\n<p>Đ&egrave;n LED nền đẹp mắt tr&ecirc;n b&agrave;n ph&iacute;m gi&uacute;p tăng độ ch&iacute;nh x&aacute;c khi d&ugrave;ng ph&iacute;m nhất l&agrave; v&agrave;o ban đ&ecirc;m, ngo&agrave;i ra đ&egrave;n ph&iacute;m c&ograve;n tạo điểm nhấn kh&aacute;c biệt v&agrave; đầy sang trọng gi&uacute;p chiếc Apple Macbook Pro 2017 c&agrave;ng long lanh hơn v&agrave; đẹp mắt hơn.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Chuột cảm ứng lực</strong></span></h5>\r\n\r\n<p>Apple Macbook Pro 2017 được trang bị th&ecirc;m chuột cảm ứng lực (Force Touch) mới, gi&uacute;p đem lại cho người d&ugrave;ng một cảm gi&aacute;c si&ecirc;u mượt m&agrave; v&agrave; cực tiện lợi để xem th&ocirc;ng tin nhanh khi sử dụng nữa.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Cổng Thunderbolt hiện đại, truyền tải dữ liệu nhanh ch&oacute;ng</strong></span></h5>\r\n\r\n<p>Sản phẩm được trang bị 2 cổng Thunderbolt 3 sử dụng dạng cổng USB-C để truyền t&iacute;n hiệu. Thunderbolt 3 cho ph&eacute;p truyền dữ liệu l&ecirc;n tới 40 Gbps, cao gấp đ&ocirc;i so với Thunderbolt 2 chỉ 20 Gbs trong khi điện năng ti&ecirc;u thụ th&igrave; chỉ bằng một nửa.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh hiển thị sắc n&eacute;t</strong></span></h5>\r\n\r\n<p>Apple Macbook Pro 2017 c&oacute; m&agrave;n h&igrave;nh rộng 13.3 inch, độ ph&acirc;n giải l&agrave; Retina (2560 x 1600) sử dụng c&ocirc;ng nghệ m&agrave;n h&igrave;nh IPS v&agrave; LED Backlit cho h&igrave;nh ảnh hiển thị kh&aacute; chất lượng, tươi s&aacute;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Thời lượng pin l&ecirc;n đến 10 giờ sử dụng</strong></span></h5>\r\n\r\n<p>Vấn đề pin với sản phẩm n&agrave;y kh&aacute; đơn giản, kh&ocirc;ng phải lo &acirc;u nhiều khi m&agrave; thời gian khoảng 10 tiếng sử dụng sau một lần sạc đầy.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/mb-1/mb-ct-6.jpg\" style=\"height:416px; width:750px\" /></p>\r\n', 4, '2019-06-17 06:38:16', 'Bình thường', 'Bình thường', 1),
(29, 'Apple Macbook Air 2018 Core i5/ 8GB/ 256GB - Hàng Chính Hãng', 31790000, 32990000, 10, 'Chip: Intel Core i5 8th 1.6GHz dual-core (Turbo Boost up to 3.6GHz)\r\n\r\nRAM: 8GB 2133MHz LPDDR3\r\n\r\nỔ Cứng: 256GB SSD\r\n\r\nChipset đồ họa: Intel UHD Graphics 617\r\n\r\nMàn hình: 13.3 inch Retina (2560 x 1600) Diagonal LED-backlit, IPS\r\n\r\nVân tay: Touch ID\r\n\r\nCổng kết nối: 2 x Thunderbolt 3                                                                                                                                                ', 'mb4.jpg', 'mb4-1.jpg', 'mb4-2.jpg', 'mb4-3.jpg', 'mb4-4.jpg', 'mb4-5.jpg', 'mb4-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế tinh tế, sang trọng</strong></span></h5>\r\n\r\n<p><strong>Apple Macbook Air 2018</strong>&nbsp;c&oacute; thiết kế tối giản nhưng kh&ocirc;ng k&eacute;m phần sang trọng. To&agrave;n th&acirc;n m&aacute;y được gia c&ocirc;ng từ nh&ocirc;m nguy&ecirc;n khối một c&aacute;ch tỉ mỉ v&agrave; ch&iacute;nh x&aacute;c, tạo n&ecirc;n vẻ liền lạc v&agrave; chắc chắn lại vừa thanh tho&aacute;t, sang trọng. N&uacute;t Power khởi động m&aacute;y được t&iacute;ch hợp lu&ocirc;n v&agrave;o g&oacute;c tr&ecirc;n b&ecirc;n phải của b&agrave;n ph&iacute;m, vừa gọn vừa thẩm mỹ. Cả phần m&agrave;n h&igrave;nh cũng được gia c&ocirc;ng kiểu Unibody hợp kim nh&ocirc;m nguy&ecirc;n khối, c&aacute;c cạnh được bo tr&ograve;n v&agrave; d&aacute;t mỏng tạo n&ecirc;n tổng thể m&aacute;y một thiết kế tuyệt mỹ, tuyệt đẹp&nbsp;trong c&aacute;c d&ograve;ng Ultrabook.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/mb-2/mb-ct-7.jpg\" style=\"height:330px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh 13.3 inch Retina, IPS</strong></span></h5>\r\n\r\n<p>Sở hữu m&agrave;n h&igrave;nh LED-backlit 13.3 inch Retina có đ&ocirc;̣ ph&acirc;n giải&nbsp;(2560 x 1600) để xem mọi thứ tốt hơn, mang đến những h&igrave;nh ảnh chất lượng, chi tiết, r&otilde; n&eacute;t, m&agrave;u sắc cũng cực kỳ trung thực v&agrave; sống động.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/mb-2/mb-ct-9.jpg\" style=\"height:422px; width:750px\" /></p>\r\n\r\n<p>Tấm nền IPS gi&uacute;p bạn c&oacute; thể trải nghiệm g&oacute;c nh&igrave;n rộng 178&deg; đem lại h&igrave;nh ảnh sắc n&eacute;t, sống động v&agrave; độ tương phản cao ngay cả ở những g&oacute;c nh&igrave;n hẹp.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bộ vi xử l&yacute; Intel Core i5 8th</strong></span></h5>\r\n\r\n<p>M&aacute;y được trang bị bộ vi xử l&yacute; Intel Core i5 8th dual-core 1.6GHz, đạt tối đa 3.6GHz, RAM&nbsp;8GB 2133MHz LPDDR3,&nbsp;card đồ họa Intel UHD Graphics 617 gi&uacute;p m&aacute;y c&oacute; thể xử l&yacute; nhanh ch&oacute;ng v&agrave; mượt m&agrave; c&aacute;c t&aacute;c vụ của người d&ugrave;ng như soạn thảo văn bản, chơi game, lướt web, nghe nhạc, Autocad, Photoshop&hellip; Ngo&agrave;i ra, m&aacute;y c&ograve;n được trang bị ổ cứng SSD 256GB cung cấp cho người d&ugrave;ng kh&ocirc;ng gian rộng r&atilde;i để lưu trữ dữ liệu hay những bộ phim y&ecirc;u th&iacute;ch.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/mb-2/mb-ct-10.jpg\" style=\"height:492px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>T&iacute;nh bảo mật v&agrave; tiện lợi cao cấp của Touch ID</strong></span></h5>\r\n\r\n<p>T&iacute;nh bảo mật v&agrave; tiện lợi cao cấp của Touch ID hiện được t&iacute;ch hợp ngay trong MacBook Air. Đơn giản chỉ cần đặt ng&oacute;n tay của bạn tr&ecirc;n cảm biến Touch ID v&agrave; - như thế - m&aacute;y Mac của bạn mở kh&oacute;a. Sử dụng dấu v&acirc;n tay của bạn để truy cập ngay lập tức t&agrave;i liệu, ghi ch&uacute; v&agrave; c&agrave;i đặt hệ thống bị kh&oacute;a m&agrave; kh&ocirc;ng cần nhập mật khẩu kh&aacute;c. Với Touch ID, th&ocirc;ng tin c&aacute; nh&acirc;n của bạn vẫn ở chế độ ri&ecirc;ng tư.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/mb-2/mb-ct-8.jpg\" style=\"height:422px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Chip bảo mật Apple T2</strong></span></h5>\r\n\r\n<p>MacBook Air c&oacute; Chip bảo mật của Apple T2 - silicon Mac thế hệ thứ hai được thiết kế bởi Apple để l&agrave;m cho MacBook Air trở n&ecirc;n an to&agrave;n hơn. V&igrave; vậy, khi bạn sử dụng Touch ID để mở kh&oacute;a m&aacute;y Mac, x&aacute;c thực t&agrave;i liệu hoặc thanh to&aacute;n trực tuyến cho người b&aacute;n, th&ocirc;ng tin của bạn sẽ an to&agrave;n. Với m&atilde; h&oacute;a dữ liệu on-the-fly, tất cả dữ liệu của bạn được lưu trữ tr&ecirc;n SSD được tự động v&agrave; m&atilde; h&oacute;a ho&agrave;n to&agrave;n. V&agrave; nhờ v&agrave;o chip bảo mật T2, một giọng n&oacute;i quen thuộc đến với MacBook Air - Hey Siri lu&ocirc;n sẵn s&agrave;ng mở ứng dụng, t&igrave;m t&agrave;i liệu, ph&aacute;t nhạc v&agrave; trả lời c&aacute;c c&acirc;u hỏi của bạn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/mb-2/mb-ct-12.jpg\" style=\"height:580px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>&Acirc;m thanh tốt hơn bao giờ hết</strong></span></h5>\r\n\r\n<p>Với kỹ thuật xử l&yacute; v&agrave; điều chỉnh mới nhất, MacBook Air c&oacute; &acirc;m thanh tốt hơn bao giờ hết. Loa &acirc;m thanh nổi cung cấp gấp đ&ocirc;i &acirc;m trầm v&agrave; &acirc;m lượng nhiều hơn 25 phần trăm so với thế hệ trước. V&agrave; &acirc;m thanh stereo rộng hơn sẽ tạo ra &acirc;m thanh sống động khi bạn đang nghe nhạc hoặc xem phim. Camera FaceTime độ n&eacute;t cao cho ph&eacute;p bạn xem một hoặc nhiều - bạn b&egrave; v&agrave; gia đ&igrave;nh của bạn với chi tiết sắc n&eacute;t. V&agrave; ba micr&ocirc; tạo th&agrave;nh một mảng thu được ch&iacute;nh x&aacute;c hơn giọng n&oacute;i của bạn cho c&aacute;c cuộc gọi FaceTime, đọc ch&iacute;nh tả v&agrave; n&oacute;i chuyện với Siri.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/mb-2/mb-ct-13.jpg\" style=\"height:580px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Kết nối c&aacute;c cổng tốc độ cao</strong></span></h5>\r\n\r\n<p>Macbook c&ograve;n cho ph&eacute;p đồng bộ dữ liệu giữa Mac v&agrave; c&aacute;c thiết bị chạy iOS, hỗ trợ nhiều ứng dụng chuy&ecirc;n biệt. Hơn nữa, thiết bị trang bị 2 cổng Thunderbolt 3 độc quyền c&oacute; tốc độ truyền tải dữ liệu nhanh gấp 2 lần USB 3.0 hiện nay.</p>\r\n', 4, '2019-07-07 07:26:54', 'Bán chạy', 'Nổi bật', 1),
(30, 'Lenovo Legion Y530-15ICH 81FV00SUVN Core i7-8750H/Dos (15.6\" FHD)', 22239000, 24850000, 30, 'Chip: Intel Core i7-8750H (2.2GHz up to 4.1GHz 9MB)\r\n\r\nRAM: 8GB DDR4 2666MHz (2x SO-DIMM socket, up to 32GB SDRAM)\r\n\r\nỔ cứng: HDD 1TB 7200rpm + 128GB SSD M.2 PCIe\r\n\r\nChipset đồ họa: NVIDIA GeForce GTX 1050 4GB GDDR5 + Intel UHD Graphics 630\r\n\r\nMàn hình: 15.6 inch Full HD (1920 x 1080) IPS, Anti-Glare\r\n\r\nHệ điều hành: Free DOS\r\n\r\nPin: 3 Cell 52.5 WHr', 'lenovo5.jpg', 'lenovo5-1.jpg', 'lenovo5-2.jpg', 'lenovo5-3.jpg', 'lenovo5-4.jpg', 'lenovo5-5.jpg', 'lenovo5-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế gập 180 độ</strong></span></h5>\r\n\r\n<p><strong>Laptop Lenovo Legion Y530-15ICH 81FV00SUVN&nbsp;</strong>mang đến cho người d&ugrave;ng một chiếc laptop mạnh mẽ v&agrave; bền vững nhờ được bao bọc bởi bộ khung chắc chắn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-4/lenovo-ct-1.jpg\" style=\"height:419px; width:750px\" /></p>\r\n\r\n<p>Lớp vỏ ngo&agrave;i l&agrave;m từ chất liệu cao cấp vừa mang lại một độ bền vượt trội để đủ sức bảo vệ phần m&agrave;n h&igrave;nh v&agrave; c&aacute;c linh kiện b&ecirc;n trong nhưng vẫn đảm bảo tối ưu sự linh hoạt v&agrave; tiện dụng cho người d&ugrave;ng mỗi khi mang theo m&aacute;y b&ecirc;n m&igrave;nh. Ngo&agrave;i ra, bản lề của m&aacute;y được thiết kế đặc biệt gi&uacute;p m&aacute;y c&oacute; thể gập 180 độ như quyển tập.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh 15.6 inch&nbsp;Full HD, IPS cho g&oacute;c nh&igrave;n rộng</strong></span></h5>\r\n\r\n<p>Được trang bị m&agrave;n h&igrave;nh 15.6 inch với độ ph&acirc;n giải Full HD (1920 x 1080) pixels cho chất lượng hiển thị kh&aacute; tốt khi h&igrave;nh ảnh được hiển thị sắc n&eacute;t, độ chi tiết cao v&agrave; m&agrave;u sắc trung thực ở khoảng c&aacute;ch sử dụng th&ocirc;ng thường của người d&ugrave;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-4/lenovo-ct-2.jpg\" style=\"height:456px; width:750px\" /></p>\r\n\r\n<p>Đồng thời máy được tích hợp m&agrave;n h&igrave;nh IPS đem đ&ecirc;́n g&oacute;c nh&igrave;n rộng gi&uacute;p bạn nhìn mọi thứ rõ ràng hơn, cùng tấm nền chống ch&oacute;i Anti-glare gi&uacute;p bạn c&oacute; thể trải nghiệm tốt hơn ở m&ocirc;i trường b&ecirc;n ngo&agrave;i, c&oacute; khả năng chống ch&oacute;i tốt.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bộ vi xử l&yacute; Intel Core i7</strong></span></h5>\r\n\r\n<p>M&aacute;y được trang bị bộ vi xử l&yacute; Intel Core i7-8750H với xung nhịp c&oacute; thể l&ecirc;n đến 4GHz, cho bạn những gi&acirc;y ph&uacute;t lướt web nhanh hơn, mạnh mẽ hơn. B&ecirc;n cạnh đ&oacute;, với bộ nhớ trong Ram 8GB DDR4 Bus 2666MHz đảm bảo xử l&iacute; c&aacute;c t&aacute;c vụ đa nhiệm thật mượt m&agrave; v&agrave; ổn định.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-4/lenovo-ct-3.jpg\" style=\"height:381px; width:750px\" /></p>\r\n\r\n<p>Ngo&agrave;i ra,&nbsp;Lenovo Legion Y530-15ICH 81FV00SUVN&nbsp;c&ograve;n trang bị th&ecirc;m &ocirc;̉ cứng kép HDD 1TB 7200rpm + 128GB SSD M.2 PCIe cho ph&eacute;p người d&ugrave;ng lưu trữ dữ liệu một c&aacute;ch thoải m&aacute;i, cũng như truy xuất dữ liệu một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Card đồ họa NVIDIA GeForce GTX 1050</strong></span></h5>\r\n\r\n<p>Đặc biệt, với card đồ họa&nbsp;rời&nbsp;NVIDIA GeForce GTX 1050 b&ocirc;̣ nhớ 4GB bạn c&oacute; thể ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m rằng m&aacute;y c&oacute; thể h&ocirc;̃ trợ bạn tối đa từ c&aacute;c việc đơn giản đến c&aacute;c c&ocirc;ng việc mang t&iacute;nh chất đ&ograve;i hỏi cao như thiết kế đồ họa, 3D, photoshop,... cũng như giải tr&iacute; với những game 3D khủng nhất hiện nay,&nbsp;đ&aacute;p ứng tối đa hiệu năng bạn cần v&agrave; cho ph&eacute;p bạn chơi game mượt với tốc độ khung h&igrave;nh cao.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>C&ocirc;ng nghệ &acirc;m thanh Harman Speakers with Dolby Audio</strong></span></h5>\r\n\r\n<p>Laptop sử dụng c&ocirc;ng nghệ &acirc;m thanh Harman Speakers with Dolby Audio gi&uacute;p cải thiện tốt hơn &acirc;m thanh loa ngo&agrave;i đồng thời gi&uacute;p &acirc;m lượng to hơn nhưng vẫn kh&ocirc;ng thay đổi về chất lượng. Nhờ đ&oacute;, bạn sẽ c&oacute; những ph&uacute;t gi&acirc;y thật tuyệt vời c&ugrave;ng những giai điệu &acirc;m nhạc hay những bộ phim &ldquo;đỉnh&rdquo; nhất.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m chiclet hiện đại, touchpad đa điểm</strong></span></h5>\r\n\r\n<p>M&aacute;y được trang bị b&agrave;n ph&iacute;m chiclet hiện đại với c&aacute;c ph&iacute;m c&oacute; khoảng c&aacute;ch hợp l&yacute;, bấm &ecirc;m v&agrave; h&agrave;nh tr&igrave;nh ph&iacute;m vừa đủ để tạo cảm gi&aacute;c g&otilde; thoải m&aacute;i cho người d&ugrave;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-4/lenovo-ct-4.jpg\" style=\"height:427px; width:750px\" /></p>\r\n\r\n<p>Phần touchpad đi k&egrave;m cũng c&oacute; diện th&iacute;ch kh&aacute; lớn, nhạy v&agrave; hỗ trợ đa điểm tốt n&ecirc;n người d&ugrave;ng c&oacute; thể thoải m&aacute;i cuộn văn bản, thua ph&oacute;ng hay xoay h&igrave;nh ảnh dễ d&agrave;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>USB Type C tất cả trong một kết nối</strong></span></h5>\r\n\r\n<p>Laptop sở hữu cổng kết nối USB Type C đa dụng nhất hiện nay, với thiết kế đồng đẳng gi&uacute;p bạn c&oacute; thể cắm cổng kết nối ở bất cứ chiều n&agrave;o. Ngo&agrave;i ra với c&ocirc;ng nghệ n&agrave;y sẽ cho tốc độ truyền tải dữ liệu nhanh gấp 10 lần so với c&aacute;c cổng kết nối cũ hiện nay.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-4/lenovo-ct-5.jpg\" style=\"height:180px; width:750px\" /></p>\r\n\r\n<p>Hỗ trợ nhiều cổng kết nối như cổng HDMI, Bluetooth, khe thẻ nhớ, jack tai nghe cho khả năng kết nối với c&aacute;c thiết bị kh&aacute;c một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Thời lượng pin ổn định</strong></span></h5>\r\n\r\n<p>Pin 3 cell cho Lenovo Legion Y530-15ICH 81FV00SUVN&nbsp;thời gian sử dụng tương đối. C&ugrave;ng với c&ocirc;ng nghệ tiết kiệm pin, m&aacute;y c&oacute; thể hoạt động m&aacute;t, &ecirc;m &aacute;i v&agrave; tiết kiệm điện năng n&ecirc;n c&oacute; thể đ&aacute;p ứng tốt nhu cầu sử dụng b&igrave;nh thường.</p>\r\n', 5, '2019-06-17 06:38:16', 'Bình thường', 'Bình thường', 1),
(31, 'Acer Predator Helios 300 PH315-51-759Y Core i7-8750H', 32990000, 34590000, 30, 'Tặng Balo Acer Predator Gaming BackPack\r\n\r\nThông số Acer Predator Helios 300 PH315-51-759Y\r\n\r\nCPU	Intel Core i7-8750H 2.2GHz up to 4.1GHz 9MB\r\n\r\nRAM	16GB DDR4 2666MHz (2x SO-DIMM socket, up to 32GB SDRAM)\r\n\r\nỔ cứng	HDD 1TB 5400rpm + 256GB SSD PCIE G3X4\r\n\r\nCard đồ họa	NVIDIA GeForce GTX 1060 6GB GDDR5\r\n\r\nMàn hình	15.6\" FHD (1920 x 1080) IPS, Anti-Glare, 144Hz/ 3ms\r\n\r\nCổng giao tiếp	2x USB 2.0, 1x USB 3.0, 1x USB 3.1 Type C, 1x HDMI\r\n\r\nỔ quang	None\r\n\r\nHệ điều hành	Free DOS\r\n\r\nMàu sắc	Obsidian Black\r\n\r\nPin	4 Cell 48 WHrs                        ', 'acer5.jpg', 'acer5-1.jpg', 'acer5-2.jpg', 'acer5-3.jpg', 'acer5-4.jpg', 'acer5-5.jpg', 'acer5-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế vỏ kim loại chắc chắn</strong></span></h5>\r\n\r\n<p>Laptop Acer Predator Helios 300 PH315-51-759Y NH.Q3FSV.004&nbsp;sở hữu thiết kế vỏ kim loại mang lại cảm gi&aacute;c chắc chắn, đặc trưng của d&ograve;ng Predator với kiểu d&aacute;ng g&oacute;c cạnh hầm hố, hốc tản nhiệt lớn. Bề mặt laptop l&agrave; v&acirc;n phay xước bền bỉ v&agrave; hạn chế b&aacute;m bẩn, nước sơn đen hoạ tiết th&ecirc;m đường chỉ đỏ đặc trưng của d&ograve;ng laptop gaming Acer v&agrave; đặc biệt, ch&iacute;nh giữa l&agrave; logo Predator l&agrave;m tăng th&ecirc;m độ ngầu của m&aacute;y.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/acer-4/acer-ct-1.jpg\" style=\"height:558px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh 15.6 inch Full HD</strong></span></h5>\r\n\r\n<p>Sở hữu m&agrave;n h&igrave;nh 15.6 inch gi&uacute;p hiển thị h&igrave;nh ảnh Full HD (1920 x 1080) để xem mọi thứ tốt hơn, cùng c&ocirc;ng nghệ màn hình LED Backlit mang đến những h&igrave;nh ảnh chất lượng, chi tiết, r&otilde; n&eacute;t, m&agrave;u sắc cũng cực kỳ trung thực v&agrave; sống động.&nbsp;Tấm nền IPS, Anti-glare cho m&agrave;u sắc trung thực ở mọi g&oacute;c độ gi&uacute;p game thủ c&oacute; những ph&uacute;t gi&acirc;y trải nghiệm game tuyệt vời nhất, gi&uacute;p bạn c&oacute; thể trải nghiệm tốt hơn ở m&ocirc;i trường b&ecirc;n ngo&agrave;i, c&oacute; khả năng chống ch&oacute;i tốt.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/acer-4/acer-ct-2.jpg\" style=\"height:565px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bộ vi xử l&yacute; Intel Core i7</strong></span></h5>\r\n\r\n<p>Được trang bị bộ vi xử l&yacute; Intel Core i7-8750H tốc độ l&ecirc;n đ&ecirc;́n 4.1GHz, đem lại cho m&aacute;y một sức mạnh xử l&yacute; ổn định v&agrave; tiết kiệm điện năng hiệu quả. C&ugrave;ng bộ nhớ RAM DDR4 dung lượng 16GB cho hiệu suất xử l&yacute; tốt cũng như đ&aacute;p ứng c&aacute;c t&aacute;c vụ đa nhiệm mượt m&agrave; c&aacute;c ứng dụng. Chipset đồ họa NVIDIA GeForce GTX 1060 hỗ trợ xử l&yacute; hiệu ứng h&igrave;nh ảnh 3D c&aacute;c c&ocirc;ng việc li&ecirc;n quan đến đồ hoạ, giải tr&iacute; của bạn. Ngo&agrave;i ra, Acer&nbsp;c&ograve;n trang bị cho m&aacute;y ổ cứng HDD dung lượng 1TB và &ocirc;̉ 256GB SSD gi&uacute;p m&aacute;y hoạt động mượt m&agrave; hơn, khởi động nhanh ch&oacute;ng hơn.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hệ thống l&agrave;m m&aacute;t</strong></span></h5>\r\n\r\n<p>Hệ thống 2 quạt tản nhiệt bằng th&eacute;p với 59 c&aacute;nh quạt &aacute;p dụng c&ocirc;ng nghệ AeroBlade 3D độc quyền của Acer tăng luồng gi&oacute; th&ecirc;m 25%, đảm bảo hệ thống lu&ocirc;n m&aacute;t mẻ khi chơi game trong thời gian d&agrave;i.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Ứng dụng PredatorSense</strong></span></h5>\r\n\r\n<p>Phần mềm PredatorSense gi&uacute;p game thủ c&oacute; thể dễ d&agrave;ng t&ugrave;y chỉnh c&aacute;c thiết lập cho hệ thống như điều chỉnh tốc độ quạt, theo d&otilde;i nhiệt độ của CPU, GPU.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m LED backlit m&agrave;u đỏ nổi bật v&agrave; touchpad đa điểm nhạy b&eacute;n</strong></span></h5>\r\n\r\n<p>Laptop c&oacute; b&agrave;n ph&iacute;m được gia c&ocirc;ng tốt, cảm gi&aacute;c g&otilde; tốt, kết hợp với cụm WASD m&agrave;u đỏ nổi bật đậm chất gaming. Ngo&agrave;i ra hệ thống đ&egrave;n LED backlit m&agrave;u đỏ nổi bật gi&uacute;p game thủ c&oacute; thể thao t&aacute;c ch&iacute;nh x&aacute;c trong những trận chiến game khốc liệt kể cả ng&agrave;y hay đ&ecirc;m. Thiết kế b&agrave;n ph&iacute;m c&acirc;n đối n&ecirc;n nh&igrave;n v&agrave;o kh&aacute; bắt mắt. Touchpad cảm ứng đa điểm nhạy b&eacute;n theo từng thao t&aacute;c chạm.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/acer-4/acer-ct-3.jpg\" style=\"height:514px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>USB Type C &ndash; tất cả trong một kết nối</strong></span></h5>\r\n\r\n<p>Laptop sở hữu cổng kết nối USB Type C đa dụng nhất hiện nay, với thiết kế đồng đẳng gi&uacute;p bạn c&oacute; thể cắm cổng kết nối ở bất cứ chiều n&agrave;o. Ngo&agrave;i ra với c&ocirc;ng nghệ n&agrave;y sẽ cho tốc độ truyền tải dữ liệu nhanh gấp 10 lần so với c&aacute;c cổng kết nối cũ hiện nay.</p>\r\n\r\n<p>Acer Predator Helios 300 PH315-51-759Y NH.Q3FSV.004&nbsp;còn hỗ trợ nhiều cổng kết nối như cổng HDMI, Bluetooth, khe thẻ nhớ, jack tai nghe cho khả năng kết nối với c&aacute;c thiết bị kh&aacute;c một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Thời lượng pin&nbsp;</strong></span></h5>\r\n\r\n<p>Sản ph&acirc;̉m chuy&ecirc;n sử dụng các tác vụ nặng , n&ecirc;n khuy&ecirc;́n khích khách hàng cắm sạc trực ti&ecirc;́p đ&ecirc;̉ sử dụng mượt mà nh&acirc;́t.&nbsp;</p>\r\n', 7, '2019-06-18 06:38:16', 'Bán chạy', 'Bình thường', 1),
(32, 'MSI GF63 8RC-243VN Core i5-8300H/Win10 (15.6\" FHD IPS)/128GB SSD M.2 PCIe', 19690000, 22900000, 30, 'Chip: Intel Core i5-8300H (2.3GHz upto 4.0GHz, 4Cores, 8Threads, 8MB cache, FSB 8GT/s)\r\n\r\nRAM: 8GB DDR4-2666MHz, 2 Slots, Max 32GB\r\n\r\nỔ cứng: 128GB SSD M.2 PCIe + 1TB HDD 5400rpm\r\n\r\nChipset đồ họa: NVIDIA GeForce GTX 1050 4GB GDDR5\r\n\r\nMàn hình: 15.6-inch Full HD (1920 x 1080), IPS-Level 45% NTSC, Thin Bezel\r\n\r\nHệ điều hành: Windows 10 Home bản quyền\r\n\r\nPin: 3-Cell 51 Whr', 'msi3.jpg', 'msi3-1.jpg', 'msi3-2.jpg', 'msi3-3.jpg', 'msi3-4.jpg', 'msi3-5.jpg', 'msi3-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế Gaming chuẩn hiện đại</strong></span></h5>\r\n\r\n<p><strong>Laptop MSI GF63 8RC-243VN&nbsp;</strong>l&agrave; chiếc laptop chơi game được thiết kế theo xu hướng mới hiện nay: mỏng nhẹ v&agrave; tinh tế. M&aacute;y c&oacute; c&aacute;c chỉ số thiết kế cực kỳ ấn tượng với trọng lượng chỉ 1.86kg v&agrave; k&iacute;ch thước chỉ 21.7mm. Với chất liệu chủ đạo l&agrave; nh&ocirc;m phay xước v&agrave; mặt th&ocirc;ng gi&oacute; chữ X mang lại cho chiếc laptop chơi game n&agrave;y sự c&acirc;n bằng ho&agrave;n hảo giữa thiết kế v&agrave; hiệu suất.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/msi-2/msi-ct-1.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh 15.6 inch Full HD</strong></span></h5>\r\n\r\n<p>M&agrave;n h&igrave;nh 15.6 inch Full HD&nbsp;(1920 x 1080) cho bạn m&agrave;u sắc ch&iacute;nh x&aacute;c, chi tiết v&agrave; mượt m&agrave;. Tấm nền IPS gi&uacute;p bạn c&oacute; thể trải nghiệm g&oacute;c nh&igrave;n rộng 178&deg; đem lại h&igrave;nh ảnh sắc n&eacute;t, sống động v&agrave; độ tương phản cao ngay cả ở những g&oacute;c nh&igrave;n hẹp, v&agrave; độ phủ m&agrave;u 94% NTSC đảm bảo xuất ra h&igrave;nh ảnh với chất lượng sắc n&eacute;t v&agrave; ch&acirc;n thật nhất.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/msi-2/msi-ct-2.jpg\" style=\"height:382px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bộ vi xử l&yacute; Intel Core i5</strong></span></h5>\r\n\r\n<p>M&aacute;y được trang bị bộ vi xử l&yacute; Intel Core i5-8300H với xung nhịp c&oacute; thể l&ecirc;n đến 4.0GHz, cho bạn những gi&acirc;y ph&uacute;t lướt web nhanh hơn, mạnh mẽ hơn. B&ecirc;n cạnh đ&oacute;, với bộ nhớ trong Ram&nbsp;8GB DDR4 (2666MHz) đảm bảo xử l&iacute; c&aacute;c t&aacute;c vụ đa nhiệm thật mượt m&agrave; v&agrave; ổn định. Ngo&agrave;i ra, m&aacute;y c&ograve;n trang bị th&ecirc;m &ocirc;̉ cứng kép 128GB SSD + 1TB HDD, cho bạn lưu trữ cũng như vận h&agrave;nh m&aacute;y một c&aacute;ch nhanh ch&oacute;ng, mượt m&agrave; hơn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/msi-2/msi-ct-3.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Card đồ họa thế hệ mới nhất của NVIDIA</strong></span></h5>\r\n\r\n<p>Với chip NVIDIA GeForce GTX 1050 b&ocirc;̣ nhớ đ&ocirc;̀ họa 4GB GDDR5 mang lại trải nghiệm chơi game tuyệt vời nhất, được th&uacute;c đẩy bởi c&ocirc;ng nghệ gaming ti&ecirc;n tiến nhất v&agrave; hiệu năng nhanh nhất, tạo n&ecirc;n một hệ sinh th&aacute;i gaming tuyệt vời.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bộ nhớ RAM mới nhất</strong></span></h5>\r\n\r\n<p>Nhanh hơn, nhiều dung lượng hơn, nhưng lại tiết kiệm điện năng hơn, bộ nhớ DDR4 tr&ecirc;n&nbsp;MSI GF63 8RC-243VN&nbsp;l&agrave; ti&ecirc;u chuẩn mới cho trải nghiệm chơi game cấp độ tiếp theo.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Nahimic 3 - Quản l&yacute; &acirc;m thanh ho&agrave;n hảo</strong></span></h5>\r\n\r\n<p>Phần mềm Nahimic 3 độc quyền của MSI tr&ecirc;n MSI GF63 8RC-243VN&nbsp;gi&uacute;p tăng cường chất lượng &acirc;m thanh v&ograve;m 3D trong tr&ograve; chơi v&agrave; kiểm so&aacute;t hữu hạn &acirc;m nhạc, phim v&agrave; cuộc gọi hội nghị của bạn.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Audio Boost - Chất lượng &acirc;m thanh l&agrave; tr&ecirc;n hết</strong></span></h5>\r\n\r\n<p>N&acirc;ng cao mọi chi tiết &acirc;m thanh l&ecirc;n 30% bằng AMP t&iacute;ch hợp (Bộ khuếch đại &acirc;m thanh) v&agrave; giắc &acirc;m thanh được mạ v&agrave;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/msi-2/msi-ct-4.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>DRAGON CENTER 2.0 - N&acirc;ng cấp ho&agrave;n thiện</strong></span></h5>\r\n\r\n<p>Thiết kế mới Dragon Center 2.0 tr&ecirc;n MSI GF63 8RC-243VN&nbsp;sẽ tối ưu h&oacute;a mọi thứ ho&agrave;n to&agrave;n l&ecirc;n một tầm cao mới. B&acirc;y giờ n&oacute; kh&ocirc;ng chỉ gi&aacute;m s&aacute;t m&agrave; c&ograve;n tối ưu h&oacute;a hiệu suất hệ thống th&ocirc;ng qua c&aacute;c cấu h&igrave;nh t&ugrave;y biến kh&aacute;c nhau, v&agrave; giải ph&oacute;ng bộ nhớ hệ thống để ưu ti&ecirc;n hiệu suất trong tr&ograve; chơi.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>USB Type C tất cả trong một kết nối</strong></span></h5>\r\n\r\n<p>Laptop sở hữu cổng kết nối USB Type C đa dụng nhất hiện nay, với thiết kế đồng đẳng gi&uacute;p bạn c&oacute; thể cắm cổng kết nối ở bất cứ chiều n&agrave;o. Ngo&agrave;i ra với c&ocirc;ng nghệ n&agrave;y sẽ cho tốc độ truyền tải dữ liệu nhanh gấp 10 lần so với c&aacute;c cổng kết nối cũ hiện nay.</p>\r\n\r\n<p>MSI GF63 8RC-243VN Hỗ trợ nhiều cổng kết nối như cổng HDMI, 1 x Mini-DisplayPort, Bluetooth, khe thẻ nhớ, jack tai nghe cho khả năng kết nối với c&aacute;c thiết bị kh&aacute;c một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Pin hơn 7 giờ sử dụng</strong></span></h5>\r\n\r\n<p>C&ocirc;ng nghệ sạc nhanh sẽ gi&uacute;p bạn sẵn s&agrave;ng tr&ograve; chơi ở bất kỳ l&uacute;c n&agrave;o. Pin mở rộng mới mang lại cho bạn hơn 7 giờ sử dụng khi l&agrave;m việc v&agrave; giải tr&iacute; tr&ecirc;n MSI GF63 8RC-243VN&nbsp;m&agrave; kh&ocirc;ng cần cắm sạc.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/msi-2/msi-ct-5.jpg\" style=\"height:500px; width:750px\" /></p>\r\n', 6, '2019-06-17 06:38:16', 'Bình thường', 'Bình thường', 1),
(33, 'Dell Latitude 3490 42LT340011 Core i7-8550U/ Radeon 530/ Dos (14\" HD)', 21119000, 21990000, 30, 'CPU: Intel Core i7-8550U Processor (1.8GHz upto 4.0GHz, 4Cores, 8Threads, 8MB cache)\r\n\r\nRAM: 8GB (1x8GB) 2400MHz DDR4\r\n\r\nHDD: 1TB 5400rpm\r\n\r\nVGA: Intel UHD Graphics 620\r\n\r\nMàn hình: 14\" HD (1366x768)\r\n\r\nHệ điều hành: Free Dos\r\n\r\nPin: 4 Cell 56Whr', 'dell9.jpg', 'dell9-1.jpg', 'dell9-2.jpg', 'dell9-3.jpg', 'dell9-4.jpg', 'dell9-5.jpg', 'dell9-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế thanh lịch, hiện đại</strong></span></h5>\r\n\r\n<p><strong>Laptop Dell Inspiron 5570 M5I5413</strong>&nbsp;c&oacute; thiết kế cuốn h&uacute;t người d&ugrave;ng với vẻ ngo&agrave;i thanh lịch v&agrave; tinh tế. Sản phẩm c&oacute; kiểu d&aacute;ng nhỏ gọn n&ecirc;n bạn c&oacute; thể y&ecirc;n t&acirc;m mang theo b&ecirc;n m&igrave;nh v&agrave; di chuyển bất cứ nơi đ&acirc;u bạn muốn.&nbsp;</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh 14 inch nhỏ gọn</strong></span></h5>\r\n\r\n<p>M&agrave;n h&igrave;nh 14.0 inch Non-Touch độ ph&acirc;n giải HD (1366x768) Anti-Glare LCD mang đến cho bạn những h&igrave;nh ảnh ch&acirc;n thực, chi tiết v&agrave; sinh động. G&oacute;c nh&igrave;n rộng 175&ordm; v&agrave; c&oacute; khả năng chống l&oacute;a. M&aacute;y cũng c&oacute; c&aacute;c cổng giao tiếp đa dạng gồm cổng VGA, HDMI, RJ-45, USB Type-A v&agrave; USB Type-C để kết nối với c&aacute;c thiết bị ngoại vi kh&aacute;c.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Cấu h&igrave;nh mạnh mẽ</strong></span></h5>\r\n\r\n<p>Chiếc laptop được trang bị bộ vi xử l&yacute; Intel Core i7 8550U 1.8GHz, 8MB, dung lượng RAM 8GB gi&uacute;p thực hiện c&aacute;c t&aacute;c vụ nhanh ch&oacute;ng, mượt m&agrave;, phục vụ tốt nhu cầu l&agrave;m việc v&agrave; giải tr&iacute; của người d&ugrave;ng. Ổ cứng 1TB gi&uacute;p lưu trữ được nhiều dữ liệu v&agrave; c&agrave;i đặt c&aacute;c phần mềm, ứng dụng cần thiết.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hệ thống b&agrave;n ph&iacute;m v&agrave; touchpad</strong></span></h5>\r\n\r\n<p>Với hệ thống b&agrave;n ph&iacute;m được bố tr&iacute; khoa học c&ugrave;ng với touchpad được phủ một lớp nh&aacute;m gi&uacute;p thao t&aacute;c tr&ecirc;n c&aacute;c ng&oacute;n tay của bạn dễ d&agrave;ng v&agrave; thuận tiện hơn rất nhiều. K&iacute;ch thước touchpad đủ rộng để người d&ugrave;ng c&oacute; thể sử dụng thoải m&aacute;i v&agrave; kh&ocirc;ng chiếm nhiều diện t&iacute;ch của b&agrave;n ph&iacute;m. Ngo&agrave;i ra m&aacute;y cũng được t&iacute;ch hợp c&aacute;c cổng USB để dễ d&agrave;ng kết nối với c&aacute;c thiết bị ngoại vi.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>C&ocirc;ng nghệ bảo mật dữ liệu</strong></span></h5>\r\n\r\n<p>L&agrave; sản phẩm phục vụ doanh nh&acirc;n n&ecirc;n Latitude 3490 được trang bị c&ocirc;ng nghệ bảo mật dữ liệu Dell Data Protection c&oacute; khả năng x&aacute;c thực người d&ugrave;ng th&ocirc;ng phần mềm m&atilde; h&oacute;a dữ liệu v&agrave; hệ thống Dell Client Command Suite gồm c&aacute;c c&ocirc;ng cụ tự động h&oacute;a nhằm gi&aacute;m s&aacute;t c&aacute;c thiết bị, tự động cập nhật chương tr&igrave;nh...</p>\r\n', 1, '2019-06-17 06:38:16', 'Bình thường', 'Bình thường', 1),
(34, 'Dell Inspiron 3168 TRDM71 Intel Pentium N3710 1.6GHz DDR3', 10500000, 11220000, 30, 'Chip: Intel Pentium N3710 1.6GHz up to max 2.56GHz\r\n\r\nRAM: DDR3 4GB\r\n\r\nỔ cứng: HDD 500GB\r\n\r\nChipset đồ họa: Intel HD Graphics 405\r\n\r\nMàn hình: 11.6 Inches\r\n\r\nHệ điều hành: Windows 10 + Office 365\r\n\r\nPin: 2 Cell', 'dell10.jpg', 'dell10-1.jpg', 'dell10-2.jpg', 'dell10-3.jpg', 'dell10-4.jpg', 'dell10-5.jpg', 'dell10-6.jpg', '<h3><span style=\"font-size:24px\"><strong>Tốc độ xử l&yacute; hiệu quả</strong></span></h3>\r\n\r\n<p>Laptop Dell Inspiron 3168 TRDM71 sử dụng chip Intel Pentium N3710 c&ugrave;ng bộ nhớ RAM 4GB cho tốc độ truyền v&agrave; xử l&yacute; dữ hiệu nhanh ch&oacute;ng, tr&aacute;nh sự cố lag m&aacute;y khi t&aacute;c vụ đa nhiệm tr&ecirc;n m&aacute;y. Kết hợp với ổ cứng SSD c&oacute; dung lượng l&ecirc;n đến 500GB sẽ gi&uacute;p bạn kh&ocirc;ng phải chờ đợi l&acirc;u khi truyền tải dữ liệu, gi&uacute;p bạn tiết kiệm thời gian v&agrave; đ&aacute;p ứng được nhu cầu cho c&ocirc;ng việc.</p>\r\n\r\n<h3><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh 11.6 inches với độ ph&acirc;n giải cao</strong></span></h3>\r\n\r\n<p>Laptop Dell Inspiron 3168 TRDM71 được thiết kế với m&agrave;n h&igrave;nh HD 11.6 inches cảm ứng c&ugrave;ng độ ph&acirc;n giải l&ecirc;n đến 1366 x 768 pixel cho h&igrave;nh ảnh trung thực, sống động v&agrave; r&otilde; n&eacute;t hơn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-1/dell-ct-1.jpg\" style=\"height:1037px; width:1495px\" /><span style=\"font-size:24px\"><strong>Cổng kết nối tiện dụng</strong></span></p>\r\n\r\n<p>Laptop được hỗ trợ với nhiều cổng kết nối như USB 2.0; USB 3.0; Kết nối HDMI/VGA 1 HDMI; Khe cắm thẻ nhớ 2-in-1 Card reader (SD, SDHC, SDXC); Tai nghe 1x jack 3.5mn; Camera 1.0MP MegaPixels sẽ gi&uacute;p bạn dễ d&agrave;ng kết nối với c&aacute;c thiết bị điện tử, hỗ trợ cho c&ocirc;ng việc, học tập v&agrave; giải tr&iacute;.</p>\r\n\r\n<h3><span style=\"font-size:24px\"><strong>Card đồ họa Intel HD Graphics 405</strong></span></h3>\r\n\r\n<p>Laptop sử dụng card đồ họa Intel HD Graphics 405 chuy&ecirc;n dụng sẽ đ&aacute;p ứng nhu cầu sử dụng của bạn với c&ocirc;ng việc thiết kế đồ họa, chơi game 3D hay xem phim HD</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-1/dell-ct-2.jpg\" style=\"height:1200px; width:1200px\" /></p>\r\n\r\n<h3><span style=\"font-size:24px\"><strong>Kiểu d&aacute;ng hiện đại</strong></span></h3>\r\n\r\n<p>Laptop Dell Inspiron 3168 TRDM71 c&oacute; kiểu d&aacute;ng nhỏ gọn với c&acirc;n nặng 1.39kg. Với thiết kế n&agrave;y, bạn sẽ dễ d&agrave;ng mang theo laptop đến bất kỳ nơi đ&acirc;u để l&agrave;m việc, học tập m&agrave; kh&ocirc;ng g&acirc;y trở ngại n&agrave;o.</p>\r\n\r\n<p>Ngo&agrave;i ra, bạn c&oacute; thể linh động sử dụng sản phẩm như một chiếc laptop th&ocirc;ng dụng hay tablet dễ d&agrave;ng với trục xoay chắc chắn, mang đến đẳng cấp v&agrave; sự tiện dụng tối đa cho bạn.</p>\r\n', 1, '2019-06-17 06:38:16', 'Bình thường', 'Bình thường', 1),
(35, 'Dell Inspiron 3543 (N3543A) /Intel Core i3-5005U/4GB/Intel HD Graphics', 9000000, 9590000, 30, 'Chip: Intel Core i3-5005U\r\n\r\nRAM: 4GB\r\n\r\nỔ cứng: 500GB\r\n\r\nChipset đồ họa: Intel HD Graphics 5500\r\n\r\nMàn hình: 15.6 inches\r\n\r\nHệ điều hành: Free Dos\r\n\r\nPin: 4 cell', 'dell11.jpg', 'dell11-1.jpg', 'dell11-2.jpg', 'dell11-3.jpg', 'dell11-4.jpg', 'dell11-5.jpg', 'dell11-6.jpg', '<h3><span style=\"font-size:24px\"><strong>Thi&ecirc;́t k&ecirc;́ ph&acirc;̀n cứng mạnh mẽ và hi&ecirc;̣n đại</strong></span></h3>\r\n\r\n<p>Đ&acirc;̀u ti&ecirc;n chúng ta n&ecirc;n xét v&ecirc;̀ ki&ecirc;̉u dáng được DELL thi&ecirc;́t k&ecirc;́ cho Laptop Dell Inspiron 3543 (N3543A), ki&ecirc;̉u thi&ecirc;́t k&ecirc;́ này mang phong cách năng đ&ocirc;̣ng và trẻ trung, từ cách bo tròn các góc m&ocirc;̣t cách dức khoát cho đ&ecirc;́n vi&ecirc;̣c thi&ecirc;́t k&ecirc;́ touchpad hi&ecirc;̣n đại. Thi&ecirc;́t k&ecirc;́ lớp vỏ màu đen sang trọng, ch&ocirc;́ng bám dính v&acirc;n tay mà còn mang đ&ecirc;́n cho sản ph&acirc;̉m m&ocirc;̣t hình dáng hi&ecirc;̣n đại và tràn đ&acirc;̀y sáng tạo.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/new2/dell-ct-3.jpg\" style=\"height:621px; width:845px\" /></p>\r\n\r\n<h3><span style=\"font-size:24px\"><strong>Laptop Dell Inspiron 3543 (N3543A) Đen sở hữu kích thước hợp lý</strong></span></h3>\r\n\r\n<p>Màn hình của chi&ecirc;́c laptop này có đ&ocirc;̣ lớn 15,6 inches, máy có đ&ocirc;̣ dày hơn 20mm với kh&ocirc;́i lượng nhẹ 2.3 kg. Người dùng sẽ thoải mái di chuy&ecirc;̉n máy mà kh&ocirc;ng lo ngại v&ecirc;̀ trọng lượng cũng như khả năng linh đ&ocirc;̣ng trong khi làm vi&ecirc;̣c và giải trí.</p>\r\n\r\n<h3><span style=\"font-size:24px\"><strong>C&acirc;́u hình máy mạnh, t&ocirc;́c đ&ocirc;̣ xử lý dữ li&ecirc;̣u t&ocirc;́t</strong></span></h3>\r\n\r\n<p>K&ecirc;́t hợp CPU Core i3 t&ocirc;́c đ&ocirc;̣ 2.0 GHz với &ocirc;̉ cứng HDD 500GB, RAM 4GB t&ocirc;́c đ&ocirc;̣ Bus l&ecirc;n đ&ecirc;́n 1600MHz tạo ra cho Laptop Dell Inspiron 3543 (N3543A) Đen m&ocirc;̣t c&acirc;́u hình mạnh, khả năng xử lý dữ li&ecirc;̣u nhanh chóng và &ocirc;̉n định. Ngoài ra, sản ph&acirc;̉m này còn cho phép n&acirc;ng c&acirc;́p RAM giúp người dùng có th&ecirc;̉ n&acirc;ng c&acirc;́u hình của máy mạnh hơn phục vụ cho c&ocirc;ng vi&ecirc;̣c và giải trí.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/new2/dell-ct-4.jpg\" style=\"height:600px; width:800px\" /></p>\r\n\r\n<h3><span style=\"font-size:24px\"><strong>C&ocirc;ng ngh&ecirc;̣ màn hình LED Blacklit hi&ecirc;̣n đại</strong></span></h3>\r\n\r\n<p>Dell đã trang bị cho Laptop Dell Inspiron 3543 (N3543A) Đen màn hình LCD 15,6 inches với đ&ocirc;̣ ph&acirc;n giải cao. Với c&ocirc;ng ngh&ecirc;̣ màn hình LED Blacklit cho hình ảnh sáng, ch&acirc;n th&acirc;̣t và ti&ecirc;́t ki&ecirc;̣m năng lượng.</p>\r\n', 1, '2019-07-07 07:09:17', 'Bình thường', 'Nổi bật', 1);
INSERT INTO `product` (`id`, `product_name`, `price_new`, `price_old`, `qty_product`, `product_desc`, `product_thumb`, `list_thumb_1`, `list_thumb_2`, `list_thumb_3`, `list_thumb_4`, `list_thumb_5`, `list_thumb_6`, `product_content`, `cat_id`, `created_at`, `selling_products`, `featured_products`, `status`) VALUES
(36, 'Dell Inspiron 3558/Intel Core i5-5200U 2.2GHz/Nvidia Geforce', 10500000, 12990000, 30, '- Vi xử lý: Intel Core i5-5200U 2.2GHz\r\n\r\n- Bộ nhớ RAM: 4GB\r\n\r\n- Ổ cứng lưu trữ: 500GB\r\n\r\n- Card đồ họa rời:  Nvidia Geforce GT 820M 2GB\r\n\r\n- Màn hình: 15.6” HD (1366x768) - Webcam\r\n\r\n- Kết nối: Lan 10/100 - Wifi – HDMI – USB 3.0\r\n\r\n- Đầu đọc thẻ nhớ: Reader\r\n\r\n- Trọng lượng: 2.4kg – Pin: 4 Cell\r\n\r\n- Hệ điều hành: Free DOS', 'dell12.jpg', 'dell12-1.jpg', 'dell12-2.jpg', 'dell12-3.jpg', 'dell12-4.jpg', 'dell12-5.jpg', 'dell12-6.jpg', '<p><span style=\"font-size:24px\"><strong>Laptop Dell Inspiron N3558 (Free Dos)</strong></span></p>\r\n\r\n<p><strong>Laptop Dell Inspiron N3558 (Free Dos)&nbsp;</strong>l&agrave; chiếc laptop tầm trung đơn giản v&agrave; mạnh mẽ đ&aacute;p ứng tốt nhu cầu l&agrave;m việc h&agrave;ng ng&agrave;y v&agrave; lưu trữ. Dell Inspiron N3558 được trang bị bộ vi xử l&yacute; Intel Core i5-5200U 2.2GHz si&ecirc;u tiết kiệm điện năng thế hệ mới nhất c&ugrave;ng với bộ nhớ RAM 4GB cho khả năng chạy đa nhiệm mạnh mẽ. Đặc biệt, Dell c&ograve;n trang bị cho N3558<strong>&nbsp;</strong>card đồ họa rời Nvidia Geforce GT 820M 2GB mạnh mẽ, đ&aacute;p ứng tối đa nhu cầu xử l&yacute; đồ họa v&agrave; giải tr&iacute; cao. Dell Inspiron N3558 c&oacute; một m&agrave;n h&igrave;nh 15.6 cho bạn trải nghiệm video v&agrave; h&igrave;nh ảnh thoải m&aacute;i. Sản phẩm ph&ugrave; hợp với rất nhiều đối tượng người d&ugrave;ng từ y&ecirc;u cầu sự bền bỉ, mạnh mẽ cho c&ocirc;ng việc h&agrave;ng ng&agrave;y, cho tới y&ecirc;u cầu cho một thiết bị giải tr&iacute; tại gia.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dellg7/dell-ct-5.jpg\" style=\"height:600px; width:900px\" /></p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>Thiết kế nam t&iacute;nh v&agrave; mạnh mẽ</strong></span></p>\r\n\r\n<p>Laptop Dell Inspiron N3558 c&oacute; kiểu d&aacute;ng vu&ocirc;ng vức, g&oacute;c cạnh thẳng, từng đường n&eacute;t đều được v&aacute;t dứt kho&aacute;t, sắc sảo tạo n&ecirc;n một tổng thể mạnh mẽ v&agrave; chắc chắn. M&aacute;y được l&agrave;m từ chất liệu nhựa cứng c&aacute;p, kết cấu chắc chắn với c&aacute;c chi tiết được gia c&ocirc;ng tỉ mỉ v&agrave; c&oacute; độ ho&agrave;n thiện cao. M&agrave;u đen thống nhất tr&ecirc;n to&agrave;n th&acirc;n m&aacute;y từ lớp vỏ, b&agrave;n ph&iacute;m, cả b&agrave;n di chuột g&oacute;p phần l&agrave;m m&aacute;y trở n&ecirc;n sang trọng v&agrave; lịch l&atilde;m hơn. B&agrave;n ph&iacute;m chiclet ti&ecirc;u chuẩn với c&aacute;c ph&iacute;m bấm rộng r&atilde;i, b&agrave;n di chuột hỗ trợ cử chỉ rất nhạy, cho bạn sự thoải m&aacute;i v&agrave; cảm gi&aacute;c r&otilde; tốt hơn khi thao t&aacute;c.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>Vi xử l&yacute; Core i5 thế hệ mới nhất, Card đồ họa rời hiệu năng cao</strong></span></p>\r\n\r\n<p>Dell Inspiron N3558 được trang bị một m&agrave;n h&igrave;nh 15.6inch HD 1366x768 cho khả năng hiển thị sắc n&eacute;t v&agrave; l&agrave;m việc tốt hơn với những nội dung cần m&agrave;n h&igrave;nh lớn như bảng t&iacute;nh hay h&igrave;nh ảnh. Thiết bị được trang bị bộ vi xử l&yacute; Intel Core i5-5200U Broadwell thế hệ thứ 5- 2.2GHz si&ecirc;u tiết kiệm điện năng mới nhất cho khả năng xử l&yacute; c&aacute;c t&aacute;c vụ h&agrave;ng ng&agrave;y của bạn kh&ocirc;ng những trơn tru, mượt m&agrave;, Dell Inspiron N3558 cũng c&oacute; thời gian sử dụng pin l&acirc;u, gi&uacute;p cho bạn l&agrave;m việc li&ecirc;n tục m&agrave; kh&ocirc;ng bị gi&aacute;n đoạn. C&ugrave;ng với card đồ họa rời Nvidia Geforce GT 820M 2GB, Laptop Dell Inspiron N3558 cho hiệu năng vượt trội khi xử l&yacute; đồ họa tr&ecirc;n Photoshop v&agrave; chơi một số d&ograve;ng game cơ bản.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dellg7/dell-ct-6.jpg\" style=\"height:600px; width:900px\" /></p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>Dung lượng ổ cứng lớn, cổng kết nối đa dạng</strong></span></p>\r\n\r\n<p>Bạn sẽ c&oacute; kh&ocirc;ng gian lưu trữ tất cả h&igrave;nh ảnh, nhạc hay video tr&ecirc;n Laptop Dell Inspiron N3558 m&agrave; kh&ocirc;ng phải lo lắng về dung lượng. Đầu đọc thẻ nhớ mở rộng cho ph&eacute;p bạn dễ d&agrave;ng chia sẻ h&igrave;nh ảnh v&agrave; video cho mọi người cũng như th&ocirc;ng qua cổng USB 3.0 được trang bị cho Dell Inspiron N3558, tốc độ truyền tải dữ liệu được n&acirc;ng l&ecirc;n gấp nhiều lần so với cổng USB 2.0 trước đ&oacute;. Ngo&agrave;i ra, sản phẩm cũng được trang bị kết nối Wi-Fi, cổng mạng LAN, HDMI để kết nối tới c&aacute;c thiết bị ngo&agrave;i hoặc với m&agrave;n h&igrave;nh lớn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>N&acirc;ng cao trải nghiệm &acirc;m thanh, dễ d&agrave;ng kết nối</strong></span></p>\r\n\r\n<p>Laptop Dell Inspiron N3558 được trang bị c&ocirc;ng nghệ &acirc;m thanh Waves Maxx Audio danh tiếng v&agrave; hệ thống loa 2.1 sẽ gi&uacute;p &acirc;m thanh trở n&ecirc;n r&otilde; r&agrave;ng v&agrave; trong trẻo. Ngo&agrave;i ra, Dell Inspiron N3558 cũng được trang bị camera HD 720 c&ugrave;ng với microphone t&iacute;ch hợp cho ph&eacute;p bạn lu&ocirc;n kết nối với bạn b&egrave; người th&acirc;n v&agrave; đồng nghiệp.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dellg7/dell-ct-8.jpg\" style=\"height:600px; width:900px\" /></p>\r\n', 1, '2019-06-18 06:38:16', 'Bình thường', 'Bình thường', 1),
(37, 'Dell Inspiron 7559 70071890/i5-6300HQ/4GB RAM', 21000000, 21220000, 30, 'Vi xử lý: i5-6300HQ\r\n\r\nRAM: 4GB RAM\r\n\r\nỔ cứng: 1TB HDD\r\n\r\nMàn hình: 15.6 inch\r\n\r\nCard đồ họa: 4GB GeForce GTX 960M\r\n\r\nHệ điều hành: Win 10 Home\r\n\r\nNo DVD', 'dell13.jpg', 'dell13-1.jpg', 'dell13-2.jpg', 'dell13-3.jpg', 'dell13-4.jpg', 'dell13-5.jpg', 'dell13-6.jpg', '<p><span style=\"font-size:24px\"><strong>Laptop Dell Inspiron 7559 70071890</strong></span></p>\r\n\r\n<p><strong>Laptop Dell Inspiron 7559 70071890&nbsp;</strong>sẽ mang đến cho bạn th&ecirc;m một sự lựa chọn trong d&ograve;ng m&aacute;y cấu h&igrave;nh mạnh d&agrave;nh cho game thủ. M&aacute;y được trang bị bộ xử l&yacute;<strong>&nbsp;Intel Core i5-6300HQ</strong>,&nbsp;<strong>RAM 4GB</strong>&nbsp;kết hợp c&ugrave;ng ổ cứng<strong>&nbsp;1TB</strong>&nbsp;v&agrave; hệ điều h&agrave;nh&nbsp;<strong>Win 10</strong>. Ngo&agrave;i ra, m&agrave;n h&igrave;nh&nbsp;<strong>15.6 inch FULL HD&nbsp;</strong>sẽ mang đến kh&ocirc;ng gian rộng r&atilde;i c&ugrave;ng những h&igrave;nh ảnh r&otilde; r&agrave;ng v&agrave; sắc n&eacute;t để mang đến cho người d&ugrave;ng những trải nghiệm ho&agrave;n hảo nhất.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-3/dell-ct-9.jpg\" style=\"height:665px; width:833px\" /></p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>Thiết kế mạnh mẽ, cứng c&aacute;p</strong></span></p>\r\n\r\n<p>Laptop Dell Inspiron 7559 được thiết kế đậm chất game thủ với từng đường n&eacute;t mạnh mẽ v&agrave; chắc chắn tạo cảm gi&aacute;c tin cậy v&agrave; độ bền cao. C&aacute;ch bố tr&iacute; từ phần m&agrave;n h&igrave;nh, phần b&agrave;n ph&iacute;m đến c&aacute;c cổng kết nối, quạt tản nhiệt đều c&oacute; sự sắp xếp khoa học v&agrave; c&acirc;n xứng đem lại cho bạn sự thoải m&aacute;i khi sử dụng. M&aacute;y c&oacute; b&agrave;n ph&iacute;m rộng c&oacute; ph&iacute;m số, đặc biệt c&oacute; đ&egrave;n b&agrave;n ph&iacute;m s&aacute;ng tiện lợi cho c&ocirc;ng việc buổi tối.</p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>Hiệu năng mạnh mẽ</strong></span></p>\r\n\r\n<p>Laptop Dell Inspiron 7559 70071890 sẽ gi&uacute;p bạn c&oacute; những thao t&aacute;c v&ocirc; c&ugrave;ng thoải m&aacute;i, nhanh nhạy v&agrave; kh&ocirc;ng c&oacute; độ trễ. Sản phẩm&nbsp;sở hữu bộ xử l&yacute;&nbsp;Intel Core i5-6300HQ kết hợp c&ugrave;ng RAM 4GB gi&uacute;p m&aacute;y xử l&yacute; mọi t&aacute;c vụ nặng v&agrave; đa nhiệm. Dung lượng ổ cứng 1TB cho bạn thoải m&aacute;i lưu trữ những bộ phim v&agrave; bản nhạc y&ecirc;u th&iacute;ch c&ugrave;ng những file t&agrave;i liệu quan trọng. M&aacute;y chạy hệ điều h&agrave;nh bản quyền&nbsp;Win 10.</p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>Card đồ họa rời mạnh mẽ</strong></span></p>\r\n\r\n<p>Laptop Dell Inspiron 7559 70071890&nbsp;được t&iacute;ch hợp card đồ họa rời NVIDIA GeForce GTX 960M dung lượng 4GB gi&uacute;p n&acirc;ng cao chất lượng đồ họa c&ugrave;ng cấu h&igrave;nh cực khủng cho ph&eacute;p bạn thỏa sức chơi những game 3D khủng nhất hoặc sử dụng c&aacute;c phần mềm đồ họa nặng. Đ&acirc;y l&agrave; một trong những mẫu card đồ họa mạnh nhất của Nvidia cho Laptop chỉ sau GTX 980M.</p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>Trải nghiệm giải tr&iacute; đỉnh cao</strong></span></p>\r\n\r\n<p>Laptop Dell Inspiron 7559 70071890&nbsp;mang đến cho bạn kh&ocirc;ng gian rộng r&atilde;i 15.6 inch với&nbsp;độ ph&acirc;n giải cao 1080x1920 pixels mang đến cho bạn những h&igrave;nh ảnh v&ocirc; c&ugrave;ng sắc n&eacute;t v&agrave; ch&iacute;nh x&aacute;c đến kinh ngạc. Ngo&agrave;i ra, m&aacute;y mang đến cho bạn những &acirc;m thanh v&ocirc; c&ugrave;ng sống động, chất lượng cao rất th&iacute;ch hợp cho những gi&acirc;y ph&uacute;t thư gi&atilde;n của bạn.</p>\r\n', 1, '2019-06-18 06:38:16', 'Bình thường', 'Bình thường', 1),
(38, 'Dell Inspiron 5370 N3I3002W Core i3-8130U/Win10 (13.3\" FHD)', 14520000, 15990000, 30, 'Chip: Intel Core i3-8130U (2.20GHz up to 3.40GHz, 2 Cores 4 Threads, 4MB Cache)\r\n\r\nRAM: 4GB DDR4 2400MHz\r\n\r\nỔ cứng: 128GB SSD M.2 NVMe\r\n\r\nChipset đồ họa: Intel UHD Graphics 620\r\n\r\nMàn hình: 13.3 inch Full HD (1920 x 1080), IPS, Anti-Glare, LED-Backlit Display\r\n\r\nHệ điều hành: Windows 10 Home\r\n\r\nPin: 3 Cell 38 Whrs', 'dell14.jpg', 'dell14-1.jpg', 'dell14-2.jpg', 'dell14-3.jpg', 'dell14-4.jpg', 'dell14-5.jpg', 'dell14-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế vỏ ngo&agrave;i bằng nh&ocirc;m mạnh mẽ</strong></span></h5>\r\n\r\n<p><strong>Laptop Dell Inspiron 5370 N3I3002W</strong><strong>&nbsp;</strong>thiết kế đẹp c&ugrave;ng với vỏ ngo&agrave;i bằng nh&ocirc;m mạnh mẽ. Kiểu d&aacute;ng nhỏ gọn với trọng lượng vỏn vẹn 1.4kg thuận tiện trong việc mang x&aacute;ch, kiểu d&aacute;ng thanh tho&aacute;t nhưng kh&ocirc;ng k&eacute;m phần sang trọng v&agrave; mạnh mẽ. G&oacute;c cạnh được bo tr&ograve;n tạo n&ecirc;n n&eacute;t tinh tế đồng thời tạo cảm gi&aacute;c cầm nắm chắc chắn hơn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-5/dell-ct-10.jpg\" style=\"height:437px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh LED 13.3 inch Full HD</strong></span></h5>\r\n\r\n<p>Sở hữu m&agrave;n h&igrave;nh 13.3 inch gi&uacute;p hiển thị h&igrave;nh ảnh Full HD (1920 x 1080) để xem mọi thứ tốt hơn, cùng c&ocirc;ng nghệ màn hình LED-Backlit mang đến những h&igrave;nh ảnh chất lượng, chi tiết, r&otilde; n&eacute;t, m&agrave;u sắc cũng cực kỳ trung thực v&agrave; sống động.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-5/dell-ct-11.jpg\" style=\"height:437px; width:750px\" /></p>\r\n\r\n<p>Cùng tấm nền chống ch&oacute;i Anti-glare, IPS gi&uacute;p bạn c&oacute; thể trải nghiệm tốt hơn ở m&ocirc;i trường b&ecirc;n ngo&agrave;i, c&oacute; khả năng chống ch&oacute;i tốt, cho g&oacute;c nh&igrave;n rộng 178&deg; đem lại h&igrave;nh ảnh sắc n&eacute;t, sống động v&agrave; độ tương phản cao ngay cả ở những g&oacute;c nh&igrave;n hẹp.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bộ vi xử l&yacute; Intel Core i3</strong></span></h5>\r\n\r\n<p>M&aacute;y được trang bị bộ vi xử l&yacute; Intel Core i3-8130U với xung nhịp 3.4GHz, cho bạn những gi&acirc;y ph&uacute;t lướt web nhanh hơn, mạnh mẽ hơn. B&ecirc;n cạnh đ&oacute;, với bộ nhớ trong Ram 4GB DDR4 2400MHz đảm bảo xử l&iacute; c&aacute;c t&aacute;c vụ đa nhiệm thật mượt m&agrave; v&agrave; ổn định. Ngo&agrave;i ra, m&aacute;y c&ograve;n trang bị th&ecirc;m bộ nhớ&nbsp;128GB SSD, cho bạn lưu trữ cũng như vận h&agrave;nh m&aacute;y một c&aacute;ch nhanh ch&oacute;ng, mượt m&agrave; hơn</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-5/dell-ct-12.jpg\" style=\"height:389px; width:750px\" /></p>\r\n\r\n<p>Khả năng xử l&yacute; đồ họa của&nbsp;Dell Inspiron 5370 N3I3002W&nbsp;cũng được tăng cường với card đồ họa t&iacute;ch hợp Intel UHD Graphics 620, cho khả năng chạy tốt v&agrave; ổn định c&aacute;c phần mềm đồ họa cũng như đ&aacute;p ứng nhu cầu giải tr&iacute; của người d&ugrave;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>&Acirc;m thanh&nbsp;r&otilde; n&eacute;t&nbsp;với Waves MaxxAudio</strong></span></h5>\r\n\r\n<p>Waves MaxxAudio Pro cung cấp &acirc;m thanh chuy&ecirc;n nghiệp, tự động điều chỉnh &acirc;m thanh của bạn với độ cao r&otilde; n&eacute;t hơn, tăng &acirc;m trầm v&agrave; tăng &acirc;m lượng để mọi thứ m&agrave; bạn nghe đều ch&iacute;nh x&aacute;c theo c&aacute;ch bạn mu&ocirc;́n, bạn sẽ được trải nghiệm chất lượng &acirc;m thanh đỉnh cao với chuy&ecirc;n gia &acirc;m thanh Waves MaxxAudio Pro đ&ocirc;̣c quy&ecirc;̀n từ Dell Inspiron 5370 N3I3002W.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Chơi game v&agrave; stream li&ecirc;n tục</strong></span></h5>\r\n\r\n<p>C&ocirc;ng nghệ SmartByte l&agrave;m cho tr&ograve; chơi v&agrave; qu&aacute; tr&igrave;nh stream của bạn tr&ocirc;i chảy v&agrave; kh&ocirc;ng bị gi&aacute;n đoạn, do đ&oacute; bạn kh&ocirc;ng bao giờ bỏ lỡ bất kỳ gi&acirc;y ph&uacute;t n&agrave;o. Giải ph&aacute;p mạng n&agrave;y đảm bảo rằng c&aacute;c ứng dụng quan trọng nhất của bạn c&oacute; được băng th&ocirc;ng cần thiết cho hiệu năng tối ưu.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m c&oacute; đ&egrave;n nền v&agrave; touchpad rộng r&atilde;i</strong></span></h5>\r\n\r\n<p>Hệ thống b&agrave;n ph&iacute;m c&oacute; đ&egrave;n nền được thiết kế với khoảng c&aacute;ch vừa phải, rộng r&atilde;i, độ nảy cao, ph&iacute;m &ecirc;m, b&agrave;n ph&iacute;m chữ v&agrave; số thuận tiện gi&uacute;p bạn đ&aacute;nh m&aacute;y hay g&otilde; ph&iacute;m một c&aacute;ch hiệu quả nhất. Touchpad của d&ograve;ng Dell Inspiron 5370 N3I3002W&nbsp;được thiết kế rộng r&atilde;i, mịn m&agrave;ng, cực nhạy, gi&uacute;p bạn thao t&aacute;c đơn giản, dễ d&agrave;ng v&agrave; ch&iacute;nh x&aacute;c tương tự như sử dụng chuột rời.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>USB Type C tất cả trong một kết nối</strong></span></h5>\r\n\r\n<p>Laptop sở hữu cổng kết nối USB Type C đa dụng nhất hiện nay, với thiết kế đồng đẳng gi&uacute;p bạn c&oacute; thể cắm cổng kết nối ở bất cứ chiều n&agrave;o. Ngo&agrave;i ra với c&ocirc;ng nghệ n&agrave;y sẽ cho tốc độ truyền tải dữ liệu nhanh gấp 10 lần so với c&aacute;c cổng kết nối cũ hiện nay.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-5/dell-ct-13.jpg\" style=\"height:436px; width:750px\" /></p>\r\n\r\n<p>Dell Inspiron 5370 N3I3002W còn hỗ trợ nhiều cổng kết nối như cổng HDMI, Bluetooth, khe thẻ nhớ, jack tai nghe cho khả năng kết nối với c&aacute;c thiết bị kh&aacute;c một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Thời lượng pin l&acirc;u hơn</strong></span></h5>\r\n\r\n<p>Với vi&ecirc;n Pin 3 cell Dell Inspiron 5370 N3I3002W&nbsp;cho thời gian sử dụng tương đối. C&ocirc;ng nghệ tiết kiệm pin hoạt động m&aacute;t, &ecirc;m &aacute;i v&agrave; tiết kiệm điện năng n&ecirc;n c&oacute; thể đ&aacute;p ứng tốt nhu cầu sử dụng b&igrave;nh thường của bạn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-5/dell-ct-14.jpg\" style=\"height:384px; width:750px\" /></p>\r\n\r\n<p>.</p>\r\n', 1, '2019-06-18 06:38:16', 'Bình thường', 'Bình thường', 1),
(39, 'Dell Inspiron 3567 N3567S Core i3-7020U/Dos (15.6\" HD)/4GB', 9379000, 10890000, 29, 'Chip: Intel Core i3-7020U (2.30GHz, 2Cores, 4Threads, 3MB cache, FSB 4GT/s)\r\n\r\nRAM: 4GB DDR4-2400MHz, Max 16GB\r\n\r\nỔ cứng: 1TB HDD 5400rpm\r\n\r\nChipset đồ họa: Intel HD Graphics 620\r\n\r\nMàn hình: 15.6 inch HD (1366 x 738) LED\r\n\r\nHệ điều hành: Free DOS\r\n\r\nPin: 4 Cell 40Whr', 'dell15.jpg', 'dell15-1.jpg', 'dell15-2.jpg', 'dell15-3.jpg', 'dell15-4.jpg', 'dell15-5.jpg', 'dell15-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế thanh lịch</strong></span></h5>\r\n\r\n<p><strong>Laptop Dell Inspiron 3567 N3567S</strong><strong>&nbsp;</strong>với thiết kế tạo sự liền mạch, t&ocirc;n l&ecirc;n vẻ đẹp ấn tượng với người d&ugrave;ng, kiểu d&aacute;ng thanh tho&aacute;t nhưng kh&ocirc;ng k&eacute;m phần sang trọng v&agrave; mạnh mẽ. G&oacute;c cạnh được bo tr&ograve;n tạo n&ecirc;n n&eacute;t tinh tế đồng thời tạo cảm gi&aacute;c cầm nắm chắc chắn hơn.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh LED 15.6 inch HD</strong></span></h5>\r\n\r\n<p>Sở hữu m&agrave;n h&igrave;nh 15.6 inch gi&uacute;p hiển thị h&igrave;nh ảnh HD (1366 x 768) để xem mọi thứ tốt hơn, cùng c&ocirc;ng nghệ màn hình LED mang đến những h&igrave;nh ảnh chất lượng, chi tiết, r&otilde; n&eacute;t, m&agrave;u sắc cũng cực kỳ trung thực v&agrave; sống động.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-5/dell-ct-15.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bộ vi xử l&yacute; Intel Core i3</strong></span></h5>\r\n\r\n<p>M&aacute;y được trang bị bộ vi xử l&yacute; Intel Core i3-7020U với xung nhịp 2.3GHz, cho bạn những gi&acirc;y ph&uacute;t lướt web nhanh hơn, mạnh mẽ hơn. B&ecirc;n cạnh đ&oacute;, với bộ nhớ trong Ram DDR4 4GB (2400MHz) đảm bảo xử l&iacute; c&aacute;c t&aacute;c vụ đa nhiệm thật mượt m&agrave; v&agrave; ổn định. Ngo&agrave;i ra, m&aacute;y c&ograve;n trang bị th&ecirc;m bộ nhớ&nbsp;1TB HDD, cho bạn lưu trữ cũng như vận h&agrave;nh m&aacute;y một c&aacute;ch nhanh ch&oacute;ng, mượt m&agrave; hơn.</p>\r\n\r\n<p>Đặc biệt, với card đồ họa Intel HD Graphics 620 m&aacute;y c&ograve;n c&oacute; thể đ&aacute;p ứng tốt c&aacute;c như cầu giải tr&iacute; v&agrave; l&agrave;m việc kh&aacute;c như game online, nhiều game 3D, c&aacute;c c&ocirc;ng cụ đồ họa Photoshop, Lightroom&hellip;</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>&Acirc;m thanh&nbsp;r&otilde; n&eacute;t&nbsp;với Waves MaxxAudio 3</strong></span></h5>\r\n\r\n<p>Waves MaxxAudio 3 cung cấp &acirc;m thanh chuy&ecirc;n nghiệp, tự động điều chỉnh &acirc;m thanh của bạn với độ cao r&otilde; n&eacute;t hơn, tăng &acirc;m trầm v&agrave; tăng &acirc;m lượng để mọi thứ m&agrave; bạn nghe đều ch&iacute;nh x&aacute;c theo c&aacute;ch bạn mu&ocirc;́n, bạn sẽ được trải nghiệm chất lượng &acirc;m thanh đỉnh cao với chuy&ecirc;n gia &acirc;m thanh Waves MaxxAudio 3 đ&ocirc;̣c quy&ecirc;̀n từ&nbsp;Dell Inspiron 3567 N3567S.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Ổ đĩa quang tiện lợi</strong></span></h5>\r\n\r\n<p>Ổ đĩa quang DVD-RW c&oacute; thể đọc được hầu hết c&aacute;c định dạng đĩa như CD-ROM, DVD, cũng như ghi nội dung dữ liệu ra CD hay DVD một c&aacute;ch nhanh ch&oacute;ng, xem phim, chơi tr&ograve; chơi hoặc nghe nhạc y&ecirc;u th&iacute;ch của bạn với ổ đĩa DVD ti&ecirc;u chuẩn hoặc ghi đĩa của bạn để sao lưu tập tin.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m độ nảy cao, ph&iacute;m &ecirc;m v&agrave; touchpad rộng r&atilde;i</strong></span></h5>\r\n\r\n<p>Hệ thống b&agrave;n ph&iacute;m được thiết kế với khoảng c&aacute;ch vừa phải, rộng r&atilde;i, độ nảy cao, ph&iacute;m &ecirc;m, b&agrave;n ph&iacute;m chữ v&agrave; số thuận tiện gi&uacute;p bạn đ&aacute;nh m&aacute;y hay g&otilde; ph&iacute;m một c&aacute;ch hiệu quả nhất. Touchpad của d&ograve;ng Dell Inspiron 3567 N3567S&nbsp;được thiết kế rộng r&atilde;i, mịn m&agrave;ng, cực nhạy, gi&uacute;p bạn thao t&aacute;c đơn giản, dễ d&agrave;ng v&agrave; ch&iacute;nh x&aacute;c tương tự như sử dụng chuột rời.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-5/dell-ct-16.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hỗ trợ nhiều cổng kết nối</strong></span></h5>\r\n\r\n<p>Hỗ trợ nhiều cổng kết nối như cổng HDMI, Bluetooth, khe thẻ nhớ, jack tai nghe cho khả năng kết nối với c&aacute;c thiết bị kh&aacute;c một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Thời lượng pin l&acirc;u hơn</strong></span></h5>\r\n\r\n<p>Với vi&ecirc;n Pin 4 cell Dell Inspiron 3567 N3567S&nbsp;cho thời gian sử dụng tương đối. C&ocirc;ng nghệ tiết kiệm pin hoạt động m&aacute;t, &ecirc;m &aacute;i v&agrave; tiết kiệm điện năng n&ecirc;n c&oacute; thể đ&aacute;p ứng tốt nhu cầu sử dụng b&igrave;nh thường của bạn.</p>\r\n', 1, '2019-07-04 08:48:32', 'Bình thường', 'Bình thường', 1),
(40, 'Dell Inspiron 5370 N3I3002W Core i3-8130U/ Win10 (13.3 inch FHD) (Pink)', 14295000, 15990000, 30, 'Chip: Intel Core i3-8130U (2.20GHz up to 3.40GHz, 2 Cores 4 Threads, 4MB Cache)\r\n\r\nRAM: 4GB DDR4 2400MHz\r\n\r\nỔ cứng: 128GB SSD M.2 NVMe\r\n\r\nChipset đồ họa: Intel UHD Graphics 620\r\n\r\nMàn hình: 13.3 inch Full HD (1920 x 1080), IPS, Anti-Glare, LED-Backlit Display\r\n\r\nHệ điều hành: Windows 10 Home\r\n\r\nPin: 3 Cell 38 Whrs', 'dell16.jpg', 'dell16-1.jpg', 'dell16-2.jpg', 'dell16-3.jpg', 'dell16-4.jpg', 'dell16-5.jpg', 'dell16-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế vỏ ngo&agrave;i bằng nh&ocirc;m mạnh mẽ</strong></span></h5>\r\n\r\n<p><strong>Laptop Dell Inspiron 5370 N3I3002W Core i3-8130U/ Win10 (13.3 inch FHD) (Pink)</strong><strong>&nbsp;</strong>thiết kế đẹp c&ugrave;ng với vỏ ngo&agrave;i bằng nh&ocirc;m mạnh mẽ. Kiểu d&aacute;ng nhỏ gọn với trọng lượng vỏn vẹn 1.4kg thuận tiện trong việc mang x&aacute;ch, kiểu d&aacute;ng thanh tho&aacute;t nhưng kh&ocirc;ng k&eacute;m phần sang trọng v&agrave; mạnh mẽ. G&oacute;c cạnh được bo tr&ograve;n tạo n&ecirc;n n&eacute;t tinh tế đồng thời tạo cảm gi&aacute;c cầm nắm chắc chắn hơn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-5/dell-ct-17.jpg\" style=\"height:437px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh LED 13.3 inch Full HD</strong></span></h5>\r\n\r\n<p>Sở hữu m&agrave;n h&igrave;nh 13.3 inch gi&uacute;p hiển thị h&igrave;nh ảnh Full HD (1920 x 1080) để xem mọi thứ tốt hơn, cùng c&ocirc;ng nghệ màn hình LED-Backlit mang đến những h&igrave;nh ảnh chất lượng, chi tiết, r&otilde; n&eacute;t, m&agrave;u sắc cũng cực kỳ trung thực v&agrave; sống động.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-5/dell-ct-18.jpg\" style=\"height:437px; width:750px\" /></p>\r\n\r\n<p>Cùng tấm nền chống ch&oacute;i Anti-glare, IPS gi&uacute;p bạn c&oacute; thể trải nghiệm tốt hơn ở m&ocirc;i trường b&ecirc;n ngo&agrave;i, c&oacute; khả năng chống ch&oacute;i tốt, cho g&oacute;c nh&igrave;n rộng 178&deg; đem lại h&igrave;nh ảnh sắc n&eacute;t, sống động v&agrave; độ tương phản cao ngay cả ở những g&oacute;c nh&igrave;n hẹp.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bộ vi xử l&yacute; Intel Core i3</strong></span></h5>\r\n\r\n<p>M&aacute;y được trang bị bộ vi xử l&yacute; Intel Core i3-8130U với xung nhịp 3.4GHz, cho bạn những gi&acirc;y ph&uacute;t lướt web nhanh hơn, mạnh mẽ hơn. B&ecirc;n cạnh đ&oacute;, với bộ nhớ trong Ram 4GB DDR4 2400MHz đảm bảo xử l&iacute; c&aacute;c t&aacute;c vụ đa nhiệm thật mượt m&agrave; v&agrave; ổn định. Ngo&agrave;i ra, m&aacute;y c&ograve;n trang bị th&ecirc;m bộ nhớ&nbsp;128GB SSD, cho bạn lưu trữ cũng như vận h&agrave;nh m&aacute;y một c&aacute;ch nhanh ch&oacute;ng, mượt m&agrave; hơn.</p>\r\n\r\n<p>Khả năng xử l&yacute; đồ họa của&nbsp;Dell Inspiron 5370 N3I3002W&nbsp;cũng được tăng cường với card đồ họa t&iacute;ch hợp Intel UHD Graphics 620, cho khả năng chạy tốt v&agrave; ổn định c&aacute;c phần mềm đồ họa cũng như đ&aacute;p ứng nhu cầu giải tr&iacute; của người d&ugrave;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>&Acirc;m thanh&nbsp;r&otilde; n&eacute;t&nbsp;với Waves MaxxAudio</strong></span></h5>\r\n\r\n<p>Waves MaxxAudio Pro cung cấp &acirc;m thanh chuy&ecirc;n nghiệp, tự động điều chỉnh &acirc;m thanh của bạn với độ cao r&otilde; n&eacute;t hơn, tăng &acirc;m trầm v&agrave; tăng &acirc;m lượng để mọi thứ m&agrave; bạn nghe đều ch&iacute;nh x&aacute;c theo c&aacute;ch bạn mu&ocirc;́n, bạn sẽ được trải nghiệm chất lượng &acirc;m thanh đỉnh cao với chuy&ecirc;n gia &acirc;m thanh Waves MaxxAudio Pro đ&ocirc;̣c quy&ecirc;̀n từ Dell Inspiron 5370 N3I3002W.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Chơi game v&agrave; stream li&ecirc;n tục</strong></span></h5>\r\n\r\n<p>C&ocirc;ng nghệ SmartByte l&agrave;m cho tr&ograve; chơi v&agrave; qu&aacute; tr&igrave;nh stream của bạn tr&ocirc;i chảy v&agrave; kh&ocirc;ng bị gi&aacute;n đoạn, do đ&oacute; bạn kh&ocirc;ng bao giờ bỏ lỡ bất kỳ gi&acirc;y ph&uacute;t n&agrave;o. Giải ph&aacute;p mạng n&agrave;y đảm bảo rằng c&aacute;c ứng dụng quan trọng nhất của bạn c&oacute; được băng th&ocirc;ng cần thiết cho hiệu năng tối ưu.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m c&oacute; đ&egrave;n nền v&agrave; touchpad rộng r&atilde;i</strong></span></h5>\r\n\r\n<p>Hệ thống b&agrave;n ph&iacute;m c&oacute; đ&egrave;n nền được thiết kế với khoảng c&aacute;ch vừa phải, rộng r&atilde;i, độ nảy cao, ph&iacute;m &ecirc;m, b&agrave;n ph&iacute;m chữ v&agrave; số thuận tiện gi&uacute;p bạn đ&aacute;nh m&aacute;y hay g&otilde; ph&iacute;m một c&aacute;ch hiệu quả nhất. Touchpad của d&ograve;ng Dell Inspiron 5370 N3I3002W&nbsp;được thiết kế rộng r&atilde;i, mịn m&agrave;ng, cực nhạy, gi&uacute;p bạn thao t&aacute;c đơn giản, dễ d&agrave;ng v&agrave; ch&iacute;nh x&aacute;c tương tự như sử dụng chuột rời.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>USB Type C tất cả trong một kết nối</strong></span></h5>\r\n\r\n<p>Laptop sở hữu cổng kết nối USB Type C đa dụng nhất hiện nay, với thiết kế đồng đẳng gi&uacute;p bạn c&oacute; thể cắm cổng kết nối ở bất cứ chiều n&agrave;o. Ngo&agrave;i ra với c&ocirc;ng nghệ n&agrave;y sẽ cho tốc độ truyền tải dữ liệu nhanh gấp 10 lần so với c&aacute;c cổng kết nối cũ hiện nay.</p>\r\n\r\n<p>Dell Inspiron 5370 N3I3002W còn hỗ trợ nhiều cổng kết nối như cổng HDMI, Bluetooth, khe thẻ nhớ, jack tai nghe cho khả năng kết nối với c&aacute;c thiết bị kh&aacute;c một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Thời lượng pin l&acirc;u hơn</strong></span></h5>\r\n\r\n<p>Với vi&ecirc;n Pin 3 cell Dell Inspiron 5370 N3I3002W&nbsp;cho thời gian sử dụng tương đối. C&ocirc;ng nghệ tiết kiệm pin hoạt động m&aacute;t, &ecirc;m &aacute;i v&agrave; tiết kiệm điện năng n&ecirc;n c&oacute; thể đ&aacute;p ứng tốt nhu cầu sử dụng b&igrave;nh thường của bạn.</p>\r\n', 1, '2019-07-07 07:08:56', 'Bán chạy', 'Nổi bật', 1),
(41, 'Laptop Dell XPS 13 9370 415PX2 Core i7-8550U/Win10 (13.3 inch)', 50388000, 52490000, 30, 'Chip: Intel Core i7-8550U (1.8GHz Upto 4.0GHz, 4Cores, 8Threads, 8MB cache, FSB 4GT/s)\r\n\r\nRAM: 16GB LPDDR3 2133MHz\r\n\r\nỔ cứng: 512GB SSD M2 PCIe3x4\r\n\r\nChipset đồ họa: Intel UHD Graphics 620\r\n\r\nMàn hình: 13.3 inch 4K Ultra HD (3840 x 2160) IPS InfinityEdge touch display\r\n\r\nHệ điều hành: Windows 10 home\r\n\r\nPin: 4 Cell 52 Whrs', 'dell17.jpg', 'dell17-1.jpg', 'dell17-2.jpg', 'dell17-3.jpg', 'dell17-4.jpg', 'dell17-5.jpg', 'dell17-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Chế t&aacute;c từ nh&ocirc;m anodize cao cấp</strong></span></h5>\r\n\r\n<p><strong>Laptop Dell XPS 13 9370 415PX2</strong><strong>&nbsp;</strong>vỏ ngo&agrave;i được chế t&aacute;c từ nh&ocirc;m anodize cao cấp kết hợp c&ugrave;ng sợi thủy tinh gi&uacute;p chiếc laptop c&oacute; khả năng chống tia cực t&iacute;m v&agrave; chống bẩn. Phần c&ograve;n lại của vỏ m&aacute;y được l&agrave;m bằng sợi thủy tinh tại phần nghỉ tay gi&uacute;p laptop mỏng 7.62mm và nhẹ chỉ 1.21kg giúp bạn có th&ecirc;̉ mang laptop di chuy&ecirc;̉n m&ocirc;̣t cách nhanh chóng ti&ecirc;̣n lợi, c&aacute;c g&oacute;c cạnh của m&aacute;y được bo cong mềm mại c&ugrave;ng với logo Dell được mạ chrome b&oacute;ng tạo điểm nhấn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-6/dell-ct-1.jpg\" style=\"height:506px; width:800px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Màn hình cảm ứng&nbsp;13.3 inch&nbsp;4K Ultra HD</strong></span></h5>\r\n\r\n<p>Dell đ&atilde; n&acirc;ng cấp mọi chi tiết của m&agrave;n h&igrave;nh tr&ecirc;n Dell XPS13 9370-415PX2. Tận hưởng tầm nh&igrave;n tưởng chừng như kh&ocirc;ng c&oacute; giới hạn với viền m&agrave;n h&igrave;nh InfinityEdge mỏng hơn tới 23% - tức chỉ 4mm, đạt tỉ lệ khung h&igrave;nh chiếm 80.7% th&acirc;n m&aacute;y.</p>\r\n\r\n<p>Sở hữu m&agrave;n h&igrave;nh 13.3 inch gi&uacute;p hiển thị h&igrave;nh ảnh 4K Ultra HD (3840 x 2160) để xem mọi thứ tốt hơn, cùng t&acirc;̀m n&ecirc;́n IPS mang đến cho bạn góc nhìn r&ocirc;̣ng, tăng độ s&aacute;ng v&agrave; khả năng t&aacute;i tạo m&agrave;u sắc cho bạn h&igrave;nh ảnh chất lượng, chi tiết, r&otilde; n&eacute;t, m&agrave;u sắc cũng cực kỳ trung thực v&agrave; sống động.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-6/dell-ct-2.jpg\" style=\"height:530px; width:800px\" /></p>\r\n\r\n<p>C&ocirc;ng ngh&ecirc;̣ màn hình cảm ứng được phủ k&iacute;nh Gorilla Glass tích hợp tr&ecirc;n Dell XPS 13 9370 giúp bạn ki&ecirc;̉m soát mọi thứ trong t&acirc;̀m tay chỉ c&acirc;̀n trượt, ph&oacute;ng to, thu nhỏ hoặc t&ugrave;y chỉnh c&aacute;c c&ocirc;ng cụ bằng cảm ứng đa điểm.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>C&ocirc;ng nghệ Dell Cinema - Chuyển tải h&igrave;nh ảnh sống động</strong></span></h5>\r\n\r\n<p><strong>CinemaColor:</strong>&nbsp;Từng h&igrave;nh ảnh hiển thị tr&ecirc;n Dell XPS 13 9370 415PX2 sống động như ch&iacute;nh thế giới của bạn. Được thiết kế n&ecirc;n với c&ocirc;ng nghệ tấm nền tốt nhất trong ng&agrave;nh, c&ugrave;ng với phần cứng v&agrave; phần mềm ti&ecirc;n tiến để cung cấp cho nội dung dải m&agrave;u tương phản vượt trội - đem tới m&agrave;u đen s&acirc;u thẳm, &aacute;nh s&aacute;ng ch&oacute;i l&ograve;a nhất v&agrave; độ n&eacute;t tới từng chi tiết tr&ecirc;n to&agrave;n bộ phổ m&agrave;u.</p>\r\n\r\n<p><strong>CinemaStream:</strong>&nbsp;Hệ thống kết nối Killer Wireless tr&ecirc;n Dell XPS13 9370-415PX2 cung cấp băng tần tối đa cho videos hay &acirc;m nhạc của bạn, đem lại trải nghiệm trơn tru kh&ocirc;ng gi&aacute;n đoạn.</p>\r\n\r\n<p><strong>CinemaSound:</strong>&nbsp;Waves MaxxAudio Pro tăng cường &acirc;m lượng v&agrave; l&agrave;m r&otilde; từng &acirc;m sắc, để bạn c&oacute; thể trải nghiệm &acirc;m thanh mang đẳng cấp ph&ograve;ng thu.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bộ vi xử l&yacute; Intel Core i7</strong></span></h5>\r\n\r\n<p>M&aacute;y được trang bị bộ vi xử l&yacute; Intel Core i7-8550U với xung nhịp c&oacute; thể l&ecirc;n đến 4GHz, cho bạn những gi&acirc;y ph&uacute;t lướt web nhanh hơn, mạnh mẽ hơn. B&ecirc;n cạnh đ&oacute;, với bộ nhớ trong Ram LPDDR3 16GB (2133MHz) đảm bảo xử l&iacute; c&aacute;c t&aacute;c vụ đa nhiệm thật mượt m&agrave; v&agrave; ổn định.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-6/dell-ct-3.jpg\" style=\"height:503px; width:800px\" /></p>\r\n\r\n<p>Ngo&agrave;i ra, m&aacute;y c&ograve;n trang bị th&ecirc;m bộ nhớ&nbsp;512GB SSD, cho bạn lưu trữ cũng như vận h&agrave;nh m&aacute;y một c&aacute;ch nhanh ch&oacute;ng, mượt m&agrave; hơn. Khả năng xử l&yacute; đồ họa của&nbsp;Dell XPS 13 9370 415PX2&nbsp;cũng được tăng cường với card đồ họa t&iacute;ch hợp Intel UHD Graphics 620, cho khả năng chạy tốt v&agrave; ổn định c&aacute;c phần mềm đồ họa cũng như đ&aacute;p ứng nhu cầu giải tr&iacute; của người d&ugrave;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>&Acirc;m thanh&nbsp;r&otilde; n&eacute;t&nbsp;với Waves MaxxAudio</strong></span></h5>\r\n\r\n<p>Waves MaxxAudio Pro cung cấp &acirc;m thanh chuy&ecirc;n nghiệp, tự động điều chỉnh &acirc;m thanh của bạn với độ cao r&otilde; n&eacute;t hơn, tăng &acirc;m trầm v&agrave; tăng &acirc;m lượng để mọi thứ m&agrave; bạn nghe đều ch&iacute;nh x&aacute;c theo c&aacute;ch bạn mu&ocirc;́n, bạn sẽ được trải nghiệm chất lượng &acirc;m thanh đỉnh cao với chuy&ecirc;n gia &acirc;m thanh Waves MaxxAudio Pro đ&ocirc;̣c quy&ecirc;̀n từ&nbsp;Dell XPS 13 9370 415PX2.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-6/dell-ct-4.jpg\" style=\"height:375px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Tr&ograve; chuyện thỏa th&iacute;ch</strong></span></h5>\r\n\r\n<p>Webcam c&oacute; độ ph&acirc;n giải 1MP cho h&igrave;nh ảnh kh&aacute; tốt. Nhờ đ&oacute; bạn c&oacute; thể chụp ảnh &ldquo;tự sướng&rdquo; v&agrave; chia sẻ h&igrave;nh ảnh với bạn b&egrave; qua c&aacute;c mạng x&atilde; hội như Facebook, Twitter &hellip;Ngo&agrave;i ra, bạn cũng c&oacute; thể tr&ograve; chuyện thỏa th&iacute;ch với người th&acirc;n qua c&aacute;c ứng dụng như: Skype, yahoo chat.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m v&agrave; touchpad</strong></span></h5>\r\n\r\n<p>B&agrave;n ph&iacute;m cho khả năng soạn thảo văn bản trơn tru v&agrave; nhanh gọn, c&aacute;c ph&iacute;m c&oacute; độ rộng vừa phải, độ nảy &ecirc;m, t&iacute;ch hợp đ&egrave;n LED gi&uacute;p thao t&aacute;c dễ d&agrave;ng v&agrave; l&agrave;m việc hiệu quả trong m&ocirc;i trường thiếu s&aacute;ng. Touchpad c&oacute; bề mặt rộng, tho&aacute;ng, cảm ứng nhạy, bắt chuẩn x&aacute;c theo thao t&aacute;c di chuyển.<img alt=\"\" src=\"/uploads/files/dell-6/dell-ct-5.jpg\" style=\"height:505px; width:800px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hệ điều h&agrave;nh Windows 10</strong></span></h5>\r\n\r\n<p>M&aacute;y được c&agrave;i sẵn hệ điều h&agrave;nh Windows 10 tiện &iacute;ch, gi&uacute;p m&aacute;y chạy mượt m&agrave;. Hệ điều h&agrave;nh sẽ hỗ trợ hầu hết những phần mềm hiện nay n&ecirc;n rất tiện cho c&ocirc;ng việc.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>USB Type C &ndash; tất cả trong một kết nối</strong></span></h5>\r\n\r\n<p>Laptop sở hữu cổng kết nối USB Type C đa dụng nhất hiện nay, với thiết kế đồng đẳng gi&uacute;p bạn c&oacute; thể cắm cổng kết nối ở bất cứ chiều n&agrave;o. Ngo&agrave;i ra với c&ocirc;ng nghệ n&agrave;y sẽ cho tốc độ truyền tải dữ liệu nhanh gấp 10 lần so với c&aacute;c cổng kết nối cũ hiện nay.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-6/dell-ct-6.jpg\" style=\"height:506px; width:800px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Linh hoạt nhờ kết nối th&ocirc;ng minh</strong></span></h5>\r\n\r\n<p>C&aacute;c cổng kết nối th&ocirc;ng minh của Dell XPS 13 9370 415PX2 đ&atilde; được thay đổi với 3 cổng USB-C. Trong đ&oacute; một cổng USB 3.1 hỗ trợ PowerShare (sạc) v&agrave; tr&igrave;nh xuất DisplayPort; 2 cổng c&ograve;n lại l&agrave; Thunderbolt 3, cũng hỗ trợ sạc v&agrave; tr&igrave;nh xuất DisplayPort.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-6/dell-ct-7.jpg\" style=\"height:506px; width:800px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Thời lượng pin l&acirc;u hơn</strong></span></h5>\r\n\r\n<p>Được thiết kế để ph&ugrave; hợp với phong c&aacute;ch sống năng động hiện nay, Dell XPS 13 9370 415PX2&nbsp;sẽ cung cấp cho bạn thời lượng pin để c&oacute; thể sử dụng suốt cả ng&agrave;y. Với c&ocirc;ng nghệ pin Lithium Polymer sẽ cho m&aacute;y khả năng vận h&agrave;nh li&ecirc;n tục l&ecirc;n đến 7 tiếng đồng hồ. Điểm đặc biệt của d&ograve;ng pin Lithium Polymer c&oacute; thể duy tr&igrave; được năng lượng sạc lần đầu cho d&ugrave; sau h&agrave;ng trăm chu kỳ sạc, l&acirc;u hơn gấp 3 lần so với c&aacute;c loại pin ti&ecirc;u chuẩn hiện nay, gi&uacute;p bạn khỏi phải lo lắng về hao m&ograve;n hao m&ograve;n điện năng pin.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-6/dell-ct-8.jpg\" style=\"height:375px; width:750px\" /></p>\r\n', 1, '2019-06-20 06:38:16', 'Bình thường', 'Bình thường', 1),
(42, 'Dell Inspiron 7370 7D61Y3 Core i7-8550U/Win10 + Office365 (13.3\" FHD IPS)', 24569000, 26990000, 30, 'Chip: Intel Core i7-8550U (1.8GHz Upto 4.0GHz, 4Cores, 8Threads, 8MB cache, FSB 4GT/s)\r\n\r\nRAM: 8GB DDR4 2400MHz\r\n\r\nỔ cứng: 256GB SSD M.2 Sata\r\n\r\nChipset đồ họa: Intel UHD Graphics 620\r\n\r\nMàn hình: 13.3 inch Full HD (1920 x 1080) IPS Truelife LED-Backlit Narrow Border Display\r\n\r\nHệ điều hành: Windows 10 Home\r\n\r\nPin: 3 Cell 38 Whrs', 'dell18.jpg', 'dell18-1.jpg', 'dell18-2.jpg', 'dell18-3.jpg', 'dell18-4.jpg', 'dell18-5.jpg', 'dell18-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế nh&ocirc;m nguy&ecirc;n khối</strong></span></h5>\r\n\r\n<p><strong>Laptop Dell Inspiron 7370 7D61Y3</strong><strong>&nbsp;</strong>với chất liệu nh&ocirc;m nguy&ecirc;n khối c&ugrave;ng sắc bạc tinh tế, m&aacute;y th&iacute;ch hợp với mọi đối tượng, bạn c&oacute; thể dễ d&agrave;ng mang theo chiếc laptop n&agrave;y b&ecirc;n m&igrave;nh suốt ng&agrave;y d&agrave;i nhờ trọng lượng nhẹ 1.31kg của m&aacute;y, h&ocirc;̃ trợ bạn tối đa trong c&ocirc;ng việc cũng như giải tr&iacute;.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-7/dell-ct-9.jpg\" style=\"height:478px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh LED 13.3 inch Full HD,&nbsp;IPS</strong></span></h5>\r\n\r\n<p>Sở hữu m&agrave;n h&igrave;nh 13.3 inch gi&uacute;p hiển thị h&igrave;nh ảnh Full HD (1920 x 1080) để xem mọi thứ tốt hơn, cùng c&ocirc;ng nghệ màn hình LED-backlit mang đến những h&igrave;nh ảnh chất lượng, chi tiết, r&otilde; n&eacute;t, m&agrave;u sắc cũng cực kỳ trung thực v&agrave; sống động. Ngo&agrave;i ra m&agrave;n h&igrave;nh IPS c&ograve;n cung cấp g&oacute;c nh&igrave;n l&ecirc;n tới 178 độ so với phương ngang, điều n&agrave;y c&oacute; nghĩa l&agrave; người d&ugrave;ng kh&ocirc;ng nhất thiết phải ngồi trực diện vẫn c&oacute; thể trải nghiệm hết chất lượng của h&igrave;nh ảnh.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bộ vi xử l&yacute; Intel Core i7</strong></span></h5>\r\n\r\n<p>M&aacute;y được trang bị bộ vi xử l&yacute; Intel Core i7-8550U với xung nhịp 4.0GHz, cho bạn những gi&acirc;y ph&uacute;t lướt web nhanh hơn, mạnh mẽ hơn. B&ecirc;n cạnh đ&oacute;, với bộ nhớ trong Ram 8GB DDR4 2400MHz đảm bảo xử l&iacute; c&aacute;c t&aacute;c vụ đa nhiệm thật mượt m&agrave; v&agrave; ổn định. Ngo&agrave;i ra, m&aacute;y c&ograve;n trang bị th&ecirc;m &ocirc;̉ cứng 256GB SSD, cho bạn lưu trữ cũng như vận h&agrave;nh m&aacute;y một c&aacute;ch nhanh ch&oacute;ng, mượt m&agrave; hơn. Khả năng xử l&yacute; đồ họa của&nbsp;Dell Inspiron 7370 7D61Y3&nbsp;cũng được tăng cường với card đồ họa t&iacute;ch hợp Intel UHD Graphics 620, cho khả năng chạy tốt v&agrave; ổn định c&aacute;c phần mềm đồ họa cũng như đ&aacute;p ứng nhu cầu giải tr&iacute; của người d&ugrave;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-7/dell-ct-10.jpg\" style=\"height:424px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>&Acirc;m thanh&nbsp;r&otilde; n&eacute;t&nbsp;với Waves MaxxAudio</strong></span></h5>\r\n\r\n<p>Waves MaxxAudio Pro cung cấp &acirc;m thanh chuy&ecirc;n nghiệp, tự động điều chỉnh &acirc;m thanh của bạn với độ cao r&otilde; n&eacute;t hơn, tăng &acirc;m trầm v&agrave; tăng &acirc;m lượng để mọi thứ m&agrave; bạn nghe đều ch&iacute;nh x&aacute;c theo c&aacute;ch bạn mu&ocirc;́n, bạn sẽ được trải nghiệm chất lượng &acirc;m thanh đỉnh cao với chuy&ecirc;n gia &acirc;m thanh Waves MaxxAudio Pro đ&ocirc;̣c quy&ecirc;̀n từ Dell Inspiron 7370 7D61Y3.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-7/dell-ct-11.jpg\" style=\"height:343px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hệ điều h&agrave;nh Windows 10</strong></span></h5>\r\n\r\n<p>M&aacute;y được c&agrave;i sẵn hệ điều h&agrave;nh Windows 10 tiện &iacute;ch, gi&uacute;p m&aacute;y chạy mượt m&agrave;. Hệ điều h&agrave;nh sẽ hỗ trợ hầu hết những phần mềm hiện nay n&ecirc;n rất tiện cho c&ocirc;ng việc.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m v&agrave; touchpad</strong></span></h5>\r\n\r\n<p>Ngo&agrave;i ra m&aacute;y được trang bị b&agrave;n ph&iacute;m với c&aacute;c ph&iacute;m bấm được sắp xếp khoa học, k&iacute;ch thước v&agrave; khoảng c&aacute;ch hợp l&iacute;, độ nảy ph&iacute;m tốt gi&uacute;p người d&ugrave;ng nhập liệu nhanh ch&oacute;ng v&agrave; ch&iacute;nh x&aacute;c. Touchpad của d&ograve;ng Dell Inspiron 7370 7D61Y3&nbsp;được thiết kế rộng r&atilde;i, mịn m&agrave;ng, cực nhạy, gi&uacute;p bạn thao t&aacute;c đơn giản, dễ d&agrave;ng v&agrave; ch&iacute;nh x&aacute;c tương tự như sử dụng chuột rời.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>USB Type C tất cả trong một kết nối</strong></span></h5>\r\n\r\n<p>Laptop sở hữu cổng kết nối USB Type C đa dụng nhất hiện nay, với thiết kế đồng đẳng gi&uacute;p bạn c&oacute; thể cắm cổng kết nối ở bất cứ chiều n&agrave;o. Ngo&agrave;i ra với c&ocirc;ng nghệ n&agrave;y sẽ cho tốc độ truyền tải dữ liệu nhanh gấp 10 lần so với c&aacute;c cổng kết nối cũ hiện nay.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-7/dell-ct-12.jpg\" style=\"height:387px; width:750px\" /></p>\r\n\r\n<p>Hỗ trợ nhiều cổng kết nối như cổng HDMI, HD webcam, Bluetooth, khe thẻ nhớ, jack tai nghe cho khả năng kết nối với c&aacute;c thiết bị kh&aacute;c một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Thời lượng pin l&acirc;u hơn</strong></span></h5>\r\n\r\n<p>Với vi&ecirc;n Pin 3 cell Dell Inspiron 7370 7D61Y3&nbsp;cho thời gian sử dụng tương đối. C&ocirc;ng nghệ tiết kiệm pin hoạt động m&aacute;t, &ecirc;m &aacute;i v&agrave; tiết kiệm điện năng n&ecirc;n c&oacute; thể đ&aacute;p ứng tốt nhu cầu sử dụng b&igrave;nh thường của bạn.</p>\r\n', 1, '2019-06-20 06:38:16', 'Bình thường', 'Bình thường', 1),
(43, 'Dell Inspiron 15 5570 M5I5335W Core i5-8250U/ Win10 (15.6 inch FHD)', 19788000, 21990000, 30, 'CPU: Intel Core i5-8250U (1.6 GHz 3.4 GHz/6MB/4 nhân, 8 luồng)\r\n\r\nMàn hình: 15.6\" (1920 x 1080), không cảm ứng\r\n\r\nRAM: 1 x 8GB DDR4 2400MHz\r\n\r\nĐồ họa: Intel UHD Graphics 620/ AMD Radeon 530 2GB GDDR5\r\n\r\nLưu trữ: 2TB HDD 5400RPM\r\n\r\nHệ điều hành: Windows 10 Home SL 64-bit\r\n\r\nPin: 3 cell 42 Wh Pin liền', 'dell19.jpg', 'dell19-1.jpg', 'dell19-2.jpg', 'dell19-3.jpg', 'dell19-4.jpg', 'dell19-5.jpg', 'dell19-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế đơn giản, sang trọng</strong></span></h5>\r\n\r\n<p><strong>Laptop Dell Inspiron 15 5570 M5I5335W Core i5-8250U/ Win10 (15.6 inch FHD) - H&agrave;ng Ch&iacute;nh H&atilde;ng</strong>&nbsp;được thiết kế đơn giản nhưng kh&ocirc;ng k&eacute;m phần sang trọng, tinh tế với t&ocirc;ng m&agrave;u bạc v&agrave; phần khung m&aacute;y cứng c&aacute;p chắc chắn gi&uacute;p bảo vệ tốt c&aacute;c linh kiện b&ecirc;n trong, c&aacute;c g&oacute;c cạnh được bo tr&ograve;n nhẹ tạo cảm gi&aacute;c thoải m&aacute;i khi cầm nắm.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>H&igrave;nh ảnh v&agrave; &acirc;m thanh chất lượng</strong></span></h5>\r\n\r\n<p>Sản phẩm sở hữu m&agrave;n h&igrave;nh chống ch&oacute;i 15.6 inch c&oacute; độ ph&acirc;n giải Full HD (1920 x 1080) cho chất lượng hiển thị tốt, h&igrave;nh ảnh chi tiết r&otilde; n&eacute;t, m&agrave;u sắc trung thực ấn tượng. C&ocirc;ng nghệ SmartByte gi&uacute;p tr&ograve; chơi v&agrave; ph&aacute;t trực tiếp mượt m&agrave;, kh&ocirc;ng bị gi&aacute;n đoạn v&igrave; vậy bạn kh&ocirc;ng bao giờ bỏ lỡ gi&acirc;y n&agrave;o.</p>\r\n\r\n<p>Ngo&agrave;i ra, với Waves MaxxAudio Pro &acirc;m thanh của bạn sẽ được tăng l&ecirc;n, do đ&oacute; bạn c&oacute; thể nghe mọi thứ từ c&aacute;c buổi h&ograve;a nhạc đến những bộ phim bom tấn hay chơi game với &acirc;m sắc phong ph&uacute;, cộng hưởng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hiệu năng tối ưu</strong></span></h5>\r\n\r\n<p>Laptop Dell Inspiron 15 5570 M5I5335W Silver được tiếp sức mạnh bởi bộ vi xử l&yacute; Intel Core i5 thế hệ thứ 8 với bộ nhớ Ram 8GB đảm bảo khả năng xử l&yacute; c&aacute;c t&aacute;c vụ đa nhiệm mượt m&agrave;, ổn định. Ổ cứng HDD 2TB cho bạn thoải m&aacute;i lưu trữ c&aacute;c h&igrave;nh ảnh, bộ phim, b&agrave;i h&aacute;t y&ecirc;u th&iacute;ch hoặc những t&agrave;i liệu quan trọng. B&ecirc;n cạnh đ&oacute;, m&aacute;y c&ograve;n được trang bị card đồ họa rời AMD Redeon 530 2G DDR5. Với cấu h&igrave;nh n&agrave;y, bạn c&oacute; thể l&agrave;m việc tốt với c&aacute;c phần mềm văn ph&ograve;ng, chỉnh sửa h&igrave;nh ảnh/video hoặc chơi một số tựa game online đang được y&ecirc;u th&iacute;ch hiện nay.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>C&ocirc;ng kết nối đa dạng</strong></span></h5>\r\n\r\n<p>Trong suốt qu&aacute; tr&igrave;nh sử dụng m&aacute;y, bạn lu&ocirc;n cần kết nối thiết bị của m&igrave;nh với c&aacute;c thiết bị kh&aacute;c để trao đổi dữ liệu. Ch&iacute;nh v&igrave; vậy, Laptop Dell Inspiron 15 5570 M5I5335W Silver đ&atilde; được lắp đặt đầy đủ c&aacute;c cổng kết nối điển h&igrave;nh như cổng USB 3.1 Type-C với khả năng kết nối hai chiều v&agrave; tốc độ trao đổi dữ liệu nhanh hơn nhiều lần so với cổng USB 2.0. Ngo&agrave;i ra, m&aacute;y c&ograve;n được trang bị c&aacute;c cổng kh&aacute;c nữa như USB 3.1, USB 2.0, HDMI.</p>\r\n', 1, '2019-06-20 06:38:16', 'Bán chạy', 'Bình thường', 1);
INSERT INTO `product` (`id`, `product_name`, `price_new`, `price_old`, `qty_product`, `product_desc`, `product_thumb`, `list_thumb_1`, `list_thumb_2`, `list_thumb_3`, `list_thumb_4`, `list_thumb_5`, `list_thumb_6`, `product_content`, `cat_id`, `created_at`, `selling_products`, `featured_products`, `status`) VALUES
(44, 'Dell Inspiron 5480 X6C892 Core i5-8265U/ Win10 + Office365 (14.0 inch FHD)', 21190000, 23990000, 30, 'Chip: Intel Core i5-8265U (1.60GHz Up to 3.90 GHz, 4Cores, 8Threads, 6MB Cache, FSB 4GT/s)\r\n\r\nRAM: 8GB DDR4 2666MHz\r\n\r\nỔ cứng: 256GB SSD M.2 NVMe\r\n\r\nChipset đồ họa: NVIDIA GeForce MX150 2GB GDDR5 + Intel UHD Graphics 620\r\n\r\nMàn hình: 14 inch Full HD (1920 x 1080) IPS Truelife LED-Backlit Narrow Border\r\n\r\nHệ điều hành: Windows 10 Home + Office 365 bản quyền\r\n\r\nPin: 4 Cell 40WHr', 'dell20.jpg', 'dell20-1.jpg', 'dell20-2.jpg', 'dell20-3.jpg', 'dell20-4.jpg', 'dell20-5.jpg', 'dell20-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế thanh lịch</strong></span></h5>\r\n\r\n<p><strong>Laptop Dell Inspiron 5480 X6C892</strong><strong>&nbsp;</strong>với thiết kế tạo sự liền mạch, t&ocirc;n l&ecirc;n vẻ đẹp ấn tượng với người d&ugrave;ng, kiểu d&aacute;ng thanh tho&aacute;t nhưng kh&ocirc;ng k&eacute;m phần sang trọng v&agrave; mạnh mẽ. G&oacute;c cạnh được bo tr&ograve;n tạo n&ecirc;n n&eacute;t tinh tế đồng thời tạo cảm gi&aacute;c cầm nắm chắc chắn hơn.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh LED 14 inch Full HD</strong></span></h5>\r\n\r\n<p>Sở hữu m&agrave;n h&igrave;nh 14 inch gi&uacute;p hiển thị h&igrave;nh ảnh&nbsp;Full HD (1920 x 1080) để xem mọi thứ tốt hơn, cùng c&ocirc;ng nghệ màn hình LED mang đến những h&igrave;nh ảnh chất lượng, chi tiết, r&otilde; n&eacute;t, m&agrave;u sắc cũng cực kỳ trung thực v&agrave; sống động.</p>\r\n\r\n<p>Tấm nền IPS gi&uacute;p bạn c&oacute; thể trải nghiệm g&oacute;c nh&igrave;n rộng 178&deg; đem lại h&igrave;nh ảnh sắc n&eacute;t, sống động v&agrave; độ tương phản cao ngay cả ở những g&oacute;c nh&igrave;n hẹp.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bộ vi xử l&yacute; Intel Core i5</strong></span></h5>\r\n\r\n<p>M&aacute;y được trang bị bộ vi xử l&yacute; Intel Core i5-8265U với xung nhịp 3.9GHz, cho bạn những gi&acirc;y ph&uacute;t lướt web nhanh hơn, mạnh mẽ hơn. B&ecirc;n cạnh đ&oacute;, với bộ nhớ trong Ram 8GB DDR4 2666MHz đảm bảo xử l&iacute; c&aacute;c t&aacute;c vụ đa nhiệm thật mượt m&agrave; v&agrave; ổn định. Ngo&agrave;i ra, m&aacute;y c&ograve;n trang bị th&ecirc;m bộ nhớ 256GB SSD, cho bạn lưu trữ cũng như vận h&agrave;nh m&aacute;y một c&aacute;ch nhanh ch&oacute;ng, mượt m&agrave; hơn.</p>\r\n\r\n<h5>Card đồ họa NVIDIA GeForce MX150</h5>\r\n\r\n<p>Đặc biệt, với card đồ họa NVIDIA GeForce MX150 bạn c&oacute; thể ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m rằng m&aacute;y c&oacute; thể h&ocirc;̃ trợ bạn tối đa từ c&aacute;c việc đơn giản đến c&aacute;c c&ocirc;ng&nbsp; việc mang t&iacute;nh chất đ&ograve;i hỏi cao như thiết kế đồ họa, 3D, photoshop,... cũng như giải tr&iacute; với những game 3D khủng nhất hiện nay.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>&Acirc;m thanh&nbsp;r&otilde; n&eacute;t&nbsp;với Waves MaxxAudio Pro</strong></span></h5>\r\n\r\n<p>Waves MaxxAudio Pro cung cấp &acirc;m thanh chuy&ecirc;n nghiệp, tự động điều chỉnh &acirc;m thanh của bạn với độ cao r&otilde; n&eacute;t hơn, tăng &acirc;m trầm v&agrave; tăng &acirc;m lượng để mọi thứ m&agrave; bạn nghe đều ch&iacute;nh x&aacute;c theo c&aacute;ch bạn mu&ocirc;́n, bạn sẽ được trải nghiệm chất lượng &acirc;m thanh đỉnh cao với chuy&ecirc;n gia &acirc;m thanh Waves MaxxAudio Pro đ&ocirc;̣c quy&ecirc;̀n từ&nbsp;Dell Inspiron 5480 X6C892.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m độ nảy cao, ph&iacute;m &ecirc;m v&agrave; touchpad rộng r&atilde;i</strong></span></h5>\r\n\r\n<p>Hệ thống b&agrave;n ph&iacute;m được thiết kế với khoảng c&aacute;ch vừa phải, rộng r&atilde;i, độ nảy cao, ph&iacute;m &ecirc;m, b&agrave;n ph&iacute;m chữ v&agrave; số thuận tiện gi&uacute;p bạn đ&aacute;nh m&aacute;y hay g&otilde; ph&iacute;m một c&aacute;ch hiệu quả nhất. Touchpad của d&ograve;ng Dell Inspiron 5480 X6C892&nbsp;được thiết kế rộng r&atilde;i, mịn m&agrave;ng, cực nhạy, gi&uacute;p bạn thao t&aacute;c đơn giản, dễ d&agrave;ng v&agrave; ch&iacute;nh x&aacute;c tương tự như sử dụng chuột rời.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hỗ trợ nhiều cổng kết nối</strong></span></h5>\r\n\r\n<p>Hỗ trợ nhiều cổng kết nối như cổng HDMI, Bluetooth, khe thẻ nhớ, jack tai nghe cho khả năng kết nối với c&aacute;c thiết bị kh&aacute;c một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Thời lượng pin l&acirc;u hơn</strong></span></h5>\r\n\r\n<p>Với vi&ecirc;n Pin 4 cell Dell Inspiron 5480 X6C892&nbsp;cho thời gian sử dụng tương đối. C&ocirc;ng nghệ tiết kiệm pin hoạt động m&aacute;t, &ecirc;m &aacute;i v&agrave; tiết kiệm điện năng n&ecirc;n c&oacute; thể đ&aacute;p ứng tốt nhu cầu sử dụng b&igrave;nh thường của bạn.</p>\r\n', 1, '2019-06-20 06:38:16', 'Bình thường', 'Bình thường', 1),
(45, 'Dell Vostro 5481 70175949 Core i7-8565U/ MX130/ Win10 (14 FHD IPS)', 24110000, 25990000, 10, 'CPU: Intel Core i7-8565U ( 1.8 GHz - 4.6 GHz / 8MB / 4 nhân, 8 luồng )\r\n\r\nMàn hình: 14\" IPS ( 1920 x 1080 ) , không cảm ứng\r\n\r\nRAM: 1 x 8GB DDR4 2666MHz\r\n\r\nĐồ họa: Intel UHD Graphics 620 / NVIDIA GeForce MX130 2GB GDDR5\r\n\r\nLưu trữ: 128GB SSD M.2 NVMe / 1TB HDD 5400RPM\r\n\r\nHệ điều hành: Windows 10 Home SL 64-bit + Office Personal 365\r\n\r\nPin: 3 cell 42 Wh Pin liền                                                                                                                                                                                                                                                                                                                                                ', 'dell21.jpg', 'dell21-1.jpg', 'dell21-2.jpg', 'dell21-3.jpg', 'dell21-4.jpg', 'dell21-5.jpg', 'dell21-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế tinh tế</strong></span></h5>\r\n\r\n<p><strong>Laptop Dell Vostro 5481 70175949 Core i7-8565U/ MX130/ Win10 (14 FHD IPS)</strong>&nbsp;sở hữu thiết kế đẹp mắt, chắc chắn với lớp vỏ ngo&agrave;i cứng c&aacute;p, vu&ocirc;ng vắn c&ugrave;ng m&agrave;u hồng ấn tượng gi&uacute;p t&ocirc;n th&ecirc;m vẻ hiện đại, sang trọng. C&aacute;c g&oacute;c cạnh được bo tr&ograve;n tạo cảm gi&aacute;c chắc tay cho người d&ugrave;ng khi cầm nắm, c&oacute; thể di chuyển đi mọi nơi m&agrave; kh&ocirc;ng lo tốn diện t&iacute;ch hoặc mang v&aacute;c nặng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh 14 inch sắc n&eacute;t</strong></span></h5>\r\n\r\n<p>Sở hữu m&agrave;n h&igrave;nh 14.0 inch gi&uacute;p hiển thị h&igrave;nh ảnh Full HD (1920 x 1080) để xem mọi thứ tốt hơn đẹp v&agrave; sắc n&eacute;t, m&agrave;u sắc tươi tắn, sống động cùng với tấm nền chống ch&oacute;i Anti-glare gi&uacute;p bạn c&oacute; thể trải nghiệm tốt hơn ở m&ocirc;i trường b&ecirc;n ngo&agrave;i.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bộ vi xử l&yacute; Intel Core i5 mạnh mẽ</strong></span></h5>\r\n\r\n<p>Được trang bị bộ vi xử l&yacute; Intel Core i7-8265U tốc độ l&ecirc;n đ&ecirc;́n 4.6 GHz, đem lại cho m&aacute;y một sức mạnh xử l&yacute; ổn định v&agrave; tiết kiệm điện năng hiệu quả. C&ugrave;ng bộ nhớ RAM DDR4 dung lượng 4GB cho hiệu suất xử l&yacute; tốt cũng như đ&aacute;p ứng c&aacute;c t&aacute;c vụ đa nhiệm mượt m&agrave; c&aacute;c ứng dụng. Chipset đồ họa Intel UHD Graphics 620 + card NVIDIA GeForce MX130 2GB GDDR5 hỗ trợ xử l&yacute; hiệu ứng h&igrave;nh ảnh 3D c&aacute;c c&ocirc;ng việc li&ecirc;n quan đến đồ hoạ, đáp ứng giải tr&iacute; với game đ&ocirc;̀ họa nặng. Ngo&agrave;i ra, Dell c&ograve;n trang bị cho m&aacute;y ổ cứng 1TB HDD 5400rpm + 128GB SSD M.2 NVMe gi&uacute;p m&aacute;y hoạt động mượt m&agrave; hơn, khởi động nhanh ch&oacute;ng hơn, cho ph&eacute;p bạn lưu trữ số lượng nội dung đ&aacute;ng kinh ngạc m&agrave; kh&ocirc;ng phải lo lắng về hết dung lượng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m v&agrave; touchpad</strong></span></h5>\r\n\r\n<p>B&agrave;n ph&iacute;m với đ&egrave;n nền t&ugrave;y chọn mức s&aacute;ng c&oacute; k&iacute;ch thước vừa phải, khoảng c&aacute;ch ph&iacute;m hợp l&yacute; v&agrave; độ nảy &ecirc;m, gi&uacute;p người d&ugrave;ng nhập liệu nhanh v&agrave; ch&iacute;nh x&aacute;c. Touchpad rộng r&atilde;i với bề mặt nhẵn mịn, cảm ứng đa điểm c&oacute; độ nhạy vừa phải cho ph&eacute;p bạn thao t&aacute;c dễ d&agrave;ng m&agrave; kh&ocirc;ng cần đến chuột.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>USB Type C &ndash; tất cả trong một kết nối</strong></span></h5>\r\n\r\n<p>Laptop sở hữu cổng kết nối USB Type C đa dụng nhất hiện nay, với thiết kế đồng đẳng gi&uacute;p bạn c&oacute; thể cắm cổng kết nối ở bất cứ chiều n&agrave;o. Ngo&agrave;i ra với c&ocirc;ng nghệ n&agrave;y sẽ cho tốc độ truyền tải dữ liệu nhanh gấp 10 lần so với c&aacute;c cổng kết nối cũ hiện nay.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hỗ trợ nhiều cổng kết nối</strong></span></h5>\r\n\r\n<p>Với chuẩn wifi 802.11ac nhanh gấp 6 lần so với chuẩn wifi 802.11n, hỗ trợ nhiều cổng kết nối như cổng&nbsp; HDMI, jack tai nghe, Bluetooth 4.2, 1 khe thẻ nhớ cho khả năng kết nối với c&aacute;c thiết bị kh&aacute;c một c&aacute;ch dễ d&agrave;ng. Ngo&agrave;i ra m&aacute;y c&ograve;n hỗ trợ bảo mật v&acirc;n tay gi&uacute;p người d&ugrave;ng c&oacute; thể lưu trữ th&ocirc;ng tin tốt hơn, an to&agrave;n hơn chỉ với 1 lần chạm.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Phần mếm v&agrave; hệ điều h&agrave;nh bản quyền</strong></span></h5>\r\n\r\n<p>T&iacute;ch hợp sẵn trong m&aacute;y với hệ điều h&agrave;nh Windows 10 hỗ trợ người d&ugrave;ng rất nhiều chức năng ở c&aacute;c d&ograve;ng hệ điều h&agrave;nh từ Win 7 trở về trước kh&ocirc;ng c&oacute;. Với Windows 10 người d&ugrave;ng đươc cập nhật li&ecirc;n tục về c&aacute;c th&ocirc;ng tin thời tiết, chứng kho&aacute;n, gi&aacute; cả bằng c&aacute;c ứng dụng t&iacute;ch hợp sẵn của hệ điều h&agrave;nh. Ngo&agrave;i ra, đối với Dell Vostro 5481 được c&agrave;i đặt Office Personal 365 gi&uacute;p người d&ugrave;ng trải nghiệm phần mềm bản quyền an to&agrave;n hơn, tr&aacute;nh gặp phải virus, m&atilde; độc đến từ c&aacute;c lỗ hổng của phần mềm lậu.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Thời lượng pin l&acirc;u hơn</strong></span></h5>\r\n\r\n<p>Được thiết kế để ph&ugrave; hợp với phong c&aacute;ch sống năng động hiện nay, Dell Vostro 5481 sẽ cung cấp cho bạn thời lượng pin để c&oacute; thể sử dụng suốt cả ng&agrave;y. Với c&ocirc;ng nghệ pin Lithium Polymer sẽ cho m&aacute;y khả năng vận h&agrave;nh li&ecirc;n tục l&ecirc;n đến 7 tiếng đồng hồ. Điểm đặc biệt của d&ograve;ng pin Lithium Polymer c&oacute; thể duy tr&igrave; được năng lượng sạc lần đầu cho d&ugrave; sau h&agrave;ng trăm chu kỳ sạc, l&acirc;u hơn gấp 3 lần so với c&aacute;c loại pin ti&ecirc;u chuẩn hiện nay, gi&uacute;p bạn khỏi phải lo lắng về hao m&ograve;n hao m&ograve;n điện năng pin.</p>\r\n', 1, '2019-07-07 07:27:43', 'Bình thường', 'Bình thường', 0),
(46, 'DELL Tour G3 15.6-inch (i7-8750H 8G 128GSSD 1T GTX1060MQ 6G)/Win10', 32333000, 33990000, 30, 'CPU: Intel Core i7 8950H\r\n\r\nRAM: Memory capacity 8GB\r\n\r\nSSD: 1TB PCIe\r\n\r\nVGA: Nvidia Quadro P2000 4GB & Intel UHD Graphics 630\r\n\r\nMàng hình: 15.6\" UHD 4K 3840*2160 Touch', 'dell22.jpg', 'dell22-1.jpg', 'dell22-2.jpg', 'dell22-3.jpg', 'dell22-4.jpg', 'dell22-5.jpg', 'dell22-6.jpg', '<h2>Hiệu năng&nbsp;</h2>\r\n\r\n<p>Th&agrave;nh vi&ecirc;n ausmarton từ reddit khi test&nbsp;Dell Tour G3 qua b&agrave;i test chạy tất cả c&aacute;c l&otilde;i của m&aacute;y trong khoảng thời gian 10 ph&uacute;t, kết quả&nbsp;Dell Tour G3 tốc độ rơi v&agrave;o mức 3.13 GHz - 3.15 GHz, kh&ocirc;ng c&oacute; hiện tượng tụt xuống mức 3.13 GHz n&agrave;o tr&ecirc;n&nbsp;Dell Tour G3.&nbsp;</p>\r\n\r\n<p>Về phần test GPU của&nbsp;Dell Tour G3, c&oacute; vẻ th&uacute; vị hơn v&igrave; c&oacute; thể h&igrave;nh dung hiệu suất trong suốt thời gian thử nghiệm. nhiệt độ&nbsp;Dell Tour G3 rơi v&agrave;o 63 - 65 độ.</p>\r\n\r\n<p>V&agrave; khi test cả hai CPU v&agrave; GPU Tour G3 c&ugrave;ng l&uacute;c, kết quả cho thấy CPU rơi v&agrave;o mức 3 GHz tại 10 ph&uacute;t ở phần đầu ti&ecirc;n, v&agrave; sau đ&oacute; v&agrave;i gi&acirc;y&nbsp;Dell Tour G3 rơi xuống mức 2.78 GHz, Tiếp tục&nbsp;Dell Tour G3 tăng l&ecirc;n v&agrave; giữ tại mức xung quanh 2.9 GHz - 3 GHz. Trong điều kiện GPU c&ugrave;ng l&uacute;c, dường như c&oacute; thể thấy n&oacute; sử dụng 19%-20% trong b&agrave;i test, nhiệt độ tầm 72 độ C.&nbsp;Quạt tản nhiệt Dell Tour G3&nbsp;nghe tiếng trong b&agrave;i test CPU v&agrave; GPU.</p>\r\n\r\n<h2>Thời lượng Pin</h2>\r\n\r\n<p>Theo th&agrave;nh vi&ecirc;n từ reddit,&nbsp;Dell Tour G3 c&oacute; thời lượng pin 5 tiếng đồng hồ kể từ khi sạc đầy.</p>\r\n', 1, '2019-06-21 06:38:16', 'Bình thường', 'Bình thường', 1),
(47, 'Dell Latitude 7290 12.5-Inch (I5-8250U 4GB 256SSD 4C W10 HOME 3PRO)', 26125000, 27990000, 30, 'CPU: 8th Gen Intel Core i5-8250U Processor (Quad Core, 6M Cache, 1.6GHz,15W, vPro) up to 3.4GHz\r\n\r\nRAM: 8GB DDR4 2400MHz\r\n\r\nĐĩa cứng: M.2 256GB SATA Class 20 Solid State Drive\r\n\r\nMàn hình: Anti-Glare Display 12.5 inch HD (1366 x 768)\r\n\r\nCard đồ họa: Share Intel HD Graphics 620.\r\n\r\nHệ điều hành mặc định: Win 10 Pro', 'dell23.jpg', 'dell23-1.jpg', 'dell23-2.jpg', 'dell23-3.jpg', 'dell23-4.jpg', 'dell23-5.jpg', 'dell23-6.jpg', '<p><span style=\"font-size:24px\"><strong>Dell Latitude 7290 &ndash; Thiết kế mỏng, hiệu năng vượt trội</strong></span></p>\r\n\r\n<p>Latitude E7290 l&agrave; chiếc Laptop doanh nh&acirc;n mỏng nhất v&agrave; nhẹ nhất từ trước giờ của Dell.</p>\r\n\r\n<p>Dell Latitude E7290 được trang bị những t&iacute;nh năng l&agrave;m việc cần thiết, bảo mật, v&agrave; t&ugrave;y chọn m&agrave;n h&igrave;nh cảm ứng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-7/dell-ct-1.png\" style=\"height:461px; width:1110px\" /></p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>N&acirc;ng cấp t&iacute;nh di động xuất sắc</strong></span></p>\r\n\r\n<p>Di động hơn: Được trang bị m&agrave;n h&igrave;nh 12,5&rdquo; HD 9, Dell Latitude E7290 mỏng hơn thế hệ trước 6%, nhỏ hơn 5%, nặng chỉ 1,18kg gi&uacute;p bạn tiết kiệm kh&ocirc;ng gian nhiều hơn.</p>\r\n\r\n<p>Chất liệu cao cấp: Thiết kế nguy&ecirc;n khối đem lại ngoại h&igrave;nh liền mạch v&agrave; tinh tế với kết cấu vững chải, được phủ lớp sơn mịn v&agrave; t&ugrave;y chọn vỏ bằng sợi carbon.</p>\r\n\r\n<p>Đăng nhập dễ d&agrave;ng: Đăng nhập Windows dễ d&agrave;ng với Windows Hello th&ocirc;ng qua t&ugrave;y chọn camera hồng ngoại gi&uacute;p nhận diện khu&ocirc;n mặt.</p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh hiển thị tuyệt vời cho cả ng&agrave;y l&agrave;m việc.</strong></span></p>\r\n\r\n<p><span style=\"font-size:24px\"><strong><img alt=\"\" src=\"/uploads/files/dell-7/dell-ct-2.jpg\" style=\"height:298px; width:390px\" /></strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave;n h&igrave;nh 12,5&rdquo; HD (1366x768) chống ch&oacute;i v&agrave; chống trầy xướt hiệu quả.</p>\r\n\r\n<p>Dell Latitude E7290 được trang bị pin k&eacute;o d&agrave;i cả ng&agrave;y v&agrave; cải thiện thời gian duyệt web cũng như xem video. Được trang bị vi xử l&yacute; Intel thế hệ 8 tiết kiệm điện ultra-low volt (U) v&agrave; ổ SSD PCIe cho tốc độ truyền tải dữ liệu nhanh.</p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>Bảo mật</strong></span></p>\r\n\r\n<p>Dell Latitude E7290 cung cấp khả năng bảo mật h&agrave;ng đầu, bao gồm t&ugrave;y chọn cảm biến v&acirc;n tay v&agrave; t&iacute;ch hợp c&aacute;c phần mềm chống m&atilde; độc mạnh mẽ nhất. Hơn nữa, với Dell Data Protection | Protected Workspace, dữ liệu của bạn sẽ được an to&agrave;n tr&ecirc;n tất cả c&aacute;c thiết bị đầu cuối, bao gồm c&aacute;c thẻ nhớ ngo&agrave;i, ổ đĩa tự m&atilde; h&oacute;a v&agrave; c&aacute;c dịch vụ lưu trữ đ&aacute;m m&acirc;y c&ocirc;ng cộng. &nbsp;</p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>Dễ quản l&iacute;</strong></span></p>\r\n\r\n<p>Dell Latitude E7290 được x&acirc;y dựng để c&oacute; thể điều chỉnh v&agrave; tự động BIOS v&agrave; cấu h&igrave;nh hệ thống th&ocirc;ng qua c&ocirc;ng cụ miễn ph&iacute; Dell Client Command Suite. Nhờ đ&oacute; bạn c&oacute; thể dễ d&agrave;ng triển khai, theo d&otilde;i v&agrave; cập nhập m&aacute;y t&iacute;nh của bạn nhanh nhất.,</p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>Đ&aacute;ng tin cậy</strong></span></p>\r\n\r\n<p>Khung m&aacute;y của Dell Latitude E7290 được x&acirc;y dựng bền bỉ để c&oacute; thể vượt qua c&aacute;c b&agrave;i thử khắc nghiệt MIL-STD 810G của qu&acirc;n đội Mỹ. Do đ&oacute;, bạn c&oacute; thể y&ecirc;n t&acirc;m l&agrave;m việc với Dell Latitude E7290 trong bất k&igrave; m&ocirc;i trường n&agrave;o.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-7/dell-ct-3.jpg\" style=\"height:393px; width:965px\" /></p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>Khe v&agrave; cổng kết nối</strong></span></p>\r\n\r\n<p>1. Audio Combo | 2. uSIM | 3. uSD | 4. USB3.0 | 5. RJ45 | 6. Nobel Wedge lock slot | 7. Power| 8. USB Type-C Port | 9. HDMI | 10. USB3.0 Powershare | 11. SC</p>\r\n', 1, '2019-06-21 06:38:16', 'Bình thường', 'Bình thường', 1),
(48, 'Dell G5_5587 (P72F002) core i7 8750H/ Win 10 (15.6inch) - Đen', 25800000, 26990000, 30, 'Chip: Intel Core i7-8750H (2.2GHz up to 4.1GHz, 9MB)\r\n\r\nRAM: 8GB DDR4 2400MHz\r\n\r\nỔ cứng: HDD 1TB 5400rpm + 128GB SSD M.2\r\n\r\nChipset đồ họa: NVIDIA GeForce GTX 1050Ti 4GB GDDR5\r\n\r\nMàn hình: 15.6 inch Full HD (1920 x 1080) IPS, Anti-Glare, LED Backlit Display\r\n\r\nHệ điều hành: Window 10 Home\r\n\r\nPin: 4 Cell 56 Whrs', 'dell24.jpg', 'dell24-1.jpg', 'dell24-2.jpg', 'dell24-3.jpg', 'dell24-4.jpg', 'dell24-5.jpg', 'dell24-6.jpg', '<p><span style=\"font-size:24px\"><strong>B&ecirc;n cạnh thương hiệu cao cấp Alienware, Dell vừa tr&igrave;nh l&agrave;ng thương hiệu laptop gi&aacute; rẻ d&agrave;nh cho game thủ.</strong></span></p>\r\n\r\n<p>Trong năm 2018 Dell tiếp tục ra mắt sản phẩm mới thay thế Dell Inspiron 7577 đ&atilde; ho&agrave;n hảo trước đ&oacute; kh&ocirc;ng l&acirc;u, d&ograve;ng laptop chơi game Inspiron của Dell tập trung v&agrave;o việc cung cấp đồ họa chất lượng cao ở độ ph&acirc;n giải full HD cho người d&ugrave;ng c&oacute; ng&acirc;n quỹ trung b&igrave;nh. Tất cả c&aacute;c mẫu laptop chơi game Inspiron đều c&oacute; gi&aacute; vừa phải</p>\r\n\r\n<p>Tuy nhi&ecirc;n, c&aacute;i t&ecirc;n Inspiron vốn được d&agrave;nh cho c&aacute;c m&aacute;y t&iacute;nh d&agrave;nh cho sinh vi&ecirc;n v&agrave; doanh nghiệp chưa bao giờ ph&ugrave; hợp để đặt cho c&aacute;c laptop chơi game. Ch&iacute;nh v&igrave; thế, ngo&agrave;i việc bổ sung c&aacute;c model mới, Dell vừa đổi t&ecirc;n d&ograve;ng laptop chơi game gi&aacute; rẻ của họ th&agrave;nh G-Series,&nbsp;D&ograve;ng Gaming G-Series với thiết kế mới , m&agrave;u sắc mới, cấu h&igrave;nh đa dạng, gi&uacute;p kh&aacute;ch h&agrave;ng c&oacute; th&ecirc;m nhiều lựa chọn</p>\r\n\r\n<p>D&ograve;ng&nbsp;Dell G5-5587&nbsp;m&agrave;u đen&nbsp;vẫn dựa tr&ecirc;n thiết kế cũ của d&ograve;ng Inspiron 7577 v&agrave; được c&aacute;ch t&acirc;n với cấu h&igrave;nh CPU&nbsp;Hexa core&nbsp;(thế hệ 8)&nbsp;i7 8570H 12 x 2.2GHz, SSD&nbsp;128GB M2 + 1TB&nbsp;HDD , Ram&nbsp;8GB bus 2666MHz, VGA&nbsp;Geforce GTX1050Ti 4GB, display&nbsp;15.6&quot; Full HD c&ocirc;ng nghệ IPS</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-8/dell-ct-4.jpg\" style=\"height:1000px; width:1000px\" /></p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>Thiết kế hầm hố&nbsp;</strong></span></p>\r\n\r\n<p>Dell G5-5587&nbsp;Kh&ocirc;ng chỉ mang lại lựa chọn đồ họa v&agrave; bộ xử l&yacute; phong ph&uacute;, m&agrave; c&ograve;n mang đến cho người d&ugrave;ng c&aacute;c t&ugrave;y chọn SSD M.2 v&agrave; HDD 2,5 inch với c&aacute;c mức dung lượng cũng đa dạng kh&ocirc;ng k&eacute;m.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-8/dell-ct-5.jpg\" style=\"height:1000px; width:1000px\" /></p>\r\n\r\n<p><span style=\"font-size:24px\">Tốc độ xử l&yacute; tuyệt vời&nbsp;</span><br />\r\nDell G5-5587&nbsp;được trang bị bộ vi xử l&yacute; Coffee Lake mới nhất hiện nay, đồng thời m&aacute;y h&ocirc; trợ RAM theo m&aacute;y l&agrave; 8GB bus 2666MHz, dung lượng SSD 128GB v&agrave; 1 HDD 1TB, m&aacute;y trang bị VGA Geforce GTX1050Ti dung lượng 4GB gi&uacute;p bạn c&oacute; thể chiến tất cả c&aacute;c loại Game nặng hiện nay dễ d&agrave;ng .</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-8/dell-ct-6.jpg\" style=\"height:1000px; width:1000px\" /></p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh sắc n&eacute;t FHD IPS&nbsp;</strong></span><br />\r\nNhững mẫu laptop Dell G-Series vừa ra mắt đều hỗ trợ độ ph&acirc;n giải m&agrave;n h&igrave;nh đạt mức Full HD v&agrave; d&ugrave;ng panel IPS. Với card đồ họa NVIDIA GeForce GTX1050, GTX1050Ti Dung lượng 4GB hoặc GTX1060 c&ocirc;ng nghệ Max-Q l&ecirc;n đến 6GB bộ nhớ video GDDR5, gameplay l&agrave; r&otilde; r&agrave;ng v&agrave; chi tiết.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-8/dell-ct-7.jpg\" style=\"height:1000px; width:1000px\" /></p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>Khe tản nhiệt lớn v&agrave; c&aacute;ch t&acirc;n</strong>&nbsp;</span><br />\r\nDell G5-5587&nbsp;được trang bị 2 Fan tản nhiệt lớn v&agrave; 2 ống đồng , đồng thời việc n&acirc;ng cấp cực kỳ đơn giản , chỉ cần th&aacute;o 1 ốc l&agrave; bạn c&oacute; thể thay thế hoặc n&acirc;ng cấp to&agrave;n bộ linh kiện b&ecirc;n trong m&aacute;y .</p>\r\n\r\n<p>Dell rất quan t&acirc;m vấn đề tản nhiệt cho sản phẩm Gaming với thiết kế tản nhiệt lớn v&agrave; c&aacute;ch t&acirc;n trong thiết kế so với d&ograve;ng tiền nhiệm l&agrave; Dell Inspiron 7577 .</p>\r\n\r\n<p>D&ograve;ng&nbsp;Dell G5-5587&nbsp;c&oacute; thiết kế đẹp hơn, đồng thời thiết kế b&oacute;ng l&agrave;m cho phần khe tản nhiệt th&ecirc;m lung linh hơn v&agrave; cho tản nhiệt tốt hơn .&nbsp;Lớp lưới m&agrave;u đồng trong gi&uacute;p ngăn bụi hiệu quả v&agrave; tặng th&ecirc;m vẻ đẹp cho d&ograve;ng Gaming n&agrave;y .</p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m chắc chắn</strong>&nbsp;</span><br />\r\nDell G5-5587&nbsp;được thiết kế với b&agrave;n ph&iacute;m chắc chắn , kh&ocirc;ng c&oacute; hiện tượng Flex khi ấn ph&iacute;m , đồng thời Dell đ&atilde; trang bị cho d&ograve;ng Series-G5 đền nền ph&iacute;m m&agrave;u đa dạng rất ấn tượng .</p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>Đa dạng cổng kết nối tr&ecirc;n Dell Series-G&nbsp;</strong></span><br />\r\nĐể đ&aacute;p ứng tốt nhất cho nhu cầu l&agrave;m việc hoặc chơi game . Dell thiết kế đa dạng c&aacute;c cổng kết nối .</p>\r\n\r\n<p><span style=\"font-size:20px\"><strong><em>Cụ thể cạnh tr&aacute;i được thiết kế bao gồm :</em></strong></span><br />\r\n- Khe kh&oacute;a bảo mật Noble Lock<br />\r\n- Power / DC-in Jack<br />\r\n- Gigabit Ethernet RJ-45<br />\r\n- SuperSpeed USB 3.1 Gen 1 Type-A<br />\r\n- Thẻ SD / MicroMedia 2 trong 1</p>\r\n\r\n<p><span style=\"font-size:20px\"><strong><em>Cạnh phải sẽ c&oacute; th&ecirc;m những cổng kết nối sau :</em></strong></span><br />\r\n- Headphone / Mic<br />\r\n- SuperSpeed USB 3.1 Gen 1 Type-A<br />\r\n- 3. Type-C Thunderbolt 3 với hỗ trợ cho Thunderbolt 40 Gbps v&agrave; DisplayPort<br />\r\n- 4. HDMI 2.0</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/dell-8/dell-ct-9.jpg\" style=\"height:1000px; width:1000px\" /></p>\r\n', 1, '2019-06-21 06:38:16', 'Bình thường', 'Bình thường', 1),
(49, 'Asus ROG Strix SCAR II GL704GW-EV048T Core i7-8750H/ RTX 2070 8GB', 56990000, 58990000, 26, 'Chip: Intel Core i7-8750H (2.20GHz upto 4.10GHz, 6Cores, 12Threads, 9MB cache, FSB 8GT/s)\r\n\r\nRAM: 16GB DDR4 2666MHz, 2 slot Ram, Max 32GB\r\n\r\nỔ cứng: 512GB SSD M.2 PCIe3x4 + 1TB HDD 5400rpm (SSHD 8GB)\r\n\r\nChipset đồ họa: NVIDIA GeForce RTX 2070 8GB GDDR6\r\n\r\nMàn hình: 17.3-inch NanoEdge FHD (1920x1080) IPS Non-Glare, 100% sRGB, 144Hz/3ms, 300nits\r\n\r\nHệ điều hành: Windows 10 bản quyền\r\n\r\nPin: 4-Cell 66WHrs', 'asus5.jpg', 'asus5-1.jpg', 'asus5-2.jpg', 'asus5-3.jpg', 'asus5-4.jpg', 'asus5-5.jpg', 'asus5-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế m&agrave;n h&igrave;nh si&ecirc;u mỏng - Chơi game kh&ocirc;ng giới hạn</strong></span></h5>\r\n\r\n<p><strong>Laptop Asus ROG Strix SCAR II GL704GW-EV048T Core i7-8750H/ RTX 2070 8GB/ Win10 (17.3&quot; FHD IPS)</strong>&nbsp;sở hữu m&agrave;n h&igrave;nh si&ecirc;u mỏng với tần số qu&eacute;t 144Hz/3ms - một m&agrave;n h&igrave;nh cần thiết cho c&aacute;c game thủ hardcore v&agrave; người chơi esports. Tốc độ qu&eacute;t cực cao đảm bảo trải nghiệm 144fps thực sự với độ trễ đầu v&agrave;o tối thiểu, trong khi thời gian đ&aacute;p ứng si&ecirc;u nhanh l&agrave;m giảm mờ v&agrave; chuyển động mờ để mang lại h&igrave;nh ảnh r&otilde; hơn. Thiết kế mới n&agrave;y cũng tạo ra khung h&igrave;nh được khai ph&oacute;ng, cho ph&eacute;p k&iacute;ch thước m&aacute;y nhỏ gọn với trọng lượng tổng thể chỉ 2.4kg gi&uacute;p tối đa h&oacute;a sự di chuyển của bạn để bạn c&oacute; thể chiến đấu bất cứ nơi n&agrave;o bạn đi.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-4/asus-ct-1.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Aura lighting - Hệ thống chiếu s&aacute;ng ho&agrave;n hảo</strong></span></h5>\r\n\r\n<p>Hệ thống chiếu s&aacute;ng Aura sẽ trở th&agrave;nh t&acirc;m điểm trong mọi kh&ocirc;ng gian chơi game của bạn. Biểu tượng ROG tr&ecirc;n nắp, thanh &aacute;nh s&aacute;ng ph&iacute;a trước, c&aacute;c keycaps WASD trong suốt v&agrave; c&aacute;c cạnh ph&iacute;m b&aacute;n trong suốt cho ph&eacute;p Aura tỏa s&aacute;ng hơn bao giờ hết. H&atilde;y để thể hiện phong c&aacute;ch chơi game độc đ&aacute;o của ri&ecirc;ng bạn với Asus ROG Strix SCAR II GL704.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-4/asus-ct-2.jpg\" style=\"height:375px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hiệu suất chơi game đỉnh cao</strong></span></h5>\r\n\r\n<p>Asus ROG Strix SCAR II GL704 được trang bị chip Intel thế hệ thứ 8 mới nhất, l&ecirc;n đến 32GB bộ nhớ DDR4 2666MHz, v&agrave; lựa chọn một trong hai card đồ họa NVIDIA GeForce GTX v&agrave; RTX mạnh nhất hiện nay. Lưu trữ kh&ocirc;ng phải l&agrave; một vấn đề nhờ kết hợp giữa SSD 512GB M.2 PCIe NMVe v&agrave; một 1TB Seagate SSHD FireCuda tr&ecirc;n cung cấp. Tuy nhi&ecirc;n, bạn trang bị cho Asus ROG Strix SCAR II GL704 của m&igrave;nh, bạn được đảm bảo l&agrave; hỏa lực chiến đấu đ&atilde; được chứng minh để cai trị tr&ograve; chơi.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-4/asus-ct-3.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hệ thống tản nhiệt HyperCool Pro độc quyền</strong></span></h5>\r\n\r\n<p>Hệ thống tản nhiệt&nbsp;<strong>HyperCool Pro</strong>&nbsp;độc quyền của Asus c&oacute; một loạt c&aacute;c t&iacute;nh năng gi&uacute;p Asus ROG Strix SCAR II GL704 tận dụng tối đa GPU v&agrave; CPU của n&oacute;. C&oacute; một tấm l&agrave;m m&aacute;t mở rộng để truyền nhiệt đồng đều, v&agrave; hai quạt 12V hiệu suất cao cung cấp luồng kh&iacute; mạnh hơn v&agrave; tăng &aacute;p suất kh&ocirc;ng kh&iacute; để đảm bảo một hệ thống ổn định ngay cả khi tải nặng. HyperCool Pro cũng bao gồm hệ thống l&agrave;m m&aacute;t chống bụi được cấp bằng s&aacute;ng chế để trục xuất c&aacute;c hạt bụi v&agrave; bụi bẩn nhằm cải thiện độ tin cậy v&agrave; độ bền của hệ thống.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-4/asus-ct-4.jpg\" style=\"height:499px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh thực sự d&agrave;nh cho game thủ</strong></span></h5>\r\n\r\n<p>Asus ROG Strix SCAR II GL704 c&oacute; m&agrave;n h&igrave;nh hiển thị IPS với tốc độ qu&eacute;t 144Hz v&agrave; thời gian phản hồi 3ms cho h&igrave;nh ảnh si&ecirc;u n&eacute;t với &iacute;t hoặc kh&ocirc;ng c&oacute; chuyển động mờ. M&agrave;n h&igrave;nh với độ s&aacute;ng 300cd/m&sup2; đảm bảo h&igrave;nh ảnh s&aacute;ng hơn 20% so với bảng điều khiển IPS cấp thấp.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-4/asus-ct-5.jpg\" style=\"height:422px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>ROG RangeBoost - Cho kết nối Wifi Zero-Dead-Spot</strong></span></h5>\r\n\r\n<p>M&aacute;y t&iacute;nh chơi game Asus ROG Strix SCAR II GL704 l&agrave; h&atilde;ng đầu ti&ecirc;n tr&ecirc;n thế giới sử dụng Wi-Fi đa ăng-ten với phạm vi lớn hơn 30%, th&ocirc;ng lượng Wi-Fi cao hơn v&agrave; &iacute;t kết nối hơn. C&ocirc;ng nghệ ROG RangeBoost được cấp bằng s&aacute;ng chế kết hợp bốn ăng-ten nằm ở mặt sau v&agrave; mặt trước của m&aacute;y với một thuật to&aacute;n độc đ&aacute;o li&ecirc;n tục qu&eacute;t v&agrave; chọn cặp ăng-ten tốt nhất c&oacute; thể che phủ c&aacute;c điểm chết của nhau, dẫn đến t&iacute;n hiệu mạnh nhất c&oacute; thể. Chuẩn 802.11ac Wave 2 mới nhất cung cấp tốc độ l&ecirc;n tới 1,7Gbps - gấp đ&ocirc;i 802.11ac 2x2 v&agrave; gấp 12 lần so với 802.11b/g/n.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-4/asus-ct-6.jpg\" style=\"height:422px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m với độ ch&iacute;nh x&aacute;c tuyệt vời</strong></span></h5>\r\n\r\n<p>B&agrave;n ph&iacute;m được n&acirc;ng cấp với c&ocirc;ng nghệ HyperStrike Pro gi&uacute;p bạn kiểm so&aacute;t mọi trận chiến trong tầm tay. B&agrave;n ph&iacute;m với đ&egrave;n nền RGB t&ugrave;y chỉnh tr&ecirc;n bốn khu vực, một nh&oacute;m ph&iacute;m WASD được đ&aacute;nh dấu với c&aacute;c cạnh trong suốt, h&agrave;nh tr&igrave;nh ph&iacute;m 1.8mm v&agrave; keycap độ l&otilde;m 0.25mm để g&otilde; thoải m&aacute;i hơn. C&ocirc;ng nghệ Rollover N-key đảm bảo nhiều lần bấm ph&iacute;m đồng thời được ghi lại ngay lập tức, trong khi anti-ghosting đảm bảo kh&ocirc;ng c&oacute; lệnh giả n&agrave;o được đăng k&yacute; - v&igrave; vậy bạn c&oacute; to&agrave;n quyền kiểm so&aacute;t khi cần c&oacute; độ ch&iacute;nh x&aacute;c. Với tuổi thọ 20 triệu lần nhấn ph&iacute;m, Asus ROG Strix SCAR II GL704 c&oacute; một trong những b&agrave;n ph&iacute;m bền nhất được t&igrave;m thấy trong m&aacute;y t&iacute;nh x&aacute;ch tay - gấp đ&ocirc;i so với ti&ecirc;u chuẩn c&ocirc;ng nghiệp hiện tại.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-4/asus-ct-7.jpg\" style=\"height:422px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Aura Sync - Đồng bộ &aacute;nh s&aacute;ng c&aacute; nh&acirc;n</strong></span></h5>\r\n\r\n<p>C&ocirc;ng nghệ Aura Sync cung cấp cho to&agrave;n bộ thiết lập chơi game của bạn một giao diện nhất qu&aacute;n. Ph&ugrave; hợp với m&agrave;u sắc của logo ROG, b&agrave;n ph&iacute;m v&agrave; thanh &aacute;nh s&aacute;ng tr&ecirc;n Asus ROG Strix SCAR II GL704 với chuột chơi game hỗ trợ Aura Sync, tai nghe v&agrave; c&aacute;c thiết bị ngoại vi kh&aacute;c. Đầy đủ c&aacute;c m&agrave;u sắc v&agrave; bảy chế độ &aacute;nh s&aacute;ng kh&aacute;c nhau c&oacute; thể dễ d&agrave;ng truy cập th&ocirc;ng qua bảng điều khiển Aura.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-4/asus-ct-8.jpg\" style=\"height:789px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>&Acirc;m thanh chơi game r&otilde; n&eacute;t</strong></span></h5>\r\n\r\n<p>Asus ROG Strix SCAR II GL704 mang lại &acirc;m thanh r&otilde; r&agrave;ng hơn, to hơn v&agrave; ch&iacute;nh x&aacute;c hơn th&ocirc;ng qua c&aacute;c loa k&iacute;ch hoạt mạnh mẽ gi&uacute;p bạn đắm ch&igrave;m trong &acirc;m thanh. Ngay cả những &acirc;m thanh nhỏ nhất cũng c&oacute; thể được nghe thấy, v&igrave; vậy bạn sẽ biết một kẻ địch đang tiến đến từ đ&acirc;u. C&ocirc;ng nghệ bộ khuếch đại th&ocirc;ng minh gi&aacute;m s&aacute;t đầu ra &acirc;m thanh v&agrave; điều chỉnh thời gian thực, v&igrave; vậy bạn c&oacute; thể tăng &acirc;m lượng với dải động v&agrave; &acirc;m trầm tăng l&ecirc;n. Sonic Studio III cung cấp &acirc;m thanh trong tr&ograve; chơi chất lượng cao, &acirc;m thanh v&ograve;m cho tai nghe của bạn, cũng như cải thiện khả năng ph&aacute;t trực tuyến v&agrave; ghi &acirc;m để thu &acirc;m giọng n&oacute;i ho&agrave;n hảo v&agrave; giảm tiếng ồn. Mỗi lợi thế nhỏ đều c&oacute; thể gi&uacute;p bạn d&agrave;nh chiến thắng - v&agrave; Asus ROG Strix SCAR II GL704 đảm bảo bạn nghe mọi t&iacute;n hiệu &acirc;m thanh ch&iacute;nh x&aacute;c.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-4/asus-ct-9.jpg\" style=\"height:421px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>ROG Gaming Center - Quản l&yacute; chơi game hiệu quả</strong></span></h5>\r\n\r\n<p>K&iacute;ch hoạt ROG Gaming Center th&ocirc;ng qua ph&iacute;m n&oacute;ng ROG để xem to&agrave;n bộ bảng th&ocirc;ng tin hữu &iacute;ch, bao gồm cả tần số CPU v&agrave; GPU - v&agrave; sử dụng bảng điều khiển để điều chỉnh theo thời gian thực. N&oacute; cho ph&eacute;p bạn truy cập v&agrave;o ROG GameFirst, GameVisual, Sonic Studio, Sonic Radar, Aura Core v&agrave; XSplit Gamecaster v&agrave; thậm ch&iacute; bạn c&oacute; thể điều chỉnh c&agrave;i đặt của quạt T&ugrave;y chọn v&agrave; th&ocirc;ng số tr&ograve; chơi của bạn c&oacute; thể được lưu trong đ&aacute;m m&acirc;y để bạn c&oacute; thể truy cập ch&uacute;ng ngay cả khi bạn thay đổi m&aacute;y t&iacute;nh x&aacute;ch tay. Tải ứng dụng ROG Gaming Center xuống thiết bị Android hoặc iOS của bạn để c&oacute; thể giữ kết nối với Asus ROG Strix SCAR II GL704 mọi nơi.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-4/asus-ct-10.jpg\" style=\"height:375px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Cổng kết nồi đầy đủ</strong></span></h5>\r\n\r\n<p>Ngo&agrave;i ra, Asus ROG Strix SCAR II GL704 c&ograve;n t&iacute;ch hợp đầy đủ c&aacute;c cổng kết nối với một USB 3.1 Gen 2 Type-C, một USB 3.1 Gen 2 Type-A v&agrave; hai cổng USB 3.1 Gen 1 Type-A, gi&uacute;p kết nối với c&aacute;c thiết bị ngoại vi chơi game mới nhất. HDMI 2.0 hỗ trợ đầu ra 4K UHD ở tần số 60Hz để chơi game si&ecirc;u &acirc;m hoặc thưởng thức nội dung 4K UHD từ Netflix hoặc Amazon Prime tr&ecirc;n TV m&agrave;n h&igrave;nh phẳng lớn của bạn. Mini DisplayPort 1.2 hỗ trợ đầu ra 4K UHD ở mức tối đa 75Hz v&agrave; đảm bảo khả năng tương th&iacute;ch với m&agrave;n h&igrave;nh hỗ trợ G-SYNC.</p>\r\n', 2, '2019-07-01 17:00:00', 'Bán chạy', 'Bình thường', 1),
(50, ' ASUS ROG Zephyrus S GX531GW-ES006T Core i7-8750H/ RTX 2070 8GB', 56750000, 59990000, 10, 'Bộ vi xử lý: Intel Core i7 8750H\r\n\r\nRAM: 16GB DDR4 2666MHz\r\n\r\nỔ cứng SSD: 512GB SSD NVMe M.2 PCIe Gen 3 x 4\r\n\r\nGPU: Geforce RTX 2070 MaxQ 8GB\r\n\r\nMàn hình: 15.6\" FHD (1920×1080) IPS\r\n\r\nHệ điều hành: Windows 10 SL 64 Bit\r\n\r\nPin: 4 Cell, 50 Whrs                                                ', 'asus6.jpg', 'asus6-1.jpg', 'asus6-2.jpg', 'asus6-3.jpg', 'asus6-4.jpg', 'asus6-5.jpg', 'asus6-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế mỏng nhẹ, ấn tượng</strong></span></h5>\r\n\r\n<p><strong>Laptop ASUS ROG Zephyrus S GX531GW-ES006T</strong>&nbsp;sở hữu thiết kế mỏng nhẹ theo ti&ecirc;u chuẩn mới cho d&ograve;ng laptop Gaming hiện đại v&agrave; kết hợp sự kh&eacute;o l&eacute;o của c&aacute;c chuy&ecirc;n gia để đưa những con chip cao cấp v&agrave;o chiếc laptop chơi game mỏng nhất từ ​​trước tới nay. Chỉ mỏng 14,95 ~ 15,75mm, khung kim loại v&agrave; nắp unibody CNC-l&agrave;m cho Zephyrus S GX531 đủ linh động cho cuộc sống hằng ng&agrave;y khi di chuyển nhiều, phần ph&iacute;m số sẽ t&iacute;ch hợp chung với toudpad c&oacute; thể chuyển dổi t&ugrave;y th&iacute;ch. Một chi tiết đặc biệt của m&aacute;y l&agrave; dưới m&aacute;y sẽ c&oacute; ngăn tản nhiệt cửa ẩn, n&oacute; biến đổi khi bạn đ&oacute;ng hoặc mở m&aacute;y giống như một con Transformers Mini.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-5/asus-ct-11.jpg\" style=\"height:851px; width:1600px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh Bezels si&ecirc;u mỏng</strong></span></h5>\r\n\r\n<p>M&agrave;n h&igrave;nh Bezels si&ecirc;u mỏng thu h&uacute;t &aacute;nh nh&igrave;n tr&ecirc;n m&agrave;n h&igrave;nh. Tốc độ xử l&yacute; h&igrave;nh ảnh l&ecirc;n đến 144Hz gi&uacute;p tr&ograve; chơi mượt m&agrave; hơn v&agrave; giảm t&igrave;nh trạng giật lag, v&agrave; thời gian phản hồi của khung h&igrave;nh chỉ 3ms tạo ra h&igrave;nh ảnh sắc n&eacute;t hơn m&agrave; kh&ocirc;ng bị mờ. Với gam m&agrave;u sRGB đủ sắc v&agrave; g&oacute;c nh&igrave;n rộng, ROG Zephyrus S GX531 pha trộn tốc độ v&agrave; vẻ đẹp.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-5/asus-ct-12.jpg\" style=\"height:852px; width:1269px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hiệu suất c&oacute; sức mạnh cực khủng</strong></span></h5>\r\n\r\n<p>Đừng bị đ&aacute;nh lừa bởi vẻ bền ngo&agrave;i mỹ miều của n&oacute;, Laptop Zephyrus S GX531 c&oacute; sức mạnh cực khủng để c&oacute; thể chơi được những game hạng AAA, trải nghiệm VR v&agrave; c&ocirc;ng việc đồ họa. CPU Intel Core TMi7 thế hệ thứ 8 v&agrave; GeForce GTX 1070 với GPU thiết kế Max-Q mang lại hiệu suất như m&aacute;y t&iacute;nh để b&agrave;n, trong khi ổ SSD NVMe l&ecirc;n đến 1TB cung cấp dung lượng lưu trữ nhanh ch&oacute;ng cho thư viện tr&ograve; chơi v&agrave; c&aacute;c file &ldquo;Học tập&rdquo; 4k uncensored của bạn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hệ thống tản nhiệt kh&iacute; động học duy nhất</strong></span></h5>\r\n\r\n<p>Hệ thống tản nhiệt kh&iacute; động học duy nhất tr&ecirc;n Zephyrus s GX531, khe mở rộng để gi&uacute;p n&oacute; duy tr&igrave; GPU cao v&agrave; ổn định với c&aacute;c tr&ograve; chơi hạng nặng. M&aacute;y n&acirc;ng cấp nắp mở ra 5 mm l&agrave;m tăng luồng kh&ocirc;ng kh&iacute; l&ecirc;n đến 22% so với thiết kế truyền thống. Hệ th&ocirc;ng tản nhiệt của Zephyrus S GX531 mới nay kh&ocirc;ng chỉ giữ m&aacute;y ở nhiệt độ ổn định m&agrave; c&ograve;n giữ v&agrave; đẩy bụi ra b&ecirc;n ngo&agrave;i nhờ c&ocirc;ng nghệ Anti-dust.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-5/asus-ct-13.jpg\" style=\"height:845px; width:1600px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>T&iacute;nh năng AURA SYNC</strong></span></h5>\r\n\r\n<p>B&agrave;n ph&iacute;m đảm bảo dộ nhậy v&agrave; ch&iacute;nh x&aacute;c khi đang chơi những tựa game nặng đ&ocirc;. Bạn c&oacute; thể t&ugrave;y chỉnh m&agrave;u nền v&agrave; hiệu ứng cho ph&ugrave; hợp với sở th&iacute;ch của bạn, v&agrave; một v&ugrave;ng RGB cở ph&iacute;a sau m&aacute;y c&oacute; thể t&ugrave;y chỉnh được. Với t&iacute;nh năng Aura Sync tr&ecirc;n Zephyrus s Gx531 sẽ kết nối c&aacute;c thiết bị Asus tương th&iacute;ch như chuột, b&agrave;n ph&iacute;m, tai nghe v&agrave; c&aacute;c thiết bị ngoại vi kh&aacute;c để li&ecirc;n kết tất cả lại với nhau.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>&Acirc;m thanh nổi bật</strong></span></h5>\r\n\r\n<p>ROG Zephyrus S đặt &acirc;m thanh phần tr&ecirc;n bạn ph&iacute;m với loa k&eacute;p nhắm v&agrave;o bạn. C&ocirc;ng nghệ khuếch đại th&ocirc;ng minh giữ ch&uacute;ng trong phạm vi người d&ugrave;ng, v&igrave; vậy bạn c&oacute; thể tăng l&ecirc;n &acirc;m lượng m&agrave; kh&ocirc;ng bị r&egrave; hay lo&atilde;n &acirc;m thanh. &Acirc;m thanh quả đầu ra to hơn, với nhiều &acirc;m trầm v&agrave; bass gi&uacute;p bạn nghe mọi &acirc;m thanh, v&agrave; bạn c&oacute; thể cắm tai nghe để cảm nhận r&otilde; hơn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-5/asus-ct-14.jpg\" style=\"height:719px; width:1600px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Phần mềm hỗ trợ ROG Armory Crate</strong></span></h5>\r\n\r\n<p>Phần mềm ROG Armory Crate sẽ thống kế tất cả hệ thống v&agrave; điều khiển m&agrave;u v&agrave; tắt cả c&aacute;c th&ocirc;ng số, kỹ thuật trong 1 phần mềm duy nhất. Bạn c&oacute; thể dễ d&agrave;ng thay đổi c&agrave;i đặt v&agrave; c&oacute; thể t&ugrave;y chỉnh tối đa 4 thiết lập boost tr&ograve; chơi t&ugrave;y chỉnh của bạn khi khởi chạy App của m&igrave;nh. C&aacute;c t&ugrave;y chọn n&agrave;y chỉ l&agrave; một phần nhỏ trong phần mềm ROG Armory Crate phần mềm n&agrave;y c&ograve;n rất nhi&ecirc;u t&iacute;nh năng linh hoạt kh&aacute;c sẽ chờ bạn kh&aacute;m ph&aacute;.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Đa dạng cổng kết nối</strong></span></h5>\r\n\r\n<p>Laptop Zephyrus S GX531 c&oacute; tất cả c&aacute;c kết nối cần thiết để phục vụ để chơi game hoặc l&agrave;m việc. Cổng Type-C (USB-C )&nbsp; v&agrave; tốc độ USB 3.1 Gen 2 v&agrave; Gen 1, trong khi cổng Type-A USB 3.1 Gen 2 v&agrave; USB 2.0 ho&agrave;n hảo cho một gamepad v&agrave; chuột. HDMI 2.0 ở mặt sau hỗ trợ 4K UHD hiển thị l&ecirc;n tới 60Hz, v&igrave; vậy bạn c&oacute; thể chơi game v&agrave; giải tr&iacute; tr&ecirc;n TV m&agrave;n h&igrave;nh lớn.</p>\r\n', 2, '2019-06-22 06:38:16', 'Bình thường', 'Bình thường', 1);
INSERT INTO `product` (`id`, `product_name`, `price_new`, `price_old`, `qty_product`, `product_desc`, `product_thumb`, `list_thumb_1`, `list_thumb_2`, `list_thumb_3`, `list_thumb_4`, `list_thumb_5`, `list_thumb_6`, `product_content`, `cat_id`, `created_at`, `selling_products`, `featured_products`, `status`) VALUES
(51, 'Asus TUF Gaming FX504GM-EN303T Core i7-8750H/Win10/8GB DDR4', 28590000, 30990000, 30, 'Chip: Intel Core i7-8750H (2.2GHz up to 4.1GHz 9MB)\r\n\r\nRAM: 8GB DDR4 2666MHz (2x SO-DIMM socket, up to 32GB SDRAM)\r\n\r\nỔ cứng: HDD 1TB 5400rpm + 256GB SSD PCIE G3X2\r\n\r\nChipset đồ họa: NVIDIA GeForce GTX 1060 6GB GDDR5\r\n\r\nMàn hình: 15.6 inch Full HD (1920 x 1080) Non-Glare, 94% NTSC 120Hz, 3ms, 300nits\r\n\r\nHệ điều hành: Windows 10 Home\r\n\r\nPin: 4 Cells 48 Whrs', 'asus7.jpg', 'asus7-1.jpg', 'asus7-2.jpg', 'asus7-3.jpg', 'asus7-4.jpg', 'asus7-5.jpg', 'asus7-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Laptop TUF Gaming đầu ti&ecirc;n - Vượt xa chuẩn mực</strong></span></h5>\r\n\r\n<p><strong>Laptop Asus TUF Gaming FX504GM-EN303T</strong><strong>&nbsp;</strong>với thiết kế ấn tượng v&agrave; hiệu năng mạnh mẽ, c&oacute; thể chơi được hầu hết c&aacute;c tựa game mới nhất v&agrave; đa nhiệm dễ d&agrave;ng, đồng thời c&oacute; độ bền cao hơn, ổn định hơn v&agrave; vẫn kh&ocirc;ng bị &#39;lỗi thời&#39; trong nhiều năm nữa.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-6/asus-ct-1.jpg\" style=\"height:421px; width:750px\" /></p>\r\n\r\n<p>Hệ thống tản nhiệt chống bụi th&ocirc;ng minh cung cấp đầy đủ luồng kh&iacute; m&aacute;t cho m&aacute;y ngay cả khi chơi những game nặng hơn, k&eacute;o d&agrave;i tuổi thọ hệ thống, loại bỏ bụi bẩn để kh&ocirc;ng g&acirc;y tắc nghẽn hệ thống tản nhiệt. Được lựa chọn kỹ từ 4 thiết kế kh&aacute;c nhau để chọn ra mẫu ph&ugrave; hợp nhất cho mỗi c&aacute; nh&acirc;n.&nbsp;Asus TUF Gaming FX504GM-EN303T&nbsp;c&oacute; độ bền vượt ra khỏi chuẩn mực th&ocirc;ng thường d&ugrave; l&agrave; trong c&ocirc;ng việc hay khi chơi game.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh 15.6 inch Full HD hiển thị cực nhanh</strong></span></h5>\r\n\r\n<p>M&agrave;n h&igrave;nh 15.6 inch Full HD&nbsp;(1920 x 1080) độ phủ m&agrave;u 94% NTSC đảm bảo xuất ra h&igrave;nh ảnh với chất lượng sắc n&eacute;t v&agrave; ch&acirc;n thật nhất.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-6/asus-ct-2.jpg\" style=\"height:421px; width:750px\" /></p>\r\n\r\n<p>Asus TUF Gaming FX504GM-EN303T n&acirc;ng cấp tầm nh&igrave;n của bạn, với tốc độ l&agrave;m tươi đến 120Hz và thời gian đáp ứng 3ms để mang lại h&igrave;nh ảnh mượt m&agrave;, ch&iacute;nh x&aacute;c, cũng như loại bỏ độ trễ v&agrave; mờ của chuyển động. Tấm nền IPS gi&uacute;p bạn c&oacute; thể trải nghiệm g&oacute;c nh&igrave;n rộng 178&deg; đem lại h&igrave;nh ảnh sắc n&eacute;t, sống động v&agrave; độ tương phản cao ngay cả ở những g&oacute;c nh&igrave;n hẹp.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bộ vi xử l&yacute; Intel Core i7</strong></span></h5>\r\n\r\n<p>Được trang bị bộ vi xử l&yacute; Intel Core i7-8750H, c&oacute; khả năng đạt được tốc độ 4.0GHz đ&aacute;ng kinh ngạc. Hiệu năng được bổ trợ với pin dung lượng cao để đảm bảo sự mượt m&agrave; cho những trận đấu k&eacute;o d&agrave;i. Cho d&ugrave; bạn cần phải sử dụng đa nhiệm cho c&ocirc;ng việc hoặc học tập, trong khi đang xem phim, nghe nhạc v&agrave; gaming tr&ecirc;n Asus TUF Gaming FX504GM-EN303T.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-6/asus-ct-3.jpg\" style=\"height:421px; width:750px\" /></p>\r\n\r\n<p>C&ugrave;ng bộ nhớ RAM DDR4 dung lượng 8GB cho hiệu suất xử l&yacute; tốt cũng như đ&aacute;p ứng c&aacute;c t&aacute;c vụ đa nhiệm mượt m&agrave; c&aacute;c ứng dụng. Ngo&agrave;i ra, Asus c&ograve;n trang bị cho m&aacute;y ổ cứng kép HDD 1TB +&nbsp;256GB SSD gi&uacute;p m&aacute;y hoạt động mượt m&agrave; hơn, khởi động nhanh ch&oacute;ng hơn.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Card đồ họa NVIDIA GeForce GTX 1060</strong></span></h5>\r\n\r\n<p>Được trang bị card đồ họa NVIDIA GeForce GTX 1060 b&ocirc;̣ nhớ đ&ocirc;̀ họa 6GB được x&acirc;y dựng tr&ecirc;n kiến tr&uacute;c Pascal mới nhất, cho m&aacute;y t&iacute;nh của bạn một hiệu suất mạnh mẽ c&oacute; thể xử l&yacute; tốt hầu hết c&aacute;c tựa game v&agrave; c&aacute;c ứng dụng đ&ograve;i hỏi khả năng đồ họa cao.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-6/asus-ct-4.jpg\" style=\"height:421px; width:750px\" /></p>\r\n\r\n<p>C&ocirc;ng nghệ NVIDIA G-SYNC đồng bộ h&oacute;a tốc độ l&agrave;m tươi m&agrave;n h&igrave;nh với tốc độ khung h&igrave;nh của GPU để l&agrave;m cho gameplay mịn m&agrave;ng. G-SYNC giảm thiểu việc h&igrave;nh ảnh bị lắp, giảm độ trễ đầu v&agrave;o, v&agrave; loại bỏ hiện tượng r&aacute;nh m&agrave;n h&igrave;nh trực quan m&agrave; kh&ocirc;ng ảnh hưởng đến hiệu năng của hệ thống. Bạn sẽ c&oacute; được trải nghiệm mịn m&agrave;ng, nhanh nhất v&agrave; hấp dẫn nhất qua một loạt c&aacute;c tr&ograve; chơi bom tấn tr&ecirc;n Asus TUF Gaming FX504GM-EN303T.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>DTS HeadphoneX - &Acirc;m thanh v&ograve;m 7.1</strong></span></h5>\r\n\r\n<p>DTS Headphone:X mang lại trải nghiệm &acirc;m thanh v&ograve;m 7.1 tr&ecirc;n tai nghe v&agrave; loa ngo&agrave;i ấn tượng. D&ugrave; bạn chơi game, xem phim hay cổ vũ cho đội tuyển bạn y&ecirc;u th&iacute;ch, DTS vẫn lu&ocirc;n cho bạn chất &acirc;m căng đầy, gi&uacute;p bạn như được sống trong m&ocirc;i trường h&igrave;nh ảnh bạn đang xem, phản ứng nhanh hơn với tiếng động từ nhiều ph&iacute;a.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-6/asus-ct-5.jpg\" style=\"height:421px; width:750px\" /></p>\r\n\r\n<p>Như DTS Headphone:X c&oacute; thể m&ocirc; phỏng ch&iacute;nh x&aacute;c trải nghiệm &acirc;m thanh v&ograve;m. Hơn nữa, độn c&acirc;n bằng t&iacute;ch hợp sẽ lu&ocirc;n đ&aacute;p ứng được nhu cầu audio của mọi người, cung cấp cho người d&ugrave;ng c&aacute;c t&ugrave;y chỉnh &acirc;m thanh chi tiết ph&ugrave; hợp với từng c&aacute; nh&acirc;n.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>C&ocirc;ng nghệ Hypercool cải thiện nhiệt độ v&agrave; tuổi thọ pin</strong></span></h5>\r\n\r\n<p>Thiết kế tản nhiệt của Asus TUF Gaming FX504GM-EN303T&nbsp;k&eacute;o d&agrave;i đ&aacute;ng kể tuổi thọ của m&aacute;y v&agrave; cung cấp hiệu suất ổn định hơn cho c&aacute;c tựa game đồ họa cao, với c&ocirc;ng nghệ HyperCool kết hợp với hệ thống ADC tự l&agrave;m sạch bụi bẩn v&agrave; t&ugrave;y chỉnh tốc độ quạt Fan Overboost. Loại bỏ bụi bẩn ra khỏi hệ thống bằng 2 ống chuy&ecirc;n dụng, ADC hoạt động như hệ thống miễn dịch tr&ecirc;n laptop &ndash; Ngăn bụi bẩn đ&oacute;ng lại l&agrave;m tắc nghẽn khe tản nhiệt, gi&uacute;p hệ thống ổn định v&agrave; hoạt động được l&acirc;u hơn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-6/asus-ct-6.jpg\" style=\"height:421px; width:750px\" /></p>\r\n\r\n<p>Fan Overboost c&oacute; thể điều chỉnh được, ph&ugrave; hợp với nhiều nhu cầu kh&aacute;c nhau giữa l&agrave;m việc đa nhiệm v&agrave; chơi game: Bạn c&oacute; thể t&ugrave;y chọn giữ 3 chế độ kh&aacute;c nhau để t&igrave;m được sự c&acirc;n bằng ho&agrave;n hảo giữa hiệu xuất quạt v&agrave; độ ồn. M&aacute;y c&oacute; 2 ống đồng tản nhiệt v&agrave; quạt l&agrave;m m&aacute;t cho CPU, GPU độc lập, với khung gầm được tối ưu h&oacute;a luồng kh&ocirc;ng kh&iacute;, tăng hiệu suất l&agrave;m m&aacute;t l&ecirc;n đến 27% so với m&aacute;y t&iacute;nh th&ocirc;ng thường. Một quạt th&ocirc;ng minh được đặt ngay b&ecirc;n dưới cụm ph&iacute;m WASD để đảm bảo b&agrave;n ph&iacute;m lu&ocirc;n &#39;m&aacute;t&#39; ngay cả khi chơi game trong thời gian d&agrave;i.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Dải Microphone - Stream game ho&agrave;n hảo</strong></span></h5>\r\n\r\n<p>Microphone được t&iacute;ch hợp bộ lọc tiếng ồn để đảm bảo bắt được giọng n&oacute;i của bạn một c&aacute;ch r&otilde; r&agrave;ng nhất, ngay cả trong m&ocirc;i trường nhiều tạp &acirc;m. D&ugrave; bạn đang tr&ograve; chuyện tr&ecirc;n Skype với bạn b&egrave;, hay chơi game online m&agrave; kh&ocirc;ng c&oacute; tai nghe, chiếc mic của Asus TUF Gaming FX504GM-EN303T&nbsp;sẽ gi&uacute;p bạn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-6/asus-ct-7.jpg\" style=\"height:421px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m tối ưu cho chơi game v&agrave; g&otilde; văn bản</strong></span></h5>\r\n\r\n<p>Thiết kế của từng bộ phận Asus TUF Gaming FX504GM-EN303T&nbsp;lu&ocirc;n được đầu tư kỹ lưỡng từ việc l&ecirc;n &yacute; tưởng đến việc thực hiện, v&agrave; chiếc b&agrave;n ph&iacute;m được lấy cảm hứng từ m&aacute;y t&iacute;nh để b&agrave;n ch&iacute;nh l&agrave; sự tối ưu h&oacute;a ho&agrave;n hảo d&agrave;nh cho FX504. Với độ bền ph&iacute;m l&ecirc;n tới 20 triệu lần nhấn, Asus TUF Gaming FX504 được xem l&agrave; chiếc m&aacute;y t&iacute;nh c&oacute; b&agrave;n ph&iacute;m bền nhất đến hiện tại &ndash; Gấp đ&ocirc;i độ bền của b&agrave;n ph&iacute;m laptop th&ocirc;ng thường chỉ đạt 10 triệu lần nhấn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-6/asus-ct-8.jpg\" style=\"height:421px; width:750px\" /></p>\r\n\r\n<p>C&aacute;c ph&iacute;m c&oacute; keycap l&otilde;m 0.25mm v&agrave; h&agrave;nh tr&igrave;nh ph&iacute;m 1.8mm gi&uacute;p cho việc thao t&aacute;c nhạy hơn v&agrave; thoải m&aacute;i hơn, ngo&agrave;i ra b&aacute;n ph&iacute;m c&ograve;n c&oacute; đ&egrave;n nền rất hữu &iacute;ch cho người chơi game hoặc l&agrave;m việc trong b&oacute;ng tối. C&ocirc;ng nghệ Overstroke độc quyền đảm bảo cho thao t&aacute;c ph&iacute;m ch&iacute;nh x&aacute;c hơn. Cụm ph&iacute;m WASD nổi bật, ph&iacute;m space rộng, c&aacute;c ph&iacute;m mũi t&ecirc;n được t&aacute;ch rời gi&uacute;p thao t&aacute;c g&otilde; ph&iacute;m kh&ocirc;ng bị nhầm lẫn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-6/asus-ct-9.jpg\" style=\"height:421px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Truy cập Wifi nhanh v&agrave; xa hơn</strong></span></h5>\r\n\r\n<p>Được trang bị một trong những chuẩn Wi-Fi nhanh nhất hiện tại &ndash; Phi&ecirc;n bản mới nhất của Intel 802.11ac Wave 2 với tốc độ truyền dữ liệu l&ecirc;n đến 1.7Gbps &ndash; 12X nhanh hơn so với chuẩn 802.11b/g/n. Ngo&agrave;i ra cấu h&igrave;nh kh&ocirc;ng d&acirc;y 2x2 cung cấp phạm vi rộng hơn 30% so với cấu h&igrave;nh 1x1. Trực tiếp bộ phim bạn y&ecirc;u th&iacute;ch với chất lượng cao hoặc xem online m&agrave; &iacute;t bị giật hơn, d&ugrave; c&oacute; đặt Asus TUF Gaming FX504GM-EN303T&nbsp;ở xa router.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Cổng kết nối được bố tr&iacute; ho&agrave;n hảo</strong></span></h5>\r\n\r\n<p>Tất cả cổng kết nối m&agrave; bạn cần để chơi game v&agrave; đ&aacute;p ứng năng suất giải tr&iacute;, 2 c&ocirc;̉ng USB 3.0 port(s) Type A d&agrave;nh cho thiết bị ngoại vi chơi game, trong khi HDMI cho ph&eacute;p bạn mở rộng hiển thị tr&ecirc;n m&agrave;n h&igrave;nh rồi. V&agrave; tất cả cổng kết nối được thiết kế b&ecirc;n tr&aacute;i, v&agrave; đương nhi&ecirc;n bạn sẽ kh&ocirc;ng bị vướng d&acirc;y chuột khi chơi game.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Pin 4 cell</strong></span></h5>\r\n\r\n<p>Với vi&ecirc;n pin 4 cell Asus TUF Gaming FX504GM-EN303T&nbsp;cho thời gian sử dụng tương đối. C&ocirc;ng nghệ tiết kiệm pin hoạt động m&aacute;t, &ecirc;m &aacute;i v&agrave; tiết kiệm điện năng n&ecirc;n c&oacute; thể đ&aacute;p ứng tốt nhu cầu sử dụng b&igrave;nh thường của bạn. Với c&ocirc;ng nghệ pin Lithium Polymer sẽ cho m&aacute;y khả năng vận h&agrave;nh li&ecirc;n tục l&ecirc;n đến 8 tiếng đồng hồ.</p>\r\n\r\n<p>Điểm đặc biệt của d&ograve;ng pin Lithium Polymer c&oacute; thể duy tr&igrave; được năng lượng sạc lần đầu cho d&ugrave; sau h&agrave;ng trăm chu kỳ sạc, l&acirc;u hơn gấp 3 lần so với c&aacute;c loại pin ti&ecirc;u chuẩn hiện nay, gi&uacute;p bạn khỏi phải lo lắng về hao m&ograve;n hao m&ograve;n điện năng pin.</p>\r\n', 2, '2019-06-22 06:38:16', 'Bình thường', 'Bình thường', 1),
(52, 'Asus Zenbook 15 UX533FD-A9027T Core i7-8565U/ Win10 (15.6\" FHD)', 29990000, 31990000, 22, 'Chip: Intel Core i7-8565U Processor (8M Cache, up to 4.60 GHz)\r\n\r\nRAM: 8GB DDR4 2400MHz\r\n\r\nỔ cứng: 512GB SSD M.2 PCIE G3X2\r\n\r\nChipset đồ họa: NVIDIA GeForce GTX 1050 2GB GDDR5\r\n\r\nMàn hình: 15.6-inch FHD (1920 x 1080) 300nits Anti-Glare NTSC 72%\r\n\r\nHệ điều hành: Windows 10\r\n\r\nPin: 4-Cell 73WHr', 'asus8.jpg', 'asus8-1.jpg', 'asus8-2.jpg', 'asus8-3.jpg', 'asus8-4.jpg', 'asus8-5.jpg', 'asus8-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế vỏ nh&ocirc;m&nbsp;cao cấp</strong></span></h5>\r\n\r\n<p><strong>Laptop Asus Zenbook 15 UX533FD-A9027T</strong><strong>&nbsp;</strong>tr&ecirc;n nắp m&aacute;y được thiết kế bằng hợp kim nh&ocirc;m với c&aacute;c v&ograve;ng tr&ograve;n đồng t&acirc;m đặc trưng, ngo&agrave;i ra mặt tr&ecirc;n c&ograve;n được phủ th&ecirc;m một lớp giả kim cương h&agrave;o nho&aacute;ng với kỹ thuật dập ảnh nổi nano cao cấp, đạt chuẩn &quot;mỏng nhẹ&quot; với trọng lượng 1.67kg. Thiết kế của m&aacute;y thon dần về ph&iacute;a b&agrave;n ph&iacute;m, do vậy việc cầm m&aacute;y hay di chuyển cũng vẫn thoải m&aacute;i, dễ d&agrave;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-7/asus-ct-10.jpg\" style=\"height:331px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh NanoEdge 15.6-inch Full HD</strong></span></h5>\r\n\r\n<p>Laptop có m&agrave;n h&igrave;nh NanoEdge 15.6-inch Full HD&nbsp;(1920 x 1080) tối đa h&oacute;a trải nghiệm xem của bạn, với dải m&agrave;u rộng đạt 100% sRGB v&agrave; độ tương phản cao, đ&aacute;p ứng xuất sắc nhu cầu chơi games lẫn chỉnh sửa đồ họa của người d&ugrave;ng chuy&ecirc;n nghiệp, đảm bảo xuất ra h&igrave;nh ảnh với chất lượng sắc n&eacute;t v&agrave; ch&acirc;n thật nhất. Tấm nền chống ch&oacute;i Anti-glare gi&uacute;p bạn c&oacute; thể trải nghiệm tốt hơn ở m&ocirc;i trường b&ecirc;n ngo&agrave;i, c&oacute; khả năng chống ch&oacute;i tốt.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-7/asus-ct-11.jpg\" style=\"height:382px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>T&ugrave;y chỉnh m&agrave;u sắc tối ưu</strong></span></h5>\r\n\r\n<p>Nhờ tiện &iacute;ch ASUS Splendid được t&iacute;ch hợp sẵn tr&ecirc;n&nbsp;Asus Zenbook 15 UX533FD-A9027T, bạn sẽ c&oacute; thể thoải m&aacute;i thiết lập m&agrave;u sắc tối ưu tr&ecirc;n m&agrave;n h&igrave;nh đem lại một trải nghiệm h&igrave;nh ảnh ph&ugrave; hợp mọi l&uacute;c mọi nơi. Với c&aacute;c chế độ được thiết lập sẵn như sau: Chế độ thường &ndash; m&agrave;u sắc được thiết lập tối ưu, chế độ Eye Care &ndash; giảm thiểu &aacute;nh xanh gi&uacute;p bảo vệ mắt, chế độ sống động &ndash; m&agrave;u sặc được hiệu chỉnh rực rỡ hơn, chế độ chỉnh tay &ndash; t&ugrave;y chỉnh m&agrave;u sắc theo sở th&iacute;ch của bạn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-7/asus-ct-12.jpg\" style=\"height:281px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bộ vi xử l&yacute; Intel Core i7</strong></span></h5>\r\n\r\n<p>M&aacute;y được trang bị bộ vi xử l&yacute; Intel Core i7-8565U với xung nhịp c&oacute; thể l&ecirc;n đến 4.6GHz, cho bạn những gi&acirc;y ph&uacute;t lướt web nhanh hơn, mạnh mẽ hơn. B&ecirc;n cạnh đ&oacute;, với bộ nhớ trong Ram 8GB DDR4 2400MHz đảm bảo xử l&iacute; c&aacute;c t&aacute;c vụ đa nhiệm thật mượt m&agrave; v&agrave; ổn định. Ngo&agrave;i ra, m&aacute;y c&ograve;n trang bị th&ecirc;m bộ nhớ 512GB SSD M.2 PCIE G3X2, cho bạn lưu trữ cũng như vận h&agrave;nh m&aacute;y một c&aacute;ch nhanh ch&oacute;ng, mượt m&agrave; hơn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-7/asus-ct-13.jpg\" style=\"height:434px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Card đồ họa NVIDIA GeForce GTX 1050</strong></span></h5>\r\n\r\n<p>Đặc biệt, với card đồ họa NVIDIA GeForce GTX 1050 bạn c&oacute; thể ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m rằng m&aacute;y c&oacute; thể h&ocirc;̃ trợ bạn tối đa từ c&aacute;c việc đơn giản đến c&aacute;c c&ocirc;ng việc mang t&iacute;nh chất đ&ograve;i hỏi cao như thiết kế đồ họa, 3D, photoshop, cũng như giải tr&iacute; với những game 3D khủng nhất hiện nay.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>&Acirc;m thanh Harman Kardon vượt ngo&agrave;i mong đợi</strong></span></h5>\r\n\r\n<p>&Acirc;m thanh Harman Kardon bao gồm c&ocirc;ng nghệ khuếch đại th&ocirc;ng minh cho &acirc;m thanh lớn hơn m&agrave; kh&ocirc;ng bị m&eacute;o tiếng v&agrave; phần mềm được thiết kế th&ocirc;ng minh đảm bảo rằng Asus Zenbook 15 UX533FD-A9027T&nbsp;sẽ cho bạn một trải nghiệm &acirc;m thanh đỉnh cao.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-7/asus-ct-14.jpg\" style=\"height:470px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Trải nghiệm Windows 10 tối ưu</strong></span></h5>\r\n\r\n<p>Laptop Asus thiết lập ti&ecirc;u chuẩn cho trải nghiệm Windows 10 tuyệt vời nhất, v&agrave; c&oacute; một d&atilde;y microphone hỗ trợ Cortana, trợ l&yacute; kỹ thuật số c&aacute; nh&acirc;n c&oacute; thể gi&uacute;p bạn l&agrave;m việc dễ d&agrave;ng hơn. D&atilde;y microphone gi&uacute;p bạn kiểm so&aacute;t giọng n&oacute;i ch&iacute;nh x&aacute;c hơn, kể cả trong c&aacute;c điều kiện ồn &agrave;o. Cortana c&ograve;n c&oacute; thể gi&uacute;p bạn theo d&otilde;i c&aacute;c mối quan t&acirc;m, những nơi ưa th&iacute;ch, v&agrave; mọi tin tức m&agrave; bạn muốn cập nhật cho một trải nghiệm c&aacute; nh&acirc;n h&oacute;a ho&agrave;n to&agrave;n.</p>\r\n\r\n<h5><strong><span style=\"font-size:24px\">B&agrave;n ph&iacute;m chiclet v&agrave; b&agrave;n di chuột&nbsp;Numpad</span></strong></h5>\r\n\r\n<p>B&agrave;n ph&iacute;m được trang bị tr&ecirc;n Asus Zenbook 15 UX533FD-A9027T&nbsp;l&agrave; dạng b&agrave;n ph&iacute;m chiclet với thiết kế nguy&ecirc;n khối, h&agrave;nh tr&igrave;nh ph&iacute;m 1.4mm chuẩn mực v&agrave; hỗ trợ th&ecirc;m đ&egrave;n nền b&agrave;n ph&iacute;m sẽ gi&uacute;p bạn cảm gi&aacute;c g&otilde; ph&iacute;m tốt hơn trong khi l&agrave;m việc v&agrave; giải tr&iacute;. B&ecirc;n cạnh đ&oacute; với touchpad&nbsp;Numpad được chế t&aacute;c th&ocirc;ng minh cho độ chuẩn x&aacute;c cao, được phủ th&ecirc;m lớp k&iacute;nh để di chuột thoải m&aacute;i hơn, sử dụng c&ocirc;ng nghệ ph&aacute;t hiện l&ograve;ng b&agrave;n tay v&agrave; hỗ trợ đa điểm.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>USB Type C tất cả trong một kết nối</strong></span></h5>\r\n\r\n<p>Sở hữu cổng kết nối USB Type C đa dụng nhất hiện nay, với thiết kế đồng đẳng gi&uacute;p bạn c&oacute; thể cắm cổng kết nối ở bất cứ chiều n&agrave;o. Ngo&agrave;i ra với c&ocirc;ng nghệ n&agrave;y sẽ cho tốc độ truyền tải dữ liệu nhanh gấp 10 lần so với c&aacute;c cổng kết nối cũ hiện nay.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-7/asus-ct-15.jpg\" style=\"height:398px; width:750px\" /></p>\r\n\r\n<p>Hỗ trợ nhiều cổng kết nối như cổng&nbsp; HDMI,&nbsp;LAN (RJ45), HD webcam, Bluetooth, khe thẻ nhớ, jack tai nghe cho khả năng kết nối với c&aacute;c thiết bị kh&aacute;c một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-7/asus-ct-16.jpg\" style=\"height:381px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Thời lượng pin ổn định</strong></span></h5>\r\n\r\n<p>Pin lithium polymer 4-Cell 73WHr đặc biệt cho Asus Zenbook 15 UX533FD-A9027T&nbsp;có khả năng tự vận h&agrave;nh l&ecirc;n đến 9 tiếng. C&aacute;c pin lithium polymer c&oacute; thể duy tr&igrave; hầu hết dung t&iacute;ch sạc ban đầu kể cả sau h&agrave;ng trăm chu kỳ sạc l&acirc;u hơn gấp 3 lần so với c&aacute;c pin ti&ecirc;u chuẩn để bạn kh&ocirc;ng phải lo nghĩ nhiều về hao m&ograve;n hiệu năng pin.</p>\r\n', 2, '2019-06-22 06:38:16', 'Bình thường', 'Bình thường', 1),
(53, 'Lenovo ThinkPad X Carbon 6 20KHS01900 Core i7-8550U/Win10 (14 inch)', 48929000, 50490000, 27, 'Chip: Intel Core i7-8550U\r\n\r\nRAM: 8GB LPDDR3 2133MHz\r\n\r\nỔ cứng: 256GB SSD M.2 NVMe\r\n\r\nChipset đồ họa: Intel HD Graphics 620\r\n\r\nMàn hình: 14 inch WQHD (2560 x 1440) IPS Anti-Glare\r\n\r\nHệ điều hành: Windows 10 Home\r\n\r\nPin: 3 Cells 57 Whrs', 'lenovo6.jpg', 'lenovo6-1.jpg', 'lenovo6-2.jpg', 'lenovo6-3.jpg', 'lenovo6-4.jpg', 'lenovo6-5.jpg', 'lenovo6-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế gập 180 độ</strong></span></h5>\r\n\r\n<p><strong>Laptop Lenovo ThinkPad X Carbon 6 20KHS01900</strong>&nbsp;mang đến cho người d&ugrave;ng một chiếc laptop mạnh mẽ v&agrave; bền vững nhờ được bao bọc bởi bộ khung chắc chắn. Lớp vỏ ngo&agrave;i l&agrave;m từ chất liệu cao cấp vừa mang lại một độ bền vượt trội để đủ sức bảo vệ phần m&agrave;n h&igrave;nh v&agrave; c&aacute;c linh kiện b&ecirc;n trong nhưng vẫn đảm bảo tối ưu sự linh hoạt v&agrave; tiện dụng cho người d&ugrave;ng mỗi khi mang theo m&aacute;y b&ecirc;n m&igrave;nh. Ngo&agrave;i ra, bản lề của m&aacute;y được thiết kế đặc biệt gi&uacute;p m&aacute;y c&oacute; thể gập 180 độ như quyển tập.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-5/lenovo-ct-3.jpg\" style=\"height:484px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Màn hình 14 inch WQHD</strong></span></h5>\r\n\r\n<p>M&aacute;y được trang bị m&agrave;n h&igrave;nh 14 inch với độ ph&acirc;n giải WQHD (2560 x 1440) cho chất lượng hiển thị rất ấn tượng, cùng t&acirc;̀m n&ecirc;̀n IPS mang đến cho bạn góc nhìn r&ocirc;̣ng, tăng độ s&aacute;ng v&agrave; khả năng t&aacute;i tạo m&agrave;u sắc cho bạn h&igrave;nh ảnh chất lượng, chi tiết, r&otilde; n&eacute;t, m&agrave;u sắc cũng cực kỳ trung thực v&agrave; sống động cùng với tấm nền chống ch&oacute;i Anti-glare gi&uacute;p bạn c&oacute; thể trải nghiệm tốt hơn ở m&ocirc;i trường b&ecirc;n ngo&agrave;i, c&oacute; khả năng chống ch&oacute;i tốt.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-5/lenovo-ct-4.jpg\" style=\"height:499px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bộ vi xử l&yacute; Intel Core i7</strong></span></h5>\r\n\r\n<p>M&aacute;y được trang bị bộ vi xử l&yacute; Intel Core&nbsp;i7-8550U với xung nhịp c&oacute; thể l&ecirc;n đến 4.0GHz, cho bạn những gi&acirc;y ph&uacute;t lướt web nhanh hơn, mạnh mẽ hơn. B&ecirc;n cạnh đ&oacute;, với bộ nhớ trong Ram 8GB LPDDR3 đảm bảo xử l&iacute; c&aacute;c t&aacute;c vụ đa nhiệm thật mượt m&agrave; v&agrave; ổn định. Ngo&agrave;i ra, m&aacute;y c&ograve;n trang bị th&ecirc;m bộ nhớ 256GB SSD, cho bạn lưu trữ cũng như vận h&agrave;nh m&aacute;y một c&aacute;ch nhanh ch&oacute;ng, mượt m&agrave; hơn.&nbsp;Khả năng xử l&yacute; đồ họa của&nbsp;ThinkPad X Carbon 6 20KHS01900&nbsp;cũng được tăng cường với card đồ họa t&iacute;ch hợp Intel HD Graphics 620, cho khả năng chạy tốt v&agrave; ổn định c&aacute;c phần mềm đồ họa cũng như đ&aacute;p ứng nhu cầu giải tr&iacute; của người d&ugrave;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-5/lenovo-ct-5.jpg\" style=\"height:427px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>C&ocirc;ng nghệ &acirc;m thanh Dolby Home Theater</strong></span></h5>\r\n\r\n<p>Laptop sử dụng c&ocirc;ng nghệ &acirc;m thanh Dolby Audio gi&uacute;p cải thiện tốt hơn &acirc;m thanh loa ngo&agrave;i đồng thời gi&uacute;p &acirc;m lượng to hơn nhưng vẫn kh&ocirc;ng thay đổi về chất lượng. Nhờ đ&oacute;, bạn sẽ c&oacute; những ph&uacute;t gi&acirc;y thật tuyệt vời c&ugrave;ng những giai điệu &acirc;m nhạc hay những bộ phim &ldquo;đỉnh&rdquo; nhất.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-5/lenovo-ct-6.jpg\" style=\"height:457px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Tr&ograve; chuyện thỏa th&iacute;ch</strong></span></h5>\r\n\r\n<p>HD Webcam cho h&igrave;nh ảnh kh&aacute; tốt, có thanh n&acirc;́p trượt giúp bạn bảo v&ecirc;̣ camera m&ocirc;̣t cách t&ocirc;́i ưu ti&ecirc;̣n lợi. Nhờ đ&oacute; bạn c&oacute; thể chụp ảnh &ldquo;tự sướng&rdquo; v&agrave; chia sẻ h&igrave;nh ảnh với bạn b&egrave; qua c&aacute;c mạng x&atilde; hội như Facebook, Twitter &hellip;Ngo&agrave;i ra, bạn cũng c&oacute; thể tr&ograve; chuyện thỏa th&iacute;ch với người th&acirc;n qua c&aacute;c ứng dụng như: Skype, yahoo chat.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-5/lenovo-ct-7.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hệ điều h&agrave;nh Windows 10</strong></span></h5>\r\n\r\n<p>M&aacute;y được c&agrave;i sẵn hệ điều h&agrave;nh Windows 10 tiện &iacute;ch, gi&uacute;p m&aacute;y chạy mượt m&agrave;. Hệ điều h&agrave;nh sẽ hỗ trợ hầu hết những phần mềm hiện nay n&ecirc;n rất tiện cho c&ocirc;ng việc.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-5/lenovo-ct-8.jpg\" style=\"height:467px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m v&agrave; touchpad</strong></span></h5>\r\n\r\n<p>B&agrave;n ph&iacute;m của ThinkPad X Carbon 6 20KHS01900&nbsp;được thiết kế khoa học, c&aacute;c ph&iacute;m bấm c&oacute; khoảng c&aacute;ch v&agrave; độ nảy cao tạo cảm gi&aacute;c thoải m&aacute;i v&agrave; sự tiện dụng tối đa cho người d&ugrave;ng trong từng thao t&aacute;c nhập liệu. B&ecirc;n cạnh đ&oacute;, sự hiện diện của n&uacute;t trackpoint đặc trưng cũng gi&uacute;p người d&ugrave;ng thuận tiện hơn, c&ugrave;ng với đ&oacute; l&agrave; b&agrave;n di chuột cảm ứng touchpad được thiết kế rộng hơn đi k&egrave;m với 3 n&uacute;t chức năng gi&uacute;p bạn l&agrave;m việc dễ d&agrave;ng v&agrave; chuy&ecirc;n nghiệp hơn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-5/lenovo-ct-9.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bảo mật tối ưu với cảm biến v&acirc;n tay</strong></span></h5>\r\n\r\n<p>C&ocirc;ng nghệ cảm biến v&acirc;n tay tr&ecirc;n&nbsp;ThinkPad X Carbon 6 20KHS01900&nbsp;được h&atilde;ng t&iacute;ch hợp gọn ghẽ v&agrave; ho&agrave;n hảo tr&ecirc;n máy chỉ với một chạm bạn sẽ dễ d&agrave;ng đăng nhập v&agrave;o m&agrave;n h&igrave;nh desktop v&agrave; tăng cường khả năng bảo mật cho bạn.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hỗ trợ nhiều cổng kết nối</strong></span></h5>\r\n\r\n<p>Với chuẩn wifi 802.11ac nhanh gấp 6 lần so với chuẩn wifi 802.11n. Hỗ trợ nhiều cổng kết nối như cổng&nbsp;2 x USB 3.1 Gen 1 (one Always On), 2 x USB 3.1 Gen 2 Type-C / Intel Thunderbolt 3 (Power Delivery, DisplayPort, Data transfer), HDMI, HD webcam, Bluetooth, khe thẻ nhớ, jack tai nghe cho khả năng kết nối với c&aacute;c thiết bị kh&aacute;c một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Pin 3 cell</strong></span></h5>\r\n\r\n<p>Được trang bị pin 3 cell cho ph&eacute;p người d&ugrave;ng xem phim li&ecirc;n tục l&ecirc;n đến 8 giờ. C&ugrave;ng với c&ocirc;ng nghệ tiết kiệm pin, m&aacute;y c&oacute; thể hoạt động m&aacute;t, &ecirc;m &aacute;i v&agrave; tiết kiệm điện năng n&ecirc;n c&oacute; thể đ&aacute;p ứng tốt nhu cầu sử dụng b&igrave;nh thường.</p>\r\n', 5, '2019-07-07 07:08:59', 'Bình thường', 'Nổi bật', 1),
(54, 'Lenovo ThinkPad X1 Extreme 20MG0015VN Core i5-8400H/ Win10 Pro (15.6 FHD IPS)', 48879000, 53990000, 30, 'Chip: Intel Core i5-8400H (2.50GHz Upto 4.20GHz, 4Cores, 8Threads, 8MB cache, FSB 8GT/s)\r\n\r\nRAM: 16GB DDR4-2666MHz, 2 Slot, Max 64GB\r\n\r\nỔ cứng: 256GB SSD M.2 PCIe\r\n\r\nChipset đồ họa: NVIDIA GeForce GTX 1050Ti 4GB GDDR5 + Intel UHD Graphics 630\r\n\r\nMàn hình: 15.6-inch FHD (1920x1080) IPS, Anti-glare\r\n\r\nHệ điều hành: Windows 10 Pro bản quyền\r\n\r\nPin: 4-Cells 80Whr', 'lenovo7.jpg', 'lenovo7-1.jpg', 'lenovo7-2.jpg', 'lenovo7-3.jpg', 'lenovo7-4.jpg', 'lenovo7-5.jpg', 'lenovo7-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế đơn giản tinh tế</strong></span></h5>\r\n\r\n<p><strong>Laptop Lenovo ThinkPad X1 Extreme 20MG0015VN Core i5-8400H/ Win10 Pro (15.6 FHD IPS)</strong>&nbsp;được thiết kế đơn giản nhưng kh&ocirc;ng k&eacute;m phần sang trọng, tinh tế với lớp vỏ ngo&agrave;i cứng c&aacute;p gi&uacute;p bảo vệ tốt c&aacute;c linh kiện b&ecirc;n trong v&agrave; c&aacute;c g&oacute;c cạnh được bo tr&ograve;n nhẹ tạo cảm gi&aacute;c thoải m&aacute;i khi cầm nắm. Ngo&agrave;i ra, m&aacute;y c&ograve;n c&oacute; một vẻ ngo&agrave;i kh&aacute; mỏng nhẹ n&ecirc;n bạn c&oacute; thể bỏ n&oacute; v&agrave;o t&uacute;i x&aacute;ch hoặc balo v&agrave; mang theo b&ecirc;n m&igrave;nh đến bất cứ nơi n&agrave;o m&agrave; bạn muốn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-5/lenovo-6/lenovo-ct-10.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hiệu năng mượt m&agrave;</strong></span></h5>\r\n\r\n<p>Được tiếp sức mạnh bởi bộ vi xử l&yacute; Intel Core i5 thế hệ thứ 8 với bộ nhớ Ram 16GB DDR4, Lenovo ThinkPad X1 Extreme xử l&yacute; đa nhiệm mượt m&agrave; ổn định. Ổ cứng SSD 256GB gi&uacute;p m&aacute;y t&iacute;nh khởi động nhanh ch&oacute;ng chỉ trong v&agrave;i gi&acirc;y. B&ecirc;n cạnh đ&oacute;, m&aacute;y c&ograve;n được trang bị card đồ họa rời NVIDIA GeForce GTX 1050Ti 4GB GDDR5. Với cấu h&igrave;nh n&agrave;y, bạn c&oacute; thể chỉnh sửa h&igrave;nh ảnh/video hoặc chơi một số tựa game online đang được y&ecirc;u th&iacute;ch hiện nay.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-5/lenovo-6/lenovo-ct-11.jpg\" style=\"height:372px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>H&igrave;nh ảnh sắc n&eacute;t, &acirc;m thanh nổi trổi trong kh&ocirc;ng gian giải tr&iacute;</strong></span></h5>\r\n\r\n<p>C&aacute;ch thiết kế m&agrave;n h&igrave;nh viền mỏng hai cạnh b&ecirc;n mang đến diện t&iacute;ch hiển thị rộng hơn trong một kh&ocirc;ng gian tiết kiệm hơn để bạn tập trung v&agrave;o c&aacute;c nội dung hiển thị tr&ecirc;n m&agrave;n h&igrave;nh v&agrave; l&agrave;m mọi việc thật năng suất. M&agrave;n hinch 15.6 inch c&oacute; độ ph&acirc;n giải Full HD (1920 x 1080) kết hợp với tấm nền IPS mang đến cho bạn những ph&uacute;t gi&acirc;y giải tr&iacute; v&ocirc; c&ugrave;ng ấn tượng với những h&igrave;nh ảnh chi tiết sống động, m&agrave;u sắc trung thực. Đến với Lenovo ThinkPad X1 Extreme bạn sẽ được thưởng thức những &acirc;m thanh tuyệt vời c&ugrave;ng Dolby Atmos kể cả khi cắm tai nghe.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-5/lenovo-6/lenovo-ct-12.jpg\" style=\"height:480px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Được thiết kế theo ti&ecirc;u chuẩn qu&acirc;n đội</strong></span></h5>\r\n\r\n<p>Giống như tất cả c&aacute;c ThinkPad, ThinkPad X1 Extreme được kiểm tra theo 12 cấp độ qu&acirc;n sự v&agrave; hơn 200 b&agrave;i kiểm tra chất lượng để đảm bảo hiệu năng cao nhất. D&ograve;ng sản phẩm ThinkPad lu&ocirc;n trải qua một số thử nghiệm sản phẩm nghi&ecirc;m ngặt nhất trong ng&agrave;nh, do đ&oacute; bạn c&oacute; được sự pha trộn ho&agrave;n hảo của gi&aacute; trị v&agrave; độ bền. Từ v&ugrave;ng hoang d&atilde; Bắc cực đến b&atilde;o c&aacute;t sa mạc, m&ocirc;i trường kh&ocirc;ng trọng lực, bạn c&oacute; thể ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m về độ b&ecirc;n của ch&uacute;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-5/lenovo-6/lenovo-ct-13.jpg\" style=\"height:279px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m &ecirc;m, độ nảy tốt</strong></span></h5>\r\n\r\n<p>B&agrave;n ph&iacute;m được thiết kế theo dạng Full Size với độ rộng giữa c&aacute;c ph&iacute;m được sắp xếp hợp l&yacute;, c&oacute; nộ nảy tốt gi&uacute;p việc g&otilde; ph&iacute;m nhanh v&agrave; &iacute;t nhầm lẫn hơn. Hơn nữa, với việc c&oacute; th&ecirc;m h&agrave;ng ph&iacute;m phụ gi&uacute;p người d&ugrave;ng t&iacute;nh to&aacute;n hay nhập số liệu nhanh hơn kh&aacute; nhiều.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-5/lenovo-6/lenovo-ct-14.jpg\" style=\"height:480px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Cổng kết nối đa dạng</strong></span></h5>\r\n\r\n<p>Để đảm bảo khả năng kết nối v&agrave; trao đổi dữ liệu với c&aacute;c thiết bị kh&aacute;c, m&aacute;y đ&atilde; được lắp đặt đầy đủ c&aacute;c cổng kết nối cần thiết như cổng USB 3.1, USB-C Thunderbolt 3, HDMI.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Thời lượng Pin ấn tượng</strong></span></h5>\r\n\r\n<p>ThinkPad X1 Extreme cho thời lượng pin l&ecirc;n tới 14 giờ. Nếu bạn sắp hết pin, c&ocirc;ng nghệ sạc nhanh Rapid Charge sẽ sạc Pin l&ecirc;n đến 80% chỉ sau một giờ. V&igrave; vậy, với thời gian nghỉ trưa hoặc nghỉ ngơi trong 60 ph&uacute;t c&oacute; thể dễ d&agrave;ng tăng pin l&ecirc;n đến 12 giờ.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-5/lenovo-6/lenovo-ct-15.jpg\" style=\"height:246px; width:750px\" /></p>\r\n', 5, '2019-06-25 06:38:16', 'Bình thường', 'Bình thường', 1),
(55, 'Acer Predator Triton 500 PT515-51-79ZP NH.Q4WSV.002 Core i7', 73990000, 76990000, 30, 'CPU: Intel Core i7-8750H (2.2 GHz - 4.1 GHz / 9MB / 6 nhân, 12 luồng)\r\n\r\nMàn hình: 15.6\" IPS (1920 x 1080), không cảm ứng\r\n\r\nRAM: 32GB DDR4 2666MHz\r\n\r\nĐồ họa: Intel UHD Graphics 630 / NVIDIA GeForce RTX 2080 8GB GDDR6\r\n\r\nLưu trữ: 512GB SSD M.2 NVMe\r\n\r\nHệ điều hành: Windows 10 Home SL 64-bit\r\n\r\nPin: 4 cell 82 Wh, Pin liền', 'acer6.jpg', 'acer6-1.jpg', 'acer6-2.jpg', 'acer6-3.jpg', 'acer6-4.jpg', 'acer6-5.jpg', 'acer6-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế d&agrave;nh ri&ecirc;ng cho game thủ</strong></span></h5>\r\n\r\n<p><strong>Laptop Acer Predator Triton 500 PT515-51-79ZP NH.Q4WSV.002</strong>&nbsp;l&agrave; d&ograve;ng m&aacute;y được thiết kế d&agrave;nh ri&ecirc;ng cho những game thủ thực thụ, Predator Triton 500 nổi bật với thiết kế sắc xảo v&agrave; tinh tế, mỗi chi tiết hướng đến trải nghiệm gaming tốt nhất d&agrave;nh cho game thủ. Vỏ ngo&agrave;i của Triton 500 l&agrave; vỏ nh&ocirc;m nguy&ecirc;n khối, kết hợp với logo c&oacute; đ&egrave;n led v&agrave; hốc tản nhiệt m&agrave;u Electric Blue khiến cho Triton 500 được nhận diện từ xa l&agrave; một th&agrave;nh vi&ecirc;n của gia đ&igrave;nh Predator. Tuy l&agrave; laptop gaming nhưng với trọng lượng nhẹ ấn tượng chỉ 2.1 kg v&agrave; độ mỏng 17.9mm, Predator Triton 500 kh&ocirc;ng qu&aacute; khổ như những chiếc laptop gaming kh&aacute;c m&agrave; mang một n&eacute;t thanh tao nhưng vẫn hầm hố chuẩn Predator.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/acer-3/acer-ct-1.jpg\" style=\"height:900px; width:1600px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m LED RGB nổi bật</strong></span></h5>\r\n\r\n<p>B&agrave;n ph&iacute;m với c&aacute;c cụm WASD v&agrave; cụm ph&iacute;m số được l&agrave;m nổi bật, chuyển m&agrave;u h&agrave;i h&ograve;a theo sự thay đổi m&agrave;u của b&agrave;n ph&iacute;m led RGB với 3 zone m&agrave;u chủ đạo. M&agrave;n h&igrave;nh của Triton 500 c&oacute; viền mỏng chỉ 6.3 mm, gi&uacute;p chiếc m&aacute;y đạt tỷ lệ m&agrave;n h&igrave;nh so với th&acirc;n m&aacute;y l&agrave; 81%. V&ograve;ng bo m&agrave;n h&igrave;nh ngũ gi&aacute;c g&oacute;c cạnh kết hợp với viền m&agrave;n h&igrave;nh si&ecirc;u mỏng v&agrave; khả năng mở m&agrave;n h&igrave;nh g&oacute;c 180 độ khiến thiết kế của Acer Triton 500 l&agrave; thiết kế gaming độc đ&aacute;o kh&ocirc;ng thể nhầm lẫn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/acer-3/acer-ct-2.jpg\" style=\"height:900px; width:1600px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Quạt tản nhiệt AeroBlade 3D mới thế hệ thứ 4</strong></span></h5>\r\n\r\n<p>Acer Predator Triton 500 PT515-51-79ZP NH.Q4WSV.002 được t&iacute;ch hợp quạt tản nhiệt AeroBlade 3D mới thế hệ thứ 4, phi&ecirc;n bản 2019, một lần nữa vượt ngưỡng mong đợi với hiệu năng tản nhiệt hơn 45%. Thiết kế quạt c&oacute; c&aacute;c r&atilde;nh răng cưa được m&ocirc; phỏng theo cấu tạo độc đ&aacute;o của c&aacute;nh chim c&uacute; m&egrave;o, gi&uacute;p giảm tiếng động ph&aacute;t ra từ hệ thống tản nhiệt, hỗ trợ hệ thống vận h&agrave;nh mạnh mẽ hơn v&agrave; ho&agrave;n to&agrave;n y&ecirc;n tĩnh. Đồng thời ở đầu cuối mỗi c&aacute;nh quạt được gia cố th&ecirc;m c&aacute;c c&aacute;nh phụ (wingtips) nhằm tăng chắc chắn cho mỗi c&aacute;nh quạt. N&uacute;t Turbo được thiết kế tiện lợi đặt ngay tr&ecirc;n cụm WASD cho ph&eacute;p tăng th&ecirc;m tốc độ quạt l&ecirc;n ngay lập tức, gi&uacute;p nhiệt độ hệ thống giữ ở mức ổn định đồng thời &quot;giữ nhiệt&quot; cho những trận combat đầy h&agrave;o hứng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/acer-3/acer-ct-3.jpg\" style=\"height:900px; width:1600px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Cấu h&igrave;nh mạnh mẽ</strong></span></h5>\r\n\r\n<p>Acer Predator Triton 500 PT515-51-79ZP NH.Q4WSV.002 sử dụng Intel Core i7-8750H chip thuộc thế hệ thứ 8 của d&ograve;ng i7 với 6 nh&acirc;n &ndash; 12 luồng gi&uacute;p độ xung nhịp l&ecirc;n tới 2.2 GHz &ndash; 4.1 GHz v&ocirc; c&ugrave;ng mạnh mẽ gần như tương đương với những sản phẩm đứng đầu hiện nay. Người d&ugrave;ng được trải nghiệm một thiết bị Laptop chạy đa nhiệm v&ocirc; c&ugrave;ng tốt m&agrave; kh&ocirc;ng ảnh hưởng đến chất lượng.&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/acer-3/acer-ct-4.jpg\" style=\"height:545px; width:850px\" /></p>\r\n\r\n<p>M&aacute;y sử dụng 1 ram 32GB DDR4 2666MHz thuộc dạng ram c&oacute; tốc độ rất cao v&agrave; ph&ugrave; hợp với rất nhiều đối tượng, kh&ocirc;ng quan t&acirc;m người sử dụng l&agrave; 1 game thủ hay designer (2 đối tượng y&ecirc;u cầu nhiều nhất) cũng đều được đ&aacute;p ứng. Ưu điểm nằm ở ch&iacute;nh sự vận h&agrave;nh của n&oacute; với khả năng chạy nhiều tab vụ cũng như chạy tốt c&aacute;c ứng dụng y&ecirc;u cầu nhiều bộ nhớ flash như Adobe photoshop, Adobe after effect, tr&igrave;nh duyệt tốn nhiều&nbsp; dung lượng như Chrome, Cốc cốc, hay ngay cả những game cấu h&igrave;nh cao như PUBG, Overwatch, Destiny 2, với 2 khe cắm, người d&ugrave;ng c&oacute; thể tận dụng để n&acirc;ng cấp Ram.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/acer-3/acer-ct-5.jpg\" style=\"height:875px; width:1600px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Card đồ họa NVIDIA GeForce RTX 2080</strong></span></h5>\r\n\r\n<p>Card m&agrave;n h&igrave;nh NVIDIA GeForce RTXTM 2060 8G-GDDR6 thuộc thế hệ card mới nhất hiện nay, c&oacute; sức mạnh đ&aacute;ng nể hỗ trợ tất cả c&aacute;c game với đồ họa si&ecirc;u khủng. C&oacute; thể được xem như thuộc top 5 của thế hệ card c&oacute; chất lượng cao nhất hiện nay. Sở hữu c&ocirc;ng nghệ xử l&yacute; &aacute;nh s&aacute;ng đồ họa chưa từng c&oacute; tr&ecirc;n kiến tr&uacute;c Pascal gi&uacute;p m&ocirc; tả lại &aacute;nh s&aacute;ng như thực v&agrave; thể hiện được r&otilde; những h&igrave;nh ảnh tối. RTX 2060 hỗ trợ cho Acer Predator Triton 500 c&oacute; thể chơi được c&aacute;c game ở mức cấu h&igrave;nh High Setting điển h&igrave;nh như PUBG, Shadow of the Tomb Raider.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/acer-3/acer-ct-6.jpg\" style=\"height:900px; width:1600px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh 15,6 inch viền si&ecirc;u mỏng</strong></span></h5>\r\n\r\n<p>Acer Predator Triton 500 PT515-51-79ZP NH.Q4WSV.002 được trang bị m&agrave;n h&igrave;nh 15,6 inch viền si&ecirc;u mỏng Full HD IPS 300 nits c&oacute; độ ph&acirc;n giải 1920 x 1080. Với tấm nền IPS người d&ugrave;ng sẽ nhận được &aacute;nh s&aacute;ng r&otilde; hơn, g&oacute;c nh&igrave;n rộng hơn từ đ&oacute; tăng th&ecirc;m phần trăm chiến thằng. B&ecirc;n cạnh đ&oacute; &aacute;p dụng c&ocirc;ng nghệ Acer ComfyView&nbsp; m&agrave; tốc độ phản hồi 3ms, tần số qu&eacute;t h&igrave;nh ảnh l&ecirc;n tới 144Hz. Tốc độ phản hồi đ&aacute;ng mơ ước của c&aacute;c game thủ&nbsp; khi m&agrave; m&agrave;n h&igrave;nh nền IPS th&ocirc;ng thường chỉ c&oacute; khoảng 5-6ms, với tần số qu&eacute;t l&ecirc;n tới 144Hz tương đương 1 gi&acirc;y m&agrave;n h&igrave;nh hiển thị 144 h&igrave;nh ảnh đưa đến người sử dụng những h&igrave;nh ảnh li&ecirc;n tục kh&ocirc;ng ngắt qu&atilde;ng &ndash; rất hữu &iacute;ch đối với c&aacute;c thể loại game FPS bởi t&iacute;nh chớp thời cơ v&ocirc; c&ugrave;ng quan trọng. C&ocirc;ng nghệ NVIDIA G-SYNC gi&uacute;p ổn định h&igrave;nh ảnh, kh&ocirc;ng bị giật hay x&eacute; khung h&igrave;nh khi game thủ phải chinh chiến trong những trận đấu căng thẳng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/acer-3/acer-ct-7.jpg\" style=\"height:900px; width:1600px\" /></p>\r\n', 7, '2019-06-26 06:38:16', 'Bình thường', 'Bình thường', 1),
(56, 'Acer Predator Helios 300 PH315-51-77BQ NH.Q4HSV.001 Core i7', 40990000, 42990000, 18, 'Chip: Intel Core i7-8750H (2.2GHz up to 4.1GHz, 9MB)\r\n\r\nRAM: 8GB DDR4 2666MHz (2x SO-DIMM socket, up to 32GB SDRAM)\r\n\r\nỔ cứng: HDD 1TB 5400rpm + 256GB SSD PCIE G3X4\r\n\r\nChipset đồ họa: NVIDIA GeForce GTX 1060 6GB GDDR5\r\n\r\nMàn hình: 15.6 inch Full HD (1920 x 1080) IPS, Anti-Glare, 144Hz/ 3ms\r\n\r\nHệ điều hành: Windows 10 Home\r\n\r\nPin: 4 Cell 48 Whrs', 'acer7.jpg', 'acer7-1.jpg', 'acer7-2.jpg', 'acer7-3.jpg', 'acer7-4.jpg', 'acer7-5.jpg', 'acer7-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế vỏ kim loại chắc chắn</strong></span></h5>\r\n\r\n<p><strong>Laptop Acer Predator Helios 300 PH315-51-77BQ NH.Q4HSV.001</strong>&nbsp;sở hữu thiết kế vỏ kim loại mang lại cảm gi&aacute;c chắc chắn, đặc trưng của d&ograve;ng Predator với kiểu d&aacute;ng g&oacute;c cạnh hầm hố, hốc tản nhiệt lớn. Bề mặt laptop l&agrave; v&acirc;n phay xước bền bỉ v&agrave; hạn chế b&aacute;m bẩn, nước sơn đen hoạ tiết th&ecirc;m đường chỉ đỏ đặc trưng của d&ograve;ng laptop gaming Acer v&agrave; đặc biệt, ch&iacute;nh giữa l&agrave; logo Predator l&agrave;m tăng th&ecirc;m độ ngầu của m&aacute;y.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/acer-4/acer-ct-8.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh 15.6 inch Full HD</strong></span></h5>\r\n\r\n<p>Sở hữu m&agrave;n h&igrave;nh 15.6 inch gi&uacute;p hiển thị h&igrave;nh ảnh Full HD (1920 x 1080) để xem mọi thứ tốt hơn, cùng c&ocirc;ng nghệ màn hình LED Backlit mang đến những h&igrave;nh ảnh chất lượng, chi tiết, r&otilde; n&eacute;t, m&agrave;u sắc cũng cực kỳ trung thực v&agrave; sống động.&nbsp;Tấm nền IPS, Anti-glare cho m&agrave;u sắc trung thực ở mọi g&oacute;c độ gi&uacute;p game thủ c&oacute; những ph&uacute;t gi&acirc;y trải nghiệm game tuyệt vời nhất, gi&uacute;p bạn c&oacute; thể trải nghiệm tốt hơn ở m&ocirc;i trường b&ecirc;n ngo&agrave;i, c&oacute; khả năng chống ch&oacute;i tốt.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/acer-4/acer-ct-9.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bộ vi xử l&yacute; Intel Core i7</strong></span></h5>\r\n\r\n<p>Được trang bị bộ vi xử l&yacute; Intel Core i7-8750H tốc độ l&ecirc;n đ&ecirc;́n 4.1GHz, đem lại cho m&aacute;y một sức mạnh xử l&yacute; ổn định v&agrave; tiết kiệm điện năng hiệu quả. C&ugrave;ng bộ nhớ RAM DDR4 dung lượng 8GB cho hiệu suất xử l&yacute; tốt cũng như đ&aacute;p ứng c&aacute;c t&aacute;c vụ đa nhiệm mượt m&agrave; c&aacute;c ứng dụng. Chipset đồ họa NVIDIA GeForce GTX 1060 hỗ trợ xử l&yacute; hiệu ứng h&igrave;nh ảnh 3D c&aacute;c c&ocirc;ng việc li&ecirc;n quan đến đồ hoạ, giải tr&iacute; của bạn. Ngo&agrave;i ra, Acer&nbsp;c&ograve;n trang bị cho m&aacute;y ổ cứng HDD dung lượng 1TB và &ocirc;̉ 256GB SSD gi&uacute;p m&aacute;y hoạt động mượt m&agrave; hơn, khởi động nhanh ch&oacute;ng hơn.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hệ thống l&agrave;m m&aacute;t</strong></span></h5>\r\n\r\n<p>Hệ thống 2 quạt tản nhiệt bằng th&eacute;p với 59 c&aacute;nh quạt &aacute;p dụng c&ocirc;ng nghệ AeroBlade 3D độc quyền của Acer tăng luồng gi&oacute; th&ecirc;m 25%, đảm bảo hệ thống lu&ocirc;n m&aacute;t mẻ khi chơi game trong thời gian d&agrave;i.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Ứng dụng PredatorSense</strong></span></h5>\r\n\r\n<p>Phần mềm PredatorSense gi&uacute;p game thủ c&oacute; thể dễ d&agrave;ng t&ugrave;y chỉnh c&aacute;c thiết lập cho hệ thống như điều chỉnh tốc độ quạt, theo d&otilde;i nhiệt độ của CPU, GPU.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m LED backlit m&agrave;u đỏ nổi bật v&agrave; touchpad đa điểm nhạy b&eacute;n</strong></span></h5>\r\n\r\n<p>Laptop c&oacute; b&agrave;n ph&iacute;m được gia c&ocirc;ng tốt, cảm gi&aacute;c g&otilde; tốt, kết hợp với cụm WASD m&agrave;u đỏ nổi bật đậm chất gaming. Ngo&agrave;i ra hệ thống đ&egrave;n LED backlit m&agrave;u đỏ nổi bật gi&uacute;p game thủ c&oacute; thể thao t&aacute;c ch&iacute;nh x&aacute;c trong những trận chiến game khốc liệt kể cả ng&agrave;y hay đ&ecirc;m. Thiết kế b&agrave;n ph&iacute;m c&acirc;n đối n&ecirc;n nh&igrave;n v&agrave;o kh&aacute; bắt mắt. Touchpad cảm ứng đa điểm nhạy b&eacute;n theo từng thao t&aacute;c chạm.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/acer-4/acer-ct-10.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>USB Type C &ndash; tất cả trong một kết nối</strong></span></h5>\r\n\r\n<p>Laptop sở hữu cổng kết nối USB Type C đa dụng nhất hiện nay, với thiết kế đồng đẳng gi&uacute;p bạn c&oacute; thể cắm cổng kết nối ở bất cứ chiều n&agrave;o. Ngo&agrave;i ra với c&ocirc;ng nghệ n&agrave;y sẽ cho tốc độ truyền tải dữ liệu nhanh gấp 10 lần so với c&aacute;c cổng kết nối cũ hiện nay.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/acer-4/acer-ct-11.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<p>Acer Predator Helios 300 PH315-51-77BQ NH.Q4HSV.001&nbsp;còn hỗ trợ nhiều cổng kết nối như cổng HDMI, Bluetooth, khe thẻ nhớ, jack tai nghe cho khả năng kết nối với c&aacute;c thiết bị kh&aacute;c một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Thời lượng pin l&acirc;u hơn</strong></span></h5>\r\n\r\n<p>Được thiết kế để ph&ugrave; hợp với phong c&aacute;ch sống năng động hiện nay,&nbsp;Acer Predator Helios 300 PH315-51-77BQ NH.Q4HSV.001&nbsp;sẽ cung cấp cho bạn thời lượng pin để c&oacute; thể sử dụng suốt cả ng&agrave;y. Với c&ocirc;ng nghệ pin Lithium Polymer sẽ cho m&aacute;y khả năng vận h&agrave;nh li&ecirc;n tục l&ecirc;n đến 7 tiếng đồng hồ. Điểm đặc biệt của d&ograve;ng pin Lithium Polymer c&oacute; thể duy tr&igrave; được năng lượng sạc lần đầu cho d&ugrave; sau h&agrave;ng trăm chu kỳ sạc, l&acirc;u hơn gấp 3 lần so với c&aacute;c loại pin ti&ecirc;u chuẩn hiện nay, gi&uacute;p bạn khỏi phải lo lắng về hao m&ograve;n hao m&ograve;n điện năng pin.</p>\r\n', 7, '2019-06-26 06:38:16', 'Bình thường', 'Bình thường', 1);
INSERT INTO `product` (`id`, `product_name`, `price_new`, `price_old`, `qty_product`, `product_desc`, `product_thumb`, `list_thumb_1`, `list_thumb_2`, `list_thumb_3`, `list_thumb_4`, `list_thumb_5`, `list_thumb_6`, `product_content`, `cat_id`, `created_at`, `selling_products`, `featured_products`, `status`) VALUES
(57, 'Acer Aspire 7 A717-72G-57Y3 NH.GXDSV.002 Core i5-8300H/ Dos', 21990000, 24990000, 28, 'Chip: Intel Core i5-8300H (2.3GHz up to 4.0GHz 8MB)\r\n\r\nRAM: 8GB DDR4 2666MHz (2x SO-DIMM socket, up to 32GB SDRAM)\r\n\r\nỔ cứng: HDD 1TB 5400rpm, x1 slot SSD M.2 (SATA/ PCIe)\r\n\r\nChipset đồ họa: NVIDIA GeForce GTX 1050 4GB GDDR5 + Intel UHD Graphics 630\r\n\r\nMàn hình: 17.3 inch Full HD (1920 x 1080) IPS, Anti-Glare\r\n\r\nHệ điều hành: Free Dos\r\n\r\nPin: 4 Cell 3220 mAh', 'acer8.jpg', 'acer8-1.jpg', 'acer8-2.jpg', 'acer8-3.jpg', 'acer8-4.jpg', 'acer8-5.jpg', 'acer8-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế tinh tế</strong></span></h5>\r\n\r\n<p><strong>Laptop Acer Aspire 7 A717-72G-57Y3 NH.GXDSV.002</strong><strong>&nbsp;</strong>được thiết kế kh&aacute; mỏng, c&aacute;c cạnh được bo tr&ograve;n mềm mại kết hợp bề mặt với hoa văn sần mịn lấy &yacute; tưởng từ d&ograve;ng nước chảy, cho cảm gi&aacute;c cầm nắm chắc tay v&agrave; kh&ocirc;ng b&aacute;m bụi, v&acirc;n tay qu&aacute; nhiều.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/acer-5/acer-ct-12.jpg\" style=\"height:633px; width:900px\" /></p>\r\n\r\n<p>Viền m&aacute;y c&oacute; những vết cắt kh&aacute; sắc, mang lại sự cứng c&aacute;p hơn cho chiếc m&aacute;y, nhỏ gọn gi&uacute;p người d&ugrave;ng c&oacute; thể dễ d&agrave;ng bỏ m&aacute;y v&agrave;o trong t&uacute;i x&aacute;ch, balo v&agrave; mang theo b&ecirc;n m&igrave;nh để l&agrave;m việc m&agrave; kh&ocirc;ng cảm thấy vướng v&iacute;u kh&oacute; chịu.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh 17.3 inch Full HD</strong></span></h5>\r\n\r\n<p>M&aacute;y được trang bị m&agrave;n h&igrave;nh 17.3 inch độ ph&acirc;n giải Full HD (1920 x 1080) c&ugrave;ng tấm nền IPS chống l&oacute;a cho g&oacute;c nh&igrave;n rộng l&ecirc;n đến 178 độ. Ngo&agrave;i ra, bản lề m&aacute;y cho ph&eacute;p mở l&ecirc;n đến 180 độ. Điều n&agrave;y gi&uacute;p cho người d&ugrave;ng thấy thoải m&aacute;i với nhiều g&oacute;c độ l&agrave;m việc kh&aacute;c nhau với m&aacute;y.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/acer-5/acer-ct-13.jpg\" style=\"height:590px; width:900px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Cấu h&igrave;nh mạnh mẽ</strong></span></h5>\r\n\r\n<p>B&ecirc;n trong thiết kế thanh lịch của Acer Aspire 7 l&agrave; bộ đ&ocirc;i xử l&iacute; cực khỏe CPU thế hệ 7 của Intel Core i5-8300H, RAM 8GB DDR4 cho khả năng chạy nhiều chương tr&igrave;nh t&aacute;c vụ c&ugrave;ng l&uacute;c m&agrave; kh&ocirc;ng lo giật lag, &ocirc;̉ cứng 1TB cho kh&ocirc;ng gian lưu trữ rộng lớn. K&egrave;m theo đ&oacute; l&agrave; Chip đồ họa Intel UHD Graphics 630 t&iacute;ch hợp trong CPU, card đồ họa rời GeForce GTX 1050 2GB GDDR5 của NVIDIA cho m&aacute;y c&oacute; hiệu năng mạnh mẽ, đủ để thực hiện c&aacute;c t&aacute;c vụ y&ecirc;u cầu khả năng đồ họa kh&aacute; như photoshop, hay chơi game.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/acer-5/acer-ct-14.jpg\" style=\"height:672px; width:900px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&aacute;y ảnh HD v&agrave; hệ thống microphone</strong></span></h5>\r\n\r\n<p>Thưởng thức c&aacute;c cuộc gọi điện video chất lượng cao đến gia đ&igrave;nh v&agrave; bạn b&egrave; bằng m&aacute;y ảnh HD v&agrave; hệ thống microphone k&eacute;p được t&iacute;ch hợp sẵn với c&ocirc;ng nghệ giảm tiếng ồn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/acer-5/acer-ct-15.jpg\" style=\"height:529px; width:900px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hệ thống tản nhiệt</strong></span></h5>\r\n\r\n<p>Hệ thống tản nhiệt Acer Aspire 7 c&oacute; 2 quạt tản nhiệt nằm kề nhau v&agrave; kh&aacute; gần vị tr&iacute; của CPU v&agrave; GPU. Điều n&agrave;y rất c&oacute; &yacute; nghĩa v&igrave; bộ đ&ocirc;i quạt hầu như sẽ l&agrave;m m&aacute;t tức th&igrave; cho CPU v&agrave; GPU. Mặt dưới m&aacute;y l&agrave; h&agrave;ng loạt c&aacute;c khe tản nhiệt đặt tại c&aacute;c vị tr&iacute; c&oacute; mức sản sinh nhiệt cao, quạt h&uacute;t tản nhiệt sẽ đảm bảo nhiệt độ ổn định cho hệ thống.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/acer-5/acer-ct-16.jpg\" style=\"height:609px; width:900px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>&Acirc;m thanh chất lượng</strong></span></h5>\r\n\r\n<p>Bạn sẽ được trải nghiệm chất lượng &acirc;m thanh đỉnh cao với chuy&ecirc;n gia &acirc;m thanh Dolby Audio Premium k&iacute;ch hoạt những phút gi&acirc;y giải tr&iacute; của bạn bằng c&aacute;ch cung cấp &acirc;m thanh chất lượng rạp h&aacute;t tại nh&agrave;.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/acer-5/acer-ct-17.jpg\" style=\"height:867px; width:900px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m đ&egrave;n nền LED trắng v&agrave; Touchpad</strong></span></h5>\r\n\r\n<p>B&agrave;n ph&iacute;m Acer Aspire 7 có bề mặt ph&iacute;m mịn cho cảm gi&aacute;c chạm v&agrave; vuốt tốt. Ngo&agrave;i ra, Acer cũng trang bị đ&egrave;n nền LED trắng với độ s&aacute;ng vừa đủ Trackpad b&ecirc;n dưới keyboard c&oacute; độ lớn vừa phải, c&oacute; viền kim loại s&aacute;ng trắng xung quanh gi&uacute;p dễ nhận diện trong điều kiện thiếu s&aacute;ng. Cảm ứng v&acirc;n tay tr&ecirc;n đ&oacute; cũng kh&aacute; hữu dụng khi đăng nhập Windows.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/acer-5/acer-ct-18.jpg\" style=\"height:611px; width:900px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>USB Type C &ndash; tất cả trong một kết nối</strong></span></h5>\r\n\r\n<p>Laptop sở hữu cổng kết nối USB Type C đa dụng nhất hiện nay, với thiết kế đồng đẳng gi&uacute;p bạn c&oacute; thể cắm cổng kết nối ở bất cứ chiều n&agrave;o. Ngo&agrave;i ra với c&ocirc;ng nghệ n&agrave;y sẽ cho tốc độ truyền tải dữ liệu nhanh gấp 10 lần so với c&aacute;c cổng kết nối cũ hiện nay.</p>\r\n\r\n<p>Sở hữu c&aacute;c c&ocirc;ng kết nối đa dạng như cổng HDMI, 1 khe cắm thẻ nhớ, 1 jack cắm tai nghe, giúp bạn k&ecirc;́t n&ocirc;́i m&ocirc;̣t cách d&ecirc;̉ dàng dù đang ở b&acirc;́t cứ nơi đ&acirc;u.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Thời lượng pin để c&oacute; thể sử dụng suốt cả ng&agrave;y</strong></span></h5>\r\n\r\n<p>Được thiết kế để ph&ugrave; hợp với phong c&aacute;ch sống năng động hiện nay,&nbsp;Acer Aspire 7 A717-72G-57Y3 NH.GXDSV.002&nbsp;sẽ cung cấp cho bạn thời lượng pin để c&oacute; thể sử dụng suốt cả ng&agrave;y. Với c&ocirc;ng nghệ pin Lithium Polymer 48 Whr sẽ cho m&aacute;y khả năng vận h&agrave;nh li&ecirc;n tục l&ecirc;n đến 7 tiếng đồng hồ. Điểm đặc biệt của d&ograve;ng pin Lithium Polymer c&oacute; thể duy tr&igrave; được năng lượng sạc lần đầu cho d&ugrave; sau h&agrave;ng trăm chu kỳ sạc, l&acirc;u hơn gấp 3 lần so với c&aacute;c loại pin ti&ecirc;u chuẩn hiện nay, gi&uacute;p bạn kh&otilde;i phải lo lắng về hao m&ograve;n hao m&ograve;n điện năng pin.</p>\r\n', 7, '2019-07-04 08:48:32', 'Bình thường', 'Bình thường', 1),
(58, 'MSI Prestige P65 Creator 8RE-069VN Core i7-8750H/ GTX 1060 6GB/ Win10', 44990000, 47990000, 30, 'Chip: Intel Core i7-8750H ( 2.2 GHz - 4.1 GHz / 9MB / 6 nhân, 12 luồng )\r\n\r\nRAM: 2 x 8GB DDR4 2666MHz ( 2 Khe cắm / Hỗ trợ tối đa 32GB )\r\n\r\nỔ cứng: 256GB SSD M.2 NVMe\r\n\r\nChipset đồ họa: Intel UHD Graphics 630 / NVIDIA GeForce GTX 1060 6GB GDDR5\r\n\r\nMàn hình: 15.6\" IPS (1920 x 1080) không cảm ứng\r\n\r\nHệ điều hành: Windows 10 Home SL 64-bit\r\n\r\nPin: 4 cell 80 Wh', 'msi4.jpg', 'msi4-1.jpg', 'msi4-2.jpg', 'msi4-3.jpg', 'msi4-4.jpg', 'msi4-5.jpg', 'msi4-6.jpg', '<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh 15.6 inch với tấm nền IPS</strong></span></h5>\r\n\r\n<p><strong>Laptop MSI Prestige P65 Creator 8RE-069VN Core i7-8750H/ GTX 1060 6GB/ Win10 (15.6 FHD IPS)</strong>&nbsp;sở hữu m&agrave;n h&igrave;nh 15.6 inch thể hiện sự hiện diện của tấm nền IPS mang lại g&oacute;c nh&igrave;n l&ecirc;n tới 178 độ, ph&aacute;t triển với hiệu chỉnh c&ocirc;ng nghệ TrueColor MSI mang lại sự thể hiện được gần đủ 100% sRGB. Viền v&ocirc; c&ugrave;ng mỏng chỉ 4.9 mm b&ecirc;n cạnh đ&oacute; l&agrave; khả năng thể hiện h&igrave;nh ảnh Full HD với ti&ecirc;u chuẩn h&igrave;nh ảnh c&oacute; độ ph&acirc;n giải l&ecirc;n tới 1920 x 1080.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/msi4/msi-ct-1.jpg\" style=\"height:446px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hiệu năng mượt m&agrave; với vi xử l&yacute; thế hệ thứ 8</strong></span></h5>\r\n\r\n<p>Sản phẩm sử dụng chip Intel Core thế hệ thứ 8 thuộc d&ograve;ng i7, i7-8750H với 6 nh&acirc;n, 12 luồng mang đến nhịp xung l&ecirc;n tới 2.2GHz &ndash; 4.1 GHz. Cải thiện hơn khoảng 40% so với đời đầu l&agrave; kết quả so s&aacute;nh được c&aacute;c nh&agrave; thiết kế nghi&ecirc;n cứu. Với d&ograve;ng chip n&agrave;y, người d&ugrave;ng c&oacute; thể tin tưởng c&oacute; thể d&ugrave;ng trong 1 khoảng thời gian rất d&agrave;i m&agrave; kh&ocirc;ng c&ograve;n lo lỗi thời.</p>\r\n\r\n<p>Kết hợp với 2 RAM 8GB DDR4 2666MHz v&agrave; hỗ trợ th&ecirc;m 1 khe cắm hỗ trợ tối đa l&ecirc;n tới 32GB. Với 16GB dung lượng cao, người sử dụng c&oacute; thể chạy những ứng dụng l&agrave;m việc lớn như Adobe After Effect, Adobe Photoshop hay cả Adobe Illustrator&nbsp; m&agrave; kh&ocirc;ng c&ograve;n lo m&aacute;y kh&ocirc;ng thể đ&aacute;p ứng. Với dung lượng RAM lớn, người d&ugrave;ng kh&ocirc;ng chỉ c&oacute; thể sử dụng nhiều ứng dụng m&agrave; sử dụng nhiều tab ở Chrome hay Cốc cốc cũng kh&ocirc;ng th&agrave;nh vấn đề, thể thoải m&aacute;i chơi tất cả c&aacute;c dạng game v&agrave; c&oacute; thể sử dụng h&agrave;ng trăm tab tr&igrave;nh duyệt.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Chip đồ họa Intel UHD Graphics 630</strong></span></h5>\r\n\r\n<p>MSI Prestige P65 Creator được trang bị chip đồ họa Intel UHD Graphics 630 thuộc v&agrave;o dạng cao nhất hiện nay với hiệu năng được thử nghiệm qua 3D Mark Cloud Gate (đ&aacute;nh gi&aacute; về khả năng xử l&yacute; đồ họa game) Intel HD Graphics 630 nhanh hơn khoảng 10%-15% so với d&ograve;ng UHD Graphics 620 trước đ&oacute; (2015). Trải nghiệm những tựa game c&oacute; cấu h&igrave;nh cao nhất hiện nay, UHD Graphics 630 thường c&oacute; mức độ fps cao hơn khoảng từ 5 đến 10 so với d&ograve;ng trước đ&oacute;, ngo&agrave;i ra với c&aacute;c game c&oacute; đồ họa mạnh hiện nay th&igrave; chỉ c&oacute; UHD Graphics 630 c&oacute; thể hoạt động được.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute; kh&ocirc;ng thể kh&ocirc;ng nhắc tới Card đồ họa NVIDIA GeForce GTX 1060 6G GDDR5 hỗ trợ v&ocirc; c&ugrave;ng lớn để đưa 1 chiếc m&aacute;y t&iacute;nh thường trở th&agrave;nh 1 laptop gaming thật sự với sức mạnh đ&aacute;p ứng những tựa game nặng nhất hiện nay. Với d&ograve;ng CPU GeForce Max-Q n&agrave;y, mang lại cho người d&ugrave;ng trải nghiệm độ mỏng v&agrave; y&ecirc;n tĩnh h&agrave;ng đầu thế giới. Cũng nhờ c&oacute; card m&agrave;n h&igrave;nh tới từ Nvidia m&agrave; chiếc laptop n&agrave;y ho&agrave;n to&agrave;n c&oacute; thể chịu tr&aacute;ch nhiệm cho những c&ocirc;ng việc đ&ograve;i hỏi tới c&aacute;c t&aacute;c vụ đồ họa nặng nề như Edit, Render V</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Khả năng lưu trữ ho&agrave;n hảo</strong></span></h5>\r\n\r\n<p>Đặc biệt, chiếc laptop n&agrave;y sử dụng 1 ổ cứng 256GB SSD PCle NVMe thuộc loại c&oacute; tốc độ nhanh nhất hiện nay với 256GB người d&ugrave;ng c&oacute; thể sẽ phải lựa chọn số lượng game của m&igrave;nh để kh&ocirc;ng gặp giới hạn về mặt bộ nhớ.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Trang bị đa dạng cổng kết nối</strong></span></h5>\r\n\r\n<p>Thiết bị được trang bị 3 cổng USB 3.0, audio combo, 1 cổng v&agrave;o HDMI th&ocirc;ng dụng. V&agrave; c&oacute; thể n&acirc;ng cấp ổ cắm USB của m&igrave;nh l&ecirc;n 3.0 l&agrave; điều gi&aacute; trị đ&aacute;ng lưu &yacute; với tốc độ đến từ cổng USB 3.0 l&ecirc;n tới 4.8 Gbps c&ograve;n USB 2.0 chỉ c&oacute; khoảng 480Mbps hay c&ograve;n c&oacute; thể thấy l&agrave; gấp 10 lần.&nbsp;Trang bị ổ cắm d&acirc;y HDMI gi&uacute;p người d&ugrave;ng c&oacute; thể kết nối tới Tivi hay m&aacute;y chiếu 1 c&aacute;ch đơn giản rất th&iacute;ch hợp cho những bu&ocirc;i hội thảo, bảo vệ đồ &aacute;n của sinh vi&ecirc;n hay ph&aacute;t tr&ecirc;n m&aacute;y chiếu 1 chương tr&igrave;nh giải tr&iacute; gi&uacute;p mọi người x&iacute;ch lại gần nhau hơn.</p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>Kết nối MSI Matrix Display</strong></span></p>\r\n\r\n<p>Ngo&agrave;i ra, MSI Prestige P65 Creator&nbsp;c&oacute; 1 điểm đặc sắc nằm ở chế độ c&oacute; thể kết nối th&ecirc;m 2 m&agrave;n h&igrave;nh qua c&ocirc;ng nghệ MSI Matrix Display kết nối qua cổng HDMI v&agrave; cổng Mini DisplayPort, sự phối hợp của 3 m&agrave;n h&igrave;nh sẽ mang lại trải nghiệm đặc sắc với người chơi game. Matrix Display hỗ trợ đầu ra h&igrave;nh ảnh 4K sắc n&eacute;t c&oacute; độ ph&acirc;n giải 3840 x 2160 l&yacute; tưởng cho c&aacute;c game c&oacute; đồ họa khủng c&ugrave;ng c&aacute;c bộ phim c&oacute; chất lượng 4K.</p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>Hỗ trợ kết nối kh&ocirc;ng d&acirc;y</strong></span></p>\r\n\r\n<p>Sản phẩm c&ograve;n trang bị kết nối kh&ocirc;ng d&acirc;y với Wifi chuẩn 802.11ac v&agrave; Bluetooth 5.0. Sử dụng chuẩn kết nối Wifi thế hệ thứ 5 cao nhất hiện nay với tần số băng tần l&ecirc;n đến 5 GHz v&agrave; tốc độ c&oacute; thể đạt mức tối đa ở 1730 Mbps mang đến cho người d&ugrave;ng trải nghiệm tốt nhất.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>C&ocirc;ng nghệ tản nhiệt Cooler Boost TRINITY độc quyền</strong></span></h5>\r\n\r\n<p>Ngo&agrave;i ra, laptop MSI c&ograve;n &aacute;p dụng c&ocirc;ng nghệ tản nhiệt Cooler Boost TRINITY độc quyền với 3 quạt Whirlwind Blade được tăng cường với 4 ống dẫn nhiệt để c&oacute; thể ngang tầm với c&aacute;c đối thủ h&agrave;ng đầu thế giới của m&igrave;nh. B&ecirc;n cạnh đ&oacute;, thời lượng pin được n&acirc;ng l&ecirc;n với hiệu quả l&agrave;m việc hơn 8 giờ đ&atilde; được thử nghiệm theo ti&ecirc;u chuẩn MobileMark 2014 mang đến khoảng thời gian s&aacute;ng tạo li&ecirc;n tục của bạn cũng như những gi&acirc;y ph&uacute;t giải tr&iacute; &ldquo;hết m&igrave;nh&rdquo;.</p>\r\n', 6, '2019-06-26 06:38:16', 'Bình thường', 'Bình thường', 1),
(59, 'Lenovo Air 13.3 inch/Intel Core i5/NVIDIA GeForce GTX 940MX/4GB', 27990000, 29990000, 30, 'Màn hình: 13,3\" Full-HD, tấm nền IPS\r\n\r\nCPU: Intel Core i5 hoặc i7 Skylake\r\n\r\nGPU: NVIDIA GeForce GTX 940MX vRAM 2GB\r\n\r\nRAM: 4GB\r\n\r\nPin: chưa có thông tin\r\n\r\nHệ điều hành: Windows 10\r\n\r\nTính năng đặc biệt: cảm biến vân tay\r\n\r\nKết nối: Bluetooth 4.0, Wi-Fi a/b/g/n/ac, USB, mini HDMI\r\n\r\nBộ nhớ trong/Thẻ nhớ: SSD 256GB, có khe thẻ SD\r\n\r\nMỏng 1,48cm\r\n\r\nTrọng lượng: 1,29kg', 'lenovo8.jpg', 'lenovo8-1.jpg', 'lenovo8-2.jpg', 'lenovo8-3.jpg', 'lenovo8-4.jpg', 'lenovo8-5.jpg', 'lenovo8-6.jpg', '<p><img alt=\"\" src=\"/uploads/files/lenovo-8/lenovo-ct-1.png\" style=\"height:450px; width:800px\" /></p>\r\n\r\n<p>Laptop Lenovo Xiaoxin Air 13 ch&iacute;nh thức ra mắt ở Bắc Kinh. M&aacute;y d&ugrave;ng CPU Intel Core thế hệ 8 kết hợp với card đồ họa rời MX150, v&agrave; hiện người d&ugrave;ng tại Trung Quốc đ&atilde; c&oacute; thể đặt mua sản phẩm n&agrave;y.</p>\r\n\r\n<p>Lenovo Xiaoxin Air 13 c&oacute; thiết kế mỏng chỉ 14.8 mm v&agrave; th&acirc;n m&aacute;y l&agrave;m bằng kim loại, trọng lượng 1.25 kg. C&oacute; bốn lựa chọn m&agrave;u sắc để: X&aacute;m Light, đen Supreme, xanh Lake v&agrave; v&agrave;ng hồng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-8/lenovo-ct-2.png\" style=\"height:564px; width:854px\" /></p>\r\n\r\n<p>Đồng thời, Lenovo Xiaoxin Air 13 sử dụng m&agrave;n h&igrave;nh IPS Full HD 13.3 inch, chiếm 80% diện t&iacute;ch mặt trước v&agrave; c&oacute; một gam m&agrave;u sRGB 100%. Laptop được trang bị vi xử l&yacute; Intel Core i thế hệ thứ 8, ổ cứng SSD NVMe PCIe v&agrave; card đồ họa rời NVIDIA GeForce MX150 với bộ nhớ GDDR5 2 GB.</p>\r\n\r\n<p>X&eacute;t về hiệu suất hoạt động của pin, Lenovo Xiaoxin Air 13 c&oacute; pin 45 Wh, cung cấp thời lượng pin k&eacute;o d&agrave;i tới 8 giờ. Theo giới thiệu ch&iacute;nh thức, với 15 ph&uacute;t sạc pin c&oacute; ngay 2 giờ sử dụng.</p>\r\n\r\n<p>Lenovo Xiaoxin Air 13 Notebook 13.3 inch l&agrave; một Notebook ho&agrave;n hảo cho bạn sử dụng h&agrave;ng ng&agrave;y. Được trang bị m&agrave;n h&igrave;nh IPS 13,3 inch 1920 x 1080, mang lại h&igrave;nh ảnh v&agrave; video của bạn. Hệ điều h&agrave;nh Windows 10 đảm bảo cho bạn trải nghiệm người d&ugrave;ng tuyệt vời hơn. Được trang bị bộ xử l&yacute; Intel Core I5-8265U Dual Core, mang đến cho bạn khả năng vận h&agrave;nh mượt m&agrave; v&agrave; trải nghiệm đa nhiệm ổn định. Camera 1.0MP để ghi lại những khoảnh khắc đ&aacute;ng nhớ v&agrave; h&igrave;nh ảnh chất lượng cao trong cuộc sống h&agrave;ng ng&agrave;y. Với GPU NVIDIA GeForce MX150 2G, tất cả những điều n&agrave;y mang đến cho game thủ một bộ ứng dụng đầy đủ để thống trị tr&ograve; chơi v&agrave; mang đến cho game thủ trải nghiệm chơi game thực sự liền mạch.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&aacute;c t&iacute;nh năng ch&iacute;nh:&nbsp;</p>\r\n\r\n<p>● Hệ điều h&agrave;nh Windows 10</p>\r\n\r\n<p>Cung cấp hiệu suất mạnh mẽ hơn, mang đến cho bạn nhiều kinh nghiệm sử dụng mượt m&agrave; v&agrave; tuyệt vời&nbsp;</p>\r\n\r\n<p>● Intel Core I5-8265U Dual Core 1.6GHz, l&ecirc;n đến 3,4 GHz</p>\r\n\r\n<p>nền tảng si&ecirc;u điện &aacute;p thấp v&agrave; Dual Coreprocessing cung cấp tối đa hiệu quả cao sức mạnh&nbsp;</p>\r\n\r\n<p>● NVIDIA GeForce MX150 2G GPU</p>\r\n\r\n<p>On Đồ họa bộ xử l&yacute; với bộ nhớ video được chia sẻ cung cấp chất lượng h&igrave;nh ảnh cao để sử dụng Internet, chỉnh sửa ảnh cơ bản v&agrave; chơi game th&ocirc;ng thường&nbsp;</p>\r\n\r\n<p>● M&agrave;n h&igrave;nh IPS 13,3 inch 1920 x 1080</p>\r\n\r\n<p>Cung cấp trải nghiệm tốt để xem video v&agrave; duyệt web&nbsp;</p>\r\n\r\n<p>● RAM 8GB cho RAM đa nhiệm n&acirc;ng</p>\r\n\r\n<p>cao để vận h&agrave;nh trơn tru c&aacute;c tr&ograve; chơi, ảnh v&agrave; ứng dụng chỉnh sửa video của bạn&nbsp;</p>\r\n\r\n<p>● Dung lượng lưu trữ SSD 256 GB</p>\r\n\r\n<p>Cung cấp chỗ để lưu trữ h&igrave;nh ảnh, video, nhạc v&agrave; hơn thế nữa</p>\r\n\r\n<p>● Camera 1.0MP để chụp ảnh v&agrave; tr&ograve; chuyện trực tiếp</p>\r\n\r\n<p>Cho ph&eacute;p bạn ghi lại những khoảnh khắc đ&aacute;ng nhớ hoặc tr&ograve; chuyện với bạn b&egrave;&nbsp;</p>\r\n\r\n<p>● Đầu ra Micro HDMI</p>\r\n\r\n<p>Bạn c&oacute; thể kết nối thiết bị với m&agrave;n h&igrave;nh hoặc m&aacute;y chiếu độ n&eacute;t cao</p>\r\n', 5, '2019-06-26 06:38:16', 'Bình thường', 'Bình thường', 1),
(60, 'Lenovo Legion Y530-15ICH 81FV00SUVN Core i7-8750H/Dos (15.6\" FHD)', 21990000, 24990000, 30, 'Chip: Intel Core i7-8750H (2.2GHz up to 4.1GHz 9MB)\r\n\r\nRAM: 8GB DDR4 2666MHz (2x SO-DIMM socket, up to 32GB SDRAM)\r\n\r\nỔ cứng: HDD 1TB 7200rpm + 128GB SSD M.2 PCIe\r\n\r\nChipset đồ họa: NVIDIA GeForce GTX 1050 4GB GDDR5 + Intel UHD Graphics 630\r\n\r\nMàn hình: 15.6 inch Full HD (1920 x 1080) IPS, Anti-Glare\r\n\r\nHệ điều hành: Free DOS\r\n\r\nPin: 3 Cell 52.5 WHr', 'lenovo4.jpg', 'lenovo4-1.jpg', 'lenovo4-2.jpg', 'lenovo4-3.jpg', 'lenovo4-4.jpg', 'lenovo4-5.jpg', 'lenovo4-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế gập 180 độ</strong></span></h5>\r\n\r\n<p><strong>Laptop Lenovo Legion Y530-15ICH 81FV00SUVN&nbsp;</strong>mang đến cho người d&ugrave;ng một chiếc laptop mạnh mẽ v&agrave; bền vững nhờ được bao bọc bởi bộ khung chắc chắn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-4/lenovo-ct-1.jpg\" style=\"height:419px; width:750px\" /></p>\r\n\r\n<p>Lớp vỏ ngo&agrave;i l&agrave;m từ chất liệu cao cấp vừa mang lại một độ bền vượt trội để đủ sức bảo vệ phần m&agrave;n h&igrave;nh v&agrave; c&aacute;c linh kiện b&ecirc;n trong nhưng vẫn đảm bảo tối ưu sự linh hoạt v&agrave; tiện dụng cho người d&ugrave;ng mỗi khi mang theo m&aacute;y b&ecirc;n m&igrave;nh. Ngo&agrave;i ra, bản lề của m&aacute;y được thiết kế đặc biệt gi&uacute;p m&aacute;y c&oacute; thể gập 180 độ như quyển tập.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh 15.6 inch&nbsp;Full HD, IPS cho g&oacute;c nh&igrave;n rộng</strong></span></h5>\r\n\r\n<p>Được trang bị m&agrave;n h&igrave;nh 15.6 inch với độ ph&acirc;n giải Full HD (1920 x 1080) pixels cho chất lượng hiển thị kh&aacute; tốt khi h&igrave;nh ảnh được hiển thị sắc n&eacute;t, độ chi tiết cao v&agrave; m&agrave;u sắc trung thực ở khoảng c&aacute;ch sử dụng th&ocirc;ng thường của người d&ugrave;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-4/lenovo-ct-2.jpg\" style=\"height:456px; width:750px\" /></p>\r\n\r\n<p>Đồng thời máy được tích hợp m&agrave;n h&igrave;nh IPS đem đ&ecirc;́n g&oacute;c nh&igrave;n rộng gi&uacute;p bạn nhìn mọi thứ rõ ràng hơn, cùng tấm nền chống ch&oacute;i Anti-glare gi&uacute;p bạn c&oacute; thể trải nghiệm tốt hơn ở m&ocirc;i trường b&ecirc;n ngo&agrave;i, c&oacute; khả năng chống ch&oacute;i tốt.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bộ vi xử l&yacute; Intel Core i7</strong></span></h5>\r\n\r\n<p>M&aacute;y được trang bị bộ vi xử l&yacute; Intel Core i7-8750H với xung nhịp c&oacute; thể l&ecirc;n đến 4GHz, cho bạn những gi&acirc;y ph&uacute;t lướt web nhanh hơn, mạnh mẽ hơn. B&ecirc;n cạnh đ&oacute;, với bộ nhớ trong Ram 8GB DDR4 Bus 2666MHz đảm bảo xử l&iacute; c&aacute;c t&aacute;c vụ đa nhiệm thật mượt m&agrave; v&agrave; ổn định.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-4/lenovo-ct-3.jpg\" style=\"height:381px; width:750px\" /></p>\r\n\r\n<p>Ngo&agrave;i ra,&nbsp;Lenovo Legion Y530-15ICH 81FV00SUVN&nbsp;c&ograve;n trang bị th&ecirc;m &ocirc;̉ cứng kép HDD 1TB 7200rpm + 128GB SSD M.2 PCIe cho ph&eacute;p người d&ugrave;ng lưu trữ dữ liệu một c&aacute;ch thoải m&aacute;i, cũng như truy xuất dữ liệu một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Card đồ họa NVIDIA GeForce GTX 1050</strong></span></h5>\r\n\r\n<p>Đặc biệt, với card đồ họa&nbsp;rời&nbsp;NVIDIA GeForce GTX 1050 b&ocirc;̣ nhớ 4GB bạn c&oacute; thể ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m rằng m&aacute;y c&oacute; thể h&ocirc;̃ trợ bạn tối đa từ c&aacute;c việc đơn giản đến c&aacute;c c&ocirc;ng việc mang t&iacute;nh chất đ&ograve;i hỏi cao như thiết kế đồ họa, 3D, photoshop,... cũng như giải tr&iacute; với những game 3D khủng nhất hiện nay,&nbsp;đ&aacute;p ứng tối đa hiệu năng bạn cần v&agrave; cho ph&eacute;p bạn chơi game mượt với tốc độ khung h&igrave;nh cao.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>C&ocirc;ng nghệ &acirc;m thanh Harman Speakers with Dolby Audio</strong></span></h5>\r\n\r\n<p>Laptop sử dụng c&ocirc;ng nghệ &acirc;m thanh Harman Speakers with Dolby Audio gi&uacute;p cải thiện tốt hơn &acirc;m thanh loa ngo&agrave;i đồng thời gi&uacute;p &acirc;m lượng to hơn nhưng vẫn kh&ocirc;ng thay đổi về chất lượng. Nhờ đ&oacute;, bạn sẽ c&oacute; những ph&uacute;t gi&acirc;y thật tuyệt vời c&ugrave;ng những giai điệu &acirc;m nhạc hay những bộ phim &ldquo;đỉnh&rdquo; nhất.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m chiclet hiện đại, touchpad đa điểm</strong></span></h5>\r\n\r\n<p>M&aacute;y được trang bị b&agrave;n ph&iacute;m chiclet hiện đại với c&aacute;c ph&iacute;m c&oacute; khoảng c&aacute;ch hợp l&yacute;, bấm &ecirc;m v&agrave; h&agrave;nh tr&igrave;nh ph&iacute;m vừa đủ để tạo cảm gi&aacute;c g&otilde; thoải m&aacute;i cho người d&ugrave;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-4/lenovo-ct-4.jpg\" style=\"height:427px; width:750px\" /></p>\r\n\r\n<p>Phần touchpad đi k&egrave;m cũng c&oacute; diện th&iacute;ch kh&aacute; lớn, nhạy v&agrave; hỗ trợ đa điểm tốt n&ecirc;n người d&ugrave;ng c&oacute; thể thoải m&aacute;i cuộn văn bản, thua ph&oacute;ng hay xoay h&igrave;nh ảnh dễ d&agrave;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>USB Type C tất cả trong một kết nối</strong></span></h5>\r\n\r\n<p>Laptop sở hữu cổng kết nối USB Type C đa dụng nhất hiện nay, với thiết kế đồng đẳng gi&uacute;p bạn c&oacute; thể cắm cổng kết nối ở bất cứ chiều n&agrave;o. Ngo&agrave;i ra với c&ocirc;ng nghệ n&agrave;y sẽ cho tốc độ truyền tải dữ liệu nhanh gấp 10 lần so với c&aacute;c cổng kết nối cũ hiện nay.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/lenovo-4/lenovo-ct-5.jpg\" style=\"height:180px; width:750px\" /></p>\r\n\r\n<p>Hỗ trợ nhiều cổng kết nối như cổng HDMI, Bluetooth, khe thẻ nhớ, jack tai nghe cho khả năng kết nối với c&aacute;c thiết bị kh&aacute;c một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Thời lượng pin ổn định</strong></span></h5>\r\n\r\n<p>Pin 3 cell cho Lenovo Legion Y530-15ICH 81FV00SUVN&nbsp;thời gian sử dụng tương đối. C&ugrave;ng với c&ocirc;ng nghệ tiết kiệm pin, m&aacute;y c&oacute; thể hoạt động m&aacute;t, &ecirc;m &aacute;i v&agrave; tiết kiệm điện năng n&ecirc;n c&oacute; thể đ&aacute;p ứng tốt nhu cầu sử dụng b&igrave;nh thường.</p>\r\n', 5, '2019-06-27 06:38:16', 'Bình thường', 'Bình thường', 1),
(61, 'Asus ASUSPRO B9440UA-GV0512T Core i7-8550U/ Win10 (14\" FHD IPS)', 28789000, 31990000, 30, 'Chip: Intel Core i7-8550U (1.8GHz Upto 4.0GHz, 4Cores, 8Threads, 8MB cache, FSB 4GT/s)\r\n\r\nRAM: 16GB LPDDR3-2133MHz\r\n\r\nỔ cứng: 256GB SSD M.2 Sata\r\n\r\nChipset đồ họa: Intel UHD Graphics 620\r\n\r\nMàn hình: 14.0-inch FHD (1920x1080) IPS 60Hz Anti-Glare Panel with 72% NTSC\r\n\r\nHệ điều hành: Windows 10 bản quyền\r\n\r\nPin: 4-Cells 48 Whrs', 'asus9.jpg', 'asus9-1.jpg', 'asus9-2.jpg', 'asus9-3.jpg', 'asus9-4.jpg', 'asus9-5.jpg', 'asus9-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế gập 180 độ</strong></span></h5>\r\n\r\n<p><strong>Laptop Asus ASUSPRO B9440UA-GV0512T&nbsp;</strong>mang đến cho người d&ugrave;ng một chiếc laptop mạnh mẽ v&agrave; bền vững nhờ được bao bọc bởi bộ khung chắc chắn. Lớp vỏ ngo&agrave;i l&agrave;m từ chất liệu cao cấp vừa mang lại một độ bền vượt trội để đủ sức bảo vệ phần m&agrave;n h&igrave;nh v&agrave; c&aacute;c linh kiện b&ecirc;n trong nhưng vẫn đảm bảo tối ưu sự linh hoạt v&agrave; tiện dụng cho người d&ugrave;ng mỗi khi mang theo m&aacute;y b&ecirc;n m&igrave;nh. Ngo&agrave;i ra, bản lề của m&aacute;y được thiết kế đặc biệt gi&uacute;p m&aacute;y c&oacute; thể gập 180 độ như quyển tập.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-9/asus-ct-1.jpg\" style=\"height:476px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh 14 inch Full HD</strong></span></h5>\r\n\r\n<p>Laptop có m&agrave;n h&igrave;nh 14 inch Full HD (1920 x 1080) tối đa h&oacute;a trải nghiệm xem của bạn, với độ phủ m&agrave;u 94% NTSC đảm bảo xuất ra h&igrave;nh ảnh với chất lượng sắc n&eacute;t v&agrave; ch&acirc;n thật nhất. Tấm nền IPS gi&uacute;p bạn c&oacute; thể trải nghiệm g&oacute;c nh&igrave;n rộng 178&deg; đem lại h&igrave;nh ảnh sắc n&eacute;t, sống động v&agrave; độ tương phản cao ngay cả ở những g&oacute;c nh&igrave;n hẹp.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-9/asus-ct-2.jpg\" style=\"height:551px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bộ vi xử l&yacute; Intel Core i7</strong></span></h5>\r\n\r\n<p>M&aacute;y được trang bị bộ vi xử l&yacute; Intel Core i7-8550U với xung nhịp c&oacute; thể l&ecirc;n đến 4.0GHz, cho bạn những gi&acirc;y ph&uacute;t lướt web nhanh hơn, mạnh mẽ hơn. B&ecirc;n cạnh đ&oacute;, với bộ nhớ trong Ram 16GB LPDDR3-2133MHz đảm bảo xử l&iacute; c&aacute;c t&aacute;c vụ đa nhiệm thật mượt m&agrave; v&agrave; ổn định. Ngo&agrave;i ra, m&aacute;y c&ograve;n trang bị th&ecirc;m bộ nhớ 256GB SSD M.2 Sata, cho bạn lưu trữ cũng như vận h&agrave;nh m&aacute;y một c&aacute;ch nhanh ch&oacute;ng, mượt m&agrave; hơn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-9/asus-ct-3.jpg\" style=\"height:378px; width:750px\" /></p>\r\n\r\n<p>Đặc biệt,&nbsp;Asus ASUSPRO B9440UA-GV0512T&nbsp;sử dụng card đồ họa Intel UHD Graphics 620 m&aacute;y c&ograve;n c&oacute; thể đ&aacute;p ứng tốt c&aacute;c như cầu giải tr&iacute; v&agrave; l&agrave;m việc kh&aacute;c như game online, nhiều game 3D, c&aacute;c c&ocirc;ng cụ đồ họa Photoshop, Lightroom&hellip;</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>&Acirc;m thanh vượt ngo&agrave;i mong đợi</strong></span></h5>\r\n\r\n<p>&Acirc;m thanh Harman Kardon bao gồm c&ocirc;ng nghệ khuếch đại th&ocirc;ng minh cho &acirc;m thanh lớn hơn m&agrave; kh&ocirc;ng bị m&eacute;o tiếng v&agrave; phần mềm được thiết kế th&ocirc;ng minh đảm bảo rằng Asus ASUSPRO B9440UA-GV0512T&nbsp;sẽ cho bạn một trải nghiệm &acirc;m thanh đỉnh cao.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-9/asus-ct-4.jpg\" style=\"height:518px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Trải nghiệm Windows 10 tối ưu</strong></span></h5>\r\n\r\n<p>Laptop Asus thiết lập ti&ecirc;u chuẩn cho trải nghiệm Windows 10 tuyệt vời nhất, v&agrave; c&oacute; một d&atilde;y microphone hỗ trợ Cortana, trợ l&yacute; kỹ thuật số c&aacute; nh&acirc;n c&oacute; thể gi&uacute;p bạn l&agrave;m việc dễ d&agrave;ng hơn. D&atilde;y microphone gi&uacute;p bạn kiểm so&aacute;t giọng n&oacute;i ch&iacute;nh x&aacute;c hơn, kể cả trong c&aacute;c điều kiện ồn &agrave;o. Cortana c&ograve;n c&oacute; thể gi&uacute;p bạn theo d&otilde;i c&aacute;c mối quan t&acirc;m, những nơi ưa th&iacute;ch, v&agrave; mọi tin tức m&agrave; bạn muốn cập nhật cho một trải nghiệm c&aacute; nh&acirc;n h&oacute;a ho&agrave;n to&agrave;n.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m v&agrave; Touchpad</strong></span></h5>\r\n\r\n<p>B&agrave;n ph&iacute;m bấm nhẹ nh&agrave;ng, thiết kế chắc, độ nẩy tốt, khoảng c&aacute;ch hợp l&yacute; gi&uacute;p mọi thao t&aacute;c đ&aacute;nh m&aacute;y rất &ecirc;m, nhanh v&agrave; ch&iacute;nh x&aacute;c. Touchpad kh&aacute; rộng, xử l&yacute; mượt m&agrave; kh&ocirc;ng cần d&ugrave;ng đến ch&uacute; chuột b&ecirc;n cạnh.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-9/asus-ct-5.jpg\" style=\"height:330px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>USB Type C tất cả trong một kết nối</strong></span></h5>\r\n\r\n<p>Sở hữu cổng kết nối USB Type C đa dụng nhất hiện nay, với thiết kế đồng đẳng gi&uacute;p bạn c&oacute; thể cắm cổng kết nối ở bất cứ chiều n&agrave;o. Ngo&agrave;i ra với c&ocirc;ng nghệ n&agrave;y sẽ cho tốc độ truyền tải dữ liệu nhanh gấp 10 lần so với c&aacute;c cổng kết nối cũ hiện nay.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-9/asus-ct-6.jpg\" style=\"height:423px; width:750px\" /></p>\r\n\r\n<p>Hỗ trợ nhiều cổng kết nối như cổng HDMI, LAN (RJ45), HD webcam, Bluetooth, khe thẻ nhớ, jack tai nghe cho khả năng kết nối với c&aacute;c thiết bị kh&aacute;c một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Thời lượng pin ổn định</strong></span></h5>\r\n\r\n<p>Pin lithium polymer 4-Cells 48 Whrs đặc biệt cho Asus ASUSPRO B9440UA-GV0512T&nbsp;có khả năng tự vận h&agrave;nh l&ecirc;n đến 9 tiếng. C&aacute;c pin lithium polymer c&oacute; thể duy tr&igrave; hầu hết dung t&iacute;ch sạc ban đầu kể cả sau h&agrave;ng trăm chu kỳ sạc l&acirc;u hơn gấp 3 lần so với c&aacute;c pin ti&ecirc;u chuẩn để bạn kh&ocirc;ng phải lo nghĩ nhiều về hao m&ograve;n hiệu năng pin.</p>\r\n', 2, '2019-06-29 06:38:16', 'Bình thường', 'Bình thường', 1),
(62, 'Asus TUF Gaming FX705GE-EW165T Core i7-8750H/ Win10 (17.3 FHD IPS)', 24990000, 25990000, 15, 'Chip: Intel Core i7-8750H (2.20GHz upto 4.10GHz, 6Cores, 12Threads, 9MB cache, FSB 8GT/s)\r\n\r\nRAM: 8GB DDR4 2666MHz, 2 Slots RAM, Max 32GB\r\n\r\nỔ cứng: 256GB SSD M.2 PCIe + 1TB HDD 5400rpm\r\n\r\nChipset đồ họa: NVIDIA GeForce GTX 1050Ti 4GB GDDR5 + Intel UHD Graphics 630\r\n\r\nMàn hình: 17.3 inch Full HD (1920 x 1080) Anti-Glare IPS-level Panel 45% NTSC\r\n\r\nHệ điều hành: Windows 10 bản quyền\r\n\r\nPin: 4 Cell 2526mAh 48WHr Polymer', 'asus10.jpg', 'asus10-1.jpg', 'asus10-2.jpg', 'asus10-3.jpg', 'asus10-4.jpg', 'asus10-5.jpg', 'asus10-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Laptop TUF Gaming đầu ti&ecirc;n - Vượt xa chuẩn mực</strong></span></h5>\r\n\r\n<p><strong>Laptop Asus TUF Gaming FX705GE-EW165T</strong><strong>&nbsp;</strong>với thiết kế ấn tượng v&agrave; hiệu năng mạnh mẽ, c&oacute; thể chơi được hầu hết c&aacute;c tựa game mới nhất v&agrave; đa nhiệm dễ d&agrave;ng, đồng thời c&oacute; độ bền cao hơn, ổn định hơn v&agrave; vẫn kh&ocirc;ng bị &#39;lỗi thời&#39; trong nhiều năm nữa.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-10/asus-ct-7.jpg\" style=\"height:424px; width:750px\" /></p>\r\n\r\n<p>Hệ thống tản nhiệt chống bụi th&ocirc;ng minh cung cấp đầy đủ luồng kh&iacute; m&aacute;t cho m&aacute;y ngay cả khi chơi những game nặng hơn, k&eacute;o d&agrave;i tuổi thọ hệ thống, loại bỏ bụi bẩn để kh&ocirc;ng g&acirc;y tắc nghẽn hệ thống tản nhiệt. Được lựa chọn kỹ từ 4 thiết kế kh&aacute;c nhau để chọn ra mẫu ph&ugrave; hợp nhất cho mỗi c&aacute; nh&acirc;n.&nbsp;Asus TUF Gaming FX705GE-EW165T&nbsp;c&oacute; độ bền vượt ra khỏi chuẩn mực th&ocirc;ng thường d&ugrave; l&agrave; trong c&ocirc;ng việc hay khi chơi game.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Độ bền đạt ti&ecirc;u chuẩn qu&acirc;n đội</strong></span></h5>\r\n\r\n<p>Thấm nhuần tinh thần của The Ultimate Force (TUF), Asus TUF Gaming FX505 mạnh mẽ vượt qua h&agrave;ng loạt c&aacute;c b&agrave;i thử nghiệm MIL-STD-810G khắc nghiệt, lần lượt sống s&oacute;t th&agrave;nh c&ocirc;ng trong nhiều điều kiện kh&aacute;c nhau bao gồm tiếp x&uacute;c với bức xạ mặt trời, độ cao, nhiệt độ cao. Laptop đ&atilde; được chứng nhận khả năng chống chịu mọi điều kiện sử dụng h&agrave;ng ng&agrave;y, từ va đập bất ngờ cho tới rơi đột ngột tr&ecirc;n b&agrave;n.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-10/asus-ct-8.jpg\" style=\"height:419px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh 17.3 inch Full HD hiển thị cực nhanh</strong></span></h5>\r\n\r\n<p>M&agrave;n h&igrave;nh 17.3 inch Full HD&nbsp;(1920 x 1080) độ phủ m&agrave;u 45% NTSC đảm bảo xuất ra h&igrave;nh ảnh với chất lượng sắc n&eacute;t v&agrave; ch&acirc;n thật nhất.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-10/asus-ct-9.jpg\" style=\"height:398px; width:750px\" /></p>\r\n\r\n<p>Thiết kế NanoEdge mới c&oacute; viền mỏng hơn đến 6.5mm v&agrave; dải m&agrave;u đến 100% sRGB mang đến h&igrave;nh ảnh cực kỳ sống động cho kh&ocirc;ng gian hiển thị cực kỳ ch&igrave;m đắm trong một k&iacute;ch thước nhỏ gọn hơn rất nhiều. Asus TUF Gaming FX705GE-EW165T trang bị tấm nền IPS với tần số qu&eacute;t si&ecirc;u nhanh cho trải nghiệm chơi game si&ecirc;u mượt m&agrave;, hầu như kh&ocirc;ng c&oacute; nh&ograve;e ảnh do chuyển động.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bộ vi xử l&yacute; Intel Core i7</strong></span></h5>\r\n\r\n<p>Được trang bị bộ vi xử l&yacute; Intel Core i7-8750H, c&oacute; khả năng đạt được tốc độ 4.1GHz đ&aacute;ng kinh ngạc. Hiệu năng được bổ trợ với pin dung lượng cao để đảm bảo sự mượt m&agrave; cho những trận đấu k&eacute;o d&agrave;i. Cho d&ugrave; bạn cần phải sử dụng đa nhiệm cho c&ocirc;ng việc hoặc học tập, trong khi đang xem phim, nghe nhạc v&agrave; gaming tr&ecirc;n Asus TUF Gaming FX705GE-EW165T.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-10/asus-ct-13.jpg\" style=\"height:470px; width:750px\" /></p>\r\n\r\n<p>C&ugrave;ng bộ nhớ RAM DDR4 dung lượng 8GB cho hiệu suất xử l&yacute; tốt cũng như đ&aacute;p ứng c&aacute;c t&aacute;c vụ đa nhiệm mượt m&agrave; c&aacute;c ứng dụng. Ngo&agrave;i ra, Asus c&ograve;n trang bị cho m&aacute;y ổ cứng kép&nbsp;256GB SSD M.2 PCIe + 1TB HDD 5400rpm gi&uacute;p m&aacute;y hoạt động mượt m&agrave; hơn, khởi động nhanh ch&oacute;ng hơn.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Card đồ họa NVIDIA GeForce GTX 1050Ti</strong></span></h5>\r\n\r\n<p>Được trang bị card đồ họa&nbsp;NVIDIA GeForce GTX 1050Ti b&ocirc;̣ nhớ đ&ocirc;̀ họa 4GB được x&acirc;y dựng tr&ecirc;n kiến tr&uacute;c Pascal mới nhất, cho m&aacute;y t&iacute;nh của bạn một hiệu suất mạnh mẽ c&oacute; thể xử l&yacute; tốt hầu hết c&aacute;c tựa game v&agrave; c&aacute;c ứng dụng đ&ograve;i hỏi khả năng đồ họa cao.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-10/asus-ct-11.jpg\" style=\"height:431px; width:750px\" /></p>\r\n\r\n<p>C&ocirc;ng nghệ NVIDIA G-SYNC đồng bộ h&oacute;a tốc độ l&agrave;m tươi m&agrave;n h&igrave;nh với tốc độ khung h&igrave;nh của GPU để l&agrave;m cho gameplay mịn m&agrave;ng. G-SYNC giảm thiểu việc h&igrave;nh ảnh bị lắp, giảm độ trễ đầu v&agrave;o, v&agrave; loại bỏ hiện tượng r&aacute;nh m&agrave;n h&igrave;nh trực quan m&agrave; kh&ocirc;ng ảnh hưởng đến hiệu năng của hệ thống. Bạn sẽ c&oacute; được trải nghiệm mịn m&agrave;ng, nhanh nhất v&agrave; hấp dẫn nhất qua một loạt c&aacute;c tr&ograve; chơi bom tấn tr&ecirc;n Asus TUF Gaming FX705GE-EW165T.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>DTS HeadphoneX - &Acirc;m thanh v&ograve;m 7.1</strong></span></h5>\r\n\r\n<p>DTS Headphone:X mang lại trải nghiệm &acirc;m thanh v&ograve;m 7.1 tr&ecirc;n tai nghe v&agrave; loa ngo&agrave;i ấn tượng. D&ugrave; bạn chơi game, xem phim hay cổ vũ cho đội tuyển bạn y&ecirc;u th&iacute;ch, DTS vẫn lu&ocirc;n cho bạn chất &acirc;m căng đầy, gi&uacute;p bạn như được sống trong m&ocirc;i trường h&igrave;nh ảnh bạn đang xem, phản ứng nhanh hơn với tiếng động từ nhiều ph&iacute;a.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-10/asus-ct-12.jpg\" style=\"height:401px; width:750px\" /></p>\r\n\r\n<p>Như DTS Headphone:X c&oacute; thể m&ocirc; phỏng ch&iacute;nh x&aacute;c trải nghiệm &acirc;m thanh v&ograve;m. Hơn nữa, độn c&acirc;n bằng t&iacute;ch hợp sẽ lu&ocirc;n đ&aacute;p ứng được nhu cầu audio của mọi người, cung cấp cho người d&ugrave;ng c&aacute;c t&ugrave;y chỉnh &acirc;m thanh chi tiết ph&ugrave; hợp với từng c&aacute; nh&acirc;n.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>C&ocirc;ng nghệ tản nhiệt HyperCool độc quyền</strong></span></h5>\r\n\r\n<p>C&ocirc;ng nghệ HyperCool độc quyền của ASUS gồm c&oacute; hệ thống Tản nhiệt chống bụi (ADC) v&agrave; c&ocirc;ng nghệ Fan Overboost nhằm đảm bảo khả năng l&agrave;m m&aacute;t vượt trội trong những v&aacute;n game k&eacute;o d&agrave;i tr&ecirc;n Asus TUF Gaming FX705GE-EW165T.&nbsp;Ngo&agrave;i ra, khả năng l&agrave;m m&aacute;t c&ograve;n được cải thiện nhờ nắp lưng cắt h&igrave;nh thang đ&atilde; được cấp bằng s&aacute;ng chế v&agrave; c&aacute;ch bố tr&iacute; quạt song song th&ocirc;ng minh.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-10/asus-ct-13.jpg\" style=\"height:470px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Dải Microphone - Stream game ho&agrave;n hảo</strong></span></h5>\r\n\r\n<p>Microphone được t&iacute;ch hợp bộ lọc tiếng ồn để đảm bảo bắt được giọng n&oacute;i của bạn một c&aacute;ch r&otilde; r&agrave;ng nhất, ngay cả trong m&ocirc;i trường nhiều tạp &acirc;m. D&ugrave; bạn đang tr&ograve; chuyện tr&ecirc;n Skype với bạn b&egrave;, hay chơi game online m&agrave; kh&ocirc;ng c&oacute; tai nghe, chiếc mic của Asus TUF Gaming FX705GE-EW165T&nbsp;sẽ gi&uacute;p bạn.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m chuy&ecirc;n game c&oacute; đ&egrave;n nền RGB</strong></span></h5>\r\n\r\n<p>Được trang bị b&agrave;n ph&iacute;m được tối ưu h&oacute;a cho chơi game, hỗ trợ đ&egrave;n nền RGB tr&ecirc;n tất cả c&aacute;c v&ugrave;ng m&agrave; bạn c&oacute; thể điều khiển th&ocirc;ng qua bảng điều khiển TUF Aura Core để thể hiện phong c&aacute;ch ri&ecirc;ng. C&ocirc;ng nghệ HyperStrike gi&uacute;p g&otilde; ph&iacute;m ch&iacute;nh x&aacute;c hơn v&agrave; tăng cường độ c&ocirc;ng th&aacute;i học nhờ bố cục b&agrave;n ph&iacute;m giống như tr&ecirc;n m&aacute;y t&iacute;nh để b&agrave;n c&ugrave;ng với nh&oacute;m ph&iacute;m WASD nổi bật, ph&iacute;m c&aacute;ch rộng hơn, c&aacute;c ph&iacute;m mũi t&ecirc;n t&aacute;ch biệt v&agrave; khoảng c&aacute;ch c&oacute; thể nhận biết giữa c&aacute;c ph&iacute;m chức năng. Ngo&agrave;i ra, c&ocirc;ng nghệ Overstroke ghi nhận h&agrave;nh tr&igrave;nh ph&iacute;m nhanh hơn cho khả năng điều khiển ch&iacute;nh x&aacute;c v&agrave; nhẹ nh&agrave;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-10/asus-ct-14.jpg\" style=\"height:430px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Truy cập Wifi nhanh v&agrave; xa hơn</strong></span></h5>\r\n\r\n<p>Được trang bị một trong những chuẩn Wi-Fi nhanh nhất hiện tại &ndash; Phi&ecirc;n bản mới nhất của Intel 802.11ac Wave 2 với tốc độ truyền dữ liệu l&ecirc;n đến 1.7Gbps &ndash; 12X nhanh hơn so với chuẩn 802.11b/g/n. Ngo&agrave;i ra cấu h&igrave;nh kh&ocirc;ng d&acirc;y 2x2 cung cấp phạm vi rộng hơn 30% so với cấu h&igrave;nh 1x1.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-10/asus-ct-15.jpg\" style=\"height:390px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Cổng kết nối được bố tr&iacute; ho&agrave;n hảo</strong></span></h5>\r\n\r\n<p>Tất cả cổng kết nối m&agrave; bạn cần để chơi game v&agrave; đ&aacute;p ứng năng suất giải tr&iacute;, 2 c&ocirc;̉ng USB 3.0 port(s) Type A d&agrave;nh cho thiết bị ngoại vi chơi game, trong khi HDMI cho ph&eacute;p bạn mở rộng hiển thị tr&ecirc;n m&agrave;n h&igrave;nh rồi. V&agrave; tất cả cổng kết nối được thiết kế b&ecirc;n tr&aacute;i, v&agrave; đương nhi&ecirc;n bạn sẽ kh&ocirc;ng bị vướng d&acirc;y chuột khi chơi game.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-10/asus-ct-16.jpg\" style=\"height:421px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Pin 4 cell</strong></span></h5>\r\n\r\n<p>Với vi&ecirc;n pin 4 cell Asus TUF Gaming FX705GE-EW165T&nbsp;cho thời gian sử dụng tương đối. C&ocirc;ng nghệ tiết kiệm pin hoạt động m&aacute;t, &ecirc;m &aacute;i v&agrave; tiết kiệm điện năng n&ecirc;n c&oacute; thể đ&aacute;p ứng tốt nhu cầu sử dụng b&igrave;nh thường của bạn. Với c&ocirc;ng nghệ pin Lithium Polymer sẽ cho m&aacute;y khả năng vận h&agrave;nh li&ecirc;n tục l&ecirc;n đến 8 tiếng đồng hồ.</p>\r\n\r\n<p>Điểm đặc biệt của d&ograve;ng pin Lithium Polymer c&oacute; thể duy tr&igrave; được năng lượng sạc lần đầu cho d&ugrave; sau h&agrave;ng trăm chu kỳ sạc, l&acirc;u hơn gấp 3 lần so với c&aacute;c loại pin ti&ecirc;u chuẩn hiện nay, gi&uacute;p bạn khỏi phải lo lắng về hao m&ograve;n hao m&ograve;n điện năng pin.</p>\r\n', 2, '2019-06-29 06:38:16', 'Bán chạy', 'Bình thường', 1);
INSERT INTO `product` (`id`, `product_name`, `price_new`, `price_old`, `qty_product`, `product_desc`, `product_thumb`, `list_thumb_1`, `list_thumb_2`, `list_thumb_3`, `list_thumb_4`, `list_thumb_5`, `list_thumb_6`, `product_content`, `cat_id`, `created_at`, `selling_products`, `featured_products`, `status`) VALUES
(63, 'Asus Zenbook UX331UAL-EG044TS Core i7-8550U/ Win10 (13.3 inch FHD)', 20999000, 24490000, 30, 'CPU: Intel Core i7-8850H (2.6 GHz 4.3 GHz/9MB/6 nhân, 12 luồng)\r\n\r\nMàn hình: 13.3\" (1920 x 1080), không cảm ứng\r\n\r\nRAM: 8GB Onboard LPDDR3\r\n\r\nĐồ họa: Intel UHD Graphics 630\r\n\r\nLưu trữ: 256GB SSD M.2 SATA\r\n\r\nHệ điều hành: Windows 10 Home SL 64-bit\r\n\r\nPin: 3 cell 50 Wh pin liền', 'asus11.jpg', 'asus11-1.jpg', 'asus11-2.jpg', 'asus11-3.jpg', 'asus11-4.jpg', 'asus11-5.jpg', 'asus11-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế si&ecirc;u gọn nhẹ - Dễ d&agrave;ng di chuyển</strong></span></h5>\r\n\r\n<p><strong>Laptop Asus Zenbook UX331UAL-EG044TS Core i7-8850H/ Win10 (13.3 inch FHD)</strong>&nbsp;được thiết kế si&ecirc;u gọn nhẹ với trọng lượng tối ưu v&agrave; với độ mỏng cực kỳ ấn tượng 13.9mm sẽ kh&ocirc;ng bao giờ l&agrave;m bạn k&eacute;m phần phong c&aacute;ch tr&ecirc;n mọi h&agrave;nh tr&igrave;nh.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-11/asus-ct-17.jpg\" style=\"height:422px; width:750px\" /></p>\r\n\r\n<p>Thiết kế viền mỏng NanoEdge c&ograve;n mang tới sự nhỏ gọn ấn tượng của mẫu m&aacute;y t&iacute;nh x&aacute;ch tay 13 inch - Asus Zenbook UX331UAL-EG044TS chắc chắn sẽ lu&ocirc;n l&agrave; người bạn đồng h&agrave;nh trung th&agrave;nh d&ugrave; bạn đi tới bất cứ nơi đ&acirc;u.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-11/asus-ct-18.jpg\" style=\"height:422px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Độ bền ho&agrave;n hảo với hợp kim nh&ocirc;m magiee cứng c&aacute;p</strong></span></h5>\r\n\r\n<p>Được l&agrave;m từ hợp kim nh&ocirc;m magi&ecirc; cứng c&aacute;p, cực kỳ bền bỉ nhưng lại nhẹ đến kinh ngạc, nhờ đ&oacute; ZenBook UX331UAL-EG044TS sẽ kh&ocirc;ng bao giờ l&agrave;m bạn phải bận t&acirc;m trong mọi điều kiện sử dụng. ZenBook UX331UAL-EG044TS c&oacute; độ bền bỉ cao đến mức thiết bị n&agrave;y đ&atilde; vượt qua ti&ecirc;u chuẩn qu&acirc;n sự&nbsp;<strong>MIL-STD 810G</strong>&nbsp;si&ecirc;u khắt khe về độ ổn định v&agrave; độ bền. C&aacute;c b&agrave;i kiểm tra theo ti&ecirc;u chuẩn n&agrave;y bao gồm c&aacute;c m&ocirc;i trường sử dụng v&ocirc; c&ugrave;ng khắc nghiệt, l&agrave; c&aacute;c ngưỡng độ cao, nhiệt độ v&agrave; độ ẩm tới hạn, c&ugrave;ng với đ&oacute; l&agrave; c&aacute;c thử nghiệm khả năng chống rung v&agrave; chống rung m&ocirc; phỏng c&aacute;ch sử dụng c&oacute; phần &ldquo;th&ocirc; bạo&rdquo; h&agrave;ng ng&agrave;y.&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-11/asus-ct-19.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh NanoEdge với viền si&ecirc;u mỏng</strong></span></h5>\r\n\r\n<p>Để mang đến cho bạn m&agrave;n h&igrave;nh lớn nhất trong một kh&ocirc;ng gian nhỏ nhất, ZenBook UX331UAL-EG044TS trang bị một m&agrave;n h&igrave;nh NanoEdge với viền si&ecirc;u mỏng tạo n&ecirc;n tỷ lệ m&agrave;n h&igrave;nh tr&ecirc;n th&acirc;n m&aacute;y ấn tượng 80%. M&agrave;n h&igrave;nh 13.3 inch Full HD vừa vặn trong một khung m&aacute;y si&ecirc;u nhỏ gọn, gi&uacute;p ZenBook UX331UAL-EG044TS&nbsp;chỉ chiếm một kh&ocirc;ng gian &iacute;t hơn nhiều mẫu m&aacute;y 13-inch. Dải m&agrave;u rộng sRGB 100% đảm bảo m&agrave;u sắc sống động v&agrave; ch&iacute;nh x&aacute;c để mọi thứ đều xuất hiện tự nhi&ecirc;n hơn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-11/asus-ct-20.jpg\" style=\"height:422px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bộ vi xử l&yacute; Intel Core i7 thế hệ thứ 8 cho hiệu năng tuyệt vời&nbsp;</strong></span></h5>\r\n\r\n<p>Laptop Asus UX331UAL-EG044TS được trang bị bộ xử l&yacute; thế hệ thứ 8, RAM 8GB LPDDR3 với card đồ họa t&iacute;ch hợp Intel UHD Graphics 620. B&ecirc;n cạnh đ&oacute; m&aacute;y c&ograve;n được trang bị ổ cứng SSD gi&uacute;p m&aacute;y khởi động nhanh chỉ trong v&agrave;i gi&acirc;y. Với khả năng đa nhiệm nhẹ nh&agrave;ng v&agrave; tốc độ xử l&yacute; nhanh, m&aacute;y sẽ kh&ocirc;ng bao giờ để bạn phải chờ đợi.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-11/asus-ct-21.jpg\" style=\"height:375px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Năng suất kh&ocirc;ng giới hạn</strong></span></h5>\r\n\r\n<p>ZenBook 13 được trang bị b&agrave;n ph&iacute;m k&iacute;ch cỡ đầy đủ c&oacute; đ&egrave;n nền với thiết kế nguy&ecirc;n khối chắc chắn mang lại trải nghiệm g&otilde; ph&iacute;m ưu việt trong mọi điều kiện &aacute;nh s&aacute;ng. Đ&acirc;y thực sự l&agrave; một kiệt t&aacute;c c&ocirc;ng th&aacute;i học, với h&agrave;nh tr&igrave;nh ph&iacute;m 1,4 mm được tối ưu h&oacute;a để tạo cảm gi&aacute;c thoải m&aacute;i. Chuột cảm ứng ch&iacute;nh x&aacute;c được chế t&aacute;c kh&eacute;o l&eacute;o để mang lại độ thoải m&aacute;i v&agrave; ch&iacute;nh x&aacute;c tối đa, với lớp k&iacute;nh phủ cho khả năng điều khiển mượt m&agrave;.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-11/asus-ct-22.jpg\" style=\"height:422px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Đa cổng kết nối</strong></span></h5>\r\n\r\n<p>Asus Zenbook UX331UAL-EG044TS được trang bị cổng USB Kiểu-C (USB-C) mới cho ph&eacute;p kết nối hai chiều c&ugrave;ng với thiết kế hai mặt thuận tiện gi&uacute;p kết nối c&aacute;c thiết bị đơn giản nhất c&oacute; thể. USB-C c&ograve;n đem đến tốc độ truyền dữ liệu nhanh hơn đến 10 lần so với c&aacute;c chuẩn kết nối USB 2.0 cũ hơn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-11/asus-ct-23.jpg\" style=\"height:281px; width:750px\" /></p>\r\n\r\n<p>Ngo&agrave;i cổng USB-C, mẫu ZenBook n&agrave;y c&ograve;n c&oacute; c&aacute;c cổng USB 3.1 thế hệ thứ 1 kiểu-A v&agrave; cổng HDMI để bạn c&oacute; thể dễ d&agrave;ng kết nối tất cả c&aacute;c thiết bị ngoại vi, m&agrave;n h&igrave;nh v&agrave; m&aacute;y chiếu hiện c&oacute; một c&aacute;ch tiện lợi. B&ecirc;n cạnh đ&oacute;, đầu đọc thẻ MicroSD tốc độ cao, nhanh gấp hai lần đầu đọc ti&ecirc;u chuẩn, thậm ch&iacute; c&ograve;n cho ph&eacute;p bạn mở rộng tối đa khả năng lưu trữ.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Truy cập một chạm với Windows Hello</strong></span></h5>\r\n\r\n<p>Truy cập chiếc m&aacute;y t&iacute;nh x&aacute;ch tay ZenBook 13 của bạn kh&ocirc;ng thể n&agrave;o dễ d&agrave;ng v&agrave; an to&agrave;n hơn như b&acirc;y giờ. Nhờ cảm biến v&acirc;n tay t&iacute;ch hợp trong chuột cảm ứng v&agrave; Windows Hello, bạn sẽ kh&ocirc;ng cần phải nhập mật khẩu mỗi khi đăng nhập: Một lần chạm duy nhất l&agrave; đủ.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-11/asus-ct-24.jpg\" style=\"height:422px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Lưu động kh&ocirc;ng giới hạn với c&ocirc;ng nghệ ASUS Wi-Fi Master</strong></span></h5>\r\n\r\n<p>Asus UX331UAL-EG044TS t&iacute;ch hợp c&ocirc;ng nghệ ASUS Wi-Fi Master để mang đến kết nối nhanh hơn v&agrave; ổn định hơn ở khoảng c&aacute;ch xa hơn bao giờ hết. Đ&acirc;y l&agrave; c&ocirc;ng nghệ độc quyền cho ph&eacute;p bạn nhận được lợi &iacute;ch tối đa từ Wi-Fi 802.11ac hai băng tần tương th&iacute;ch với MU-MIMO tr&ecirc;n ZenBook 13, với tốc độ l&ecirc;n đến 867Mbps.</p>\r\n\r\n<p>Với Wi-Fi Master, bạn c&oacute; thể tận hưởng trải nghiệm truyền ph&aacute;t video Full HD tr&ecirc;n YouTube ở khoảng c&aacute;ch tr&ecirc;n 300 m&eacute;t3. Được kết hợp với Bluetooth 4.2 mới nhất cho ph&eacute;p bạn kết nối với c&aacute;c thiết bị y&ecirc;u th&iacute;ch với &iacute;t ảnh hưởng đến mức ti&ecirc;u thụ pin hơn, ZenBook 13 cho ph&eacute;p bạn tự do giải quyết mọi nhu cầu.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>&Acirc;m thanh vượt ngo&agrave;i mong đợi</strong></span></h5>\r\n\r\n<p>Để tạo n&ecirc;n hệ thống &acirc;m thanh mạnh mẽ được Harman Kardon chứng nhận tr&ecirc;n ZenBook 13, nh&oacute;m ASUS Golden Ear đ&atilde; ph&aacute;t triển c&ocirc;ng nghệ ASUS SonicMaster thế hệ mới. Với sự kết hợp t&ugrave;y chỉnh giữa phần cứng vượt trội -&nbsp; bao gồm c&ocirc;ng nghệ khuếch đại th&ocirc;ng minh độ m&eacute;o thấp c&oacute; thể tăng &acirc;m lượng l&ecirc;n gấp 3.5 lần - v&agrave; phần mềm được thiết kế th&ocirc;ng minh, ZenBook 13 mang đến trải nghiệm &acirc;m thanh tuyệt đỉnh.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Trải nghiệm với hệ điều h&agrave;nh Windows 10 tối ưu</strong></span></h5>\r\n\r\n<p>Với Asus Zenbook UX331UAL-EG044TS v&agrave; Windows 10, bạn sẽ nhận được trải nghiệm m&aacute;y t&iacute;nh tốt nhất. ZenBook hỗ trợ tất cả c&aacute;c t&iacute;nh năng mới tuyệt vời tr&ecirc;n Windows 10, gồm c&oacute; Modern Standby v&agrave; Cortana. Với Modern Standby, ZenBook mang đến trải nghiệm bật tắt ngay tức th&igrave; - giống như những g&igrave; bạn đang d&ugrave;ng tr&ecirc;n điện thoại th&ocirc;ng minh. Lu&ocirc;n sẵn s&agrave;ng di chuyển, n&ecirc;n bạn sẽ kh&ocirc;ng bao giờ phải chờ đợi. Với Windows Cortana, trợ l&yacute; c&aacute; nh&acirc;n ảo của bạn sẽ trợ gi&uacute;p bạn sắp xếp cuộc sống bận rộn một c&aacute;ch th&ocirc;ng minh, mang đến trải nghiệm Windows c&aacute; nh&acirc;n đ&iacute;ch thực.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-11/asus-ct-25.jpg\" style=\"height:375px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Pin suốt ng&agrave;y - Thời gian sử dụng thoải m&aacute;i</strong></span></h5>\r\n\r\n<p>Ngo&agrave;i ra, được thiết kế ph&ugrave; hợp với phong c&aacute;ch sống năng động, li&ecirc;n tục di chuyển, ZenBook UX331UAL-EG044TS&nbsp;mang trong m&igrave;nh sự tự do với thời lượng pin sử dụng cả ng&agrave;y. Pin lithium-polymer 50Wh được thiết kế đặc biệt gi&uacute;p Asus Zenbook 13 UX331UAL-EG044TS hoạt động độc lập đến 15 giờ. C&aacute;c loại pin lithium-polymer vẫn giữ được dung lượng sạc gần như nguy&ecirc;n vẹn kể cả sau h&agrave;ng trăm chu kỳ sạc &ndash; mang đến tuổi thọ gấp 3 lần so với c&aacute;c loại pin ti&ecirc;u chuẩn, v&igrave; vậy bạn sẽ kh&ocirc;ng phải lo lắng về hiệu năng pin suy giảm.</p>\r\n', 2, '2019-06-29 06:38:16', 'Bình thường', 'Bình thường', 1),
(64, 'Asus F560UD-BQ400T Core i5-8250U/ Win10/ GTX 1050 (15.6 FHD)', 16990000, 18990000, 30, 'Chip: 13422d Intel Core i5-8250U (1.6 GHz - 3.4 GHz / 6MB / 4 nhân, 8 luồng)\r\n\r\nRAM: 1 x 8GB DDR4 2400MHz (2 Khe cắm / Hỗ trợ tối đa 16GB)\r\n\r\nỔ cứng: 1TB HDD 5400RPM\r\n\r\nChipset đồ họa: Intel UHD Graphics 620 / NVIDIA GeForce GTX 1050 4GB GDDR5\r\n\r\nMàn hình: 15.6\" IPS ( 1920 x 1080 ) không cảm ứng\r\n\r\nHệ điều hành: Windows 10 Home SL 64-bit\r\n\r\nPin: 3 cell 36Wh', 'asus13.jpg', 'asus13-1.jpg', 'asus13-2.jpg', 'asus13-3.jpg', 'asus13-4.jpg', 'asus13-5.jpg', 'asus13-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế ấn tượng - Họa tiết kim cương c&aacute;ch điệu</strong></span></h5>\r\n\r\n<p><strong>Laptop Asus F560UD-BQ400T Core i5-8250U/ Win10/ GTX 1050 (15.6 FHD)</strong>&nbsp;sở hữu thiết kế ấn tượng với họa tiết kim cương c&aacute;ch điệu, rất c&aacute; t&iacute;nh v&agrave; sang trọng. Asus F560UD-BQ400T được ho&agrave;n thiện với m&agrave;u sắc xanh đen lạ mắt, c&aacute;c cạnh viền m&agrave;u xanh tia chớp chạy dọc th&acirc;n m&aacute;y ho&agrave;n to&agrave;n nổi bật, tạo n&ecirc;n điểm nhấn tương phản đầy hấp dẫn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-13/asus-ct-26.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh viền mỏng NanoEdge sắc n&eacute;t</strong></span></h5>\r\n\r\n<p>Asus F560UD-BQ400T sử dụng m&agrave;n h&igrave;nh NanoEdge viền mỏng, tỷ lệ m&agrave;n h&igrave;nh tr&ecirc;n th&acirc;n m&aacute;y cao hơn đồng nghĩa với việc bạn c&oacute; được diện t&iacute;ch m&agrave;n h&igrave;nh lớn hơn với trải nghiệm h&igrave;nh ảnh đắm ch&igrave;m tuyệt đối hơn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-13/asus-ct-27.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hi&ecirc;̣u năng tuyệt vời với vi xử l&yacute; Intel Core i5</strong></span></h5>\r\n\r\n<p>Với F560 được trang bị hiệu năng đ&aacute;ng mơ ước với vi xử l&yacute; Intel Core i5 8250U (1.6 GHz Turbo Boost 3.4 GHz), thuộc thế hệ thứ 8 mới nhất v&agrave; VGA đồ họa rời Nvidia GTX1050 2GB, bạn ho&agrave;n to&agrave;n an t&acirc;m với hiệu năng xử l&yacute; đa nhiệm nhẹ nh&agrave;ng để ho&agrave;n th&agrave;nh c&ocirc;ng việc nhanh hơn v&agrave; thưởng thức chất lượng h&igrave;nh ảnh đẹp kh&ocirc;ng t&igrave; vết khi giải tr&iacute;. Ngo&agrave;i ra Wi-Fi 802.11ac c&ograve;n mang đến tốc độ kết nối si&ecirc;u nhanh.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-13/asus-ct-28.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Truy cập dữ liệu si&ecirc;u nhanh v&agrave; dung lượng lưu trữ si&ecirc;u lớn</strong></span></h5>\r\n\r\n<p>Thiết kế ổ lưu trữ k&eacute;p của F560UD-BQ400T cho ph&eacute;p bạn trải nghiệm cả tốc độ truyền dữ liệu si&ecirc;u nhanh của ổ SSD v&agrave; dung lượng lưu trữ khổng lồ của ổ HDD. C&agrave;i đặt tất cả c&aacute;c ứng dụng tr&ecirc;n ổ SSD để c&oacute; thời gian phản hồi si&ecirc;u nhanh v&agrave; sử dụng ổ HDD để lưu trữ c&aacute;c tệp tin lớn như phim, thư viện nhạc v&agrave; album ảnh.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-13/asus-ct-29.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Thế giới mu&ocirc;n m&agrave;u với c&aacute;c c&ocirc;ng nghệ m&agrave;n h&igrave;nh độc quyền</strong></span></h5>\r\n\r\n<p>Đặc biệt, laptop sử dụng c&aacute;c c&ocirc;ng nghệ m&agrave;n h&igrave;nh độc quyền của ASUS đảm bảo bạn sẽ được tận hưởng mọi trải nghiệm h&igrave;nh ảnh tốt nhất.</p>\r\n\r\n<p><strong>C&ocirc;ng nghệ ASUS Splendid&nbsp;</strong>gi&uacute;p&nbsp;tối ưu h&oacute;a m&agrave;u sắc v&agrave; độ tương phản, đem đến h&igrave;nh ảnh ch&acirc;n thực. C&ocirc;ng nghệ n&agrave;y hỗ trợ bốn chế độ hiển thị: Th&ocirc;ng thường, Sống động, Bảo vệ mắt v&agrave; Thủ c&ocirc;ng, tất cả đều c&oacute; thể chọn chỉ bằng một c&uacute; nhấp chuột.</p>\r\n\r\n<p><strong>C&ocirc;ng nghệ ASUS Tru2Life Video</strong>&nbsp;gi&uacute;p tối ưu h&oacute;a th&ocirc;ng minh từng điểm ảnh trong khung h&igrave;nh video để tăng độ sắc n&eacute;t v&agrave; độ tương phản th&ecirc;m 150%, nhờ đ&oacute; bạn sẽ được tận hưởng chất lượng h&igrave;nh ảnh tốt nhất.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-13/asus-ct-30.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>&Acirc;m thanh lớn, sắc n&eacute;t</strong></span></h5>\r\n\r\n<p>F560UD-BQ400T trang bị c&aacute;c loa c&oacute; đường k&iacute;nh rộng với c&aacute;c buồng &acirc;m lớn hơn so với c&aacute;c mẫu m&aacute;y t&iacute;nh x&aacute;ch tay ti&ecirc;u chuẩn kh&aacute;c. Điều n&agrave;y mang đến &acirc;m bass mạnh mẽ hơn v&agrave; &acirc;m thanh c&oacute; độ trong trẻo tuyệt vời, gi&uacute;p &acirc;m thanh tr&ecirc;n F560UD-BQ400T được lớn nhất, r&otilde; nhất. C&ocirc;ng nghệ Khuếch đại th&ocirc;ng minh của ASUS giữ cho &acirc;m thanh trong trẻo tuyệt đối, ngay cả khi bạn bật tối đa &acirc;m lượng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-13/asus-ct-31.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, thiết bị c&ograve;n được trang bị c&ocirc;ng nghệ ASUS SonicMaster, l&agrave; sự kết hợp đột ph&aacute; giữa phần cứng v&agrave; phần mềm đem đến chất lượng &acirc;m thanh tuyệt vời nhất m&agrave; bạn từng được nghe tr&ecirc;n m&aacute;y t&iacute;nh x&aacute;ch tay. F560UD-BQ400T c&ograve;n hỗ trợ ASUS AudioWizard, l&agrave; bộ phần mềm mạnh mẽ cho ph&eacute;p bạn tinh chỉnh c&aacute;c c&agrave;i đặt để thu được độ c&acirc;n bằng &acirc;m thanh l&yacute; tưởng cho mọi loại nội dung. Bạn c&ograve;n c&oacute; thể tự điều chỉnh bằng c&agrave;i đặt thủ c&ocirc;ng để l&agrave;m chủ ho&agrave;n to&agrave;n chất lượng h&ograve;a &acirc;m.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hệ thống l&agrave;m m&aacute;t hiệu quả</strong></span></h5>\r\n\r\n<p>Tản nhiệt cho m&aacute;y t&iacute;nh đ&oacute;ng một vai tr&ograve; quan trọng bởi chức năng n&agrave;y ảnh hưởng trực tiếp đến hiệu năng v&agrave; quan trọng hơn cả l&agrave; sự thoải m&aacute;i của bạn. C&ocirc;ng nghệ ASUS IceCool sử dụng bộ điều khiển quạt th&ocirc;ng minh, c&aacute;c ống dẫn nhiệt mỏng v&agrave; thiết kế kỹ thuật tinh tế để tạo n&ecirc;n một hệ thống tản nhiệt hiệu quả gi&uacute;p duy tr&igrave; nhiệt độ trung b&igrave;nh tr&ecirc;n bề mặt k&ecirc; l&ograve;ng b&agrave;n tay chỉ dưới 36&deg;C (thấp hơn nhiệt độ cơ thể), nhờ đ&oacute; m&aacute;y t&iacute;nh lu&ocirc;n m&aacute;t mẻ v&agrave; bạn lu&ocirc;n thoải m&aacute;i.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-13/asus-ct-32.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Thời lượng pin d&agrave;i, sạc nhanh v&agrave; an to&agrave;n</strong></span></h5>\r\n\r\n<p>Ngo&agrave;i ra, F560UD-BQ400T c&ograve;n được&nbsp;trang bị vi&ecirc;n pin bền v&agrave; sạc nhanh an to&agrave;n để hạn chế tối đa thời gian ngừng hoạt động. C&ocirc;ng nghệ sạc nhanh của ASUS cho ph&eacute;p bạn sạc an to&agrave;n từ mức pin cạn l&ecirc;n 50% dung lượng chỉ trong 39 ph&uacute;t.</p>\r\n', 2, '2019-06-29 06:38:16', 'Bán chạy', 'Bình thường', 1),
(65, 'Asus Zenbook 13 UX333FA-A4181T Core i5-8265U/ Win10/ Numpad (13.3 FHD)', 20650000, 22990000, 0, 'Chip: 13422d Intel Core i5-8265U (1.6 GHz - 3.9 GHz/ 6MB/ 4 nhân, 8 luồng)\r\n\r\nRAM: 8 GB LPDDR3 2133 MHz\r\n\r\nỔ cứng: 256GB SSD M.2 NVMe\r\n\r\nChipset đồ họa: Intel UHD Graphics 620\r\n\r\nMàn hình: 13.3\" ( 1920 x 1080 ) không cảm ứng\r\n\r\nHệ điều hành: Windows 10 Home SL 64-bit\r\n\r\nPin: 3 cell 50 Wh                                                ', 'asus12.jpg', 'asus12-1.jpg', 'asus12-2.jpg', 'asus12-3.jpg', 'asus12-4.jpg', 'asus12-5.jpg', 'asus12-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế nhỏ gọn với m&agrave;n h&igrave;nh NanoEdge kh&ocirc;ng viền</strong></span></h5>\r\n\r\n<p><strong>Laptop Asus Zenbook 13 UX333FA-A4181T Core i5-8265U/ Win10/ Numpad (13.3 FHD)</strong>&nbsp;được thiết kế để mang đến cho bạn sự tự do, kh&aacute;m ph&aacute; năng lực s&aacute;ng tạo trong bạn, đặc biệt với m&agrave;n h&igrave;nh NanoEdge kh&ocirc;ng viền ở cả 4 cạnh, viền bezel si&ecirc;u mỏng chỉ d&agrave;y 2.8mm ở hai b&ecirc;n, 3.3 mm ở phần dưới v&agrave; ph&iacute;a tr&ecirc;n &quot;thừa&quot; ra một ch&uacute;t với 5.9mm để tạo chỗ trống cho camera. ZenBook 13 inch c&oacute; k&iacute;ch thước chỉ tương đương một cuốn s&aacute;ch mặc d&ugrave; vẫn sở hữu m&agrave;n h&igrave;nh ti&ecirc;u chuẩn 13.3 inch.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-12/asus-ct-33.jpg\" style=\"height:375px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Phi&ecirc;n bản m&agrave;u đỏ cao cấp v&agrave; nghệ thuật</strong></span></h5>\r\n\r\n<p>Zenbook 13 giờ đ&acirc;y lại kho&aacute;c th&ecirc;m m&agrave;u &aacute;o đỏ Burgundy đằm thắm, tăng th&ecirc;m phần cao cấp v&agrave; kh&aacute;c biệt d&agrave;nh ri&ecirc;ng cho bạn. Đặc biệt, Asus Zenbook 13 UX333FA-A4181T c&oacute; độ bền chuẩn qu&acirc;n đội, xuất sắc trải qua b&agrave;i thử qu&acirc;n đội MIL-STD-810G dưới nhưng m&ocirc;i trường bao gồm độ cao, nhiệt độ v&agrave; độ ẩm khắc nghiệt.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Trải nghiệm h&igrave;nh ảnh tuyệt sắc</strong></span></h5>\r\n\r\n<p>Zenbook 13 đem đến cho bạn m&agrave;n h&igrave;nh với độ ph&acirc;n giải cao, sắc n&eacute;t c&ugrave;ng với g&oacute;c nh&igrave;n rộng, m&agrave;u sắc sinh động v&agrave; ch&acirc;n thực, hỗ trợ tuyệt vời cho những c&ocirc;ng việc s&aacute;ng tạo. Asus Zenbook UX333FA-A4181T c&ograve;n được trang bị c&ocirc;ng nghệ h&igrave;nh ảnh độc quyền ASUS True2Life Video v&agrave; ASUS Splendid - tinh chỉnh sao cho mắt người d&ugrave;ng được thoải m&aacute;i nhất trong khi vẫn tối ưu được m&agrave;u sắc v&agrave; ph&ugrave; hợp với mọi nội dung.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-12/asus-ct-34.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Trang bị chốt xoay ErgoLift Hinge</strong></span></h5>\r\n\r\n<p>Zenbook 13 UX333FA-A4181T được trang bị ErgoLift Hinge sẽ gi&uacute;p biến đổi vị tr&iacute; b&agrave;n ph&iacute;m tới một g&oacute;c nghi&ecirc;ng ho&agrave;n hảo, n&acirc;ng trải nghiệm người d&ugrave;ng l&ecirc;n một tầm cao mới. Chưa dừng lại ở đ&oacute;, sự thay đổi g&oacute;c nghi&ecirc;ng hợp l&yacute; c&ograve;n gi&uacute;p cho hệ thống tản nhiệt c&ugrave;ng chất lượng &acirc;m thanh từ loa hoạt động tối ưu hơn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-12/asus-ct-35.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hiệu năng kh&ocirc;ng giới hạn với bộ xử l&yacute; Intel Core i5-8265U</strong></span></h5>\r\n\r\n<p>Zenbook 13 kh&ocirc;ng chỉ c&oacute; độc thiết kế bắt mắt m&agrave; c&ograve;n được trang bị những c&ocirc;ng nghệ v&agrave; linh kiện cao cấp. Vận h&agrave;nh bởi CPU Intel Core i5-8265U cũng như 256 GB SSD M.2 NVMe chắc chắn sẽ đem đến đủ sức mạnh điện to&aacute;n cho mọi loại c&ocirc;ng việc m&agrave; bạn phải đối đầu h&agrave;ng ng&agrave;y.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-12/asus-ct-36.jpg\" style=\"height:563px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Kết nối di động tại mọi địa điểm</strong></span></h5>\r\n\r\n<p>Laptop Asus Zenbook UX333FA-A4181T đem đến những c&ocirc;ng nghệ c&oacute; thể đ&aacute;p ứng ho&agrave;n to&agrave;n 2 ti&ecirc;u ch&iacute; tr&ecirc;n với ASUS Wifi Master, gi&uacute;p người d&ugrave;ng đạt được kết nối Wifi ổn định v&agrave; đạt cấp độ Gigabit, để c&oacute; thể tận hưởng tốc độ download nhanh c&ugrave;ng với khả năng stream h&igrave;nh ảnh. Kết hợp với Bluetooth 5.0 biến việc kết nối với c&aacute;c thiết bị v&agrave; phụ kiện ngoại vi c&agrave;ng th&ecirc;m thuận tiện.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-12/asus-ct-37.png\" style=\"height:422px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>&Acirc;m thanh sống động với loa Harman Kardon</strong></span></h5>\r\n\r\n<p>Đặc biệt, để tạo n&ecirc;n thế hệ tiếp theo cho c&ocirc;ng ngh&ecirc; &acirc;m thanh ASUS SonicMaster, đội ngũ ASUS Golden Ear c&ugrave;ng phối hợp với thương hiệu audio tự động nổi tiếng Harmon Kardon. T&aacute;c phẩm được tạo ra từ qu&aacute; tr&igrave;nh hợp t&aacute;c n&agrave;y l&agrave; sự dung hợp giữa c&ocirc;ng nghệ phần cứng v&agrave; phần mềm để tạo n&ecirc;n trải nghiệm th&iacute;nh gi&aacute;c độc nhất cho người d&ugrave;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-12/asus-ct-38.jpg\" style=\"height:422px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Thời lượng pin d&agrave;i</strong></span></h5>\r\n\r\n<p>Zenbook 13 được cung cấp năng lượng bởi pin Li-polymer dung lượng cao 50Wh để l&agrave;m việc tối đa 14h, sẽ kh&ocirc;ng l&agrave;m bạn thất vọng cho d&ugrave; ng&agrave;y l&agrave;m việc c&oacute; d&agrave;i đến thế n&agrave;o đi chăng nữa.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Trang bị Keyboard v&agrave; Touchpad đẳng cấp</strong></span></h5>\r\n\r\n<p>Asus Zenbook 13 c&ograve;n trang bị tr&ecirc;n m&igrave;nh bộ b&agrave;n ph&iacute;m full size với đ&egrave;n nền backlit, chắc chắn sẽ đem đến trải nghiệm g&otilde; ph&iacute;m chuẩn x&aacute;c v&agrave; thỏa m&atilde;n. Cộng với độ nghi&ecirc;ng ho&agrave;n hảo từ ErgoLift v&agrave; Touchpad c&oacute; khả năng biến th&agrave;nh Numpad, l&agrave;m việc chưa bao giờ th&uacute; vị đến thế với Zenbook 13.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-12/asus-ct-39.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Windows Hello - Quản l&yacute; tối cao</strong></span></h5>\r\n\r\n<p>Asus Zenbook UX333FA-A4181T v&agrave; Windows 10 sẽ trao cho bạn quyền quản l&yacute; tối cao m&agrave; kh&ocirc;ng cần phải cử động một ng&oacute;n tay. C&ocirc;ng nghệ sinh trắc học ti&ecirc;n tiến l&agrave;m việc trơn tru với Windows |Hello v&agrave; Cortana, mở kh&oacute;a th&ocirc;ng qua việc qu&eacute;t khu&ocirc;n mặt hoặc giọng n&oacute;i trong chớp mắt.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-12/asus-ct-40.png\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>T&iacute;ch hợp mọi cổng kết nối cần thiết</strong></span></h5>\r\n\r\n<p>Trong khi l&agrave;m việc di động, việc c&oacute; thể kết nối với mọi thiết bị để trao đổi dữ liệu l&agrave; điều thiết yếu. Zenbook 13 sẵn s&agrave;ng l&agrave;m việc khi được trang bị mọi cổng kết nối phổ th&ocirc;ng như USB 3.1, USB 2.0, Audio jack, SD card reader, HDMIv&agrave; USB C.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/asus-12/asus-ct-41.png\" style=\"height:328px; width:750px\" /></p>\r\n', 2, '2019-07-05 15:10:35', 'Bán chạy', 'Bình thường', 1),
(66, 'HP 15/da0051TU4ME64PA/ Intel Core i3-7020U/4GB DDR4-2133MHz', 9490000, 10490000, 9, 'Chip: Intel Core i3-7020U (2.30GHz, 2Cores, 4Threads, 3MB cache, FSB 4GT/s)\r\n\r\nRAM: 4GB DDR4-2133MHz, 2 Slots\r\n\r\nỔ cứng: 500GB HDD 5400rpm\r\n\r\nChipset đồ họa: Intel HD Graphics 620\r\n\r\nMàn hình: 15.6-inch Diagonal HD (1366 x 768) SVA BrightView WLED-backlit\r\n\r\nHệ điều hành: Windows 10 bản quyền\r\n\r\nPin: 3-Cell 41 Whr Li-ion', 'hp5.jpg', 'hp5-1.jpg', 'hp5-2.jpg', 'hp5-3.jpg', 'hp5-4.jpg', 'hp5-5.jpg', 'hp5-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế kiểu d&aacute;ng cứng c&aacute;p</strong></span></h5>\r\n\r\n<p>Laptop HP 15-da0051TU 4ME64PA&nbsp;được thiết kế kiểu d&aacute;ng cứng c&aacute;p với bốn g&oacute;c cạnh được bo tr&ograve;n vừa phải, mang đến vẻ đẹp mạnh mẽ v&agrave; đẳng cấp cho chiếc m&aacute;y, cùng với vẻ ngo&agrave;i đơn giản m&agrave; cứng c&aacute;p tạo cảm gi&aacute;c chắc tay cho người d&ugrave;ng khi cầm nắm, dễ d&agrave;ng bỏ v&agrave;o t&uacute;i x&aacute;ch để mang theo mọi l&uacute;c mọi nơi.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/hp-1/hp-ct-5.jpg\" style=\"height:450px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh 15.6 inch HD</strong></span></h5>\r\n\r\n<p>Được trang bị m&agrave;n h&igrave;nh 15.6 inch độ ph&acirc;n giải&nbsp;HD (1366 x 768) c&ugrave;ng c&ocirc;ng nghệ BrightView WLED-backlit c&oacute; chất lượng ho&agrave;n thiện kh&aacute; tốt, m&agrave;u sắc trung t&iacute;nh, viền m&agrave;n h&igrave;nh chắc chắn, c&aacute;c lớp hiển thị được &eacute;p chặt v&agrave;o nhau, hiển thị chi tiết đủ đ&aacute;p ứng nhu cầu giải tr&iacute; cơ bản của người d&ugrave;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/hp-1/hp-ct-6.jpg\" style=\"height:433px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bộ vi xử l&yacute; Intel Core i3</strong></span></h5>\r\n\r\n<p>Được trang bị vi xử l&yacute;&nbsp;Intel Core i3-7020U xung nhịp 2.3GHz đi k&egrave;m l&agrave; dung lượng RAM 4GB DDR4-2133MHz, ổ cứng&nbsp;500GB HDD laptop đ&aacute;p ứng tốt nhu cầu sử dụng hằng ng&agrave;y của người d&ugrave;ng. M&aacute;y c&oacute; thể xử l&yacute; mượt m&agrave; v&agrave; trơn tru tất cả c&aacute;c t&aacute;c vụ thường ng&agrave;y của người d&ugrave;ng như duyệt web, xem phim,&hellip;</p>\r\n\r\n<p>Khả năng xử l&yacute; đồ họa của&nbsp;HP 15-da0051TU 4ME64PA&nbsp;cũng được tăng cường với card đồ họa t&iacute;ch hợp Intel HD Graphics 620, cho khả năng chạy tốt v&agrave; ổn định c&aacute;c phần mềm đồ họa cũng như đ&aacute;p ứng nhu cầu giải tr&iacute; của người d&ugrave;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m hiện đại&nbsp;v&agrave; Touchpad rộng r&atilde;i</strong></span></h5>\r\n\r\n<p>Được trang bị b&agrave;n ph&iacute;m hiện đại với khoảng c&aacute;ch giữa c&aacute;c ph&iacute;m kh&aacute; hợp l&yacute;, bấm &ecirc;m v&agrave; h&agrave;nh tr&igrave;nh ph&iacute;m đủ s&acirc;u n&ecirc;n cho cảm gi&aacute;c g&otilde; kh&aacute; tốt, cho cảm gi&aacute;c thoải m&aacute;i d&ugrave; sử dụng trong một thời gian d&agrave;i. Chưa hết phần touchpad đi k&egrave;m cũng c&oacute; diện t&iacute;ch kh&aacute; rộng r&atilde;i, nhạy v&agrave; hỗ trợ đa điểm kh&aacute; tốt n&ecirc;n c&aacute;c thao t&aacute;c như thu ph&oacute;ng, cuộn văn bản hay xoay h&igrave;nh ảnh đều được thực hiện dễ d&agrave;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Cổng kết nối</strong></span></h5>\r\n\r\n<p>Hỗ trợ nhiều cổng kết nối như jack &acirc;m thanh, HDMI, Ethernet RJ45 c&ugrave;ng 1 khe thẻ nhớ SD media HP 15-da0051TU 4ME64PA&nbsp;cho khả năng kết nối với c&aacute;c thiết bị kh&aacute;c một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Thời lượng pin l&acirc;u hơn</strong></span></h5>\r\n\r\n<p>Được thiết kế để ph&ugrave; hợp với phong c&aacute;ch sống năng động hiện nay, HP 15-da0051TU 4ME64PA sẽ cung cấp cho bạn thời lượng pin để c&oacute; thể sử dụng suốt cả ng&agrave;y. Với c&ocirc;ng nghệ pin Lithium Polymer sẽ cho m&aacute;y khả năng vận h&agrave;nh li&ecirc;n tục l&ecirc;n đến 8 tiếng đồng hồ. Điểm đặc biệt của d&ograve;ng pin Lithium Polymer c&oacute; thể duy tr&igrave; được năng lượng sạc lần đầu cho d&ugrave; sau h&agrave;ng trăm chu kỳ sạc, l&acirc;u hơn gấp 3 lần so với c&aacute;c loại pin ti&ecirc;u chuẩn hiện nay, gi&uacute;p bạn khỏi phải lo lắng về hao m&ograve;n hao m&ograve;n điện năng pin.</p>\r\n', 3, '2019-07-07 07:09:04', 'Bình thường', 'Nổi bật', 1),
(67, 'HP Pavilion 14-ce1013TU 5JN20PA Core i5-8265U/Win10 (14.0\" FHD)', 13899000, 15490000, 30, 'Chip: Intel Core i5-8265U (1.6GHz up 3.9GHz 6MB)\r\n\r\nRAM: 4GB DDR4 2400MHz (1x SO-DIMM socket, up to 16GB SDRAM)\r\n\r\nỔ cứng: HDD 1TB 5400rpm, x1 slot SSD M.2 SATA\r\n\r\nChipset đồ họa: Intel UHD Graphics 620\r\n\r\nMàn hình: 14.0-inch Diagonal Full HD (1920 x 1080) IPS BrightView WLED-backlit\r\n\r\nHệ điều hành: Windows 10 Home\r\n\r\nPin: 3 Cell 41 Whr Li-ion', 'hp6.jpg', 'hp6-1.jpg', 'hp6-2.jpg', 'hp6-3.jpg', 'hp6-4.jpg', 'hp6-5.jpg', 'hp6-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế kiểu d&aacute;ng cứng c&aacute;p</strong></span></h5>\r\n\r\n<p><strong>Laptop HP Pavilion 14-ce1013TU 5JN20PA</strong><strong>&nbsp;</strong>được thiết kế kiểu d&aacute;ng cứng c&aacute;p với bốn g&oacute;c cạnh được bo tr&ograve;n vừa phải, mang đến vẻ đẹp mạnh mẽ v&agrave; đẳng cấp cho chiếc m&aacute;y, cùng với vẻ ngo&agrave;i đơn giản m&agrave; cứng c&aacute;p tạo cảm gi&aacute;c chắc tay cho người d&ugrave;ng khi cầm nắm, dễ d&agrave;ng bỏ v&agrave;o t&uacute;i x&aacute;ch để mang theo mọi l&uacute;c mọi nơi.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/hp-1/hp-ct-7.jpg\" style=\"height:438px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh 14 inch&nbsp;Full HD</strong></span></h5>\r\n\r\n<p>Được trang bị m&agrave;n h&igrave;nh 14 inch độ ph&acirc;n giải Full HD (1920 x 1080) c&ugrave;ng c&ocirc;ng nghệ BrightView WLED-backlit c&oacute; chất lượng ho&agrave;n thiện kh&aacute; tốt, m&agrave;u sắc trung t&iacute;nh, viền m&agrave;n h&igrave;nh chắc chắn, c&aacute;c lớp hiển thị được &eacute;p chặt v&agrave;o nhau, hiển thị chi tiết đủ đ&aacute;p ứng nhu cầu giải tr&iacute; cơ bản của người d&ugrave;ng.&nbsp;Tấm nền IPS gi&uacute;p bạn c&oacute; thể trải nghiệm g&oacute;c nh&igrave;n rộng 178&deg; đem lại h&igrave;nh ảnh sắc n&eacute;t, sống động v&agrave; độ tương phản cao ngay cả ở những g&oacute;c nh&igrave;n hẹp.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/hp-1/hp-ct-8.jpg\" style=\"height:443px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bộ vi xử l&yacute; Intel Core i5</strong></span></h5>\r\n\r\n<p>Được trang bị vi xử l&yacute;&nbsp;Intel Core i5-8265U xung nhịp 3.9GHz đi k&egrave;m l&agrave; dung lượng RAM 4GB DDR4 bus 2400MHz ổ cứng&nbsp;HDD 1TB laptop đ&aacute;p ứng tốt nhu cầu sử dụng hằng ng&agrave;y của người d&ugrave;ng. M&aacute;y c&oacute; thể xử l&yacute; mượt m&agrave; v&agrave; trơn tru tất cả c&aacute;c t&aacute;c vụ thường ng&agrave;y của người d&ugrave;ng như duyệt web, xem phim,&hellip; K&egrave;m theo đ&oacute; l&agrave; chip đồ họa Intel UHD Graphics 620 cho m&aacute;y c&oacute; hiệu năng mạnh mẽ, đủ để thực hiện c&aacute;c t&aacute;c vụ y&ecirc;u cầu khả năng đồ họa kh&aacute; như photoshop, hay chơi game.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/hp-1/hp-ct-9.jpg\" style=\"height:436px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>&Acirc;m thanh bang &amp; olufsen (B&amp;O PLAY)</strong></span></h5>\r\n\r\n<p>Laptop sẽ giúp bạn trải nghiệm chiều s&acirc;u &acirc;m nhạc mới đầy sống động v&agrave; ch&acirc;n thực với loa k&eacute;p, c&ocirc;ng nghệ khuếch &acirc;m HP Audio Boost v&agrave; &acirc;m thanh tinh chỉnh bởi c&aacute;c chuy&ecirc;n gia của B&amp;O PLAY.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m hiện đại&nbsp;v&agrave; Touchpad rộng r&atilde;i</strong></span></h5>\r\n\r\n<p>Được trang bị b&agrave;n ph&iacute;m hiện đại với khoảng c&aacute;ch giữa c&aacute;c ph&iacute;m kh&aacute; hợp l&yacute;, bấm &ecirc;m v&agrave; h&agrave;nh tr&igrave;nh ph&iacute;m đủ s&acirc;u n&ecirc;n cho cảm gi&aacute;c g&otilde; kh&aacute; tốt, cho cảm gi&aacute;c thoải m&aacute;i d&ugrave; sử dụng trong một thời gian d&agrave;i. Chưa hết phần touchpad đi k&egrave;m cũng c&oacute; diện t&iacute;ch kh&aacute; rộng r&atilde;i, nhạy v&agrave; hỗ trợ đa điểm kh&aacute; tốt n&ecirc;n c&aacute;c thao t&aacute;c như thu ph&oacute;ng, cuộn văn bản hay xoay h&igrave;nh ảnh đều được thực hiện dễ d&agrave;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>USB Type C tất cả trong một kết nối</strong></span></h5>\r\n\r\n<p>Laptop sở hữu cổng kết nối USB Type C đa dụng nhất hiện nay, với thiết kế đồng đẳng gi&uacute;p bạn c&oacute; thể cắm cổng kết nối ở bất cứ chiều n&agrave;o. Ngo&agrave;i ra với c&ocirc;ng nghệ n&agrave;y sẽ cho tốc độ truyền tải dữ liệu nhanh gấp 10 lần so với c&aacute;c cổng kết nối cũ hiện nay.</p>\r\n\r\n<p>HP Pavilion 14-ce1013TU 5JN20PA&nbsp;còn hỗ trợ nhiều cổng kết nối như jack &acirc;m thanh, HDMI, Ethernet RJ45 c&ugrave;ng 1 khe thẻ nhớ SD media cho khả năng kết nối với c&aacute;c thiết bị kh&aacute;c một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Thời lượng pin l&acirc;u hơn</strong></span></h5>\r\n\r\n<p>Được thiết kế để ph&ugrave; hợp với phong c&aacute;ch sống năng động hiện nay, HP Pavilion 14-ce1013TU 5JN20PA&nbsp;sẽ cung cấp cho bạn thời lượng pin để c&oacute; thể sử dụng suốt cả ng&agrave;y. Với c&ocirc;ng nghệ pin Lithium Polymer sẽ cho m&aacute;y khả năng vận h&agrave;nh li&ecirc;n tục l&ecirc;n đến 8 tiếng đồng hồ. Điểm đặc biệt của d&ograve;ng pin Lithium Polymer c&oacute; thể duy tr&igrave; được năng lượng sạc lần đầu cho d&ugrave; sau h&agrave;ng trăm chu kỳ sạc, l&acirc;u hơn gấp 3 lần so với c&aacute;c loại pin ti&ecirc;u chuẩn hiện nay, gi&uacute;p bạn khỏi phải lo lắng về hao m&ograve;n hao m&ograve;n điện năng pin.</p>\r\n', 3, '2019-06-30 06:38:16', 'Bình thường', 'Bình thường', 1),
(68, 'HP 15-da0358TU 6KD02PA Pentium Gold 4417U/ Win10 (15.6 HD)', 6899000, 7490000, 30, 'CPU: Intel Pentium Gold 4417U 2.3GHz 2MB\r\n\r\nRAM: 4GB DDR4 2133MHz (1x SO-DIMM socket, up to 16GB SDRAM)\r\n\r\nỔ đĩa cứng: HDD 500GB 5400rpm, x1 slot SSD M.2 (SATA/ PCIe)\r\n\r\nVGA: Intel HD Graphics 610\r\n\r\nMàn hình: 15.6\" HD (1366 x 768) SVA BrightView WLED-Backlit\r\n\r\nHệ điều hành: Windows 10 Home\r\n\r\nPin: 3 Cell', 'hp7.jpg', 'hp7-1.jpg', 'hp7-2.jpg', 'hp7-3.jpg', 'hp7-4.jpg', 'hp7-5.jpg', 'hp7-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế đơn giản nhưng hiện đại</strong></span></h5>\r\n\r\n<p><strong>Laptop HP 15-da0358TU 6KD02PA</strong>&nbsp;sở hữu thiết kế đơn giản nhưng lại rất bền bỉ v&agrave; cứng c&aacute;p. C&aacute;c g&oacute;c cạnh của m&aacute;y được bo cong nhẹ nh&agrave;ng để tạo một cảm gi&aacute;c thoải m&aacute;i nhất cho người d&ugrave;ng trong suốt qu&aacute; tr&igrave;nh sử dụng. B&ecirc;n cạnh đ&oacute;, HP 15-da0358TU 6KD02PA cũng chỉ nặng 1.77 kg n&ecirc;n dễ d&agrave;ng để bạn c&oacute; thể bỏ v&agrave;o trong t&uacute;i x&aacute;ch, ba l&ocirc; để mang laptop đi bất cứ đ&acirc;u bạn muốn.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh lớn 15.6 inch</strong></span></h5>\r\n\r\n<p>Chiếc m&aacute;y t&iacute;nh sở hữu m&agrave;n h&igrave;nh LCD với k&iacute;ch thước ti&ecirc;u chuẩn 15.6 inch v&agrave; độ ph&acirc;n giải HD (1366 x 768). Kết hợp c&ocirc;ng nghệ hiển thị HD LED backlit, bạn sẽ c&oacute; một trải nghiệm xem tối ưu v&agrave; đ&aacute;p ứng tốt nhu cầu giải tr&iacute; cơ bản tr&ecirc;n chiếc laptop n&agrave;y.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hiệu năng ổn định</strong></span></h5>\r\n\r\n<p>Được trang bị con chip đến từ Intel - Pentium N4417U xung nhịp tối đa 2.30 GHz, bạn c&oacute; thể xử l&yacute; c&aacute;c t&aacute;c vụ hằng ng&agrave;y, c&ocirc;ng việc văn ph&ograve;ng, xem phim, lướt một c&aacute;ch đơn giản tr&ecirc;n HP 15-da0358TU. B&ecirc;n cạnh đ&oacute;, m&aacute;y t&iacute;nh c&oacute; 4GB RAM v&agrave; 500GB dung lượng lưu trữ HDD để cung cấp cho người d&ugrave;ng trải nghiệm đa nhiệm mượt m&agrave; v&agrave; lưu trữ dữ liệu thoải m&aacute;i.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m rộng r&atilde;i</strong></span></h5>\r\n\r\n<p>HP đ&atilde; nghi&ecirc;n cứu v&agrave; trang bị b&agrave;n ph&iacute;m hiện đại c&ugrave;ng TouchPad rộng tr&ecirc;n chiếc m&aacute;y t&iacute;nh n&agrave;y nhằm gi&uacute;p người d&ugrave;ng c&oacute; trải nghiệm nhập dữ liệu tối ưu. Khoảng c&aacute;ch giữa c&aacute;c ph&iacute;m bấm hợp l&yacute;, độ nảy cao v&agrave; h&agrave;nh tr&igrave;nh ph&iacute;m s&acirc;u tạo cảm gi&aacute;c nhập liệu nhanh ch&oacute;ng v&agrave; thoải m&aacute;i. HP 15 d0358U đủ kh&ocirc;ng gian cho cả b&agrave;n ph&iacute;m số ri&ecirc;ng biệt cho việc nhập liệu t&iacute;nh to&aacute;n. B&agrave;n di chuột TouchPad với diện t&iacute;ch lớn, hỗ trợ bạn sử dụng cảm ứng đa điểm để cuộn văn bản, xoay h&igrave;nh ảnh v&agrave; nhiều thao t&aacute;c tiện lợi.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hệ điều h&agrave;nh Windows 10 bản quyền</strong></span></h5>\r\n\r\n<p>Phần mềm ổn định l&agrave; điều rất quan trọng trong việc sử dụng m&aacute;y t&iacute;nh. Với hệ điều h&agrave;nh Windows 10 bản quyền được c&agrave;i sẵn, HP 15-da0358TU lu&ocirc;n được cập nhật những t&iacute;nh năng mới v&agrave; c&aacute;c bản v&aacute; lỗi thường xuy&ecirc;n, để bạn lu&ocirc;n sử dụng ổn định.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Kết nối đa dạng</strong></span></h5>\r\n\r\n<p>Chiếc laptop được trang bị đầy đủ c&aacute;c cổng kết nối như cổng HDMI ti&ecirc;u chuẩn, giắc cắm tai nghe 3.5mm/microphone, một cổng USB 2.0 v&agrave; 2 cổng USB 3.1, khe cắm thẻ v&agrave; cổng kết nối mạng Lan tiện dụng, cho ph&eacute;p bạn dễ d&agrave;ng kết nối m&aacute;y t&iacute;nh với c&aacute;c thiết bị kh&aacute;c phục vụ cho nhu cầu học tập v&agrave; l&agrave;m việc.</p>\r\n', 3, '2019-06-30 06:38:16', 'Bình thường', 'Bình thường', 1),
(69, 'HP Probook 430 G6 5YN01PA Core i7-8565U/Dos (13.3 FHD)', 20409000, 22990000, 30, 'CPU: Intel Core i7-8565U ( 1.8 GHz - 4.6 GHz / 8MB / 4 nhân, 8 luồng )\r\n\r\nMàn hình: 13.3\" ( 1920 x 1080 ) , không cảm ứng\r\n\r\nRAM: 1 x 8GB DDR4 2666MHz\r\n\r\nĐồ họa: Intel UHD Graphics 620\r\n\r\nLưu trữ: 1TB HDD 5400RPM\r\n\r\nHệ điều hành: Free DOS\r\n\r\nPin: 3 cell 45 Wh Pin liền', 'hp8.jpg', 'hp8-1.jpg', 'hp8-2.jpg', 'hp8-3.jpg', 'hp8-4.jpg', 'hp8-5.jpg', 'hp8-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế mỏng, đẹp mắt</strong></span></h5>\r\n\r\n<p><strong>Laptop HP Probook 430 G6 5YN01PA Core i7-8565U/Dos (13.3 FHD)</strong>&nbsp;với thiết kế đẹp mắt, m&aacute;y được thiết kế kh&aacute; mỏng, g&oacute;c cạnh của m&aacute;y được bo tr&ograve;n mềm mại đem tới vẻ đẹp sang&nbsp; trọng của d&ograve;ng sản phẩm laptop HP Probook. Trọng lượng HP ProBook 430 G6-5YN01PA kh&aacute; nhẹ chỉ 1.4kg mang lại sự thuận tiện khi cất trong t&uacute;i x&aacute;ch, balo để sử dụng mọi nơi mọi l&uacute;c m&agrave; kh&ocirc;ng cảm thấy vướng v&iacute;u kh&oacute; chịu.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>H&igrave;nh ảnh sắc n&eacute;t</strong></span></h5>\r\n\r\n<p>M&agrave;n h&igrave;nh 13,3&rdquo; Full HD với độ ph&acirc;n giải 1920 x 1080. Hỗ trợ chip m&agrave;n h&igrave;nh Intel UHD Graphics 620 thuộc d&ograve;ng ph&acirc;n kh&uacute;c hạng trung hỗ trợ xử l&yacute; tốt c&aacute;c phần mềm đồ họa chuy&ecirc;n dụng hay đ&aacute;p ứng tốt nhu cầu giải tr&iacute; kh&aacute;c của người d&ugrave;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Xử l&iacute; c&aacute;c t&aacute;c vụ mạnh mẽ</strong></span></h5>\r\n\r\n<p>HP ProBook 430 G6-5YN01PA (i7-8565U) (Bạc) sử dụng chip Intel Core i7 đ&aacute;p ứng được hầu hết tất cả c&aacute;c ti&ecirc;u chuẩn ứng dụng hiện nay. 8565U thuộc thế hệ thứ 8 với độ xung nhịp 1.8 GHz &ndash; 4.6 GHz với 4 nh&acirc;n 8 luồng mang lại tốc độ xử l&yacute; ti&ecirc;u chuẩn cung cấp &ldquo;kh&ocirc;ng gian&rdquo; l&agrave;m việc đảm bảo gi&uacute;p người d&ugrave;ng được trải nghiệm chuy&ecirc;n s&acirc;u. Kh&ocirc;ng đơn giản l&agrave; để đ&aacute;p ứng ứng dụng, độ xung nhịp lớn cũng gi&uacute;p người d&ugrave;ng chạy đa nhiệm tốt hơn v&agrave; mang đến sự mượt m&agrave; với nhiều tab tr&ecirc;n nền c&aacute;c tr&igrave;nh duyệt.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>N&acirc;ng cấp RAM mạnh mẽ</strong></span></h5>\r\n\r\n<p>Ram 8GB DDR4 2666MHz đảm bảo để người d&ugrave;ng c&oacute; thể thực hiện c&aacute;c thao t&aacute;c phục vụ cho c&ocirc;ng việc, giải tr&iacute; cũng như học tập của nh&acirc;n vi&ecirc;n văn ph&ograve;ng hay kể cả sinh vi&ecirc;n. Ngo&agrave;i ra c&ograve;n hỗ trợ 2 khe cắm d&agrave;nh cho ram tối đa 16GB thuận tiện cho việc n&acirc;ng cấp.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Thoải m&aacute;i lưu trữ</strong></span></h5>\r\n\r\n<p>Bộ nhớ m&aacute;y 1TB HDD 5400RPM với khả năng lưu trữ th&ocirc;ng tin khi được trang bị bộ nhớ 1TB gi&uacute;p người d&ugrave;ng c&oacute; thể thoải m&aacute;i sưu tập phim, nhạc, t&agrave;i liệu hay c&aacute;c game y&ecirc;u th&iacute;ch. B&ecirc;n cạnh đ&oacute; HP ProBook 430 hỗ trợ Webcam ri&ecirc;ng tr&ecirc;n m&aacute;y với độ ph&acirc;n giải HD.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Đa dạng cổng kết nối</strong></span></h5>\r\n\r\n<p>Kết nối với c&aacute;c chuẩn hiện nay như 1 cổng USB Type C, 2 cổng USB 3.0, 1 khe cắm thẻ nhớ SD, 1 cổng HDMI v&agrave; 1 cổng hỗ trợ Audio Jack thuận lợi cho việc kết nối với c&aacute;c thiết bị. Ngo&agrave;i kết nối bằng cổng, ProBook 430 G6-5YN01PA c&ograve;n hỗ trợ mạng LAN với tốc độ 1Gb/s, Bluetooth v&agrave; chuẩn Wifi 802 .11 ac th&ocirc;ng thường.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Pin liền tiện lợi</strong></span></h5>\r\n\r\n<p>Laptop sử dụng pin 4 cell 45 Wh liền b&ecirc;n trong tr&aacute;nh tạo n&ecirc;n c&aacute;c lỗ hổng n&ecirc;n &iacute;t gặp phải t&igrave;nh trạng b&aacute;m bụi b&ecirc;n trong bảng main gi&uacute;p gia tăng tuổi thọ cho sản phẩm.</p>\r\n', 3, '2019-07-01 06:38:16', 'Bình thường', 'Bình thường', 1);
INSERT INTO `product` (`id`, `product_name`, `price_new`, `price_old`, `qty_product`, `product_desc`, `product_thumb`, `list_thumb_1`, `list_thumb_2`, `list_thumb_3`, `list_thumb_4`, `list_thumb_5`, `list_thumb_6`, `product_content`, `cat_id`, `created_at`, `selling_products`, `featured_products`, `status`) VALUES
(70, 'HP Elitebook 1050 G1 5JJ71PA Core i7-8750H/GTX 1050/Dos (15.6\" FHD IPS)', 40809000, 44990000, 30, 'CPU: Intel Core i7-8750H (2.2 GHz - 4.1 GHz / 9MB / 6 nhân, 12 luồng)\r\n\r\nMàn hình: 15.6\" IPS (1920 x 1080) , không cảm ứng\r\n\r\nRAM: 1 x 16GB DDR4 2666MHz\r\n\r\nĐồ họa: Intel UHD Graphics 630/NVIDIA GeForce GTX 1050 4GB GDDR5\r\n\r\nLưu trữ: 512GB SSD M.2 NVMe\r\n\r\nHệ điều hành: Free DOS\r\n\r\nPin: 6 cell 95 Wh Pin liền , khối lượng: 2.6 kg', 'hp9.jpg', 'hp9-1.jpg', 'hp9-2.jpg', 'hp9-3.jpg', 'hp9-4.jpg', 'hp9-5.jpg', 'hp9-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế sang trọng</strong></span></h5>\r\n\r\n<p><strong>Laptop HP Elitebook 1050 G1 5JJ71PA Core i7-8750H/GTX 1050/Dos (15.6&quot; FHD IPS) - H&agrave;ng Ch&iacute;nh H&atilde;ng</strong>&nbsp;c&oacute; những thay đổi nhỏ về mặt thiết kế nhưng lại rất hữu dụng. Do với những mẫu EliteBook 800 Series G5 trước đ&acirc;y của h&atilde;ng th&igrave; 1050 G1 mang d&aacute;ng vẻ g&oacute;c cạnh hơn nhờ 2 g&oacute;c ph&iacute;a s&aacute;t bản lề được v&aacute;t ch&eacute;o 1 g&oacute;c 45 độ tr&ocirc;ng rất lạ v&agrave; ưa nh&igrave;n. Giờ đ&acirc;y th&igrave; d&ograve;ng laptop doanh nh&acirc;n EliteBook của h&atilde;ng đ&atilde; mang hơi hướng thiết kế giống với dải sản phẩm như envy, pavilion.</p>\r\n\r\n<p>C&oacute; thể n&oacute;i rằng kh&aacute;c với những d&ograve;ng EliteBook trước đ&acirc;y th&igrave; HP đ&atilde; bỏ kiểu thiết kế truyền thống của h&atilde;ng với những d&ograve;ng m&aacute;y doanh nghiệp, 1050 G1 mang thiết kế hiện đại hơn rất nhiều. Kh&ocirc;ng c&ograve;n trackpoint với những ph&iacute;m cứng ở touchpad hay kh&ocirc;ng c&ograve;n cụm ph&iacute;m nhập liệu numpad.</p>\r\n\r\n<p>To&agrave;n bộ vỏ m&aacute;y được gia c&ocirc;ng từ một miếng nh&ocirc;m nguy&ecirc;n khối hay c&ograve;n gọi l&agrave; thiết kế unibody, điều thường thấy tr&ecirc;n những d&ograve;ng Macbook của Apple. Nhờ thiết kế n&agrave;y v&agrave;&nbsp; chất lượng ho&agrave;n thiện rất tốt n&ecirc;n độ bền bỉ cũng như sự chắc chắn kh&ocirc;ng thua k&eacute;m g&igrave; những mẫu m&aacute;y Dell XPS hay Macbook trong c&ugrave;ng ph&acirc;n kh&uacute;c cao cấp. Phần mặt b&agrave;n ph&iacute;m v&agrave; k&ecirc; tay được cắt một đường diamond cut dọc xung quanh khung của m&aacute;y khiến chiếc laptop n&agrave;y tr&ocirc;ng c&agrave;ng g&oacute;c cạnh hơn nữa. C&aacute;ch gia c&ocirc;ng n&agrave;y khiến thiết kế unibody của m&aacute;y khi cầm nắm sẽ &iacute;t bị đau tay hơn khi so với thiết kế của những mẫu Macbook được v&aacute;t thẳng.</p>\r\n\r\n<p>Phần bản l&egrave; được ho&agrave;n thiện dưới dạng 2 bản lề đơn n&ecirc;n cảm gi&aacute;c mở m&aacute;y tốt hơn so với 850 G5 được l&agrave;m bản lề dạng 1 cạnh trải d&agrave;i. Việc mở m&aacute;y rất dễ d&agrave;ng khi bạn ho&agrave;n c&oacute; thể mở nắp m&aacute;y bằng 1 tay, điều m&agrave; XPS 15 kh&ocirc;ng l&agrave;m được. Tuy nhi&ecirc;n g&oacute;c mở kh&aacute; hẹp chỉ dừng lại ở mức 130 độ do đ&oacute; khi đặt m&aacute;y l&ecirc;n đ&ugrave;i l&agrave;m việc th&igrave; sẽ kh&aacute; kh&oacute; để c&oacute; g&oacute;c nh&igrave;n trực diện với m&agrave;n h&igrave;nh. B&ugrave; lại m&aacute;y được trang bị tấm nền IPS n&ecirc;n cũng kh&ocirc;ng bị thay đổi m&agrave;u khi nh&igrave;n ở g&oacute;c n&agrave;y.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hỗ trợ nhiều cổng kết nối</strong></span></h5>\r\n\r\n<p>HP EliteBook 1050 G1 (5JJ71PA) l&agrave; mẫu m&aacute;y hướng tới đối tượng l&agrave; doanh nh&acirc;n hay người sử dụng cao cấp n&ecirc;n số cổng kết nối cũng rất đầy đủ v&agrave; cao cấp. M&aacute;y c&oacute; tới 2 cổng USB Type-C hỗ trợ ThunderBolt 3 nằm ở cạnh phải, đ&acirc;y cũng l&agrave; nơi đặt cổng sạc v&agrave; cổng HDMI full size c&ugrave;ng với khe tho&aacute;t nhiệt, jack cắm tai nghe 3,5 mm v&agrave; khe thẻ SD. B&ecirc;n cạnh tr&aacute;i l&agrave; một khe tho&aacute;t nhiệt nữa v&agrave; 2 cổng USB Type A 3.0.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hiệu năng mạnh mẽ, ấn tượng</strong></span></h5>\r\n\r\n<p>HP EliteBook 1050 G1 (5JJ71PA) c&oacute; cấu h&igrave;nh rất tốt với một mẫu m&aacute;y cao cấp. CPU được trang bị tr&ecirc;n m&aacute;y l&agrave; Core I7 8750H 2.2Ghz v&agrave; boost l&ecirc;n tới 4.1GHz, đi k&egrave;m c&ugrave;ng với card đồ họa GTX 1050 4GB Max-Q, 16 GB RAM v&agrave; 512GB SSD chuẩn NVME. Cấu h&igrave;nh n&agrave;y đủ đ&aacute;p ứng mọi nhu cầu của người d&ugrave;ng hiện nay từ chơi game đến xử l&iacute; đồ họa cũng như l&agrave;m ảnh v&agrave; dựng phim. RAM đi k&egrave;m l&agrave; 16GB đ&atilde; kh&aacute; đủ d&ugrave;ng cho mọi nhu cầu hiện nay, ch&uacute;ng ta c&oacute; 2 khe cắm RAM v&agrave; hỗ trợ n&acirc;ng tối đa 64GB RAM.</p>\r\n\r\n<p>Đ&acirc;y cũng l&agrave; sản phẩm đầu ti&ecirc;n của HP &aacute;p dụng nguy&ecirc;n l&iacute; thiết kế Max-Q v&agrave;o mẫu laptop của h&atilde;ng. Với thiết kế n&agrave;y th&igrave; d&ugrave; m&aacute;y c&oacute; cấu h&igrave;nh rất cao nhưng vẫn c&oacute; k&iacute;ch thước mỏng nhẹ chỉ l&agrave; 1,89cm d&agrave;y v&agrave; 2,1Kg nặng m&agrave; vẫn giữ được khả năng tản nhiệt tốt. Trong qu&aacute; tr&igrave;nh thử nghiệm với những t&aacute;c vụ nặng k&eacute;o d&agrave;i th&igrave; nhiệt độ của CPU cũng chỉ l&ecirc;n tới mức tối đa l&agrave; 85 độ, n&ecirc;n nhớ rằng I7 8750H l&agrave; d&ograve;ng chip hiệu năng cao với 6 nh&acirc;n v&agrave; 12 luồng được đặt trong một mẫu m&aacute;y c&oacute; th&acirc;n h&igrave;nh kh&aacute; mỏng nhẹ th&igrave; đ&acirc;y l&agrave; mức nhiệt độ l&yacute; tưởng. Th&ecirc;m nữa hệ thống tản nhiệt của m&aacute;y c&ograve;n phải g&aacute;nh th&ecirc;m cả card 1050 4GB n&ecirc;n đ&acirc;y ho&agrave;n to&agrave;n l&agrave; mức nhiệt chấp nhận được.</p>\r\n\r\n<p>Trong c&ugrave;ng ph&acirc;n kh&uacute;c gi&aacute; th&igrave; c&oacute; kh&aacute; nhiều mẫu m&aacute;y kh&aacute;c nhau như Thinkpad X1 Extreme hay XPS 15 9570 đều được trang bị card đồ họa 1050Ti 4GB Max-Q cho hiệu năng tổng thể hơn từ 30-40%. Đ&acirc;y cũng l&agrave; một điểm yếu khi so với những sản phẩm c&ugrave;ng tầm tiền, tuy nhi&ecirc;n d&ugrave; kh&ocirc;ng phải l&agrave; một mẫu m&aacute;y gaming nhưng được trang bị card đồ họa 1050 4GB th&igrave; mẫu m&aacute;y n&agrave;y vẫn ho&agrave;n to&agrave;n c&oacute; thể chơi hầu hết c&aacute;c tựa game hiện nay ở mức khung h&igrave;nh tr&ecirc;n 60 fps.</p>\r\n\r\n<p>Khi sử dụng m&aacute;y m&agrave; kh&ocirc;ng cắm sạc th&igrave; hiệu năng của to&agrave;n bộ hệ thống sẽ giảm đi khoảng 50% để duy tr&igrave; thời lượng pin. Điều n&agrave;y kh&ocirc;ng qu&aacute; quan trọng bởi mỗi khi ch&uacute;ng ta cần xử l&iacute; những t&aacute;c vụ nặng k&eacute;o d&agrave;i th&igrave; thường lu&ocirc;n cắm sạc để duy tr&igrave; nguồn điện cho GPU lẫn CPU hoạt động một c&aacute;ch tốt nhất.</p>\r\n\r\n<p>HP c&ograve;n xử l&iacute; phần nhiệt độ tr&ecirc;n mẫu m&aacute;y n&agrave;y rất tốt khi phần bề mặt n&oacute;ng nhất l&agrave; khu vực ph&iacute;a bản lề nơi m&agrave; ch&uacute;ng ta &iacute;t khi chạm tới. C&ograve;n phần k&ecirc; tay v&agrave; b&agrave;n ph&iacute;m th&igrave; lu&ocirc;n duy tr&igrave; ở mức nhiệt độ từ 39-44 độ.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh độ ph&acirc;n giải cao độ bao phủ m&agrave;u 90% sRGB</strong></span></h5>\r\n\r\n<p>L&agrave; một mẫu m&aacute;y hướng tới đối tượng người d&ugrave;ng cao cấp n&ecirc;n m&agrave;n h&igrave;nh của HP EliteBook 1050 G1 (5JJ71PA) cũng c&oacute; chất lượng hiển thị xuất sắc kh&ocirc;ng k&eacute;m. Ở phi&ecirc;n bản trong b&agrave;i viết n&agrave;y th&igrave; tấm nền trang bị tr&ecirc;n m&aacute;y chỉ l&agrave; FullHD IPS nhưng độ bao phủ m&agrave;u đ&atilde; rất lớn l&ecirc;n tới gần 90% sRGB. C&ograve;n đối với phi&ecirc;n bản cao cấp hơn được trang bị m&agrave;n h&igrave;nh 4K th&igrave; độ bao phủ m&agrave;u l&agrave; 94%. HP cũng gọi đ&acirc;y l&agrave; m&agrave;n h&igrave;nh DreamColor giống như tr&ecirc;n chiếc HP Zbook Studio 15 bởi độ bao phủ m&agrave;u gần như tuyệt đối th&ecirc;m độ tương phản cao, điều n&agrave;y cũng gi&uacute;p chất lượng hiển thị của HP EliteBook 1050 G1 (5JJ71PA) kh&ocirc;ng hề thua k&eacute;m bất cứ một sản phẩm n&agrave;o trong c&ugrave;ng ph&acirc;n kh&uacute;c của n&oacute;.</p>\r\n\r\n<p>Viền m&agrave;n h&igrave;nh 2 b&ecirc;n kh&aacute; mỏng so với một mẫu m&aacute;y 15 inch, tuy nhi&ecirc;n cạnh tr&ecirc;n v&agrave; cạnh dưới lại tương đối d&agrave;y. Ở cạnh tr&ecirc;n ch&uacute;ng ta c&oacute; hệ thống mở kh&oacute;a khu&ocirc;n mặt 3d với 2 camera v&agrave; đ&egrave;n hồng ngoại. M&aacute;y vẫn đi k&egrave;m với cảm biến v&acirc;n tay gi&uacute;p ch&uacute;ng ta c&oacute; nhiều lựa chọn về việc mở kh&oacute;a m&aacute;y hơn.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m Fullsize thao t&aacute;c g&otilde; nhẹ nh&agrave;ng</strong></span></h5>\r\n\r\n<p>Kh&aacute;c với những d&ograve;ng m&aacute;y doanh nh&acirc;n 15 inch trước đ&acirc;y như EliteBook 850 G5 hay Zbook Studio G5 th&igrave; HP EliteBook 1050 G1 (5JJ71PA) đ&atilde; được lược bỏ đi phần ph&iacute;m numpad. HP phải chấp nhận đ&aacute;nh đổi bởi để c&oacute; thể c&acirc;n bằng giữa viền m&agrave;n h&igrave;nh mỏng v&agrave; hệ thống b&agrave;n ph&iacute;m fullsize th&igrave; đ&acirc;y được coi l&agrave; giải ph&aacute;p hữu hiệu nhất. Những mẫu m&aacute;y 15 inch trong c&ugrave;ng ph&acirc;n kh&uacute;c của c&aacute;c h&atilde;ng kh&aacute;c đều đ&atilde; bỏ b&agrave;n ph&iacute;m numpad như Thinkpad X1 Extreme hay XPS 15 9570 do đ&oacute; đ&acirc;y l&agrave; điều m&agrave; h&atilde;ng cần l&agrave;m v&agrave; c&oacute; thể l&agrave;m để gi&uacute;p tối ưu phần b&agrave;n ph&iacute;m cho m&aacute;y. Tất nhi&ecirc;n c&oacute; nhiều người sử dụng cần đến phần ph&iacute;m numpad n&agrave;y nhưng hiện nay th&igrave; phần lớn người d&ugrave;ng với nhu cầu multimedia đều kh&ocirc;ng cần tới cụm ph&iacute;m n&agrave;y.</p>\r\n\r\n<p>B&ugrave; lại chất lượng g&otilde; l&agrave; rất tốt, d&ugrave; m&aacute;y chỉ mỏng 1,89cm nhưng h&agrave;nh tr&igrave;nh ph&iacute;m lại rất s&acirc;u v&agrave; c&oacute; độ nảy tốt c&ugrave;ng với độ rộng ph&iacute;m hợp l&iacute; n&ecirc;n cảm gi&aacute;c g&otilde; rất tốt. Khung m&aacute;y cũng được gia cố rất chắc chắn n&ecirc;n khi g&otilde; nhanh th&igrave; m&aacute;y kh&ocirc;ng hề c&oacute; hiện tượng flex. C&oacute; thể n&oacute;i rằng b&agrave;n ph&iacute;m của 1050 G1 chỉ thua k&eacute;m phần n&agrave;o những mẫu m&aacute;y Thinkpad c&ograve;n nếu so với Macbook Pro hay Dell XPS th&igrave; chất lượng b&agrave;n ph&iacute;m đều vượt xa.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>TouchPad cho thao t&aacute;c di chuột nhẹ nh&agrave;ng</strong></span></h5>\r\n\r\n<p>Touchpad c&oacute; k&iacute;ch thước trung b&igrave;nh, kh&ocirc;ng qu&aacute; lớn như những mẫu m&aacute;y multimedia kh&aacute;c. Điều thay đổi lớn so với những mẫu m&aacute;y 15 inch cao cấp trước đ&acirc;y của HP ch&iacute;nh l&agrave; h&atilde;ng đ&atilde; quyết định bỏ đi trackpoint v&agrave; 3 ph&iacute;m cứng ở cụm touchpad gi&uacute;p thiết kế tổng thể tr&ocirc;ng liền mạch hơn. Touchpad được phủ k&iacute;nh n&ecirc;n cảm gi&aacute;c di rất tốt, nếu HP l&agrave;m phần touchpad n&agrave;y lớn hơn nữa th&igrave; sẽ gần như l&agrave; ho&agrave;n hảo.</p>\r\n', 3, '2019-07-07 07:09:08', 'Bình thường', 'Nổi bật', 1),
(71, 'HP ProBook 450 G5 2ZD47PA Core i5-8250U/Free Dos (15.6)/256GB SSD', 17540000, 18990000, 15, 'Chip: Intel Core i5-8250U (1.60GHz Up to 3.40 GHz, 4Cores, 8Threads, 6MB Cache, FSB 4GT/s)\r\n\r\nRAM: 4GB DDR4-2400MHz, 2 Slots, Max 32GB\r\n\r\nỔ cứng: 256GB SSD M.2 PCIe\r\n\r\nChipset đồ họa: NVIDIA GeForce 930MX 2GB DDR3 + Intel UHD Graphics 620\r\n\r\nMàn hình: 15.6-inch diagonal Full HD (1920 x 1080) SVA anti-glare LED-backlit, 220 cd/m2, 45% sRGB\r\n\r\nHệ điều hành: FreeDos\r\n\r\nPin: 3 Cell 48 WHr                                                                                                ', 'hp10.jpg', 'hp10-1.jpg', 'hp10-2.jpg', 'hp10-3.jpg', 'hp10-4.jpg', 'hp10-5.jpg', 'hp10-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế palmrest</strong></span></h5>\r\n\r\n<p><strong>Laptop HP ProBook 450 G5 2ZD47PA</strong>&nbsp;thiết kế palmrest bằng vật liệu nh&ocirc;m phay xước cho cảm gi&aacute;c m&aacute;t tay hơn khi sử dụng, các cạnh được bo tròn tạo cảm gi&aacute;c liền lạc, sang trọng và mang tính chuy&ecirc;n nghiệp hơn cho người d&ugrave;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/hp-2/hp-ct-30.jpg\" style=\"height:600px; width:900px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh LED 15.6 inch Full HD</strong></span></h5>\r\n\r\n<p>Được trang bị m&agrave;n h&igrave;nh LED 15.6 inch độ ph&acirc;n giải Full HD (1920 x 1080) n&ecirc;n cho chất lượng hiển thị kh&aacute; tốt khi h&igrave;nh ảnh được hiển thị sắc n&eacute;t, độ chi tiết cao v&agrave; m&agrave;u sắc trung thực ở khoảng c&aacute;ch sử dụng th&ocirc;ng thường của người d&ugrave;ng. Cùng tấm nền chống ch&oacute;i Anti-glare gi&uacute;p bạn c&oacute; thể trải nghiệm tốt hơn ở m&ocirc;i trường b&ecirc;n ngo&agrave;i, c&oacute; khả năng chống ch&oacute;i tốt.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/hp-2/hp-ct-31.jpg\" style=\"height:600px; width:900px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bộ vi xử l&yacute; Intel Core i5</strong></span></h5>\r\n\r\n<p>Được trang bị vi xử l&yacute;&nbsp;Intel Core i5-8250U xung nhịp 3.4GHz đi k&egrave;m l&agrave; dung lượng RAM 4GB DDR4, ổ cứng&nbsp;256GB SSD M.2 PCIe laptop đ&aacute;p ứng tốt nhu cầu sử dụng hằng ng&agrave;y của người d&ugrave;ng. M&aacute;y c&oacute; thể xử l&yacute; mượt m&agrave; v&agrave; trơn tru tất cả c&aacute;c t&aacute;c vụ thường ng&agrave;y của người d&ugrave;ng như duyệt web, xem phim,&hellip;</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/hp-2/hp-ct-32.jpg\" style=\"height:600px; width:900px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Card đồ họa NVIDIA GeForce 930MX</strong></span></h5>\r\n\r\n<p>Đặc biệt, với card đồ họa NVIDIA GeForce 930MX bạn c&oacute; thể ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m rằng m&aacute;y c&oacute; thể h&ocirc;̃ trợ bạn tối đa từ c&aacute;c việc đơn giản đến c&aacute;c c&ocirc;ng việc mang t&iacute;nh chất đ&ograve;i hỏi cao như thiết kế đồ họa, 3D, photoshop, cũng như giải tr&iacute; với những game 3D khủng nhất hiện nay.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>&Acirc;m thanh bang &amp; olufsen (B&amp;O PLAY)</strong></span></h5>\r\n\r\n<p>Laptop sẽ giúp bạn trải nghiệm chiều s&acirc;u &acirc;m nhạc mới đầy sống động v&agrave; ch&acirc;n thực với loa k&eacute;p, c&ocirc;ng nghệ khuếch &acirc;m HP Audio Boost v&agrave; &acirc;m thanh tinh chỉnh bởi c&aacute;c chuy&ecirc;n gia của B&amp;O PLAY.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/hp-2/hp-ct-33.jpg\" style=\"height:600px; width:900px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m c&oacute; đ&egrave;n nền v&agrave; Touchpad nhạy, hỗ trợ đa điểm</strong></span></h5>\r\n\r\n<p>Được trang bị b&agrave;n ph&iacute;m&nbsp;c&oacute; đ&egrave;n nền hiện đại với khoảng c&aacute;ch giữa c&aacute;c ph&iacute;m kh&aacute; hợp l&yacute;, bấm &ecirc;m v&agrave; h&agrave;nh tr&igrave;nh ph&iacute;m đủ s&acirc;u n&ecirc;n cho cảm gi&aacute;c g&otilde; kh&aacute; tốt, cho cảm gi&aacute;c thoải m&aacute;i d&ugrave; sử dụng trong một thời gian d&agrave;i. Chưa hết phần touchpad đi k&egrave;m cũng c&oacute; diện t&iacute;ch kh&aacute; rộng r&atilde;i, nhạy v&agrave; hỗ trợ đa điểm kh&aacute; tốt n&ecirc;n c&aacute;c thao t&aacute;c như thu ph&oacute;ng, cuộn văn bản hay xoay h&igrave;nh ảnh đều được thực hiện dễ d&agrave;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/hp-2/hp-ct-34.jpg\" style=\"height:599px; width:900px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>USB Type C tất cả trong một kết nối</strong></span></h5>\r\n\r\n<p>Laptop sở hữu cổng kết nối USB Type C đa dụng nhất hiện nay, với thiết kế đồng đẳng gi&uacute;p bạn c&oacute; thể cắm cổng kết nối ở bất cứ chiều n&agrave;o. Ngo&agrave;i ra với c&ocirc;ng nghệ n&agrave;y sẽ cho tốc độ truyền tải dữ liệu nhanh gấp 10 lần so với c&aacute;c cổng kết nối cũ hiện nay.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/hp-2/hp-ct-35.jpg\" style=\"height:600px; width:900px\" /></p>\r\n\r\n<p>HP ProBook 450 G5 2ZD47PA&nbsp;còn hỗ trợ nhiều cổng kết nối như jack &acirc;m thanh 3.5 mm, HDMI, Ethernet RJ45 c&ugrave;ng 1 khe thẻ nhớ SD media cho khả năng kết nối với c&aacute;c thiết bị kh&aacute;c một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Thời lượng pin l&acirc;u hơn</strong></span></h5>\r\n\r\n<p>Được thiết kế để ph&ugrave; hợp với phong c&aacute;ch sống năng động hiện nay,&nbsp;HP ProBook 450 G5 2ZD47PA&nbsp;sẽ cung cấp cho bạn thời lượng pin để c&oacute; thể sử dụng suốt cả ng&agrave;y. Với c&ocirc;ng nghệ pin Lithium Polymer sẽ cho m&aacute;y khả năng vận h&agrave;nh li&ecirc;n tục l&ecirc;n đến 8 tiếng đồng hồ. Điểm đặc biệt của d&ograve;ng pin Lithium Polymer c&oacute; thể duy tr&igrave; được năng lượng sạc lần đầu cho d&ugrave; sau h&agrave;ng trăm chu kỳ sạc, l&acirc;u hơn gấp 3 lần so với c&aacute;c loại pin ti&ecirc;u chuẩn hiện nay, gi&uacute;p bạn khỏi phải lo lắng về hao m&ograve;n hao m&ograve;n điện năng pin.</p>\r\n', 3, '2019-05-25 17:00:00', 'Bán chạy', 'Nổi bật', 1),
(72, 'HP Pavilion 15-cc157TX 3PN35PA Core i5-8250U/Win10 (15.6 inch)', 14569000, 15690000, 30, 'Chip: Intel Core i5-8250U (1.6GHz up to 3.4Ghz, 4 Cores 8 Threads)\r\n\r\nRAM: 4GB DDR4 2400MHz\r\n\r\nỔ cứng: HDD 1TB 5400rpm\r\n\r\nChipset đồ họa: NVIDIA Geforce GT940MX 2GB GDDR5\r\n\r\nMàn hình: 15.6 inch diagonal HD (1366 x 768) SVA BrightView WLED-backlit\r\n\r\nHệ điều hành: Windows 10 Home\r\n\r\nPin: 3 Cell 41.9 Whrs', 'hp11.jpg', 'hp11-1.jpg', 'hp11-2.jpg', 'hp11-3.jpg', 'hp11-4.jpg', 'hp11-5.jpg', 'hp11-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế kiểu d&aacute;ng cứng c&aacute;p</strong></span></h5>\r\n\r\n<p><strong>Laptop HP Pavilion 15-cc157TX 3PN35PA</strong><strong>&nbsp;</strong>được thiết kế kiểu d&aacute;ng cứng c&aacute;p với bốn g&oacute;c cạnh được bo tr&ograve;n vừa phải, mang đến vẻ đẹp mạnh mẽ v&agrave; đẳng cấp cho chiếc m&aacute;y, cùng với vẻ ngo&agrave;i đơn giản m&agrave; cứng c&aacute;p tạo cảm gi&aacute;c chắc tay cho người d&ugrave;ng khi cầm nắm, với trọng lượng 2kg m&aacute;y dễ d&agrave;ng bỏ v&agrave;o t&uacute;i x&aacute;ch để mang theo mọi l&uacute;c mọi nơi.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/hp-2/hp-ct-36.jpg\" style=\"height:439px; width:750px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh 15.6 inch&nbsp;HD</strong></span></h5>\r\n\r\n<p>Được trang bị m&agrave;n h&igrave;nh 15.6 inch độ ph&acirc;n giải HD&nbsp;(1366 x 768) c&ugrave;ng c&ocirc;ng nghệ HD SVA BrightView WLED-backlit c&oacute; chất lượng ho&agrave;n thiện kh&aacute; tốt, m&agrave;u sắc trung t&iacute;nh, viền m&agrave;n h&igrave;nh chắc chắn, c&aacute;c lớp hiển thị được &eacute;p chặt v&agrave;o nhau, hiển thị chi tiết đủ đ&aacute;p ứng nhu cầu giải tr&iacute; cơ bản của người d&ugrave;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Bộ vi xử l&yacute; Intel Core i5</strong></span></h5>\r\n\r\n<p>Được trang bị vi xử l&yacute;&nbsp;Intel Core i5-8250U xung nhịp 3.4GHz đi k&egrave;m l&agrave; dung lượng RAM 4GB DDR4, ổ cứng&nbsp;1TB HDD laptop đ&aacute;p ứng tốt nhu cầu sử dụng hằng ng&agrave;y của người d&ugrave;ng. M&aacute;y c&oacute; thể xử l&yacute; mượt m&agrave; v&agrave; trơn tru tất cả c&aacute;c t&aacute;c vụ thường ng&agrave;y của người d&ugrave;ng như duyệt web, xem phim,&hellip;</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Card đồ họa&nbsp;NVIDIA Geforce GT940MX</strong></span></h5>\r\n\r\n<p>Đặc biệt, với card đồ họa NVIDIA Geforce GT940MX b&ocirc;̣ nhớ đ&ocirc;̀ họa 2GB bạn c&oacute; thể ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m rằng m&aacute;y c&oacute; thể h&ocirc;̃ trợ bạn tối đa từ c&aacute;c việc đơn giản đến c&aacute;c c&ocirc;ng việc mang t&iacute;nh chất đ&ograve;i hỏi cao như thiết kế đồ họa, 3D, photoshop,... cũng như giải tr&iacute; với những game 3D khủng nhất hiện nay.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>&Acirc;m thanh bang &amp; olufsen (B&amp;O PLAY)</strong></span></h5>\r\n\r\n<p>Laptop sẽ giúp bạn trải nghiệm chiều s&acirc;u &acirc;m nhạc mới đầy sống động v&agrave; ch&acirc;n thực với loa k&eacute;p, c&ocirc;ng nghệ khuếch &acirc;m HP Audio Boost v&agrave; &acirc;m thanh tinh chỉnh bởi c&aacute;c chuy&ecirc;n gia của B&amp;O PLAY.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Ổ đĩa quang DVD-RW nhanh ch&oacute;ng</strong></span></h5>\r\n\r\n<p>Ổ đĩa quang DVD-RW c&oacute; thể đọc được hầu hết c&aacute;c định dạng đĩa như CD-ROM, DVD, cũng như ghi nội dung dữ liệu ra CD hay DVD một c&aacute;ch nhanh ch&oacute;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m v&agrave; Touchpad</strong></span></h5>\r\n\r\n<p>Được trang bị b&agrave;n ph&iacute;m hiện đại với khoảng c&aacute;ch giữa c&aacute;c ph&iacute;m kh&aacute; hợp l&yacute;, bấm &ecirc;m v&agrave; h&agrave;nh tr&igrave;nh ph&iacute;m đủ s&acirc;u n&ecirc;n cho cảm gi&aacute;c g&otilde; kh&aacute; tốt, cho cảm gi&aacute;c thoải m&aacute;i d&ugrave; sử dụng trong một thời gian d&agrave;i. Chưa hết phần touchpad đi k&egrave;m cũng c&oacute; diện t&iacute;ch kh&aacute; rộng r&atilde;i, nhạy v&agrave; hỗ trợ đa điểm kh&aacute; tốt n&ecirc;n c&aacute;c thao t&aacute;c như thu ph&oacute;ng, cuộn văn bản hay xoay h&igrave;nh ảnh đều được thực hiện dễ d&agrave;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>USB Type C tất cả trong một kết nối</strong></span></h5>\r\n\r\n<p>Laptop sở hữu cổng kết nối USB Type C đa dụng nhất hiện nay, với thiết kế đồng đẳng gi&uacute;p bạn c&oacute; thể cắm cổng kết nối ở bất cứ chiều n&agrave;o. Ngo&agrave;i ra với c&ocirc;ng nghệ n&agrave;y sẽ cho tốc độ truyền tải dữ liệu nhanh gấp 10 lần so với c&aacute;c cổng kết nối cũ hiện nay.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hỗ trợ nhiều cổng kết nối</strong></span></h5>\r\n\r\n<p>Với chuẩn wifi 802.11ac nhanh gấp 6 lần so với chuẩn wifi 802.11n. Hỗ trợ nhiều cổng kết nối như 2 c&ocirc;̉ng USB 3.1 Gen 1, 1 c&ocirc;̉ng USB 3.1 Type-C Gen 1, 1 jack &acirc;m thanh combo 3.5 mm, HDMI, Ethernet RJ45 c&ugrave;ng 1 khe thẻ nhớ SD media cho khả năng kết nối với c&aacute;c thiết bị kh&aacute;c một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Thời lượng pin l&acirc;u hơn</strong></span></h5>\r\n\r\n<p>Được thiết kế để ph&ugrave; hợp với phong c&aacute;ch sống năng động hiện nay,&nbsp;HP Pavilion 15-cc157TX 3PN35PA&nbsp;sẽ cung cấp cho bạn thời lượng pin để c&oacute; thể sử dụng suốt cả ng&agrave;y. Với c&ocirc;ng nghệ pin Lithium Polymer sẽ cho m&aacute;y khả năng vận h&agrave;nh li&ecirc;n tục l&ecirc;n đến 8 tiếng đồng hồ. Điểm đặc biệt của d&ograve;ng pin Lithium Polymer c&oacute; thể duy tr&igrave; được năng lượng sạc lần đầu cho d&ugrave; sau h&agrave;ng trăm chu kỳ sạc, l&acirc;u hơn gấp 3 lần so với c&aacute;c loại pin ti&ecirc;u chuẩn hiện nay, gi&uacute;p bạn khỏi phải lo lắng về hao m&ograve;n hao m&ograve;n điện năng pin.</p>\r\n', 3, '2019-06-17 17:00:00', 'Bình thường', 'Bình thường', 1),
(73, 'HP 15-da1030TX 5NM13PA Core i7-8565U/ MX130/ Win10 (15.6\" HD)', 16759000, 18990000, 30, 'CPU: Intel Core i7-8565U (1.8 GHz - 4.6 GHz / 8MB / 4 nhân, 8 luồng)\r\n\r\nMàn hình: 15.6\" (1366 x 768), không cảm ứng\r\n\r\nRAM: 1 x 8GB DDR4 2400MHz\r\n\r\nĐồ họa: Intel UHD Graphics 620 / NVIDIA GeForce MX130 2GB GDDR5\r\n\r\nLưu trữ: 1TB HDD 5400RPM\r\n\r\nHệ điều hành: Windows 10 Home SL 64-bit\r\n\r\nPin: 3 cell 41 Wh, Pin liền', 'hp12.jpg', 'hp12-1.jpg', 'hp12-2.jpg', 'hp12-3.jpg', 'hp12-4.jpg', 'hp12-5.jpg', 'hp12-6.jpg', '<h5><span style=\"font-size:24px\"><strong>Thiết kế chắc chắn</strong></span></h5>\r\n\r\n<p><strong>Laptop HP 15-da1030TX 5NM13PA</strong>&nbsp;c&oacute; phần vỏ m&aacute;y cứng c&aacute;p chắc chắn c&oacute; thể bảo vệ tốt c&aacute;c linh kiện b&ecirc;n trong, kết hợp với t&ocirc;ng m&agrave;u bạc đem đến cho sản phẩm n&eacute;t đẹp tinh tế, sang trọng. Để đem lại cảm gi&aacute;c thoải m&aacute;i, tiện lợi tối ưu cho người d&ugrave;ng m&aacute;y đ&atilde; được thiết kế si&ecirc;u linh hoạt v&agrave; c&aacute;c g&oacute;c cạnh được bo tr&ograve;n nhẹ. Với k&iacute;ch thước tổng thể 37.6 x 24.6 x 2.25 cm v&agrave; trọng lượng 1.77kg bạn c&oacute; thể bỏ m&aacute;y v&agrave;o t&uacute;i x&aacute;ch hoặc balo v&agrave; mang theo b&ecirc;n m&igrave;nh đến bất cứ nơi n&agrave;o m&agrave; bạn muốn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/hp-1/hp-ct-37.jpg\" style=\"height:788px; width:1354px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>M&agrave;n h&igrave;nh lớn 15.6 inch</strong></span></h5>\r\n\r\n<p>Laptop HP trang bị c&ocirc;ng nghệ m&agrave;n h&igrave;nh SVA BrightView WLED-backlit HD c&ugrave;ng với k&iacute;ch thước m&agrave;n h&igrave;nh 15.6&quot; mang chất lượng hiểm thị tươi tắn, r&otilde; n&eacute;t dưới c&aacute;c điều kiện &aacute;nh s&aacute;ng kh&aacute;c nhau, mang đến những gi&acirc;y ph&uacute;t giải tr&iacute; tuyệt vời cho người d&ugrave;ng với &acirc;m thanh to r&otilde;, h&igrave;nh ảnh r&otilde; r&agrave;ng, m&agrave;u sắc trung thực.&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/hp-1/hp-ct-38.jpg\" style=\"height:406px; width:677px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Hiệu năng vượt trội</strong></span></h5>\r\n\r\n<p>Laptop HP 15-da1030TX 5NM13PA được trang bị bộ vi xử l&yacute; Intel Core i7 thế hệ 8 v&agrave; bộ nhớ Ram 8GB DDR4 đảm bảo xử l&yacute; c&aacute;c t&aacute;c vụ mượt m&agrave; ổn định. B&ecirc;n cạnh đ&oacute; m&aacute;y c&ograve;n được trang bị ổ cứng HDD dung lượng 1TB cho bạn thoải m&aacute;i lưu trữ c&aacute;c bộ phim, b&agrave;i h&aacute;t y&ecirc;u th&iacute;ch cũng như c&aacute;c t&agrave;i liệu quan trọng. Card m&agrave;n h&igrave;nh Geforce MX130 2GB gi&uacute;p m&aacute;y t&iacute;nh khởi động rất nhanh, xử l&iacute; c&aacute;c t&aacute;c xụ văn ph&ograve;ng v&agrave; tất cả loại game một c&aacute;ch rất mượt m&agrave;.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/hp-1/hp-ct-39.jpg\" style=\"height:406px; width:677px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>B&agrave;n ph&iacute;m &ecirc;m &aacute;i</strong></span></h5>\r\n\r\n<p>B&agrave;n ph&iacute;m bấm &ecirc;m &aacute;i, độ nảy tốt, hạn chế g&acirc;y tiếng ồn, gi&uacute;p người d&ugrave;ng thao t&aacute;c chuẩn x&aacute;c, đ&aacute;nh m&aacute;y hiệu quả từ đ&oacute; tăng hiệu suất l&agrave;m việc của bạn. Touchpad cảm ứng đa điểm cực nhạy.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/files/hp-1/hp-ct-40.jpg\" style=\"height:406px; width:677px\" /></p>\r\n\r\n<h5><span style=\"font-size:24px\"><strong>Đầy đủ c&aacute;c cổng kết nối</strong></span></h5>\r\n\r\n<p>Chiếc laptop được trang bị đầy đủ c&aacute;c cổng giao tiếp chuy&ecirc;n dụng nhất hiện nay như USB 2.0, USB 3.1, LAN, Khe thẻ nhớ SD&hellip; Ngo&agrave;i ra m&aacute;y c&ograve;n được trang bị th&ecirc;m cổng HDMI gi&uacute;p bạn kết nối với m&aacute;y chiếu một c&aacute;ch dễ d&agrave;ng.</p>\r\n', 3, '2019-06-20 17:00:00', 'Bình thường', 'Bình thường', 1),
